import { useEffect, useState, useCallback } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Activity,
  Brain,
  BarChart3,
  TrendingUp,
  AlertTriangle,
  RefreshCw,
  CheckCircle,
  Clock
} from "lucide-react";
import { analyticsManager } from "@/lib/analyticsManager";
import { dataStorage } from "@/lib/dataStorage";
import { formatDistanceToNow } from "date-fns";
import { logger } from "@/lib/logger";

/**
 * Represents the analytics status for a single student
 */
interface AnalyticsStatus {
  studentId: string;
  studentName: string;
  isInitialized: boolean;
  lastAnalyzed: Date | null;
  healthScore: number;
  hasMinimumData: boolean;
}

interface AnalyticsStatusIndicatorProps {
  studentId?: string;
  showDetails?: boolean;
  className?: string;
}

/**
 * AnalyticsStatusIndicator Component
 * 
 * Displays the current analytics health and status for one or all students.
 * Auto-refreshes every 30 seconds to show real-time status updates.
 * 
 * @component
 * @param {AnalyticsStatusIndicatorProps} props - Component props
 * @param {string} [props.studentId] - Optional student ID to filter analytics
 * @param {boolean} [props.showDetails=false] - Whether to show detailed analytics systems status
 * @param {string} [props.className=""] - Additional CSS classes
 */
export const AnalyticsStatusIndicator = ({ 
  studentId, 
  showDetails = false, 
  className = "" 
}: AnalyticsStatusIndicatorProps) => {
  const [analyticsStatus, setAnalyticsStatus] = useState<AnalyticsStatus[]>([]);
  const [isRefreshing, setIsRefreshing] = useState(false);

  /**
   * Load analytics status from the analytics manager.
   * Memoized to prevent recreation on every render.
   * Filters by studentId if provided.
   */
  const loadAnalyticsStatus = useCallback(() => {
    const status = analyticsManager.getAnalyticsStatus() as AnalyticsStatus[];
    if (studentId) {
      setAnalyticsStatus(status.filter(s => s.studentId === studentId));
    } else {
      setAnalyticsStatus(status);
    }
  }, [studentId]);

  /**
   * Effect to load analytics status and set up auto-refresh.
   * Properly includes loadAnalyticsStatus in dependencies.
   */
  useEffect(() => {
    // Initial load
    loadAnalyticsStatus();

    // Set up auto-refresh every 30 seconds
    const interval = setInterval(loadAnalyticsStatus, 30000);

    // Cleanup on unmount
    return () => clearInterval(interval);
  }, [loadAnalyticsStatus]);

  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      if (studentId) {
        // Get the full Student object from storage
        const student = dataStorage.getStudentById(studentId);
        if (student) {
          await analyticsManager.triggerAnalyticsForStudent(student);
        } else {
          logger.warn('Student not found for refresh', { studentId });
        }
      } else {
        await analyticsManager.triggerAnalyticsForAllStudents();
      }
      loadAnalyticsStatus();
    } catch (error) {
      logger.error('Error refreshing analytics', error);
    } finally {
      setIsRefreshing(false);
    }
  };

  const getStatusIcon = (status: AnalyticsStatus) => {
    if (!status.isInitialized) return <Clock className="h-4 w-4 text-muted-foreground" />;
    if (status.healthScore >= 80) return <CheckCircle className="h-4 w-4 text-green-600" />;
    if (status.healthScore >= 60) return <TrendingUp className="h-4 w-4 text-yellow-600" />;
    if (status.healthScore >= 40) return <Activity className="h-4 w-4 text-orange-600" />;
    return <AlertTriangle className="h-4 w-4 text-red-600" />;
  };

  const getStatusColor = (status: AnalyticsStatus): "default" | "secondary" | "destructive" | "outline" => {
    if (!status.isInitialized) return "secondary";
    if (status.healthScore >= 80) return "default";
    if (status.healthScore >= 60) return "secondary";
    return "destructive";
  };

  const getStatusText = (status: AnalyticsStatus): string => {
    if (!status.isInitialized) return "Initializing";
    if (status.healthScore >= 80) return "Excellent";
    if (status.healthScore >= 60) return "Good";
    if (status.healthScore >= 40) return "Fair";
    return "Limited";
  };

  if (!showDetails && studentId && analyticsStatus.length === 1) {
    const status = analyticsStatus[0];
    return (
      <div className={`flex items-center gap-2 ${className}`}>
        {getStatusIcon(status)}
        <Badge variant={getStatusColor(status)} className="text-xs">
          Analytics: {getStatusText(status)}
        </Badge>
        {status.lastAnalyzed && (
          <span className="text-xs text-muted-foreground">
            Updated {formatDistanceToNow(status.lastAnalyzed, { addSuffix: true })}
          </span>
        )}
      </div>
    );
  }

  return (
    <Card className={`bg-gradient-card border-0 shadow-soft ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5" />
            Analytics Status
            {studentId && ` - ${analyticsStatus[0]?.studentName || 'Student'}`}
          </CardTitle>
          <Button
            variant="outline"
            size="sm"
            onClick={handleRefresh}
            disabled={isRefreshing}
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${isRefreshing ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {analyticsStatus.length === 0 ? (
          <div className="text-center py-4 text-muted-foreground">
            <Activity className="h-8 w-8 mx-auto mb-2 opacity-50" />
            <p>No analytics data available</p>
          </div>
        ) : (
          <div className="space-y-4">
            {analyticsStatus.map((status) => (
              <div key={status.studentId} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <div className="flex items-center gap-3">
                  {getStatusIcon(status)}
                  <div>
                    <p className="font-medium text-foreground">
                      {studentId ? 'Analytics Systems' : status.studentName}
                    </p>
                    <div className="flex items-center gap-2 mt-1">
                      <Badge variant={getStatusColor(status)} className="text-xs">
                        {getStatusText(status)} ({status.healthScore}%)
                      </Badge>
                      {status.hasMinimumData ? (
                        <Badge variant="outline" className="text-xs">
                          <BarChart3 className="h-3 w-3 mr-1" />
                          Data Available
                        </Badge>
                      ) : (
                        <Badge variant="secondary" className="text-xs">
                          <Clock className="h-3 w-3 mr-1" />
                          Collecting Data
                        </Badge>
                      )}
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-sm text-muted-foreground">
                    {status.lastAnalyzed ? (
                      <>
                        Last updated:<br />
                        {formatDistanceToNow(status.lastAnalyzed, { addSuffix: true })}
                      </>
                    ) : (
                      'Never analyzed'
                    )}
                  </div>
                </div>
              </div>
            ))}

            {showDetails && (
              <div className="pt-4 border-t border-border">
                <h4 className="font-medium text-foreground mb-3">Active Analytics Systems:</h4>
                <div className="grid grid-cols-2 gap-3">
                  <div className="flex items-center gap-2 text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600" />
                    Pattern Analysis
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600" />
                    Correlation Analysis
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600" />
                    Predictive Insights
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600" />
                    Anomaly Detection
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600" />
                    Alert System
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600" />
                    Auto-Updates
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
};

import React, { memo } from 'react';
import { Info } from 'lucide-react';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { useTranslation } from '@/hooks/useTranslation';

interface ConfidenceIndicatorProps {
  confidence: number;
  dataPoints?: number;
  timeSpanDays?: number;
  rSquared?: number;
  className?: string;
}

export const ConfidenceIndicator = memo<ConfidenceIndicatorProps>(({
  confidence,
  dataPoints = 0,
  timeSpanDays = 0,
  rSquared = 0,
  className = ''
}) => {
  const { tAnalytics } = useTranslation();

  const getConfidenceLevel = (): 'low' | 'medium' | 'high' => {
    if (confidence >= 0.7) return 'high';
    if (confidence >= 0.4) return 'medium';
    return 'low';
  };

  const getConfidenceColor = () => {
    const level = getConfidenceLevel();
    switch (level) {
      case 'high': return 'text-emerald-600';
      case 'medium': return 'text-amber-600';
      case 'low': return 'text-orange-600';
    }
  };

  const generateExplanations = (): string[] => {
    const explanations: string[] = [];

    if (dataPoints < 10) {
      explanations.push(String(tAnalytics('confidence.explanations.insufficientData', { 
        count: dataPoints, 
        minimum: 10 
      })));
    }

    if (timeSpanDays < 14) {
      explanations.push(String(tAnalytics('confidence.explanations.shortTimespan', { 
        days: timeSpanDays, 
        recommended: 21 
      })));
    }

    if (rSquared < 0.3) {
      explanations.push(String(tAnalytics('confidence.explanations.weakPattern', { 
        rsquared: rSquared.toFixed(3) 
      })));
    } else if (rSquared > 0.7) {
      explanations.push(String(tAnalytics('confidence.explanations.strongPattern', { 
        rsquared: rSquared.toFixed(3) 
      })));
    } else if (rSquared > 0.4) {
      explanations.push(String(tAnalytics('confidence.explanations.moderatePattern')));
    }

    if (explanations.length === 0) {
      if (confidence >= 0.8) {
        explanations.push(String(tAnalytics('confidence.explanations.excellentData')));
      } else if (confidence >= 0.6) {
        explanations.push(String(tAnalytics('confidence.explanations.reliableInsight')));
      } else if (confidence >= 0.3) {
        explanations.push(String(tAnalytics('confidence.explanations.emergingTrend')));
      } else {
        explanations.push(String(tAnalytics('confidence.explanations.needMoreData')));
      }
    }

    return explanations;
  };

  const level = getConfidenceLevel();
  const explanations = generateExplanations();

  return (
    <TooltipProvider>
      <Tooltip>
        <TooltipTrigger asChild>
          <div className={`inline-flex items-center gap-1 cursor-help ${className}`}>
            <span className={`text-sm font-medium ${getConfidenceColor()}`}>
              {Math.round(confidence * 100)}%
            </span>
            <Info className={`h-3 w-3 ${getConfidenceColor()}`} />
          </div>
        </TooltipTrigger>
        <TooltipContent side="bottom" className="max-w-xs">
          <div className="space-y-2">
            <div className="font-medium">
              {String(tAnalytics(`confidence.${level}`))}
            </div>
            <div className="space-y-1">
              {explanations.map((explanation, index) => (
                <div key={index} className="text-xs text-muted-foreground">
                   {explanation}
                </div>
              ))}
            </div>
            <div className="text-xs text-muted-foreground pt-1 border-t border-border">
              {String(tAnalytics('confidence.tooltip'))}
            </div>
          </div>
        </TooltipContent>
      </Tooltip>
    </TooltipProvider>
  );
});

ConfidenceIndicator.displayName = 'ConfidenceIndicator';

import React from 'react';
import { ResponsiveHeatMap } from '@nivo/heatmap';
import { CorrelationResult } from '@/lib/patternAnalysis';

interface CorrelationHeatmapProps {
  data: CorrelationResult[];
}

export const CorrelationHeatmap: React.FC<CorrelationHeatmapProps> = ({ data }) => {
  const factors = Array.from(new Set(data.flatMap(d => [d.factor1, d.factor2])));
  const chartData = factors.map(factor1 => ({
    id: factor1,
    data: factors.map(factor2 => ({
      x: factor2,
      y: data.find(d => (d.factor1 === factor1 && d.factor2 === factor2) || (d.factor1 === factor2 && d.factor2 === factor1))?.correlation ?? 0,
    })),
  }));

  return (
    <div className="h-[400px]">
      <ResponsiveHeatMap
        data={chartData}
        keys={factors}
        indexBy="id"
        margin={{ top: 100, right: 60, bottom: 60, left: 60 }}
        axisTop={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: -90,
          legend: '',
          legendOffset: -70,
        }}
        axisLeft={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: '',
          legendOffset: -40,
        }}
        colors="bwr"
        cellOpacity={1}
        cellBorderWidth={1}
        cellBorderColor={{ from: 'color', modifiers: [['darker', 0.4]] }}
        labelTextColor={{ from: 'color', modifiers: [['brighter', 1.8]] }}
        defs={[
          {
            id: 'lines',
            type: 'patternLines',
            background: 'inherit',
            color: 'rgba(0, 0, 0, 0.1)',
            rotation: -45,
            lineWidth: 4,
            spacing: 7,
          },
        ]}
        fill={[{ id: 'lines' }]}
        animate={true}
        motionStiffness={80}
        motionDamping={9}
        hoverTarget="cell"
        cellHoverOthersOpacity={0.25}
      />
    </div>
  );
};

