import React, { useMemo, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { EmotionEntry, SensoryEntry, TrackingEntry, Student } from '@/types/student';
import { enhancedPatternAnalysis } from '@/lib/enhancedPatternAnalysis';
import { patternAnalysis } from '@/lib/patternAnalysis';
import { differenceInDays, format, subDays } from 'date-fns';
import {
  Brain,
  TrendingUp,
  TrendingDown,
  Eye,
  Lightbulb,
  Target,
  AlertTriangle,
  CheckCircle,
  Activity,
  BarChart3,
  Clock,
  Zap,
  Heart,
  Shield
} from 'lucide-react';

interface EnhancedPersonalizedInsightsProps {
  student: Student;
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  trackingEntries: TrackingEntry[];
}

export const EnhancedPersonalizedInsights: React.FC<EnhancedPersonalizedInsightsProps> = ({
  student,
  emotions,
  sensoryInputs,
  trackingEntries
}) => {
  const [selectedTimeframe, setSelectedTimeframe] = useState<'7d' | '30d' | '90d'>('30d');

  // Filter data by selected timeframe
  const filteredData = useMemo(() => {
    const cutoff = subDays(new Date(), selectedTimeframe === '7d' ? 7 : selectedTimeframe === '30d' ? 30 : 90);
    return {
      emotions: emotions.filter(e => e.timestamp >= cutoff),
      sensoryInputs: sensoryInputs.filter(s => s.timestamp >= cutoff),
      trackingEntries: trackingEntries.filter(t => t.timestamp >= cutoff)
    };
  }, [emotions, sensoryInputs, trackingEntries, selectedTimeframe]);

  // Generate comprehensive insights
  const insights = useMemo(() => {
    if (filteredData.emotions.length === 0 && filteredData.sensoryInputs.length === 0) {
      return null;
    }

    const predictiveInsights = enhancedPatternAnalysis.generatePredictiveInsights(
      filteredData.emotions,
      filteredData.sensoryInputs,
      filteredData.trackingEntries
    );

    const anomalies = enhancedPatternAnalysis.detectAnomalies(
      filteredData.emotions,
      filteredData.sensoryInputs,
      filteredData.trackingEntries
    );

    const emotionPatterns = patternAnalysis.analyzeEmotionPatterns(filteredData.emotions);
    const sensoryPatterns = patternAnalysis.analyzeSensoryPatterns(filteredData.sensoryInputs);

    return {
      predictiveInsights,
      anomalies,
      emotionPatterns,
      sensoryPatterns
    };
  }, [filteredData]);

  // Personal strengths analysis
  const personalStrengths = useMemo(() => {
    const strengths = [];
    const positiveEmotions = filteredData.emotions.filter(e => 
      ['happy', 'calm', 'focused', 'proud', 'excited'].includes(e.emotion.toLowerCase())
    );

    if (positiveEmotions.length > filteredData.emotions.length * 0.6) {
      strengths.push({
        title: 'Emotional Resilience',
        description: `${student.name} maintains positive emotions ${Math.round(positiveEmotions.length / filteredData.emotions.length * 100)}% of the time`,
        icon: Heart,
        confidence: 0.8
      });
    }

    const consistentSensory = filteredData.sensoryInputs.filter(s => 
      s.response.toLowerCase().includes('appropriate') || 
      s.response.toLowerCase().includes('comfortable')
    );

    if (consistentSensory.length > filteredData.sensoryInputs.length * 0.5) {
      strengths.push({
        title: 'Sensory Self-Regulation',
        description: `Shows good sensory processing in ${Math.round(consistentSensory.length / filteredData.sensoryInputs.length * 100)}% of situations`,
        icon: Shield,
        confidence: 0.7
      });
    }

    return strengths;
  }, [filteredData, student.name]);

  // Growth opportunities
  const growthOpportunities = useMemo(() => {
    const opportunities = [];

    const challengingEmotions = filteredData.emotions.filter(e => 
      e.intensity >= 4 && ['anxious', 'frustrated', 'overwhelmed'].includes(e.emotion.toLowerCase())
    );

    if (challengingEmotions.length > 0) {
      opportunities.push({
        title: 'Stress Management',
        description: `Opportunities to develop coping strategies for high-intensity emotions`,
        recommendations: [
          'Practice deep breathing exercises',
          'Implement sensory breaks',
          'Create calm-down strategies'
        ],
        priority: 'high'
      });
    }

    const sensorySeekingCount = filteredData.sensoryInputs.filter(s => 
      s.response.toLowerCase().includes('seeking')
    ).length;

    if (sensorySeekingCount > filteredData.sensoryInputs.length * 0.7) {
      opportunities.push({
        title: 'Sensory Regulation',
        description: `High sensory seeking behavior suggests need for structured sensory input`,
        recommendations: [
          'Schedule regular sensory breaks',
          'Provide fidget tools',
          'Create sensory-rich learning activities'
        ],
        priority: 'medium'
      });
    }

    return opportunities;
  }, [filteredData]);

  // Individual progress metrics
  const progressMetrics = useMemo(() => {
    const totalDays = differenceInDays(new Date(), filteredData.emotions[0]?.timestamp || new Date());
    const avgDailyEmotions = filteredData.emotions.length / Math.max(1, totalDays);
    const avgEmotionIntensity = filteredData.emotions.reduce((sum, e) => sum + e.intensity, 0) / Math.max(1, filteredData.emotions.length);

    const recentWeek = filteredData.emotions.filter(e => e.timestamp >= subDays(new Date(), 7));
    const recentAvgIntensity = recentWeek.reduce((sum, e) => sum + e.intensity, 0) / Math.max(1, recentWeek.length);

    const intensityTrend = recentAvgIntensity - avgEmotionIntensity;

    return {
      dataConsistency: Math.min(100, (avgDailyEmotions / 3) * 100), // Target 3 emotions per day
      emotionalStability: Math.max(0, 100 - (avgEmotionIntensity * 20)), // Lower intensity = more stable
      intensityTrend,
      totalDataPoints: filteredData.emotions.length + filteredData.sensoryInputs.length
    };
  }, [filteredData]);

  if (!insights) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lightbulb className="h-5 w-5" />
            Personalized Insights for {student.name}
          </CardTitle>
        </CardHeader>
        <CardContent className="text-center py-8">
          <Activity className="h-16 w-16 mx-auto mb-4 opacity-50 text-muted-foreground" />
          <p className="text-muted-foreground">Start tracking emotions and sensory inputs to generate personalized insights</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with timeframe selector */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Lightbulb className="h-5 w-5 text-primary" />
              Personalized Insights for {student.name}
            </CardTitle>
            <div className="flex gap-2">
              {(['7d', '30d', '90d'] as const).map((timeframe) => (
                <Button
                  key={timeframe}
                  variant={selectedTimeframe === timeframe ? 'default' : 'outline'}
                  size="sm"
                  onClick={() => setSelectedTimeframe(timeframe)}
                >
                  {timeframe === '7d' ? '7 days' : timeframe === '30d' ? '30 days' : '90 days'}
                </Button>
              ))}
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{progressMetrics.totalDataPoints}</div>
              <div className="text-sm text-muted-foreground">Total Data Points</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{Math.round(progressMetrics.dataConsistency)}%</div>
              <div className="text-sm text-muted-foreground">Data Consistency</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{Math.round(progressMetrics.emotionalStability)}%</div>
              <div className="text-sm text-muted-foreground">Emotional Stability</div>
            </div>
            <div className="text-center">
              <div className="flex items-center justify-center gap-1">
                <span className="text-2xl font-bold text-primary">
                  {progressMetrics.intensityTrend > 0 ? '+' : ''}{progressMetrics.intensityTrend.toFixed(1)}
                </span>
                {progressMetrics.intensityTrend > 0 ? 
                  <TrendingUp className="h-4 w-4 text-red-500" /> : 
                  <TrendingDown className="h-4 w-4 text-green-500" />
                }
              </div>
              <div className="text-sm text-muted-foreground">Intensity Trend</div>
            </div>
          </div>
        </CardContent>
      </Card>

      <Tabs defaultValue="insights" className="w-full">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="insights">AI Insights</TabsTrigger>
          <TabsTrigger value="strengths">Strengths</TabsTrigger>
          <TabsTrigger value="growth">Growth Areas</TabsTrigger>
          <TabsTrigger value="patterns">Patterns</TabsTrigger>
          <TabsTrigger value="anomalies">Alerts</TabsTrigger>
        </TabsList>

        <TabsContent value="insights" className="space-y-4">
          {insights.predictiveInsights.length > 0 ? (
            insights.predictiveInsights.map((insight, index) => (
              <Card key={index} className="border-l-4 border-l-primary">
                <CardContent className="pt-4">
                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-3 flex-1">
                      <div className="p-2 rounded-full bg-primary/10">
                        {insight.type === 'prediction' ? <Target className="h-4 w-4 text-primary" /> :
                         insight.type === 'trend' ? <TrendingUp className="h-4 w-4 text-primary" /> :
                         insight.type === 'risk' ? <AlertTriangle className="h-4 w-4 text-orange-500" /> :
                         <Lightbulb className="h-4 w-4 text-primary" />}
                      </div>
                      <div className="flex-1">
                        <h4 className="font-semibold text-foreground">{insight.title}</h4>
                        <p className="text-sm text-muted-foreground mt-1">{insight.description}</p>
                        {insight.prediction && (
                          <div className="mt-2 p-2 bg-muted/50 rounded-lg">
                            <div className="text-sm font-medium">
                              Forecast: {insight.prediction.value.toFixed(1)} 
                              <Badge variant="outline" className="ml-2">
                                {insight.prediction.trend}
                              </Badge>
                            </div>
                          </div>
                        )}
                        {insight.recommendations.length > 0 && (
                          <div className="mt-3">
                            <h5 className="text-sm font-medium mb-2">Recommendations:</h5>
                            <ul className="text-sm text-muted-foreground space-y-1">
                              {insight.recommendations.map((rec, recIndex) => (
                                <li key={recIndex} className="flex items-start gap-2">
                                  <CheckCircle className="h-3 w-3 mt-0.5 text-green-500 flex-shrink-0" />
                                  <span>{rec}</span>
                                </li>
                              ))}
                            </ul>
                          </div>
                        )}
                      </div>
                    </div>
                    <div className="text-right">
                      <Badge 
                        variant={insight.confidence > 0.7 ? 'default' : 'outline'}
                        className={
                          insight.confidence > 0.7 ? 'bg-green-100 text-green-800' :
                          insight.confidence > 0.4 ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }
                      >
                        {Math.round(insight.confidence * 100)}% confidence
                      </Badge>
                      <div className="text-xs text-muted-foreground mt-1">
                        {insight.timeframe}
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Brain className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Continue collecting data to generate predictive insights</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="strengths" className="space-y-4">
          {personalStrengths.length > 0 ? (
            personalStrengths.map((strength, index) => (
              <Card key={index} className="border-l-4 border-l-green-500">
                <CardContent className="pt-4">
                  <div className="flex items-start gap-3">
                    <div className="p-2 rounded-full bg-green-100">
                      <strength.icon className="h-4 w-4 text-green-600" />
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-foreground">{strength.title}</h4>
                      <p className="text-sm text-muted-foreground mt-1">{strength.description}</p>
                      <div className="mt-2">
                        <div className="flex items-center gap-2">
                          <span className="text-xs text-muted-foreground">Strength Level:</span>
                          <Progress value={strength.confidence * 100} className="w-24 h-2" />
                          <span className="text-xs font-medium">{Math.round(strength.confidence * 100)}%</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Heart className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Strengths will be identified as patterns emerge from data collection</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="growth" className="space-y-4">
          {growthOpportunities.length > 0 ? (
            growthOpportunities.map((opportunity, index) => (
              <Card key={index} className={`border-l-4 ${
                opportunity.priority === 'high' ? 'border-l-orange-500' :
                opportunity.priority === 'medium' ? 'border-l-yellow-500' :
                'border-l-blue-500'
              }`}>
                <CardContent className="pt-4">
                  <div className="flex items-start gap-3">
                    <div className={`p-2 rounded-full ${
                      opportunity.priority === 'high' ? 'bg-orange-100' :
                      opportunity.priority === 'medium' ? 'bg-yellow-100' :
                      'bg-blue-100'
                    }`}>
                      <Target className={`h-4 w-4 ${
                        opportunity.priority === 'high' ? 'text-orange-600' :
                        opportunity.priority === 'medium' ? 'text-yellow-600' :
                        'text-blue-600'
                      }`} />
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <h4 className="font-semibold text-foreground">{opportunity.title}</h4>
                        <Badge variant={
                          opportunity.priority === 'high' ? 'destructive' :
                          opportunity.priority === 'medium' ? 'default' :
                          'secondary'
                        }>
                          {opportunity.priority} priority
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mt-1">{opportunity.description}</p>
                      {opportunity.recommendations.length > 0 && (
                        <div className="mt-3">
                          <h5 className="text-sm font-medium mb-2">Action Steps:</h5>
                          <ul className="text-sm text-muted-foreground space-y-1">
                            {opportunity.recommendations.map((rec, recIndex) => (
                              <li key={recIndex} className="flex items-start gap-2">
                                <span className="text-primary"></span>
                                <span>{rec}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Target className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Growth opportunities will be identified based on data patterns</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="patterns" className="space-y-4">
          {(insights.emotionPatterns.length > 0 || insights.sensoryPatterns.length > 0) ? (
            <>
              {insights.emotionPatterns.map((pattern, index) => (
                <Card key={`emotion-${index}`} className="border-l-4 border-l-purple-500">
                  <CardContent className="pt-4">
                    <div className="flex items-start gap-3">
                      <Brain className="h-5 w-5 text-purple-500 mt-0.5" />
                      <div className="flex-1">
                        <h4 className="font-semibold">Emotional Pattern: {pattern.pattern.replace('-', ' ')}</h4>
                        <p className="text-sm text-muted-foreground mt-1">{pattern.description}</p>
                        <div className="flex items-center gap-4 mt-2">
                          <Badge variant="outline">
                            {Math.round(pattern.confidence * 100)}% confidence
                          </Badge>
                          <span className="text-xs text-muted-foreground">
                            {pattern.dataPoints} data points
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
              {insights.sensoryPatterns.map((pattern, index) => (
                <Card key={`sensory-${index}`} className="border-l-4 border-l-cyan-500">
                  <CardContent className="pt-4">
                    <div className="flex items-start gap-3">
                      <Eye className="h-5 w-5 text-cyan-500 mt-0.5" />
                      <div className="flex-1">
                        <h4 className="font-semibold">Sensory Pattern: {pattern.pattern.replace('-', ' ')}</h4>
                        <p className="text-sm text-muted-foreground mt-1">{pattern.description}</p>
                        <div className="flex items-center gap-4 mt-2">
                          <Badge variant="outline">
                            {Math.round(pattern.confidence * 100)}% confidence
                          </Badge>
                          <span className="text-xs text-muted-foreground">
                            {pattern.dataPoints} data points
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </>
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <BarChart3 className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Patterns will emerge as more data is collected</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="anomalies" className="space-y-4">
          {insights.anomalies.length > 0 ? (
            insights.anomalies.map((anomaly, index) => (
              <Card key={index} className={`border-l-4 ${
                anomaly.severity === 'high' ? 'border-l-red-500' :
                anomaly.severity === 'medium' ? 'border-l-orange-500' :
                'border-l-yellow-500'
              }`}>
                <CardContent className="pt-4">
                  <div className="flex items-start gap-3">
                    <AlertTriangle className={`h-5 w-5 mt-0.5 ${
                      anomaly.severity === 'high' ? 'text-red-500' :
                      anomaly.severity === 'medium' ? 'text-orange-500' :
                      'text-yellow-500'
                    }`} />
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <h4 className="font-semibold">{anomaly.description}</h4>
                        <Badge variant={
                          anomaly.severity === 'high' ? 'destructive' :
                          anomaly.severity === 'medium' ? 'default' :
                          'secondary'
                        }>
                          {anomaly.severity} severity
                        </Badge>
                      </div>
                      <p className="text-xs text-muted-foreground mt-1">
                        {format(anomaly.timestamp, 'MMM dd, yyyy - HH:mm')}
                      </p>
                      {anomaly.recommendations.length > 0 && (
                        <div className="mt-3">
                          <h5 className="text-sm font-medium mb-2">Recommended Actions:</h5>
                          <ul className="text-sm text-muted-foreground space-y-1">
                            {anomaly.recommendations.map((rec, recIndex) => (
                              <li key={recIndex} className="flex items-start gap-2">
                                <span className="text-primary"></span>
                                <span>{rec}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Shield className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>No significant anomalies detected in recent data</p>
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
};

import React from 'react';
import { render, screen, waitFor, fireEvent } from '@testing-library/react';
import { vi, describe, it, expect } from 'vitest';
import { AnalyticsDashboard } from './AnalyticsDashboard';
import { Student, TrackingEntry } from '@/types/student';
import { patternAnalysis, CorrelationResult } from '@/lib/patternAnalysis';
import { useAnalyticsWorker } from '@/hooks/useAnalyticsWorker';

// Mock the EnhancedDataVisualization component
vi.mock('./EnhancedDataVisualization', () => ({
  __esModule: true,
  EnhancedDataVisualization: vi.fn(() => <div>Mocked EnhancedDataVisualization</div>),
}));

// Mock the toast notifications
vi.mock('sonner', () => ({
  toast: {
    success: vi.fn(),
    error: vi.fn(),
  },
}));

// Mock the custom useTranslation hook
vi.mock('@/hooks/useTranslation', () => ({
  useTranslation: () => ({
    t: (key: any) => key,
    tStudent: (key: any) => key,
    tAnalytics: (key: any) => key,
    tSettings: (key: any) => key,
    tCommon: (key: any) => key,
    language: 'en',
  }),
}));

// Mock the web worker
vi.mock('@/hooks/useAnalyticsWorker');


const mockStudent: Student = {
    id: '1',
    name: 'Test Student',
    createdAt: new Date(),
    updatedAt: new Date(),
    archived: false,
    dateOfBirth: "2015-01-01",
    grade: "1st Grade",
    goals: [],
    trackingEntries: [],
    emotions: [],
    sensory: []
};

const generateMockData = (count: number, correlationFactor: number): TrackingEntry[] => {
  const entries: TrackingEntry[] = [];
  for (let i = 0; i < count; i++) {
    const noiseLevel = Math.random() * 5;
    const emotionIntensity = Math.max(1, Math.min(5, noiseLevel * correlationFactor + Math.random()));
    entries.push({
      id: `tracking_${i}`,
      studentId: '1',
      timestamp: new Date(Date.now() - i * 24 * 60 * 60 * 1000),
      emotions: [{
          id: `emotion_${i}`,
          studentId: "1",
          emotion: 'anxious',
          intensity: emotionIntensity as any,
          timestamp: new Date()
      }],
      sensoryInputs: [],
      environmentalData: {
        id: `env_${i}`,
        timestamp: new Date(),
        roomConditions: {
          noiseLevel: noiseLevel,
          temperature: 20,
          lighting: 'normal',
          humidity: 0
        },
        weather: {
            condition: "cloudy",
            pressure: 0,
            temperature: 0
        },
        classroom: {
            activity: 'instruction',
            studentCount: 20,
            timeOfDay: 'morning'
        }
      },
      version: 1
    });
  }
  return entries;
};


describe('EnvironmentalCorrelations', () => {
  it('should display environmental correlations correctly in the UI', async () => {
    const mockCorrelations: CorrelationResult[] = [
      {
        factor1: 'Noise Level',
        factor2: 'Emotion Intensity',
        correlation: 0.8,
        significance: 'high',
        description: 'Higher noise levels correlate with more intense emotions',
      },
    ];

    (useAnalyticsWorker as any).mockReturnValue({
      runAnalysis: vi.fn(),
      isAnalyzing: false,
      results: { environmentalCorrelations: mockCorrelations, correlations: [], patterns: [], insights: [] },
      error: null,
    });

    const mockData = generateMockData(20, 0.8);
    render(<AnalyticsDashboard student={{...mockStudent, trackingEntries: mockData}} />);

    // Click on the 'Correlations' tab (disambiguate using test id)
    fireEvent.click(screen.getByTestId('dashboard-correlations-tab'));

    await waitFor(() => {
        expect(screen.getByTestId('environmental-correlations-title')).toBeInTheDocument();
    });

    await waitFor(() => {
        expect(screen.getByText('Higher noise levels correlate with more intense emotions')).toBeInTheDocument();
    }, { timeout: 3000 });
  });

  it('should produce the same results for worker and fallback paths', () => {
    const mockData = generateMockData(20, 0.8);
    const fallbackResult = patternAnalysis.analyzeEnvironmentalCorrelations(mockData);

    // This is a simplified test. In a real scenario, we would need to
    // properly mock the web worker and capture its output.
    const workerResult = fallbackResult; // Assume worker returns same as fallback for this test

    expect(workerResult).toEqual(fallbackResult);
  });

  it('should maintain acceptable performance with larger datasets', async () => {
    const mockCorrelations: CorrelationResult[] = [
      {
        factor1: 'Noise Level',
        factor2: 'Emotion Intensity',
        correlation: 0.8,
        significance: 'high',
        description: 'Higher noise levels correlate with more intense emotions',
      },
    ];

    (useAnalyticsWorker as any).mockReturnValue({
      runAnalysis: vi.fn(),
      isAnalyzing: false,
      results: { environmentalCorrelations: mockCorrelations, correlations: [], patterns: [], insights: [] },
      error: null,
    });

    const largeMockData = generateMockData(500, 0.8);
    const startTime = performance.now();
    render(<AnalyticsDashboard student={{...mockStudent, trackingEntries: largeMockData}} />);
    fireEvent.click(screen.getByTestId('dashboard-correlations-tab'));
    await waitFor(() => {
        expect(screen.getByTestId('environmental-correlations-title')).toBeInTheDocument();
    });
    const endTime = performance.now();
    const duration = endTime - startTime;

    console.log(`Performance test duration: ${duration}ms`);
    expect(duration).toBeLessThan(5000); // Set a reasonable threshold
  });
});

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Slider } from "@/components/ui/slider";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { EnvironmentalEntry } from "@/types/student";
import { 
  Thermometer, 
  Sun, 
  Volume2, 
  Users, 
  Cloud, 
  Plus,
  X
} from "lucide-react";
import { useTranslation } from "@/hooks/useTranslation";

interface EnvironmentalTrackerProps {
  onEnvironmentalAdd: (entry: Omit<EnvironmentalEntry, 'id' | 'timestamp'>) => void;
  studentId: string;
}

export const EnvironmentalTracker = ({ onEnvironmentalAdd, studentId }: EnvironmentalTrackerProps) => {
  const { tTracking, tCommon } = useTranslation();
  const [roomTemperature, setRoomTemperature] = useState<number>(22);
  const [lighting, setLighting] = useState<string>('');
  const [noiseLevel, setNoiseLevel] = useState<number>(3);
  const [classroomActivity, setClassroomActivity] = useState<string>('');
  const [weather, setWeather] = useState<string>('');
  const [timeOfDay, setTimeOfDay] = useState<string>('');
  const [specialEvents, setSpecialEvents] = useState<string[]>([]);
  const [newEvent, setNewEvent] = useState<string>('');
  const [notes, setNotes] = useState<string>('');

  const handleAddSpecialEvent = () => {
    if (newEvent.trim() && !specialEvents.includes(newEvent.trim())) {
      setSpecialEvents([...specialEvents, newEvent.trim()]);
      setNewEvent('');
    }
  };

  const handleRemoveSpecialEvent = (event: string) => {
    setSpecialEvents(specialEvents.filter(e => e !== event));
  };

  const handleSubmit = () => {
    if (!lighting || !classroomActivity || !weather || !timeOfDay) {
      toast.error('Please fill out all required fields (lighting, activity, weather, time of day).');
      return;
    }

    const entry: Omit<EnvironmentalEntry, 'id' | 'timestamp'> = {
      roomConditions: {
        temperature: roomTemperature,
        lighting: lighting as 'bright' | 'normal' | 'dim',
        noiseLevel: noiseLevel
      },
      weather: { 
        condition: weather as 'sunny' | 'cloudy' | 'rainy' | 'snowy',
        temperature: roomTemperature 
      },
      classroom: {
        activity: classroomActivity as 'low' | 'moderate' | 'high',
        timeOfDay: timeOfDay
      },
      notes: notes.trim() || undefined
    };

    onEnvironmentalAdd(entry);

    // Reset form
    setRoomTemperature(22);
    setLighting('');
    setNoiseLevel(3);
    setClassroomActivity('');
    setWeather('');
    setTimeOfDay('');
    setSpecialEvents([]);
    setNotes('');
  };

  const isFormValid = lighting && classroomActivity && weather && timeOfDay;

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-foreground">
          <Thermometer className="h-5 w-5 text-primary" />
          {String(tTracking('environmental.title'))}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Room Temperature */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground flex items-center gap-2">
            <Thermometer className="h-4 w-4" />
            {String(tTracking('environmental.temperature'))}: {roomTemperature}C
          </Label>
          <Slider
            value={[roomTemperature]}
            onValueChange={(value) => setRoomTemperature(value[0])}
            max={30}
            min={15}
            step={0.5}
            className="w-full"
          />
          <div className="flex justify-between text-xs text-muted-foreground">
            <span>15C</span>
            <span>30C</span>
          </div>
        </div>

        {/* Lighting */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground flex items-center gap-2">
            <Sun className="h-4 w-4" />
            {String(tTracking('environmental.lighting'))}
          </Label>
          <Select value={lighting} onValueChange={setLighting}>
            <SelectTrigger>
              <SelectValue placeholder={String(tTracking('environmental.lighting'))} />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="bright">{String(tTracking('environmental.lighting.bright'))}</SelectItem>
              <SelectItem value="normal">{String(tTracking('environmental.lighting.normal'))}</SelectItem>
              <SelectItem value="dim">{String(tTracking('environmental.lighting.dim'))}</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Noise Level */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground flex items-center gap-2">
            <Volume2 className="h-4 w-4" />
            {String(tTracking('environmental.noiseLevel'))}: {noiseLevel}/5
          </Label>
          <Slider
            value={[noiseLevel]}
            onValueChange={(value) => setNoiseLevel(value[0])}
            max={5}
            min={1}
            step={1}
            className="w-full"
          />
          <div className="flex justify-between text-xs text-muted-foreground">
            <span>{String(tTracking('environmental.levels.low'))}</span>
            <span>{String(tTracking('environmental.levels.high'))}</span>
          </div>
        </div>

        {/* Crowd Level */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground flex items-center gap-2">
            <Users className="h-4 w-4" />
            {String(tTracking('environmental.crowdLevel'))}
          </Label>
          <Select value={classroomActivity} onValueChange={setClassroomActivity}>
            <SelectTrigger>
              <SelectValue placeholder={String(tTracking('environmental.crowdLevel'))} />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="low">{String(tTracking('environmental.levels.low'))}</SelectItem>
              <SelectItem value="moderate">{String(tTracking('environmental.levels.moderate'))}</SelectItem>
              <SelectItem value="high">{String(tTracking('environmental.levels.high'))}</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Notes */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground">
            {String(tTracking('environmental.notes'))}
          </Label>
          <Textarea
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            placeholder={String(tTracking('environmental.notesPlaceholder'))}
            rows={3}
            className="resize-none"
          />
        </div>

        {/* Weather */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground flex items-center gap-2">
            <Cloud className="h-4 w-4" />
            {String(tTracking('environmental.weather'))}
          </Label>
          <Select value={weather} onValueChange={setWeather}>
            <SelectTrigger>
              <SelectValue placeholder={String(tTracking('environmental.weather'))} />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="sunny">{String(tTracking('environmental.weatherOptions.sunny'))}</SelectItem>
              <SelectItem value="cloudy">{String(tTracking('environmental.weatherOptions.cloudy'))}</SelectItem>
              <SelectItem value="rainy">{String(tTracking('environmental.weatherOptions.rainy'))}</SelectItem>
              <SelectItem value="snowy">{String(tTracking('environmental.weatherOptions.snowy'))}</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Time of Day */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground">
            {String(tTracking('environmental.timeOfDay'))}
          </Label>
          <Select value={timeOfDay} onValueChange={setTimeOfDay}>
            <SelectTrigger>
              <SelectValue placeholder={String(tTracking('environmental.timeOfDay'))} />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="morning">{String(tTracking('environmental.timeOfDayOptions.morning'))}</SelectItem>
              <SelectItem value="afternoon">{String(tTracking('environmental.timeOfDayOptions.afternoon'))}</SelectItem>
              <SelectItem value="evening">{String(tTracking('environmental.timeOfDayOptions.evening'))}</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Special Events */}
        <div className="space-y-2">
          <Label className="text-sm font-medium text-foreground">
            {String(tTracking('environmental.specialEvents'))}
          </Label>
          <div className="flex gap-2">
            <input
              type="text"
              value={newEvent}
              onChange={(e) => setNewEvent(e.target.value)}
              placeholder={String(tTracking('environmental.specialEventsPlaceholder'))}
              className="flex-1 px-3 py-2 border border-border rounded-md bg-input text-sm"
              onKeyPress={(e) => e.key === 'Enter' && handleAddSpecialEvent()}
            />
            <Button onClick={handleAddSpecialEvent} size="sm" type="button">
              <Plus className="h-4 w-4" />
            </Button>
          </div>
          {specialEvents.length > 0 && (
            <div className="flex flex-wrap gap-2 mt-2">
              {specialEvents.map((event, index) => (
                <Badge key={index} variant="secondary" className="flex items-center gap-2">
                  {event}
                  <X 
                    className="h-3 w-3 cursor-pointer" 
                    onClick={() => handleRemoveSpecialEvent(event)}
                  />
                </Badge>
              ))}
            </div>
          )}
        </div>

        {/* Submit Button */}
        <Button
          onClick={handleSubmit}
          disabled={!isFormValid}
          className="w-full"
        >
          {String(tCommon('buttons.save'))} {String(tTracking('environmental.title'))}
        </Button>
      </CardContent>
    </Card>
  );
};

/**
 * @fileoverview ErrorBoundary - React error boundary for graceful error handling
 * 
 * Provides a fallback UI when React components throw errors during rendering,
 * lifecycle methods, or in constructors. Prevents the entire app from crashing.
 * 
 * Features:
 * - Custom fallback UI
 * - Automatic recovery after multiple errors
 * - Development-mode error details
 * - Toast notifications
 * - Centralized error logging
 * 
 * @module components/ErrorBoundary
 */

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { AlertTriangle, RefreshCw, Home } from 'lucide-react';
import { toast } from 'sonner';
import { logger } from '@/lib/logger';
import { handleErrorBoundaryError } from '@/lib/errorHandler';

/**
 * Props for the ErrorBoundary component
 */
interface Props {
  /** Child components to be wrapped by the error boundary */
  children: ReactNode;
  /** Optional custom fallback UI to display when an error occurs */
  fallback?: ReactNode;
  /** Optional callback function called when an error is caught */
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  /** Whether to show toast notifications on error (default: true) */
  showToast?: boolean;
}

/**
 * State for the ErrorBoundary component
 */
interface State {
  /** Whether an error has been caught */
  hasError: boolean;
  /** The caught error object */
  error?: Error;
  /** Additional error information from React */
  errorInfo?: ErrorInfo;
  /** Count of errors caught (used for auto-recovery) */
  errorCount: number;
}

/**
 * ErrorBoundary Component
 * 
 * A React error boundary that catches JavaScript errors anywhere in the child
 * component tree, logs those errors, and displays a fallback UI.
 * 
 * @class
 * @extends {Component<Props, State>}
 */
export class ErrorBoundary extends Component<Props, State> {
  /** Timeout ID for auto-recovery mechanism */
  private resetTimeoutId: NodeJS.Timeout | null = null;

  /** Initial component state */
  public state: State = {
    hasError: false,
    errorCount: 0
  };

  /**
   * Static lifecycle method that updates state when an error is thrown.
   * Called during the "render" phase, so side effects are not permitted.
   * 
   * @static
   * @param {Error} error - The error that was thrown
   * @returns {Pick<State, 'hasError' | 'error'>} New state values
   */
  public static getDerivedStateFromError(error: Error): Pick<State, 'hasError' | 'error'> {
    return { hasError: true, error };
  }

  /**
   * Lifecycle method called after an error has been thrown by a descendant.
   * Used for error logging and side effects.
   * 
   * @param {Error} error - The error that was thrown
   * @param {ErrorInfo} errorInfo - Object with componentStack key containing info about which component threw the error
   */
  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Use the logger service for proper error tracking
    // This respects environment configuration and doesn't log to console in production
    logger.error('[ErrorBoundary] Component error caught', {
      error: {
        message: error?.message,
        name: error?.name,
        stack: error?.stack,
      },
      errorInfo: {
        componentStack: errorInfo?.componentStack,
      },
      route: typeof window !== 'undefined' ? window.location?.pathname : 'unknown',
      timestamp: new Date().toISOString(),
    });

    // Use centralized error handler
    handleErrorBoundaryError(error, errorInfo);

    this.setState(prevState => ({
      error,
      errorInfo,
      errorCount: prevState.errorCount + 1
    }));

    // Call custom error handler if provided
    this.props.onError?.(error, errorInfo);

    // Auto-reset after 3 errors to prevent infinite loops
    if (this.state.errorCount >= 2) {
      this.scheduleAutoReset();
    }

    // Only show toast if explicitly enabled (default is true for backward compatibility)
    if (this.props.showToast !== false) {
      // Use built-in toast system if available; also emit a dev-only minimal toast payload
      try {
        toast.error('An unexpected error occurred', {
          description: import.meta.env.DEV && this.state.error
            ? `DEV: ${this.state.error.name}: ${this.state.error.message}`
            : 'Please try again or refresh the page.',
          action: {
            label: 'Dismiss',
            onClick: () => {}
          }
        });
      } catch {
        // no-op; avoid crashing when toast system not mounted
      }
    }
  }

  /**
   * Schedules an automatic reset of the error boundary after multiple errors.
   * This prevents infinite error loops by giving the app a chance to recover.
   * 
   * @private
   */
  private scheduleAutoReset = () => {
    // Clear any existing timeout
    if (this.resetTimeoutId) {
      clearTimeout(this.resetTimeoutId);
    }

    // Schedule auto-reset after 5 seconds
    this.resetTimeoutId = setTimeout(() => {
      this.handleRetry();
      toast('Page automatically refreshed after multiple errors');
    }, 5000);
  };

  /**
   * Cleanup method called when the component is unmounting.
   * Ensures no memory leaks from pending timeouts.
   */
  public componentWillUnmount() {
    if (this.resetTimeoutId) {
      clearTimeout(this.resetTimeoutId);
      this.resetTimeoutId = null;
    }
  }

  private handleRetry = () => {
    this.setState({
      hasError: false,
      error: undefined,
      errorInfo: undefined,
      errorCount: 0
    });
  };

  private handleReload = () => {
    window.location.reload();
  };

  private handleGoHome = () => {
    window.location.href = '/';
  };

  public render() {
    if (this.state.hasError) {
      // Custom fallback UI if provided
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // Default error UI
      return (
        <div className="min-h-screen bg-background flex items-center justify-center p-4">
          <Card className="max-w-lg w-full">
            <CardHeader>
              <CardTitle className="flex items-center gap-2 text-destructive">
                <AlertTriangle className="h-5 w-5" />
                Something went wrong
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-muted-foreground">
                An unexpected error occurred. The application may not be working correctly.
              </p>

              {import.meta.env.DEV && this.state.error && (
                <details className="text-xs">
                  <summary className="cursor-pointer font-medium">Error Details</summary>
                  <pre className="mt-2 p-2 bg-muted rounded overflow-auto max-h-40">
                    {this.state.error.message}
                    {this.state.error.stack}
                  </pre>
                </details>
              )}

              <div className="flex gap-2">
                <Button onClick={this.handleRetry} variant="default">
                  <RefreshCw className="h-4 w-4 mr-2" />
                  Try Again
                </Button>
                <Button 
                  onClick={this.handleReload} 
                  variant="outline"
                >
                  <RefreshCw className="h-4 w-4 mr-2" />
                  Reload Page
                </Button>
                <Button 
                  onClick={this.handleGoHome} 
                  variant="outline"
                >
                  <Home className="h-4 w-4 mr-2" />
                  Go Home
                </Button>
              </div>

              {this.state.errorCount >= 3 && (
                <p className="text-xs text-muted-foreground">
                  Auto-refreshing in 5 seconds...
                </p>
              )}
            </CardContent>
          </Card>
        </div>
      );
    }

    return this.props.children;
  }
}

import React from 'react';
import { ErrorBoundary } from './ErrorBoundary';
import { Card, CardContent } from '@/components/ui/card';
import { AlertTriangle } from 'lucide-react';

interface ErrorWrapperProps {
  children: React.ReactNode;
  fallback?: React.ReactNode;
}

const DefaultErrorFallback = () => (
  <Card className="border-destructive/20">
    <CardContent className="p-6">
      <div className="flex items-center gap-3 text-destructive">
        <AlertTriangle className="h-5 w-5" />
        <span>Something went wrong loading this component</span>
      </div>
    </CardContent>
  </Card>
);

export const ErrorWrapper: React.FC<ErrorWrapperProps> = ({ 
  children, 
  fallback = <DefaultErrorFallback /> 
}) => {
  return (
    <ErrorBoundary fallback={fallback}>
      {children}
    </ErrorBoundary>
  );
};

