import React from 'react';
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuItem,
  SidebarMenuButton,
  useSidebar,
} from '@/components/ui/sidebar';
import { useTranslation } from '@/hooks/useTranslation';
import { Student } from '@/types/student';
import { logger } from '@/lib/logger';

interface StudentProfileSidebarProps {
  student: Student;
  activeSection: string;
  onSectionChange: (section: string) => void;
}

export function StudentProfileSidebar({ 
  student, 
  activeSection, 
  onSectionChange 
}: StudentProfileSidebarProps) {
  const { state } = useSidebar();
  const { tStudent, tCommon, tAnalytics } = useTranslation();

  const menuItems = [
    {
      section: 'dashboard',
      title: tCommon('navigation.dashboard'),
      icon: 'dashboard',
      description: 'Oversikt og sammendrag'
    },
    {
      section: 'analytics',
      title: tCommon('navigation.analytics'),
      icon: 'analytics',
      description: 'Datanalyse og innsikter'
    },
    {
      section: 'goals',
      title: 'Ml',
      icon: 'flag',
      description: 'Mlstyring og progresjon'
    },
    {
      section: 'progress',
      title: 'Fremgang',
      icon: 'trending_up',
      description: 'Utviklingsanalyse'
    },
    {
      section: 'reports',
      title: tCommon('navigation.reports'),
      icon: 'description',
      description: 'Rapporter og eksport'
    }
  ];

  const toolItems = [
    {
      section: 'search',
      title: tStudent('interface.advancedSearch'),
      icon: 'search',
      description: 'Avansert sk'
    },
    {
      section: 'templates',
      title: tStudent('interface.quickTemplates'),
      icon: 'flash_on',
      description: 'Hurtigmaler'
    },
    {
      section: 'compare',
      title: 'Sammenligning',
      icon: 'compare_arrows',
      description: 'Periodesammenligning'
    }
  ];

  const isActive = (section: string) => activeSection === section;

  return (
    <Sidebar className={`${state === "collapsed" ? "w-14" : "w-64"} bg-card/95 border-border backdrop-blur-sm z-50`}>
      <SidebarContent className="bg-transparent">
        {/* Student Header */}
        <div className={`p-4 border-b border-border/20 bg-card/50 backdrop-blur-sm ${state === "collapsed" ? 'px-2' : ''}`}>
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-primary to-primary/70 flex items-center justify-center text-primary-foreground font-bold text-sm shadow-lg">
              {student.name.split(' ').map(n => n[0]).join('').toUpperCase()}
            </div>
            {state !== "collapsed" && (
              <div className="min-w-0 flex-1">
                <h3 className="font-semibold text-sm truncate text-foreground">{student.name}</h3>
                {student.grade && (
                  <p className="text-xs text-muted-foreground">{student.grade}</p>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Main Navigation */}
        <SidebarGroup>
          <SidebarGroupLabel className="text-muted-foreground text-xs font-medium uppercase tracking-wider px-3 py-2">
            Hovedseksjoner
          </SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {menuItems.map((item) => (
                <SidebarMenuItem key={item.section}>
                  <SidebarMenuButton
                    onClick={() => {
                      try { logger.debug('[UI] Sidebar nav click', { section: item.section }); } catch {}
                      onSectionChange(item.section);
                    }}
                    className={`cursor-pointer mx-2 rounded-lg transition-all duration-200 ${
                      isActive(item.section) 
                        ? 'bg-primary text-primary-foreground shadow-lg' 
                        : 'text-foreground hover:bg-accent hover:text-accent-foreground'
                    }`}
                  >
                    <span className="material-icons text-base">{item.icon}</span>
                    {state !== "collapsed" && (
                     <span className="text-sm ml-3">{String(item.title)}</span>
                    )}
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        {/* Tools Section */}
        <SidebarGroup>
          <SidebarGroupLabel className="text-muted-foreground text-xs font-medium uppercase tracking-wider px-3 py-2">
            Verkty
          </SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {toolItems.map((item) => (
                <SidebarMenuItem key={item.section}>
                  <SidebarMenuButton
                    onClick={() => {
                      try { logger.debug('[UI] Sidebar tools click', { section: item.section }); } catch {}
                      onSectionChange(item.section);
                    }}
                    className={`cursor-pointer mx-2 rounded-lg transition-all duration-200 ${
                      isActive(item.section) 
                        ? 'bg-primary text-primary-foreground shadow-lg' 
                        : 'text-foreground hover:bg-accent hover:text-accent-foreground'
                    }`}
                  >
                    <span className="material-icons text-base">{item.icon}</span>
                    {state !== "collapsed" && (
                      <span className="text-sm ml-3">{String(item.title)}</span>
                    )}
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        {/* Testing Tools Section */}
        <SidebarGroup>
          <SidebarGroupLabel className="text-muted-foreground text-xs font-medium uppercase tracking-wider px-3 py-2">
            Testing
          </SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              <SidebarMenuItem>
                <SidebarMenuButton
                  onClick={() => {
                    try { logger.debug('[UI] Sidebar testing click', { section: 'testing' }); } catch {}
                    onSectionChange('testing');
                  }}
                  className={`cursor-pointer mx-2 rounded-lg transition-all duration-200 ${
                    isActive('testing') 
                      ? 'bg-primary text-primary-foreground shadow-lg' 
                      : 'text-foreground hover:bg-accent hover:text-accent-foreground'
                  }`}
                >
                  <span className="material-icons text-base">science</span>
                  {state !== "collapsed" && (
                    <span className="text-sm ml-3">Mock Data</span>
                  )}
                </SidebarMenuButton>
              </SidebarMenuItem>
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}

import React, { useState, useEffect, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import {
  Bug,
  CheckCircle,
  AlertTriangle,
  RefreshCw,
  Database,
  Users,
  BarChart3,
  TestTube,
  Archive,
  Trash2,
  Zap,
  Activity
} from 'lucide-react';
import { analyticsManager } from '@/lib/analyticsManager';
import { universalAnalyticsInitializer } from '@/lib/universalAnalyticsInitializer';
import { dataStorage } from '@/lib/dataStorage';
import { useAnalyticsWorker } from '@/hooks/useAnalyticsWorker';
import { usePerformanceCache } from '@/hooks/usePerformanceCache';
import { AnalyticsConfigTest } from '@/components/AnalyticsConfigTest';
import { toast } from 'sonner';
import { logger } from '@/lib/logger';

interface TestingDebugPanelProps {
  className?: string;
}

interface TestResult {
  name: string;
  status: 'pass' | 'fail' | 'warn' | 'running';
  message: string;
}

interface TestData {
  test: boolean;
  timestamp: number;
}

type TestStatus = TestResult['status'];

interface CacheOperations {
  clearCache: () => void;
  invalidateStudent: (studentId: string) => void;
}

export const TestingDebugPanel: React.FC<TestingDebugPanelProps> = ({ className = "" }) => {
  const [isRunningTests, setIsRunningTests] = useState(false);
  const [testResults, setTestResults] = useState<TestResult[]>([]);

  // Analytics worker with cache stats
  const analyticsWorker = useAnalyticsWorker({ 
    enableCacheStats: true,
    cacheTTL: 5 * 60 * 1000 // 5 minutes
  });

  // Direct cache instance for UI testing
  const uiCache = usePerformanceCache({ 
    enableStats: true,
    maxSize: 100,
    ttl: 5 * 60 * 1000
  });

  // Update cache stats every second
  const [cacheStats, setCacheStats] = useState(analyticsWorker.cacheStats);

  useEffect(() => {
    const interval = setInterval(() => {
      setCacheStats(analyticsWorker.cacheStats);
    }, 1000);

    return () => clearInterval(interval);
  }, [analyticsWorker.cacheStats]);

  const runSystemTests = async () => {
    setIsRunningTests(true);
    setTestResults([]);

    const results: TestResult[] = [];

    try {
      // Test 1: Data Storage
      results.push({
        name: "Data Storage",
        status: "running",
        message: "Testing localStorage operations..."
      });
      setTestResults([...results]);

      const students = dataStorage.getStudents();
      const entries = dataStorage.getTrackingEntries();
      const storageStats = dataStorage.getStorageStats();

      results[0] = {
        name: "Data Storage",
        status: storageStats.studentsCount >= 0 ? "pass" : "fail",
        message: `Found ${storageStats.studentsCount} students, ${storageStats.entriesCount} entries`
      };
      setTestResults([...results]);

      // Test 2: Analytics Initialization
      results.push({
        name: "Analytics System",
        status: "running",
        message: "Testing analytics initialization..."
      });
      setTestResults([...results]);

      const analyticsStatus = analyticsManager.getAnalyticsStatus();
      const initStatus = universalAnalyticsInitializer.getInitializationStatus();

      results[1] = {
        name: "Analytics System",
        status: initStatus.isInitialized ? "pass" : "warn",
        message: `${initStatus.studentsWithAnalytics}/${initStatus.totalStudents} students have analytics`
      };
      setTestResults([...results]);

      // Test 3: New Student Creation (without mock data)
      results.push({
        name: "New Student Flow",
        status: "running",
        message: "Testing new student creation..."
      });
      setTestResults([...results]);

      // Create a test student temporarily
      const testStudentId = 'test-' + Date.now();
      analyticsManager.initializeStudentAnalytics(testStudentId);
      const testStatus = analyticsManager.getAnalyticsStatus().find(s => s.studentId === testStudentId);

      results[2] = {
        name: "New Student Flow",
        status: testStatus?.isInitialized && !testStatus?.hasMinimumData ? "pass" : "fail",
        message: testStatus?.isInitialized && !testStatus?.hasMinimumData 
          ? " New students start with clean slate" 
          : " New students may have unexpected data"
      };
      setTestResults([...results]);

      // Test 4: Empty State Handling
      results.push({
        name: "Empty State Handling",
        status: "running",
        message: "Testing empty data scenarios..."
      });
      setTestResults([...results]);

      const emptyStudents = students.filter(student => {
        const studentEntries = entries.filter(entry => entry.studentId === student.id);
        return studentEntries.length === 0;
      });

      results[3] = {
        name: "Empty State Handling",
        status: "pass",
        message: `Found ${emptyStudents.length} students with no data (will show empty states)`
      };
      setTestResults([...results]);

      // Test 5: Pattern Detection Ready
      results.push({
        name: "Pattern Detection",
        status: "running",
        message: "Testing pattern detection capability..."
      });
      setTestResults([...results]);

      const studentsWithData = students.filter(student => {
        const studentEntries = entries.filter(entry => entry.studentId === student.id);
        return studentEntries.length >= 3;
      });

      results[4] = {
        name: "Pattern Detection",
        status: studentsWithData.length > 0 ? "pass" : "warn",
        message: `${studentsWithData.length} students have enough data for pattern detection`
      };
      setTestResults([...results]);

      // Test 6: Cache Performance
      results.push({
        name: "Cache Performance",
        status: "running",
        message: "Testing cache functionality..."
      });
      setTestResults([...results]);

      // Test cache operations
      const testKey = 'test-cache-' + Date.now();
      const testData: TestData = { test: true, timestamp: Date.now() };
      uiCache.set(testKey, testData);
      const retrieved = uiCache.get(testKey) as TestData | undefined;

      results[5] = {
        name: "Cache Performance",
        status: retrieved?.test === true ? "pass" : "fail",
        message: cacheStats ? `Hit rate: ${cacheStats.hitRate.toFixed(1)}%, Size: ${cacheStats.size} items` : "Cache stats unavailable"
      };
      setTestResults([...results]);

      toast.success("System tests completed successfully");

    } catch (error) {
      logger.error('System test error', { error });
      results.push({
        name: "Test Error",
        status: "fail",
        message: `Error: ${error instanceof Error ? error.message : 'Unknown error'}`
      });
      setTestResults([...results]);
      toast.error("Some tests failed");
    } finally {
      setIsRunningTests(false);
    }
  };

  const getStatusIcon = useCallback((status: TestStatus): React.ReactElement => {
    switch (status) {
      case 'pass': return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'fail': return <AlertTriangle className="h-4 w-4 text-red-600" />;
      case 'warn': return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
      case 'running': return <RefreshCw className="h-4 w-4 text-blue-600 animate-spin" />;
      default: return <TestTube className="h-4 w-4 text-muted-foreground" />;
    }
  }, []);

  const getStatusColor = useCallback((status: TestStatus): 'default' | 'destructive' | 'secondary' | 'outline' => {
    switch (status) {
      case 'pass': return 'default';
      case 'fail': return 'destructive';
      case 'warn': return 'secondary';
      case 'running': return 'outline';
      default: return 'secondary';
    }
  }, []);

  const handleClearCache = useCallback(() => {
    analyticsWorker.clearCache();
    uiCache.clear();
    toast.success("Analytics cache cleared successfully");
  }, [analyticsWorker, uiCache]);

  const handleInvalidateStudent = useCallback((studentId: string) => {
    analyticsWorker.invalidateCacheForStudent(studentId);
    toast.success(`Cache invalidated for student ${studentId}`);
  }, [analyticsWorker]);

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Main Testing Panel */}
      <Card className="bg-gradient-card border-0 shadow-soft">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Bug className="h-5 w-5" />
            System Testing & Debug Panel
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <p className="text-sm text-muted-foreground">
              Test current system functionality and data integrity
            </p>
            <Button
              onClick={runSystemTests}
              disabled={isRunningTests}
              size="sm"
              variant="outline"
            >
              {isRunningTests ? (
                <>
                  <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                  Running Tests...
                </>
              ) : (
                <>
                  <TestTube className="h-4 w-4 mr-2" />
                  Run System Tests
                </>
              )}
            </Button>
          </div>

          {testResults.length > 0 && (
            <div className="space-y-3">
              <h4 className="text-sm font-medium">Test Results:</h4>
              {testResults.map((result, index) => (
                <div key={index} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                  <div className="flex items-center gap-3">
                    {getStatusIcon(result.status)}
                    <div>
                      <p className="font-medium text-sm">{result.name}</p>
                      <p className="text-xs text-muted-foreground">{result.message}</p>
                    </div>
                  </div>
                  <Badge variant={getStatusColor(result.status)} className="text-xs">
                    {result.status.toUpperCase()}
                  </Badge>
                </div>
              ))}
            </div>
          )}

          <div className="pt-4 border-t border-border">
            <h4 className="text-sm font-medium mb-2">Quick Stats:</h4>
            <div className="grid grid-cols-3 gap-3 text-center">
              <div>
                <Users className="h-4 w-4 mx-auto mb-1 text-muted-foreground" />
                <p className="text-xs text-muted-foreground">Students</p>
              </div>
              <div>
                <Database className="h-4 w-4 mx-auto mb-1 text-muted-foreground" />
                <p className="text-xs text-muted-foreground">Entries</p>
              </div>
              <div>
                <BarChart3 className="h-4 w-4 mx-auto mb-1 text-muted-foreground" />
                <p className="text-xs text-muted-foreground">Analytics</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Cache Management Panel */}
      <Card className="bg-gradient-card border-0 shadow-soft">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Archive className="h-5 w-5" />
            Analytics Cache Management
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {cacheStats && (
            <>
              {/* Cache Statistics */}
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">Cache Hit Rate</span>
                  <div className="flex items-center gap-2">
                    <Progress value={cacheStats.hitRate} className="w-24" />
                    <span className="text-sm font-mono">{cacheStats.hitRate.toFixed(1)}%</span>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="flex flex-col">
                    <span className="text-xs text-muted-foreground">Cache Size</span>
                    <span className="text-lg font-semibold">{cacheStats.size} / {analyticsWorker.cacheSize}</span>
                  </div>
                  <div className="flex flex-col">
                    <span className="text-xs text-muted-foreground">Memory Usage</span>
                    <span className="text-lg font-semibold">
                      {cacheStats.memoryUsage ? `${(cacheStats.memoryUsage / 1024).toFixed(1)} KB` : 'N/A'}
                    </span>
                  </div>
                </div>

                <div className="grid grid-cols-3 gap-2 pt-2">
                  <div className="text-center p-2 bg-muted/50 rounded">
                    <Activity className="h-4 w-4 mx-auto mb-1 text-green-600" />
                    <p className="text-xs font-medium">{cacheStats.hits}</p>
                    <p className="text-xs text-muted-foreground">Hits</p>
                  </div>
                  <div className="text-center p-2 bg-muted/50 rounded">
                    <AlertTriangle className="h-4 w-4 mx-auto mb-1 text-yellow-600" />
                    <p className="text-xs font-medium">{cacheStats.misses}</p>
                    <p className="text-xs text-muted-foreground">Misses</p>
                  </div>
                  <div className="text-center p-2 bg-muted/50 rounded">
                    <Zap className="h-4 w-4 mx-auto mb-1 text-blue-600" />
                    <p className="text-xs font-medium">{cacheStats.sets}</p>
                    <p className="text-xs text-muted-foreground">Sets</p>
                  </div>
                </div>
              </div>

              {/* Cache Actions */}
              <div className="pt-4 border-t border-border space-y-3">
                <h4 className="text-sm font-medium">Cache Actions</h4>
                <div className="flex gap-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={handleClearCache}
                    className="flex-1"
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Clear All Cache
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => uiCache.cleanup()}
                    className="flex-1"
                  >
                    <RefreshCw className="h-4 w-4 mr-2" />
                    Clean Expired
                  </Button>
                </div>

                <div className="text-xs text-muted-foreground">
                  <p> Cache TTL: 5 minutes</p>
                  <p> Eviction: LRU (Least Recently Used)</p>
                  <p> Invalidations: {cacheStats.invalidations || 0}</p>
                </div>
              </div>
            </>
          )}
        </CardContent>
      </Card>

      {/* Analytics Configuration Test Panel */}
      <AnalyticsConfigTest />
    </div>
  );
};

import React, { useState, useRef, useEffect, useMemo, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Slider } from '@/components/ui/slider';
import { Toggle } from '@/components/ui/toggle';
import { EmotionEntry, SensoryEntry, TrackingEntry } from '@/types/student';
import { format, subDays, addDays, differenceInMinutes, isWithinInterval } from 'date-fns';
import { 
  Clock, 
  ZoomIn, 
  ZoomOut, 
  RotateCcw, 
  Maximize2,
  Play,
  Pause,
  ChevronLeft,
  ChevronRight,
  Activity,
  Brain,
  Eye,
  AlertCircle
} from 'lucide-react';
import { cn } from '@/lib/utils';
import { logger } from '@/lib/logger';

interface TimelineVisualizationProps {
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  trackingEntries: TrackingEntry[];
  anomalies?: Array<{
    timestamp: Date;
    type: string;
    severity: 'low' | 'medium' | 'high';
  }>;
  onTimeRangeChange?: (start: Date, end: Date) => void;
  realtime?: boolean;
}

interface TimelineEvent {
  id: string;
  timestamp: Date;
  type: 'emotion' | 'sensory' | 'tracking' | 'anomaly';
  label: string;
  value?: number;
  color: string;
  metadata?: Record<string, string | number>;
}

interface DataStream {
  id: string;
  label: string;
  color: string;
  data: Array<{ timestamp: Date; value: number }>;
  visible: boolean;
  yScale: [number, number];
}

export const TimelineVisualization: React.FC<TimelineVisualizationProps> = ({
  emotions,
  sensoryInputs,
  trackingEntries,
  anomalies = [],
  onTimeRangeChange,
  realtime = false
}) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 400 });

  // Timeline state
  const [timeRange, setTimeRange] = useState<[Date, Date]>(() => {
    const now = new Date();
    return [subDays(now, 7), now];
  });
  const [zoomLevel, setZoomLevel] = useState(1);
  const [panOffset, setPanOffset] = useState(0);
  const [brushSelection, setBrushSelection] = useState<[Date, Date] | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [hoveredEvent, setHoveredEvent] = useState<TimelineEvent | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const dragStartDateRef = useRef<Date | null>(null);

  // Data stream visibility
  const [streamVisibility, setStreamVisibility] = useState({
    emotions: true,
    sensory: true,
    anomalies: true
  });

  // Calculate dimensions on mount and resize
  useEffect(() => {
    const updateDimensions = () => {
      if (containerRef.current) {
        const { width, height } = containerRef.current.getBoundingClientRect();
        setDimensions({ width: width - 40, height: height - 200 });
      }
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  // Process data into timeline events
  const timelineEvents = useMemo((): TimelineEvent[] => {
    const events: TimelineEvent[] = [];

    // Process emotions
    if (streamVisibility.emotions) {
      emotions.forEach((emotion, i) => {
        events.push({
          id: `emotion-${i}`,
          timestamp: emotion.timestamp,
          type: 'emotion',
          label: emotion.emotion,
          value: emotion.intensity,
          color: '#10B981',
          metadata: {
            intensity: emotion.intensity,
            triggers: emotion.triggers?.join(', ') || 'None'
          }
        });
      });
    }

    // Process sensory inputs
    if (streamVisibility.sensory) {
      sensoryInputs.forEach((input, i) => {
        events.push({
          id: `sensory-${i}`,
          timestamp: input.timestamp,
          type: 'sensory',
          label: `${input.sensoryType}: ${input.response}`,
          color: '#3B82F6',
          metadata: {
            type: input.sensoryType,
            response: input.response
          }
        });
      });
    }

    // Process anomalies
    if (streamVisibility.anomalies) {
      anomalies.forEach((anomaly, i) => {
        events.push({
          id: `anomaly-${i}`,
          timestamp: anomaly.timestamp,
          type: 'anomaly',
          label: anomaly.type,
          color: anomaly.severity === 'high' ? '#EF4444' : 
                 anomaly.severity === 'medium' ? '#F59E0B' : '#FCD34D'
        });
      });
    }

    return events.filter(event => 
      isWithinInterval(event.timestamp, { start: timeRange[0], end: timeRange[1] })
    );
  }, [emotions, sensoryInputs, anomalies, timeRange, streamVisibility]);

  // Process data streams
  const dataStreams = useMemo((): DataStream[] => {
    const streams: DataStream[] = [];

    // Emotion intensity stream
    const emotionData = emotions
      .filter(e => isWithinInterval(e.timestamp, { start: timeRange[0], end: timeRange[1] }))
      .map(e => ({ timestamp: e.timestamp, value: e.intensity }))
      .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

    if (emotionData.length > 0) {
      streams.push({
        id: 'emotion-intensity',
        label: 'Emotion Intensity',
        color: '#10B981',
        data: emotionData,
        visible: streamVisibility.emotions,
        yScale: [0, 10]
      });
    }

    // Sensory response stream (aggregated by hour)
    const sensoryByHour = new Map<string, { seeking: number; avoiding: number; neutral: number }>();
    sensoryInputs
      .filter(s => isWithinInterval(s.timestamp, { start: timeRange[0], end: timeRange[1] }))
      .forEach(input => {
        const hourKey = format(input.timestamp, 'yyyy-MM-dd HH:00');
        if (!sensoryByHour.has(hourKey)) {
          sensoryByHour.set(hourKey, { seeking: 0, avoiding: 0, neutral: 0 });
        }
        const hourData = sensoryByHour.get(hourKey);
        if (!hourData) {
          // This shouldn't happen, but add safety check
          logger.warn('Unexpected missing hour data', { hourKey });
          return;
        }
        if (input.response?.includes('seeking')) hourData.seeking++;
        else if (input.response?.includes('avoiding')) hourData.avoiding++;
        else hourData.neutral++;
      });

    const sensoryData = Array.from(sensoryByHour.entries())
      .map(([hourKey, data]) => ({
        timestamp: new Date(hourKey),
        value: data.seeking - data.avoiding // Net sensory response
      }))
      .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

    if (sensoryData.length > 0) {
      const minValue = Math.min(...sensoryData.map(d => d.value));
      const maxValue = Math.max(...sensoryData.map(d => d.value));
      streams.push({
        id: 'sensory-response',
        label: 'Sensory Response (Seeking - Avoiding)',
        color: '#3B82F6',
        data: sensoryData,
        visible: streamVisibility.sensory,
        yScale: [minValue - 1, maxValue + 1]
      });
    }

    return streams;
  }, [emotions, sensoryInputs, timeRange, streamVisibility]);

  // Timeline calculations
  const timeScale = useCallback((date: Date): number => {
    const totalDuration = timeRange[1].getTime() - timeRange[0].getTime();
    const offset = date.getTime() - timeRange[0].getTime();
    return (offset / totalDuration) * dimensions.width * zoomLevel + panOffset;
  }, [timeRange, dimensions.width, zoomLevel, panOffset]);

  const inverseTimeScale = useCallback((x: number): Date => {
    const adjustedX = (x - panOffset) / zoomLevel;
    const totalDuration = timeRange[1].getTime() - timeRange[0].getTime();
    const offset = (adjustedX / dimensions.width) * totalDuration;
    return new Date(timeRange[0].getTime() + offset);
  }, [timeRange, dimensions.width, zoomLevel, panOffset]);

  // Handle zoom
  const handleZoom = (delta: number) => {
    const newZoom = Math.max(0.5, Math.min(10, zoomLevel + delta));
    setZoomLevel(newZoom);
  };

  // Handle pan
  const handlePan = (delta: number) => {
    setPanOffset(prev => prev + delta);
  };

  // Handle brush selection
  const handleMouseDown = useCallback((e: React.MouseEvent<SVGElement>) => {
    if (!svgRef.current) return;
    const rect = svgRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const startDate = inverseTimeScale(x);

    dragStartDateRef.current = startDate;
    setIsDragging(true);
  }, [inverseTimeScale]);

  // Handle mouse events for brush selection with proper cleanup
  useEffect(() => {
    if (!isDragging || !dragStartDateRef.current || !svgRef.current) return;

    const rect = svgRef.current.getBoundingClientRect();
    const startDate = dragStartDateRef.current;

    const handleMouseMove = (e: MouseEvent) => {
      const currentX = e.clientX - rect.left;
      const currentDate = inverseTimeScale(currentX);
      setBrushSelection([
        startDate < currentDate ? startDate : currentDate,
        startDate < currentDate ? currentDate : startDate
      ]);
    };

    const handleMouseUp = () => {
      if (brushSelection && onTimeRangeChange) {
        onTimeRangeChange(brushSelection[0], brushSelection[1]);
      }
      setIsDragging(false);
      dragStartDateRef.current = null;
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    // Cleanup function to remove listeners
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, brushSelection, onTimeRangeChange, inverseTimeScale]);

  // Playback animation
  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      setTimeRange(prev => {
        const duration = prev[1].getTime() - prev[0].getTime();
        const step = (duration / 100) * playbackSpeed;
        return [
          new Date(prev[0].getTime() + step),
          new Date(prev[1].getTime() + step)
        ];
      });
    }, 100);

    return () => clearInterval(interval);
  }, [isPlaying, playbackSpeed]);

  // Real-time updates
  useEffect(() => {
    if (!realtime) return;

    const interval = setInterval(() => {
      const now = new Date();
      setTimeRange(prev => {
        const duration = prev[1].getTime() - prev[0].getTime();
        return [
          new Date(now.getTime() - duration),
          now
        ];
      });
    }, 5000); // Update every 5 seconds

    return () => clearInterval(interval);
  }, [realtime]);

  // Render timeline grid
  const renderGrid = () => {
    const gridLines = [];
    const labels = [];
    const startTime = timeRange[0].getTime();
    const endTime = timeRange[1].getTime();
    const duration = endTime - startTime;

    // Determine appropriate time interval based on zoom
    let interval: number;
    let formatStr: string;
    if (duration < 3600000) { // Less than 1 hour
      interval = 300000; // 5 minutes
      formatStr = 'HH:mm';
    } else if (duration < 86400000) { // Less than 1 day
      interval = 3600000; // 1 hour
      formatStr = 'HH:mm';
    } else if (duration < 604800000) { // Less than 1 week
      interval = 86400000; // 1 day
      formatStr = 'MMM dd';
    } else {
      interval = 604800000; // 1 week
      formatStr = 'MMM dd';
    }

    for (let time = startTime; time <= endTime; time += interval) {
      const x = timeScale(new Date(time));
      if (x >= 0 && x <= dimensions.width) {
        gridLines.push(
          <line
            key={`grid-${time}`}
            x1={x}
            y1={0}
            x2={x}
            y2={dimensions.height}
            stroke="#e5e7eb"
            strokeDasharray="2,2"
          />
        );
        labels.push(
          <text
            key={`label-${time}`}
            x={x}
            y={dimensions.height - 5}
            textAnchor="middle"
            fontSize="12"
            fill="#6b7280"
          >
            {format(new Date(time), formatStr)}
          </text>
        );
      }
    }

    return { gridLines, labels };
  };

  // Render data streams
  const renderDataStreams = () => {
    return dataStreams.filter(stream => stream.visible).map(stream => {
      const yScale = (value: number) => {
        const [min, max] = stream.yScale;
        const normalized = (value - min) / (max - min);
        return dimensions.height * 0.8 - normalized * (dimensions.height * 0.6);
      };

      const pathData = stream.data
        .map((point, i) => {
          const x = timeScale(point.timestamp);
          const y = yScale(point.value);
          return `${i === 0 ? 'M' : 'L'} ${x} ${y}`;
        })
        .join(' ');

      return (
        <g key={stream.id}>
          <path
            d={pathData}
            fill="none"
            stroke={stream.color}
            strokeWidth="2"
            opacity="0.8"
          />
          {stream.data.map((point, i) => {
            const x = timeScale(point.timestamp);
            const y = yScale(point.value);
            if (x >= 0 && x <= dimensions.width) {
              return (
                <circle
                  key={`${stream.id}-${i}`}
                  cx={x}
                  cy={y}
                  r="3"
                  fill={stream.color}
                  className="cursor-pointer hover:r-5"
                  onMouseEnter={() => setHoveredEvent({
                    id: `${stream.id}-${i}`,
                    timestamp: point.timestamp,
                    type: 'tracking',
                    label: `${stream.label}: ${point.value.toFixed(1)}`,
                    value: point.value,
                    color: stream.color
                  })}
                  onMouseLeave={() => setHoveredEvent(null)}
                />
              );
            }
            return null;
          })}
        </g>
      );
    });
  };

  // Render events
  const renderEvents = () => {
    return timelineEvents.map(event => {
      const x = timeScale(event.timestamp);
      if (x < 0 || x > dimensions.width) return null;

      const getIcon = () => {
        switch (event.type) {
          case 'emotion': return <Brain className="h-4 w-4" />;
          case 'sensory': return <Eye className="h-4 w-4" />;
          case 'anomaly': return <AlertCircle className="h-4 w-4" />;
          default: return <Activity className="h-4 w-4" />;
        }
      };

      return (
        <g key={event.id}>
          <line
            x1={x}
            y1={dimensions.height * 0.2}
            x2={x}
            y2={dimensions.height * 0.8}
            stroke={event.color}
            strokeWidth="1"
            opacity="0.3"
          />
          <circle
            cx={x}
            cy={dimensions.height * 0.2}
            r="8"
            fill={event.color}
            className="cursor-pointer"
            onMouseEnter={() => setHoveredEvent(event)}
            onMouseLeave={() => setHoveredEvent(null)}
          />
        </g>
      );
    });
  };

  // Render brush selection
  const renderBrushSelection = () => {
    if (!brushSelection) return null;

    const x1 = timeScale(brushSelection[0]);
    const x2 = timeScale(brushSelection[1]);

    return (
      <rect
        x={Math.min(x1, x2)}
        y={0}
        width={Math.abs(x2 - x1)}
        height={dimensions.height}
        fill="hsl(var(--primary))"
        opacity="0.1"
        stroke="hsl(var(--primary))"
        strokeWidth="2"
        strokeDasharray="4,2"
      />
    );
  };

  const { gridLines, labels } = useMemo(renderGrid, [timeScale, dimensions.width, dimensions.height]);

  const renderedDataStreams = useMemo(renderDataStreams, [dataStreams, timeScale, dimensions.height]);

  return (
    <Card className="w-full">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Clock className="h-5 w-5" />
            Timeline Visualization
          </CardTitle>
          <div className="flex items-center gap-2">
            {realtime && (
              <Badge variant="default" className="animate-pulse">
                Live
              </Badge>
            )}
            <Button
              size="sm"
              variant="outline"
              onClick={() => {
                setZoomLevel(1);
                setPanOffset(0);
                setBrushSelection(null);
              }}
            >
              <RotateCcw className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {/* Controls */}
        <div className="mb-4 space-y-4">
          <div className="flex items-center gap-4">
            {/* Zoom controls */}
            <div className="flex items-center gap-2">
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleZoom(-0.5)}
                disabled={zoomLevel <= 0.5}
              >
                <ZoomOut className="h-4 w-4" />
              </Button>
              <span className="text-sm font-medium w-16 text-center">
                {Math.round(zoomLevel * 100)}%
              </span>
              <Button
                size="sm"
                variant="outline"
                onClick={() => handleZoom(0.5)}
                disabled={zoomLevel >= 10}
              >
                <ZoomIn className="h-4 w-4" />
              </Button>
            </div>

            {/* Pan controls */}
            <div className="flex items-center gap-2">
              <Button
                size="sm"
                variant="outline"
                onClick={() => handlePan(50)}
              >
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={() => handlePan(-50)}
              >
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>

            {/* Playback controls */}
            <div className="flex items-center gap-2">
              <Button
                size="sm"
                variant={isPlaying ? 'default' : 'outline'}
                onClick={() => setIsPlaying(!isPlaying)}
              >
                {isPlaying ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
              </Button>
              <Slider
                value={[playbackSpeed]}
                onValueChange={([value]) => setPlaybackSpeed(value)}
                min={0.25}
                max={4}
                step={0.25}
                className="w-24"
              />
              <span className="text-sm text-muted-foreground">
                {playbackSpeed}x
              </span>
            </div>

            {/* Stream visibility toggles */}
            <div className="flex items-center gap-2 ml-auto">
              <Toggle
                size="sm"
                pressed={streamVisibility.emotions}
                onPressedChange={(pressed) => 
                  setStreamVisibility(prev => ({ ...prev, emotions: pressed }))
                }
              >
                <Brain className="h-4 w-4 mr-1" />
                Emotions
              </Toggle>
              <Toggle
                size="sm"
                pressed={streamVisibility.sensory}
                onPressedChange={(pressed) => 
                  setStreamVisibility(prev => ({ ...prev, sensory: pressed }))
                }
              >
                <Eye className="h-4 w-4 mr-1" />
                Sensory
              </Toggle>
              <Toggle
                size="sm"
                pressed={streamVisibility.anomalies}
                onPressedChange={(pressed) => 
                  setStreamVisibility(prev => ({ ...prev, anomalies: pressed }))
                }
              >
                <AlertCircle className="h-4 w-4 mr-1" />
                Anomalies
              </Toggle>
            </div>
          </div>

          {/* Time range display */}
          <div className="flex items-center justify-between text-sm text-muted-foreground">
            <span>{format(timeRange[0], 'MMM dd, yyyy HH:mm')}</span>
            <span className="font-medium">
              {differenceInMinutes(timeRange[1], timeRange[0])} minutes
            </span>
            <span>{format(timeRange[1], 'MMM dd, yyyy HH:mm')}</span>
          </div>
        </div>

        {/* Timeline visualization */}
        <div ref={containerRef} className="relative bg-gray-50 rounded-lg overflow-hidden">
          <svg
            ref={svgRef}
            width={dimensions.width}
            height={dimensions.height}
            className="cursor-crosshair"
            onMouseDown={handleMouseDown}
          >
            {/* Grid lines */}
            {gridLines}

            {/* Data streams */}
            {renderedDataStreams}

            {/* Events */}
            {renderEvents()}

            {/* Brush selection */}
            {renderBrushSelection()}

            {/* Time labels */}
            {labels}

            {/* Current time indicator (for realtime mode) */}
            {realtime && (
              <line
                x1={timeScale(new Date())}
                y1={0}
                x2={timeScale(new Date())}
                y2={dimensions.height}
                stroke="#ef4444"
                strokeWidth="2"
                strokeDasharray="4,2"
              />
            )}
          </svg>

          {/* Tooltip */}
          {hoveredEvent && (
            <div
              className={cn(
                "absolute bg-background border rounded-lg shadow-lg p-3 pointer-events-none",
                "top-5"
              )}
              style={{
                left: `${Math.min(timeScale(hoveredEvent.timestamp), dimensions.width - 200)}px`
              }}
            >
              <div className="flex items-center gap-2 mb-1">
                <div 
                  className={cn("w-3 h-3 rounded-full")}
                  style={{ backgroundColor: hoveredEvent.color }}
                />
                <span className="font-medium capitalize">{hoveredEvent.type}</span>
              </div>
              <p className="text-sm">{hoveredEvent.label}</p>
              <p className="text-xs text-muted-foreground">
                {format(hoveredEvent.timestamp, 'MMM dd, yyyy HH:mm:ss')}
              </p>
              {hoveredEvent.metadata && (
                <div className="mt-1 pt-1 border-t">
                  {Object.entries(hoveredEvent.metadata).map(([key, value]) => (
                    <p key={key} className="text-xs text-muted-foreground">
                      {key}: {value}
                    </p>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Legend */}
          <div className="absolute bottom-4 left-4 bg-background/90 backdrop-blur-sm rounded-lg p-3 shadow-lg">
            <h4 className="font-medium text-sm mb-2">Data Streams</h4>
            <div className="space-y-1">
              {dataStreams.filter(s => s.visible).map(stream => (
                <div key={stream.id} className="flex items-center gap-2">
                  <div 
                    className={cn("w-3 h-3 rounded-full")}
                    style={{ backgroundColor: stream.color }}
                  />
                  <span className="text-xs">{stream.label}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Stats */}
          <div className="absolute top-4 right-4 bg-background/90 backdrop-blur-sm rounded-lg p-3 shadow-lg">
            <div className="text-xs space-y-1">
              <p>Events: {timelineEvents.length}</p>
              <p>Zoom: {Math.round(zoomLevel * 100)}%</p>
              {brushSelection && (
                <p>Selection: {differenceInMinutes(brushSelection[1], brushSelection[0])} min</p>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

