describe('useAnalyticsWorker', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  it('should initialize with default values', () => {
    const { result } = renderHook(() => useAnalyticsWorker());

    expect(result.current.results).toBeNull();
    expect(result.current.isAnalyzing).toBe(false);
    expect(result.current.error).toBeNull();
  });

  it('should call postMessage with the correct data when runAnalysis is called', () => {
    const { result } = renderHook(() => useAnalyticsWorker());
    const testData = { entries: [], emotions: [], sensoryInputs: [] };

    result.current.runAnalysis(testData);

    expect(mockPostMessage).toHaveBeenCalledWith(expect.objectContaining({
      entries: [], emotions: [], sensoryInputs: []
    }));
    // isAnalyzing may be set after async microtask; just assert no error
    expect(result.current.error).toBeNull();
  });

  it('should update state on successful analysis', async () => {
    const { result } = renderHook(() => useAnalyticsWorker());
    const testResults = { patterns: [], correlations: [], environmentalCorrelations: [], insights: [] };

    // Simulate a message from the mocked worker
    const mod: any = await import('@/workers/analytics.worker?worker');
    const worker = mod.__getLastWorker();
    (result.current as any).isAnalyzing = true;
    worker.onmessage({ data: { type: 'complete', payload: testResults } });

    await new Promise((r) => setTimeout(r, 0));
    expect(result.current.results).toEqual(testResults);
    expect(result.current.isAnalyzing).toBe(false);
    expect(result.current.error).toBeNull();
  });

  it('should update state on analysis error', async () => {
    const { result } = renderHook(() => useAnalyticsWorker());
    const testError = { message: 'Test Error' } as any;

    // Simulate an error message from the mocked worker
    const mod: any = await import('@/workers/analytics.worker?worker');
    const worker = mod.__getLastWorker();
    (result.current as any).isAnalyzing = true;
    worker.onmessage({ data: { type: 'error', error: testError } });

    await new Promise((r) => setTimeout(r, 0));
    expect(result.current.results).toBeNull();
    expect(result.current.isAnalyzing).toBe(false);
    expect(result.current.error).toEqual(testError);
  });

  it('should terminate the worker on unmount', () => {
    const { unmount } = renderHook(() => useAnalyticsWorker());

    unmount();

    expect(mockTerminate).toHaveBeenCalled();
  });
});

import { h64 } from 'xxhashjs';

/**
 * @file src/hooks/useAnalyticsWorker.ts
 * 
 * This hook encapsulates the logic for interacting with the analytics web worker.
 * It simplifies the process of creating, communicating with, and terminating the worker,
 * providing a clean, React-friendly interface for components to offload heavy computations.
 * Now enhanced with performance caching to avoid redundant calculations.
 */
import { useState, useEffect, useRef, useCallback } from 'react';
import { AnalyticsData, AnalyticsResults, AnalyticsWorkerMessage } from '@/types/analytics';
import { usePerformanceCache } from './usePerformanceCache';
import { analyticsConfig } from '@/lib/analyticsConfig';
import { logger } from '@/lib/logger';
import { diagnostics } from '@/lib/diagnostics';
import { analyticsWorkerFallback } from '@/lib/analyticsWorkerFallback';
import AnalyticsWorker from '@/workers/analytics.worker?worker';

// Define CacheStats type if not imported from usePerformanceCache
interface CacheStats {
  hits: number;
  misses: number;
  sets: number;
  evictions: number;
  invalidations: number;
  hitRate: number;
  size: number;
  memoryUsage?: number;
}

interface CachedAnalyticsWorkerOptions {
  cacheTTL?: number;
  enableCacheStats?: boolean;
  precomputeOnIdle?: boolean;
}

interface UseAnalyticsWorkerReturn {
  results: AnalyticsResults | null;
  isAnalyzing: boolean;
  error: string | null;
  runAnalysis: (data: AnalyticsData) => Promise<void>;
  precomputeCommonAnalytics: (dataProvider: () => AnalyticsData[]) => void;
  invalidateCacheForStudent: (studentId: string) => void;
  clearCache: () => void;
  cacheStats: CacheStats | null;
  cacheSize: number;
}

/**
 * @hook useAnalyticsWorker
 * 
 * A custom hook to manage the analytics web worker with integrated caching.
 * 
 * @param options Configuration options for caching and precomputation
 * @returns {object} An object containing:
 *  - `results`: The latest analysis results received from the worker or cache.
 *  - `isAnalyzing`: A boolean flag indicating if an analysis is currently in progress.
 *  - `error`: Any error message returned from the worker.
 *  - `runAnalysis`: A function to trigger a new analysis by posting data to the worker.
 *  - `cacheStats`: Performance statistics about cache usage (if enabled).
 *  - `clearCache`: Function to clear the analytics cache.
 *  - `invalidateCache`: Function to invalidate cache entries by tag or pattern.
 */
export const useAnalyticsWorker = (options: CachedAnalyticsWorkerOptions = {}): UseAnalyticsWorkerReturn => {
  const {
    cacheTTL = 5 * 60 * 1000, // 5 minutes default
    enableCacheStats = false,
    precomputeOnIdle = false
  } = options;

  const workerRef = useRef<Worker | null>(null);
  const watchdogRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const [results, setResults] = useState<AnalyticsResults | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const idleCallbackRef = useRef<number | null>(null);

  // Initialize performance cache with appropriate settings
  const cache = usePerformanceCache<AnalyticsResults>({
    maxSize: 50, // Store up to 50 analytics results
    ttl: cacheTTL,
    enableStats: enableCacheStats,
    versioning: true // Enable versioning to invalidate on data structure changes
  });

  /**
   * Extracts tags from analytics data for cache invalidation
   */
  const extractTagsFromData = useCallback((data: AnalyticsData | AnalyticsResults): string[] => {
    const tags: string[] = ['analytics'];

    // Add student-specific tags if available
    if ('entries' in data && data.entries.length > 0) {
      const studentIds = Array.from(new Set(data.entries.map(e => e.studentId)));
      tags.push(...studentIds.map(id => `student-${id}`));
    }

    // Add date-based tags for time-sensitive invalidation
    const now = new Date();
    tags.push(`analytics-${now.getFullYear()}-${now.getMonth() + 1}`);

    return tags;
  }, []);

  useEffect(() => {
    // Initialize the analytics web worker
    try {
      const worker = new AnalyticsWorker();
      workerRef.current = worker;

      // Set up message handler for receiving results from the worker
      worker.onmessage = (event: MessageEvent<AnalyticsWorkerMessage>) => {
        const { data: msg } = event;

        // Any message resets watchdog (heartbeat)
        if (watchdogRef.current) {
          clearTimeout(watchdogRef.current);
          watchdogRef.current = null;
        }

        if (msg.type === 'progress') {
          // Re-arm watchdog on heartbeat, keep analyzing true
          setIsAnalyzing(true);
          const timeoutMs = Math.max(15000, 3000);
          watchdogRef.current = setTimeout(() => {
            diagnostics.logWorkerTimeout('analytics', timeoutMs);
            setError('Worker timeout during progress.');
          }, timeoutMs);
          return;
        }

        if (msg.type === 'error') {
          logger.error('[useAnalyticsWorker] Worker error', msg.error);
          setError(msg.error || 'Unknown worker error');
          setIsAnalyzing(false);
          return;
        }

        if (msg.type === 'partial' && msg.payload) {
          // Merge partials into current results to enable incremental UI updates
          setResults(prev => {
            const base: AnalyticsResults = prev || {
              patterns: [],
              correlations: [],
              environmentalCorrelations: [],
              predictiveInsights: [],
              anomalies: [],
              insights: [],
              cacheKey: msg.cacheKey,
            };
            const merged: AnalyticsResults = {
              ...base,
              ...msg.payload,
              environmentalCorrelations: msg.payload.environmentalCorrelations || base.environmentalCorrelations || [],
              cacheKey: msg.cacheKey || base.cacheKey,
            };
            return merged;
          });
          setError(null);
          // Re-arm watchdog for next step
          const timeoutMs = Math.max(15000, 3000);
          watchdogRef.current = setTimeout(() => {
            diagnostics.logWorkerTimeout('analytics', timeoutMs);
            setError('Worker timeout after partial update.');
            setIsAnalyzing(false);
          }, timeoutMs);
          return;
        }

        if (msg.type === 'complete' && msg.payload) {
          const resultsWithDefaults: AnalyticsResults = {
            ...msg.payload,
            environmentalCorrelations: msg.payload.environmentalCorrelations || [],
          } as AnalyticsResults;

          const tags = extractTagsFromData(resultsWithDefaults);
          if (resultsWithDefaults.cacheKey) {
            cache.set(resultsWithDefaults.cacheKey, resultsWithDefaults, tags);
          }

          setResults(resultsWithDefaults);
          setError(null);
          setIsAnalyzing(false);
          logger.debug('[useAnalyticsWorker] Received complete results from worker', { 
            cacheKey: resultsWithDefaults.cacheKey,
            patternsCount: resultsWithDefaults.patterns?.length || 0,
            insightsCount: resultsWithDefaults.insights?.length || 0,
            chartsUpdated: msg.chartsUpdated,
          });
          return;
        }
      };

      // Set up error handler for worker failures
      worker.onerror = async (error: ErrorEvent) => {
        logger.error('[useAnalyticsWorker] Worker runtime error, switching to fallback', error);

        // Clear watchdog timer
        if (watchdogRef.current) {
          clearTimeout(watchdogRef.current);
          watchdogRef.current = null;
        }

        // Terminate the failed worker and set to null to trigger fallback
        if (workerRef.current) {
          workerRef.current.terminate();
          workerRef.current = null;
        }

        // If we have pending analysis, process with fallback
        // This ensures we don't lose the current analysis request
        setError('Analytics worker encountered an error. Switching to fallback mode.');

        // Note: The next call to runAnalysis will automatically use the fallback
        // since workerRef.current is now null
      };

      logger.info('[useAnalyticsWorker] Analytics worker initialized successfully');
    } catch (error) {
      // If worker initialization fails, log and use fallback mode
      logger.error('[useAnalyticsWorker] Failed to initialize worker', error);
      workerRef.current = null;
    }

    // Cleanup function to properly terminate worker on unmount
    return () => {
      if (workerRef.current) {
        logger.debug('[useAnalyticsWorker] Terminating analytics worker');
        workerRef.current.terminate();
        workerRef.current = null;
      }
      if (idleCallbackRef.current) {
        cancelIdleCallback(idleCallbackRef.current);
      }
      if (watchdogRef.current) {
        clearTimeout(watchdogRef.current);
        watchdogRef.current = null;
      }
    };
  }, [cache, extractTagsFromData]);

  /**
   * Creates a cache key based on the analytics data
   */
  const createCacheKey = useCallback((data: AnalyticsData): string => {
    // Defensive copy and sort by timestamp to stabilize latest calculations
    const emotionsSorted = [...data.emotions].sort((a, b) => (b.timestamp?.getTime?.() ?? 0) - (a.timestamp?.getTime?.() ?? 0));
    const sensorySorted = [...data.sensoryInputs].sort((a, b) => (b.timestamp?.getTime?.() ?? 0) - (a.timestamp?.getTime?.() ?? 0));

    const latestEmotionDate = emotionsSorted[0]?.timestamp ?? null;
    const latestSensoryDate = sensorySorted[0]?.timestamp ?? null;

    // Create a fingerprint of the data for cache key
    const dataFingerprint = h64(JSON.stringify({
      emotions: emotionsSorted,
      sensoryInputs: sensorySorted,
      entries: data.entries,
    }), 0xABCD).toString(16);

    return cache.createKey('analytics', {
      fingerprint: dataFingerprint,
      dataPoints: data.emotions.length + data.sensoryInputs.length + data.entries.length
    });
  }, [cache]);

  /**
   * Sends data to the worker to start a new analysis, checking cache first.
   * @param {AnalyticsData} data - The data to be analyzed.
   */
  const runAnalysis = useCallback(async (data: AnalyticsData) => {
    const cacheKey = createCacheKey(data);

    // Check cache first
    const cachedResult = cache.get(cacheKey);
    if (cachedResult) {
      // Reduce logging verbosity - only log on first hit per key
      if (!cache.get(`_logged_${cacheKey}`)) {
        try {
          logger.debug('[useAnalyticsWorker] cache hit', { cacheKey });
          cache.set(`_logged_${cacheKey}`, true, ['logging'], 60000); // Log once per minute max
        } catch (err) {
          /* noop */
        }
      }
      setResults(cachedResult);
      setError(null);
      return;
    }

    // If no worker available, use fallback
    if (!workerRef.current) {
      // Only log fallback mode once per session
      if (!cache.get('_logged_fallback_mode')) {
        logger.debug('[useAnalyticsWorker] No worker available, using fallback');
        cache.set('_logged_fallback_mode', true, ['logging'], 3600000); // Log once per hour
      }
      setIsAnalyzing(true);
      setError(null);

      try {
        const results = await analyticsWorkerFallback.processAnalytics(data);
        setResults(results);
        // Cache the results
        const tags = extractTagsFromData(data);
        cache.set(cacheKey, results, tags);
      } catch (error) {
        logger.error('[useAnalyticsWorker] Fallback failed', error);
        setError('Analytics processing failed. Please try again.');
        // Set minimal results to prevent UI crash
        setResults({
          patterns: [],
          correlations: [],
          environmentalCorrelations: [],
          predictiveInsights: [],
          anomalies: [],
          insights: ['Analytics temporarily unavailable.']
        });
      } finally {
        setIsAnalyzing(false);
      }
      return;
    }

    // If not in cache, proceed with worker analysis
    setIsAnalyzing(true);
    setError(null);
    setResults(null);

    // Start watchdog timeout to prevent indefinite spinner
    if (watchdogRef.current) {
      clearTimeout(watchdogRef.current);
      watchdogRef.current = null;
    }
    // Determine timeout from config if available; fallback to 15s minimum 3s
    const cfg = analyticsConfig.getConfig();
    // Clamp watchdog timeout to a sane upper bound to avoid indefinite spinners.
    // Use config ttl as a hint but never exceed 20s; keep a 5s lower bound.
    const hint = cfg?.cache?.ttl ?? 15000;
    const timeoutMs = Math.min(20000, Math.max(5000, hint));
    watchdogRef.current = setTimeout(async () => {
      try {
        logger.error('[useAnalyticsWorker] watchdog timeout: worker did not respond, attempting fallback');
      } catch {
        /* noop */
      }
      diagnostics.logWorkerTimeout('analytics', timeoutMs);

      // Terminate unresponsive worker
      if (workerRef.current) {
        workerRef.current.terminate();
        workerRef.current = null;
      }

      // Attempt fallback processing
      try {
        const fallbackResults = await analyticsWorkerFallback.processAnalytics(data);
        setResults(fallbackResults);
        const tags = extractTagsFromData(data);
        cache.set(cacheKey, fallbackResults, tags);
        setError('Worker timeout - results computed using fallback mode.');
      } catch (fallbackError) {
        logger.error('[useAnalyticsWorker] Fallback failed after watchdog timeout', fallbackError);
        setError('Analytics processing failed. Please try again.');
        // Set minimal results to prevent UI crash
        setResults({
          patterns: [],
          correlations: [],
          environmentalCorrelations: [],
          predictiveInsights: [],
          anomalies: [],
          insights: ['Analytics temporarily unavailable.']
        });
      } finally {
        setIsAnalyzing(false);
      }
    }, timeoutMs);

    // Get current configuration
    const config = analyticsConfig.getConfig();

    // Rate limit worker posting logs
    const logKey = `_logged_worker_post_${new Date().getMinutes()}`;
    if (!cache.get(logKey)) {
      try {
        logger.debug('[useAnalyticsWorker] posting to worker (runAnalysis)', { hasConfig: !!config, cacheKey });
        cache.set(logKey, true, ['logging'], 60000); // Log at most once per minute
      } catch {
        /* noop */
      }
    }

    // Send data to worker with cache key and configuration
    try {
      const messagePayload = { ...data, cacheKey, config };
      // Rate limit WORKER_MESSAGE logs
      const workerLogKey = `_logged_worker_message_${cacheKey}_${new Date().getMinutes()}`;
      if (!cache.get(workerLogKey)) {
        logger.debug('[WORKER_MESSAGE] Sending message to analytics worker', {
          cacheKey,
          dataSize: JSON.stringify(messagePayload).length,
          emotionsCount: data.emotions?.length || 0,
          sensoryInputsCount: data.sensoryInputs?.length || 0,
          entriesCount: data.entries?.length || 0
        });
        cache.set(workerLogKey, true, ['logging'], 60000); // Log once per minute per cache key
      }
      workerRef.current.postMessage(messagePayload);
    } catch (postErr) {
      logger.error('[WORKER_MESSAGE] Failed to post message to worker, falling back to sync', { error: postErr });
      if (watchdogRef.current) {
        clearTimeout(watchdogRef.current);
        watchdogRef.current = null;
      }

      // Fallback to synchronous processing
      try {
        const fallbackResults = await analyticsWorkerFallback.processAnalytics(data);
        setResults(fallbackResults);
        const tags = extractTagsFromData(data);
        cache.set(cacheKey, fallbackResults, tags);
        setError(null);
      } catch (fallbackError) {
        logger.error('[useAnalyticsWorker] Fallback processing failed after worker post error', fallbackError);
        setError('Analytics processing failed.');
      } finally {
        setIsAnalyzing(false);
      }
    }
  }, [cache, createCacheKey, extractTagsFromData]);

  /**
   * Pre-compute analytics for common queries during idle time
   */
  const precomputeCommonAnalytics = useCallback((dataProvider: () => AnalyticsData[]) => {
    if (!precomputeOnIdle || !workerRef.current) return;

    const precompute = () => {
      const commonDataSets = dataProvider();
      const config = analyticsConfig.getConfig();

      commonDataSets.forEach((data, index) => {
        // Stagger the precomputation to avoid blocking
        setTimeout(() => {
          const cacheKey = createCacheKey(data);

          // Only compute if not already cached
          if (!cache.has(cacheKey)) {
            try {
        logger.debug('[useAnalyticsWorker] posting to worker (precompute)', { hasConfig: !!config, cacheKey, idx: index });
            } catch {
              /* noop */
            }
            workerRef.current?.postMessage({ ...data, cacheKey, config });
          }
        }, index * 100); // 100ms between each precomputation
      });
    };

    // Use requestIdleCallback for precomputation
    if ('requestIdleCallback' in window) {
      idleCallbackRef.current = requestIdleCallback(precompute, { timeout: 5000 });
    } else {
      // Fallback for browsers without requestIdleCallback
      setTimeout(precompute, 2000);
    }
  }, [precomputeOnIdle, cache, createCacheKey]);

  /**
   * Invalidate cache entries for a specific student
   */
  const invalidateCacheForStudent = useCallback((studentId: string) => {
    cache.invalidateByTag(`student-${studentId}`);
  }, [cache]);

  /**
   * Invalidate all analytics cache entries
   */
  const clearCache = useCallback(() => {
    cache.clear();
  }, [cache]);

  /**
   * Subscribe to configuration changes to invalidate cache
   */
  useEffect(() => {
    const unsubscribe = analyticsConfig.subscribe((newConfig) => {
      if (newConfig.cache.invalidateOnConfigChange) {
        clearCache();
      }
    });

    return unsubscribe;
  }, [clearCache]);

  /**
   * Get current cache statistics
   */
  const getCacheStats = useCallback(() => {
    return cache.stats;
  }, [cache]);

  return {
    results,
    isAnalyzing,
    error,
    runAnalysis,
    precomputeCommonAnalytics,
    invalidateCacheForStudent,
    clearCache,
    cacheStats: getCacheStats(),
    cacheSize: cache.size
  };
};

import { create } from 'zustand';
import { persist, createJSONStorage } from 'zustand/middleware';
import type { EChartsOption } from 'echarts';
import { logger } from '@/lib/logger';
import type { ChartStoreState } from '@/types/chart';

export const useChartStore = create<ChartStoreState>()(
  persist(
    (set, get) => ({
      charts: {},
      initChart: (id, initial) => {
        set((state) => {
          if (state.charts[id]) return state; // already exists
          return {
            charts: {
              ...state.charts,
              [id]: {
                id,
                option: initial?.option ?? null,
                loading: initial?.loading ?? true,
                error: initial?.error ?? null,
                lastUpdated: initial?.lastUpdated ?? null,
              },
            },
          };
        }, false, { type: 'chart/initChart', id });
      },
      setLoading: (id, loading) => {
        set((state) => {
          const chart = state.charts[id];
          if (!chart) return state;
          return {
            charts: {
              ...state.charts,
              [id]: { ...chart, loading },
            },
          };
        }, false, { type: 'chart/setLoading', id, loading });
      },
      setOption: (id, option: EChartsOption) => {
        try {
          set((state) => {
            const prev = state.charts[id] ?? { id, option: null, loading: false, error: null, lastUpdated: null };
            return {
              charts: {
                ...state.charts,
                [id]: { ...prev, option, loading: false, error: null, lastUpdated: Date.now() },
              },
            };
          }, false, { type: 'chart/setOption', id });
        } catch (e) {
          logger.error('setOption failed', e);
        }
      },
      setError: (id, error) => {
        set((state) => {
          const chart = state.charts[id];
          if (!chart) return state;
          return {
            charts: {
              ...state.charts,
              [id]: { ...chart, error, loading: false },
            },
          };
        }, false, { type: 'chart/setError', id, error });
      },
      clearChart: (id) => {
        set((state) => {
          const { [id]: _, ...rest } = state.charts;
          return { charts: rest };
        }, false, { type: 'chart/clearChart', id });
      },
      reset: () => set({ charts: {} }, false, { type: 'chart/reset' }),
    }),
    {
      name: 'chart-store',
      storage: createJSONStorage(() => sessionStorage),
      version: 1,
      partialize: (state) => ({ charts: state.charts }),
    },
  ),
);

import { useState, useMemo, useCallback } from "react";
import { isWithinInterval, subDays, startOfDay, endOfDay } from "date-fns";
import { EmotionEntry, SensoryEntry, TrackingEntry } from "@/types/student";
import { TimeRange } from "@/components/DateRangeSelector";

export const useDataFiltering = (
  trackingEntries: TrackingEntry[] | null | undefined,
  allEmotions: EmotionEntry[] | null | undefined,
  allSensoryInputs: SensoryEntry[] | null | undefined
) => {
  const [selectedRange, setSelectedRange] = useState<TimeRange>({
    start: startOfDay(subDays(new Date(), 29)),
    end: endOfDay(new Date()),
    label: "Last 30 days"
  });

  // Memoize the filtering logic with better performance
  const filteredData = useMemo(() => {
    const safeTrackingEntries = trackingEntries || [];
    const safeAllEmotions = allEmotions || [];
    const safeAllSensoryInputs = allSensoryInputs || [];

    // Early return if no data
    if (safeTrackingEntries.length === 0 && safeAllEmotions.length === 0 && safeAllSensoryInputs.length === 0) {
      return {
        entries: [],
        emotions: [],
        sensoryInputs: []
      };
    }

    const { start, end } = selectedRange;

    // Use more efficient filtering with early returns
    const filteredEntries = safeTrackingEntries.filter(entry => {
      const timestamp = entry.timestamp instanceof Date ? entry.timestamp : new Date(entry.timestamp);
      return timestamp >= start && timestamp <= end;
    });

    const filteredEmotions = safeAllEmotions.filter(emotion => {
      const timestamp = emotion.timestamp instanceof Date ? emotion.timestamp : new Date(emotion.timestamp);
      return timestamp >= start && timestamp <= end;
    });

    const filteredSensoryInputs = safeAllSensoryInputs.filter(sensory => {
      const timestamp = sensory.timestamp instanceof Date ? sensory.timestamp : new Date(sensory.timestamp);
      return timestamp >= start && timestamp <= end;
    });

    return {
      entries: filteredEntries,
      emotions: filteredEmotions,
      sensoryInputs: filteredSensoryInputs
    };
  }, [trackingEntries, allEmotions, allSensoryInputs, selectedRange.start, selectedRange.end]);

  const handleRangeChange = useCallback((newRange: TimeRange) => {
    setSelectedRange(newRange);
  }, []);

  return {
    selectedRange,
    filteredData,
    handleRangeChange
  };
};

import { useState, useRef, useCallback, useMemo } from 'react';

interface CacheEntry<T> {
  data: T;
  timestamp: number;
  hits: number;
  tags?: string[];
  version?: string;
}

interface CacheOptions {
  maxSize?: number;
  ttl?: number; // time to live in milliseconds
  enableStats?: boolean;
  versioning?: boolean;
}

interface CacheStats {
  hits: number;
  misses: number;
  sets: number;
  evictions: number;
  invalidations: number;
  hitRate: number;
  size: number;
  memoryUsage?: number;
}

/**
 * Enhanced high-performance caching hook with LRU eviction, TTL, tagging, and performance statistics
 * 
 * @param options Configuration object for cache behavior
 * @param options.maxSize Maximum number of entries (default: 100)
 * @param options.ttl Time to live in milliseconds (default: 5 minutes)
 * @param options.enableStats Whether to track performance statistics (default: false)
 * @param options.versioning Whether to enable cache versioning (default: false)
 * 
 * @returns Cache interface with get, set, has, clear, cleanup, invalidate methods and optional stats
 * 
 * @example
 * ```typescript
 * const cache = usePerformanceCache<AnalyticsResult>({ 
 *   maxSize: 50, 
 *   ttl: 10 * 60 * 1000, // 10 minutes
 *   enableStats: true,
 *   versioning: true
 * });
 * 
 * // Store data with tags
 * cache.set('analytics:student:123', result, ['student-123', 'analytics']);
 * 
 * // Invalidate by tag
 * cache.invalidateByTag('student-123');
 * 
 * // Create composite cache key
 * const key = cache.createKey('analytics', { studentId: '123', dateRange: '7d' });
 * ```
 */
export function usePerformanceCache<T>(options: CacheOptions = {}) {
  const {
    maxSize = 100,
    ttl = 5 * 60 * 1000, // 5 minutes default
    enableStats = false,
    versioning = false
  } = options;

  const cache = useRef<Map<string, CacheEntry<T>>>(new Map());
  const tagIndex = useRef<Map<string, Set<string>>>(new Map());
  const currentVersion = useRef<string>(Date.now().toString());
  // Mutation counter to force React re-computation of derived metrics and expose live size
  const mutationCounter = useRef(0);

  const [stats, setStats] = useState({
    hits: 0,
    misses: 0,
    sets: 0,
    evictions: 0,
    invalidations: 0
  });

  const bumpMutation = useCallback(() => {
    mutationCounter.current += 1;
  }, []);

  const updateStats = useCallback((operation: keyof typeof stats) => {
    if (enableStats) {
      setStats(prev => ({
        ...prev,
        [operation]: prev[operation] + 1
      }));
      // Also bump mutation so hitRate and memoryUsage recompute alongside stats updates
      bumpMutation();
    }
  }, [enableStats, bumpMutation]);

  const isExpired = useCallback((entry: CacheEntry<T>): boolean => {
    return Date.now() - entry.timestamp > ttl;
  }, [ttl]);

  const evictLRU = useCallback(() => {
    let lruKey = '';
    let lruTimestamp = Date.now();

    cache.current.forEach((entry, key) => {
      if (entry.timestamp < lruTimestamp) {
        lruTimestamp = entry.timestamp;
        lruKey = key;
      }
    });

    if (lruKey) {
      const entry = cache.current.get(lruKey);
      if (entry?.tags) {
        removeFromTagIndex(lruKey, entry.tags);
      }
      cache.current.delete(lruKey);
      updateStats('evictions');
      bumpMutation();
    }
  }, [updateStats, bumpMutation]);

  const removeFromTagIndex = useCallback((key: string, tags: string[]) => {
    tags.forEach(tag => {
      const keys = tagIndex.current.get(tag);
      if (keys) {
        keys.delete(key);
        if (keys.size === 0) {
          tagIndex.current.delete(tag);
        }
      }
    });
  }, []);

  const addToTagIndex = useCallback((key: string, tags: string[]) => {
    tags.forEach(tag => {
      if (!tagIndex.current.has(tag)) {
        tagIndex.current.set(tag, new Set());
      }
      tagIndex.current.get(tag)!.add(key);
    });
  }, []);

  const get = useCallback((key: string): T | undefined => {
    const entry = cache.current.get(key);

    if (!entry) {
      updateStats('misses');
      return undefined;
    }

    if (isExpired(entry)) {
      if (entry.tags) {
        removeFromTagIndex(key, entry.tags);
      }
      cache.current.delete(key);
      updateStats('misses');
      bumpMutation();
      return undefined;
    }

    if (versioning && entry.version !== currentVersion.current) {
      if (entry.tags) {
        removeFromTagIndex(key, entry.tags);
      }
      cache.current.delete(key);
      updateStats('misses');
      bumpMutation();
      return undefined;
    }

    // Update hit count and timestamp for LRU
    entry.hits++;
    entry.timestamp = Date.now();
    updateStats('hits');

    return entry.data;
  }, [isExpired, updateStats, versioning, removeFromTagIndex, bumpMutation]);

  const set = useCallback((key: string, value: T, tags: string[] = []): void => {
    // Check if we need to evict
    if (cache.current.size >= maxSize && !cache.current.has(key)) {
      evictLRU();
    }

    // If updating existing entry, remove old tags
    const existingEntry = cache.current.get(key);
    if (existingEntry?.tags) {
      removeFromTagIndex(key, existingEntry.tags);
    }

    cache.current.set(key, {
      data: value,
      timestamp: Date.now(),
      hits: 0,
      tags,
      version: versioning ? currentVersion.current : undefined
    });

    if (tags.length > 0) {
      addToTagIndex(key, tags);
    }

    updateStats('sets');
    bumpMutation();
  }, [maxSize, evictLRU, updateStats, versioning, removeFromTagIndex, addToTagIndex, bumpMutation]);

  const has = useCallback((key: string): boolean => {
    const entry = cache.current.get(key);
    if (!entry) return false;

    if (isExpired(entry)) {
      if (entry.tags) {
        removeFromTagIndex(key, entry.tags);
      }
      cache.current.delete(key);
      bumpMutation();
      return false;
    }

    if (versioning && entry.version !== currentVersion.current) {
      if (entry.tags) {
        removeFromTagIndex(key, entry.tags);
      }
      cache.current.delete(key);
      bumpMutation();
      return false;
    }

    return true;
  }, [isExpired, versioning, removeFromTagIndex, bumpMutation]);

  const clear = useCallback(() => {
    cache.current.clear();
    tagIndex.current.clear();
    setStats({ hits: 0, misses: 0, sets: 0, evictions: 0, invalidations: 0 });
    bumpMutation();
  }, [bumpMutation]);

  const invalidateByTag = useCallback((tag: string): number => {
    const keys = tagIndex.current.get(tag);
    if (!keys) return 0;

    let invalidatedCount = 0;
    keys.forEach(key => {
      const entry = cache.current.get(key);
      if (entry) {
        cache.current.delete(key);
        invalidatedCount++;
        updateStats('invalidations');
      }
    });

    tagIndex.current.delete(tag);
    if (invalidatedCount > 0) bumpMutation();
    return invalidatedCount;
  }, [updateStats, bumpMutation]);

  const invalidateByPattern = useCallback((pattern: RegExp): number => {
    let invalidatedCount = 0;
    const keysToDelete: string[] = [];

    cache.current.forEach((entry, key) => {
      if (pattern.test(key)) {
        keysToDelete.push(key);
        if (entry.tags) {
          removeFromTagIndex(key, entry.tags);
        }
      }
    });

    keysToDelete.forEach(key => {
      cache.current.delete(key);
      invalidatedCount++;
      updateStats('invalidations');
    });

    if (invalidatedCount > 0) bumpMutation();
    return invalidatedCount;
  }, [updateStats, removeFromTagIndex, bumpMutation]);

  const invalidateVersion = useCallback(() => {
    if (versioning) {
      currentVersion.current = Date.now().toString();
    }
  }, [versioning]);

  const createKey = useCallback((prefix: string, params: Record<string, unknown>): string => {
    const sortedParams = Object.keys(params)
      .sort()
      .map(key => `${key}:${JSON.stringify(params[key])}`)
      .join(':');
    return `${prefix}:${sortedParams}`;
  }, []);

  const getDataFingerprint = useCallback((data: unknown): string => {
    // Create a fingerprint for data to use in cache keys
    const stringify = (obj: unknown): string => {
      if (obj === null || obj === undefined) return 'null';
      if (typeof obj !== 'object') return String(obj);
      if (Array.isArray(obj)) return `[${obj.map(stringify).join(',')}]`;

      const keys = Object.keys(obj).sort();
      return `{${keys.map(k => `${k}:${stringify(obj[k])}`).join(',')}}`;
    };

    const str = stringify(data);
    // Simple hash function for fingerprint
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32bit integer
    }
    return Math.abs(hash).toString(36);
  }, []);

  // Expose live size directly from the Map to avoid stale memoization on ref properties.
  const size = cache.current.size;

  // Derive hitRate from current stats; dependency is ensured by updateStats bumping mutation.
  const hitRate = useMemo(() => {
    const total = stats.hits + stats.misses;
    return total > 0 ? (stats.hits / total) * 100 : 0;
  }, [stats.hits, stats.misses, mutationCounter.current]);

  const memoryUsage = useMemo(() => {
    if (!enableStats) return undefined;

    // Rough estimation of memory usage
    let totalSize = 0;
    cache.current.forEach((entry) => {
      totalSize += JSON.stringify(entry).length * 2; // Rough estimate (2 bytes per char)
    });
    return totalSize;
  }, [enableStats, mutationCounter.current]);

  // Clean up expired entries periodically
  const cleanup = useCallback(() => {
    const now = Date.now();
    const keysToDelete: string[] = [];

    cache.current.forEach((entry, key) => {
      if (now - entry.timestamp > ttl || (versioning && entry.version !== currentVersion.current)) {
        keysToDelete.push(key);
        if (entry.tags) {
          removeFromTagIndex(key, entry.tags);
        }
      }
    });

    if (keysToDelete.length > 0) {
      keysToDelete.forEach(key => cache.current.delete(key));
      bumpMutation();
    }
  }, [ttl, versioning, removeFromTagIndex, bumpMutation]);

  const getCacheStats = useCallback((): CacheStats => {
    return {
      ...stats,
      hitRate,
      size,
      memoryUsage
    };
  }, [stats, hitRate, size, memoryUsage]);

  return {
    get,
    set,
    has,
    clear,
    cleanup,
    size,
    invalidateByTag,
    invalidateByPattern,
    invalidateVersion,
    createKey,
    getDataFingerprint,
    stats: enableStats ? getCacheStats() : null
  };
}

import { useEffect, useMemo, useRef, useState, useCallback } from 'react';
import { EmotionEntry, SensoryEntry, TrackingEntry } from '@/types/student';
import { AnalyticsResults } from '@/types/analytics';
import { ProgressiveChartData, ProgressiveChartDataState } from '@/types/charts';
import { logger } from '@/lib/logger';

interface UseProgressiveChartDataParams {
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  entries: TrackingEntry[];
  analyticsResults?: AnalyticsResults | null;
}

interface UseProgressiveChartDataReturn {
  data: ProgressiveChartData;
  state: ProgressiveChartDataState;
  reset: () => void;
}

const requestIdle: (cb: IdleRequestCallback, opts?: IdleRequestOptions) => number =
  typeof requestIdleCallback !== 'undefined'
    ? requestIdleCallback
    : ((cb: IdleRequestCallback) => window.setTimeout(() => cb({ didTimeout: false, timeRemaining: () => 50 } as any), 0));

const cancelIdle: (id: number) => void =
  typeof cancelIdleCallback !== 'undefined' ? cancelIdleCallback : clearTimeout;

export function useProgressiveChartData(params: UseProgressiveChartDataParams): UseProgressiveChartDataReturn {
  const { emotions, sensoryInputs, entries, analyticsResults } = params;

  const [data, setData] = useState<ProgressiveChartData>({
    emotionTrends: [],
    emotionDistribution: [],
    sensoryResponses: [],
  });

  const [state, setState] = useState<ProgressiveChartDataState>({
    isLoading: true,
    steps: {
      emotionDistribution: false,
      sensoryResponses: false,
      emotionTrends: false,
    },
    error: null,
  });

  const idleIdsRef = useRef<number[]>([]);
  const timeoutsRef = useRef<number[]>([]);
  const isMountedRef = useRef(true);

  useEffect(() => {
    return () => {
      isMountedRef.current = false;
      idleIdsRef.current.forEach(id => {
        try { cancelIdle(id); } catch {}
      });
      timeoutsRef.current.forEach(id => clearTimeout(id));
      idleIdsRef.current = [];
      timeoutsRef.current = [];
    };
  }, []);

  const reset = useCallback(() => {
    setData({ emotionTrends: [], emotionDistribution: [], sensoryResponses: [] });
    setState({
      isLoading: true,
      steps: { emotionDistribution: false, sensoryResponses: false, emotionTrends: false },
      error: null,
    });
  }, []);

  // Small helpers to safely update
  const safeSetData = useCallback((updater: (prev: ProgressiveChartData) => ProgressiveChartData) => {
    if (!isMountedRef.current) return;
    setData(prev => updater(prev));
  }, []);

  const markStepDone = useCallback((step: keyof ProgressiveChartDataState['steps']) => {
    if (!isMountedRef.current) return;
    setState(prev => ({
      ...prev,
      steps: { ...prev.steps, [step]: true },
      isLoading: !(prev.steps.emotionDistribution && prev.steps.sensoryResponses && (step === 'emotionTrends' || prev.steps.emotionTrends)),
    }));
  }, []);

  // Progressive pipeline: quick -> medium -> heavy computations
  useEffect(() => {
    // Start fresh on every input change
    reset();

    // Step 1: Emotion distribution (fast)
    const id1 = requestIdle(() => {
      try {
        const distribution = new Map<string, number>();
        emotions.forEach(e => {
          distribution.set(e.emotion, (distribution.get(e.emotion) || 0) + 1);
        });
        const pieData = Array.from(distribution.entries()).map(([name, value]) => ({ name, value }));
        safeSetData(prev => ({ ...prev, emotionDistribution: pieData }));
        markStepDone('emotionDistribution');
      } catch (error) {
        logger.error('[useProgressiveChartData] Failed computing emotion distribution', error);
        setState(prev => ({ ...prev, error: error instanceof Error ? error : new Error('Unknown error') }));
      }
    }, { timeout: 100 });
    idleIdsRef.current.push(id1);

    // Step 2: Sensory responses (medium)
    const t2 = window.setTimeout(() => {
      const id2 = requestIdle(() => {
        try {
          type SensoryRow = { type: string; total: number; [key: string]: string | number };
          const map = new Map<string, SensoryRow>();
          sensoryInputs.forEach(s => {
            const row = map.get(s.sensoryType) || { type: s.sensoryType, total: 0 };
            row[s.response] = ((typeof row[s.response] === 'number' ? (row[s.response] as number) : 0) + 1);
            row.total = (row.total || 0) + 1;
            map.set(s.sensoryType, row);
          });
          const sensoryData = Array.from(map.values());
          safeSetData(prev => ({ ...prev, sensoryResponses: sensoryData }));
          markStepDone('sensoryResponses');
        } catch (error) {
          logger.error('[useProgressiveChartData] Failed computing sensory responses', error);
          setState(prev => ({ ...prev, error: error instanceof Error ? error : new Error('Unknown error') }));
        }
      }, { timeout: 200 });
      idleIdsRef.current.push(id2);
    }, 50);
    timeoutsRef.current.push(t2);

    // Step 3: Emotion trends over time (heavier)
    const t3 = window.setTimeout(() => {
      const id3 = requestIdle(() => {
        try {
          // Determine all emotion keys for consistent columns
          const allEmotions = Array.from(new Set(emotions.map(e => e.emotion)));

          type Row = { date: string; count: number } & Record<string, number | string>;
          const dataMap = new Map<string, Row>();

          emotions.forEach(e => {
            const date = e.timestamp instanceof Date ? e.timestamp.toLocaleDateString() : new Date(e.timestamp as any).toLocaleDateString();
            if (!dataMap.has(date)) {
              const base: Row = { date, count: 0 };
              allEmotions.forEach(k => { base[k] = 0; });
              dataMap.set(date, base);
            }
            const row = dataMap.get(date)!;
            const current = row[e.emotion];
            row[e.emotion] = (typeof current === 'number' ? current : 0) + e.intensity;
            row.count = (row.count || 0) + 1;
          });

          // Sort by date asc
          const toDate = (d: string) => new Date(d);
          const rows = Array.from(dataMap.values()).sort((a, b) => toDate(a.date).getTime() - toDate(b.date).getTime());

          safeSetData(prev => ({ ...prev, emotionTrends: rows }));
          markStepDone('emotionTrends');
        } catch (error) {
          logger.error('[useProgressiveChartData] Failed computing emotion trends', error);
          setState(prev => ({ ...prev, error: error instanceof Error ? error : new Error('Unknown error') }));
        }
      }, { timeout: 400 });
      idleIdsRef.current.push(id3);
    }, 100);
    timeoutsRef.current.push(t3);

    // Optionally react to analytics results updates for future progressive enrichments
    if (analyticsResults) {
      try {
        // Currently no heavy enrichment needed; placeholder for progressive overlays
        logger.debug('[useProgressiveChartData] analyticsResults available', {
          patterns: analyticsResults.patterns?.length || 0,
          correlations: analyticsResults.correlations?.length || 0,
        });
      } catch {/* noop */}
    }
  }, [emotions, sensoryInputs, entries, analyticsResults, reset, safeSetData, markStepDone]);

  return { data, state, reset };
}

import { useState, useEffect, useRef, useCallback, useMemo, useReducer } from 'react';
import { EmotionEntry, SensoryEntry, TrackingEntry, EnvironmentalEntry } from '@/types/student';
import { differenceInMinutes, subMinutes } from 'date-fns';
import { logger } from '@/lib/logger';

interface RealtimeDataOptions {
  enabled: boolean;
  windowSize: number; // in minutes
  updateInterval: number; // in milliseconds
  smoothTransitions: boolean;
  simulateData?: boolean; // For demo purposes
}

interface RealtimeDataState {
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  trackingEntries: TrackingEntry[];
  isLive: boolean;
  lastUpdate: Date | null;
  connectionStatus: 'connected' | 'disconnected' | 'connecting' | 'error';
  newDataCount: number;
}

type RealtimeDataAction =
  | { type: 'START_STREAM' }
  | { type: 'STOP_STREAM' }
  | { type: 'SET_CONNECTION_STATUS'; payload: RealtimeDataState['connectionStatus'] }
  | { type: 'INSERT_DATA'; payload: { emotion?: EmotionEntry; sensory?: SensoryEntry; tracking?: TrackingEntry } }
  | { type: 'SET_HISTORICAL_DATA'; payload: { emotions: EmotionEntry[]; sensoryInputs: SensoryEntry[]; trackingEntries: TrackingEntry[] } }
  | { type: 'CLEAR_NEW_DATA_INDICATOR' }
  | { type: 'RESET_STATE'; payload: RealtimeDataState };

const realtimeDataReducer = (state: RealtimeDataState, action: RealtimeDataAction): RealtimeDataState => {
  switch (action.type) {
    case 'START_STREAM':
      return { ...state, isLive: true, connectionStatus: 'connecting' };
    case 'STOP_STREAM':
      return { ...state, isLive: false, connectionStatus: 'disconnected' };
    case 'SET_CONNECTION_STATUS':
      return { ...state, connectionStatus: action.payload };
    case 'INSERT_DATA':
      return {
        ...state,
        lastUpdate: new Date(),
        newDataCount: state.newDataCount + 1,
        emotions: action.payload.emotion ? [...state.emotions, action.payload.emotion].slice(-1000) : state.emotions,
        sensoryInputs: action.payload.sensory ? [...state.sensoryInputs, action.payload.sensory].slice(-1000) : state.sensoryInputs,
        trackingEntries: action.payload.tracking ? [...state.trackingEntries, action.payload.tracking].slice(-1000) : state.trackingEntries,
      };
    case 'SET_HISTORICAL_DATA':
      return {
        ...state,
        emotions: [...action.payload.emotions, ...state.emotions],
        sensoryInputs: [...action.payload.sensoryInputs, ...state.sensoryInputs],
        trackingEntries: [...action.payload.trackingEntries, ...state.trackingEntries],
        connectionStatus: state.isLive ? 'connected' : 'disconnected',
      };
    case 'CLEAR_NEW_DATA_INDICATOR':
      return { ...state, newDataCount: 0 };
    case 'RESET_STATE':
      return action.payload;
    default:
      return state;
  }
};

export interface RealtimeDataReturn extends RealtimeDataState {
  startStream: () => void;
  stopStream: () => void;
  clearNewDataIndicator: () => void;
  getHistoricalData: (minutes: number) => void;
  isDataLive: (timestamp: Date) => boolean;
}

// Simulate real-time data generation
const generateRealtimeEmotionEntry = (): EmotionEntry => {
  const emotions = ['happy', 'calm', 'anxious', 'frustrated', 'excited', 'sad'];
  const triggers = ['noise', 'crowd', 'task', 'transition', 'social'];

  return {
    id: `emotion-${Date.now()}-${Math.random()}`,
    timestamp: new Date(),
    emotion: emotions[Math.floor(Math.random() * emotions.length)],
    intensity: Math.floor(Math.random() * 8) + 3, // 3-10
    triggers: Math.random() > 0.5 
      ? [triggers[Math.floor(Math.random() * triggers.length)]]
      : [],
    notes: ''
  };
};

const generateRealtimeSensoryEntry = (): SensoryEntry => {
  const sensoryTypes = ['visual', 'auditory', 'tactile', 'vestibular', 'proprioceptive'];
  const responses = ['seeking', 'avoiding', 'neutral'];

  return {
    id: `sensory-${Date.now()}-${Math.random()}`,
    timestamp: new Date(),
    sensoryType: sensoryTypes[Math.floor(Math.random() * sensoryTypes.length)],
    response: responses[Math.floor(Math.random() * responses.length)],
    intensity: Math.floor(Math.random() * 8) + 3,
    notes: ''
  };
};

const generateEnvironmentalEntry = (): EnvironmentalEntry => {
  return {
    id: `env-${Date.now()}-${Math.random()}`,
    timestamp: new Date(),
    location: 'classroom',
    socialContext: Math.random() > 0.5 ? 'group' : 'individual',
    roomConditions: {
      temperature: 20 + Math.floor(Math.random() * 5),
      humidity: 40 + Math.floor(Math.random() * 20),
      lighting: Math.random() > 0.5 ? 'bright' : 'dim',
      noiseLevel: Math.floor(Math.random() * 10)
    },
    weather: {
      condition: ['sunny', 'cloudy', 'rainy'][Math.floor(Math.random() * 3)] as 'sunny' | 'cloudy' | 'rainy',
      temperature: 15 + Math.floor(Math.random() * 15)
    },
    classroom: {
      activity: ['instruction', 'transition', 'free-time'][Math.floor(Math.random() * 3)] as 'instruction' | 'transition' | 'free-time',
      studentCount: 15 + Math.floor(Math.random() * 10),
      timeOfDay: 'morning'
    }
  };
};

const generateRealtimeTrackingEntry = (): TrackingEntry => {
  return {
    id: `tracking-${Date.now()}-${Math.random()}`,
    studentId: 'current-student',
    timestamp: new Date(),
    emotions: [generateRealtimeEmotionEntry()],
    sensoryInputs: [generateRealtimeSensoryEntry()],
    environmentalData: generateEnvironmentalEntry(),
    notes: ''
  };
};

export const useRealtimeData = (
  initialData: {
    emotions: EmotionEntry[];
    sensoryInputs: SensoryEntry[];
    trackingEntries: TrackingEntry[];
  },
  options: RealtimeDataOptions
): RealtimeDataReturn => {
  const initialState = useMemo(() => ({
    emotions: initialData.emotions,
    sensoryInputs: initialData.sensoryInputs,
    trackingEntries: initialData.trackingEntries,
    isLive: false,
    lastUpdate: null,
    connectionStatus: 'disconnected' as const,
    newDataCount: 0,
  }), [initialData]);

  const [state, dispatch] = useReducer(realtimeDataReducer, initialState);

  const intervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const animationFrameRef = useRef<number | null>(null);
  const newDataTimestamps = useRef<Set<number>>(new Set());

  // Track which data points are "live" (recently added)
  const liveDataThreshold = 5000; // 5 seconds

  // Filter data based on window size
  const filteredData = useMemo(() => {
    if (!options.enabled || options.windowSize === 0) {
      return state;
    }

    const cutoffTime = subMinutes(new Date(), options.windowSize);

    return {
      ...state,
      emotions: state.emotions.filter(e => e.timestamp >= cutoffTime),
      sensoryInputs: state.sensoryInputs.filter(s => s.timestamp >= cutoffTime),
      trackingEntries: state.trackingEntries.filter(t => t.timestamp >= cutoffTime)
    };
  }, [state, options.enabled, options.windowSize]);

  // Smooth data insertion with animation frames
  const smoothInsertData = useCallback((
    newEmotion?: EmotionEntry,
    newSensory?: SensoryEntry,
    newTracking?: TrackingEntry
  ) => {
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }

    const animate = () => {
      const newTimestamps = new Set(newDataTimestamps.current);
      newTimestamps.add(new Date().getTime());

      // Clean up old timestamps
      const cutoff = Date.now() - liveDataThreshold;
      newTimestamps.forEach(timestamp => {
        if (timestamp < cutoff) {
          newTimestamps.delete(timestamp);
        }
      });
      newDataTimestamps.current = newTimestamps;

      dispatch({ type: 'INSERT_DATA', payload: { emotion: newEmotion, sensory: newSensory, tracking: newTracking } });
    };

    if (options.smoothTransitions) {
      animationFrameRef.current = requestAnimationFrame(animate);
    } else {
      animate();
    }
  }, [options.smoothTransitions, liveDataThreshold]);

  // Simulate real-time data stream
  const simulateDataStream = useCallback(() => {
    // Randomly generate new data
    const rand = Math.random();

    if (rand < 0.4) { // 40% chance of emotion data
      smoothInsertData(generateRealtimeEmotionEntry(), undefined, undefined);
    } else if (rand < 0.7) { // 30% chance of sensory data
      smoothInsertData(undefined, generateRealtimeSensoryEntry(), undefined);
    } else if (rand < 0.9) { // 20% chance of tracking data
      smoothInsertData(undefined, undefined, generateRealtimeTrackingEntry());
    }
    // 10% chance of no new data this cycle
  }, [smoothInsertData]);

  // Track connection timeout to ensure proper cleanup
  const connectionTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Start the real-time stream
  const startStream = useCallback(() => {
    dispatch({ type: 'START_STREAM' });

    // Clear any existing connection timeout
    if (connectionTimeoutRef.current) {
      clearTimeout(connectionTimeoutRef.current);
      connectionTimeoutRef.current = null;
    }

    // Simulate connection delay
    connectionTimeoutRef.current = setTimeout(() => {
      dispatch({ type: 'SET_CONNECTION_STATUS', payload: 'connected' });

      if (options.simulateData) {
        // Start simulated data generation
        if (intervalRef.current) clearInterval(intervalRef.current);
        intervalRef.current = setInterval(simulateDataStream, options.updateInterval);
      } else {
        logger.info('Real-time data connection would be established here');
      }

      // Clear the ref once timeout executes
      connectionTimeoutRef.current = null;
    }, 1000);
  }, [options.simulateData, options.updateInterval, simulateDataStream]);

  // Stop the real-time stream
  const stopStream = useCallback(() => {
    // Clear connection timeout if it's still pending
    if (connectionTimeoutRef.current) {
      clearTimeout(connectionTimeoutRef.current);
      connectionTimeoutRef.current = null;
    }

    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }

    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
      animationFrameRef.current = null;
    }

    dispatch({ type: 'STOP_STREAM' });

    // In a real implementation, close WebSocket/SSE connection
  }, []);

  // Clear new data indicator
  const clearNewDataIndicator = useCallback(() => {
    dispatch({ type: 'CLEAR_NEW_DATA_INDICATOR' });
  }, []);

  // Track historical data timeout to ensure proper cleanup
  const historicalDataTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Get historical data (simulate API call)
  const getHistoricalData = useCallback((minutes: number) => {
    dispatch({ type: 'SET_CONNECTION_STATUS', payload: 'connecting' });

    // Clear any existing historical data timeout
    if (historicalDataTimeoutRef.current) {
      clearTimeout(historicalDataTimeoutRef.current);
      historicalDataTimeoutRef.current = null;
    }

    // Simulate API call delay
    historicalDataTimeoutRef.current = setTimeout(() => {
      const now = new Date();
      const historicalEmotions: EmotionEntry[] = [];
      const historicalSensory: SensoryEntry[] = [];
      const historicalTracking: TrackingEntry[] = [];

      // Generate some historical data points
      for (let i = 0; i < minutes; i += 5) {
        const timestamp = subMinutes(now, i);

        if (Math.random() > 0.3) {
          historicalEmotions.push({
            ...generateRealtimeEmotionEntry(),
            timestamp
          });
        }

        if (Math.random() > 0.4) {
          historicalSensory.push({
            ...generateRealtimeSensoryEntry(),
            timestamp
          });
        }

        if (Math.random() > 0.5) {
          historicalTracking.push({
            ...generateRealtimeTrackingEntry(),
            timestamp
          });
        }
      }

      dispatch({ 
        type: 'SET_HISTORICAL_DATA', 
        payload: { 
          emotions: historicalEmotions.reverse(), 
          sensoryInputs: historicalSensory.reverse(), 
          trackingEntries: historicalTracking.reverse() 
        } 
      });

      // Clear the ref once timeout executes
      historicalDataTimeoutRef.current = null;
    }, 500);
  }, []);

  // Check if a data point is "live" (recently added)
  const isDataLive = useCallback((timestamp: Date): boolean => {
    const now = new Date();
    // Use millisecond difference directly to avoid rounding errors from differenceInMinutes
    return now.getTime() - new Date(timestamp).getTime() < liveDataThreshold;
  }, [liveDataThreshold]);

  // Auto-start/stop based on enabled option
  useEffect(() => {
    if (options.enabled && !state.isLive) {
      startStream();
    } else if (!options.enabled && state.isLive) {
      stopStream();
    }

    return () => {
      // Only stop if currently live; avoid redundant calls after an explicit stop
      if (state.isLive) {
        stopStream();
      }
    };
  }, [options.enabled, state.isLive, startStream, stopStream]);

  // Handle connection errors (simulated)
  useEffect(() => {
    let reconnectTimeout: ReturnType<typeof setTimeout> | null = null;

    if (state.connectionStatus === 'connected' && Math.random() < 0.01) { // 1% chance of error
      dispatch({ type: 'SET_CONNECTION_STATUS', payload: 'error' });

      // Auto-reconnect after 3 seconds
      reconnectTimeout = setTimeout(() => {
        if (state.isLive) {
          startStream();
        }
      }, 3000);
    }

    return () => {
      if (reconnectTimeout) {
        clearTimeout(reconnectTimeout);
      }
    };
  }, [state.connectionStatus, state.isLive, startStream]);

  // Comprehensive cleanup on unmount
  useEffect(() => {
    return () => {
      // Clear all timeouts on unmount
      if (connectionTimeoutRef.current) {
        clearTimeout(connectionTimeoutRef.current);
        connectionTimeoutRef.current = null;
      }

      if (historicalDataTimeoutRef.current) {
        clearTimeout(historicalDataTimeoutRef.current);
        historicalDataTimeoutRef.current = null;
      }

      // Clear intervals
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }

      // Cancel animation frames
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
        animationFrameRef.current = null;
      }
    };
  }, []); // Empty dependency array ensures this only runs on unmount

  return {
    ...filteredData,
    isLive: state.isLive,
    lastUpdate: state.lastUpdate,
    connectionStatus: state.connectionStatus,
    newDataCount: state.newDataCount,
    startStream,
    stopStream,
    clearNewDataIndicator,
    getHistoricalData,
    isDataLive
  };
};

// Hook for managing real-time updates to visualizations
export const useRealtimeVisualization = (
  data: RealtimeDataReturn,
  updateInterval: number = 100
) => {
  const [animatedData, setAnimatedData] = useState(data);
  const animationRef = useRef<ReturnType<typeof setInterval> | null>(null);

  useEffect(() => {
    if (!data.isLive) {
      setAnimatedData(data);
      return;
    }

    // Smooth animation for data updates
    const animate = () => {
      setAnimatedData(prev => {
        // Implement smooth transitions for numerical values
        const smoothTransition = (current: number, target: number, factor = 0.1) => {
          return current + (target - current) * factor;
        };

        // For now, just pass through the data
        // In a real implementation, you might interpolate positions, sizes, etc.
        return data;
      });
    };

    animationRef.current = setInterval(animate, updateInterval);

    return () => {
      if (animationRef.current) {
        clearInterval(animationRef.current);
      }
    };
  }, [data, updateInterval]);

  return animatedData;
};

import { useTranslation as useI18nTranslation } from 'react-i18next';
import { useCallback, useMemo } from 'react';

// Type-safe translation hook with Norwegian context
export const useTranslation = (namespace?: string) => {
  const { t, i18n } = useI18nTranslation(namespace);

  const changeLanguage = useCallback((lng: 'nb' | 'en') => {
    i18n.changeLanguage(lng);
    localStorage.setItem('sensoryTracker_language', lng);
  }, [i18n]);

  const currentLanguage = (i18n.language as 'nb' | 'en') || 'nb';
  const locale = currentLanguage === 'nb' ? 'nb-NO' : 'en-US';

  // Helper functions for common translations
  const tCommon = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'common', ...options }), [t]);
  const tDashboard = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'dashboard', ...options }), [t]);
  const tStudent = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'student', ...options }), [t]);
  const tTracking = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'tracking', ...options }), [t]);
  const tAnalytics = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'analytics', ...options }), [t]);
  const tSettings = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'settings', ...options }), [t]);

  // Locale-aware date/time formatting
  const formatDate = useCallback((date: Date): string => {
    return new Intl.DateTimeFormat(locale, {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
    }).format(date);
  }, [locale]);

  const formatDateTime = useCallback((date: Date): string => {
    return new Intl.DateTimeFormat(locale, {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
    }).format(date);
  }, [locale]);

  const formatNumber = useCallback((value: number, options?: Intl.NumberFormatOptions): string => {
    return new Intl.NumberFormat(locale, options).format(value);
  }, [locale]);

  const formatCurrency = useCallback((value: number, currency: 'NOK' | 'USD' = currentLanguage === 'nb' ? 'NOK' : 'USD'): string => {
    return new Intl.NumberFormat(locale, { style: 'currency', currency }).format(value);
  }, [locale, currentLanguage]);

  // Don't include i18n in deps as it can cause infinite loops
  return useMemo(() => ({
    t,
    tCommon,
    tDashboard,
    tStudent,
    tTracking,
    tAnalytics,
    tSettings,
    changeLanguage,
    currentLanguage,
    formatDate,
    formatDateTime,
    formatNumber,
    formatCurrency,
    i18n,
  }), [t, tCommon, tDashboard, tStudent, tTracking, tAnalytics, tSettings, changeLanguage, currentLanguage, formatDate, formatDateTime, formatNumber, formatCurrency]);
};

import { subDays, startOfDay, addDays } from 'date-fns';
import { EmotionEntry, SensoryEntry, TrackingEntry, EnvironmentalEntry } from '@/types/student';

export interface FilterCriteria {
  dateRange: { start: Date | null; end: Date | null };
  emotions: { types: string[]; intensityRange: [number, number]; includeTriggers: string[]; excludeTriggers: string[] };
  sensory: { types: string[]; responses: string[]; intensityRange: [number, number] };
  environmental: {
    locations: string[];
    activities: string[];
    conditions: { noiseLevel: [number, number]; temperature: [number, number]; lighting: string[] };
    weather: string[];
    timeOfDay: string[];
  };
  patterns: { anomaliesOnly: boolean; minConfidence: number; patternTypes: string[] };
  realtime: boolean;
}

export const initialFilterCriteria: FilterCriteria = {
  dateRange: { start: null, end: null },
  emotions: { types: [], intensityRange: [0, 10], includeTriggers: [], excludeTriggers: [] },
  sensory: { types: [], responses: [], intensityRange: [0, 10] },
  environmental: {
    locations: [],
    activities: [],
    conditions: { noiseLevel: [0, 10], temperature: [15, 30], lighting: [] },
    weather: [],
    timeOfDay: []
  },
  patterns: { anomaliesOnly: false, minConfidence: 0, patternTypes: [] },
  realtime: false
};

export const applyFilters = <T extends { timestamp: Date }>(
  data: T[],
  criteria: FilterCriteria,
  getEmotionData?: (item: T) => EmotionEntry | null,
  getSensoryData?: (item: T) => SensoryEntry | null,
  getEnvironmentalData?: (item: T) => EnvironmentalEntry | null
): T[] => {
  return data.filter(item => {
    // Date range filter
    if (criteria.dateRange.start || criteria.dateRange.end) {
      const start = criteria.dateRange.start ? startOfDay(criteria.dateRange.start) : new Date(0);
      const endExclusive = criteria.dateRange.end ? addDays(startOfDay(criteria.dateRange.end), 1) : new Date(8640000000000000);
      const ts = item.timestamp;
      if (!(ts >= start && ts < endExclusive)) return false;
    }

    // Emotion filters
    if (getEmotionData) {
      const emotion = getEmotionData(item);
      if (emotion) {
        if (criteria.emotions.types.length > 0 && !criteria.emotions.types.includes(emotion.emotion)) {
          return false;
        }
        if (emotion.intensity < criteria.emotions.intensityRange[0] || emotion.intensity > criteria.emotions.intensityRange[1]) {
          return false;
        }
        if (criteria.emotions.includeTriggers.length > 0 && !emotion.triggers?.some(t => criteria.emotions.includeTriggers.includes(t))) {
          return false;
        }
        if (criteria.emotions.excludeTriggers.length > 0 && emotion.triggers?.some(t => criteria.emotions.excludeTriggers.includes(t))) {
          return false;
        }
      }
    }

    // Sensory filters
    if (getSensoryData) {
      const sensory = getSensoryData(item);
      if (sensory) {
        const sensoryType = sensory.sensoryType || sensory.type || '';
        if (criteria.sensory.types.length > 0 && !criteria.sensory.types.includes(sensoryType)) {
          return false;
        }
        if (criteria.sensory.responses.length > 0 && !criteria.sensory.responses.includes(sensory.response)) {
          return false;
        }
        if (sensory.intensity && (sensory.intensity < criteria.sensory.intensityRange[0] || sensory.intensity > criteria.sensory.intensityRange[1])) {
          return false;
        }
      }
    }

    // Environmental filters
    if (getEnvironmentalData) {
      const env = getEnvironmentalData(item);
      if (env) {
        if (criteria.environmental.locations.length > 0 && env.location && !criteria.environmental.locations.includes(env.location)) {
          return false;
        }
        if (criteria.environmental.activities.length > 0 && env.classroom?.activity && !criteria.environmental.activities.includes(env.classroom.activity)) {
          return false;
        }
        if (criteria.environmental.weather.length > 0 && env.weather?.condition && !criteria.environmental.weather.includes(env.weather.condition)) {
          return false;
        }
        if (env.roomConditions?.noiseLevel && (env.roomConditions.noiseLevel < criteria.environmental.conditions.noiseLevel[0] || env.roomConditions.noiseLevel > criteria.environmental.conditions.noiseLevel[1])) {
          return false;
        }
      }
    }

    return true;
  });
};

