/**
 * Ensures universal analytics initialization for all students in the system.
 * 
 * @async
 * @function ensureUniversalAnalyticsInitialization
 * @returns {Promise<void>} A promise that resolves when initialization is complete
 * 
 * @description This function handles the complete analytics initialization process:
 * 1. **Mock Data Generation**: If no students exist, generates a minimal set of 3 mock students
 *    to seed the analytics system with baseline data.
 * 2. **Minimum Data Thresholds**: Checks each student against configurable thresholds:
 *    - EMOTION_ENTRIES: Minimum emotion entries required
 *    - SENSORY_ENTRIES: Minimum sensory input entries required  
 *    - TRACKING_ENTRIES: Minimum tracking session entries required
 * 3. **Cache Warming Strategy**: For students meeting minimum data requirements, performs
 *    lightweight precomputation of analytics to populate caches:
 *    - Emotion pattern analysis
 *    - Sensory pattern analysis
 *    - Environmental correlation analysis (if sufficient tracking entries)
 * 
 * The function gracefully handles errors and treats cache warming as best-effort.
 * Configuration is loaded from live analytics config with fallback to defaults.
 */
export const ensureUniversalAnalyticsInitialization = async (): Promise<void> => {
  try {
    // Retrieve students via synchronous API; if none, generate a small seed using generator
    const students = dataStorage.getStudents();
    if (!students || students.length === 0) {
      const seedCount = 3;
      for (let i = 0; i < seedCount; i++) {
        // The generator in this codebase returns TrackingEntry[] for a student; persist via existing APIs
        const generatedEntries = generateMockData('seed'); // supply an argument to satisfy signature
        // Persist by using available save APIs
        try {
          // Save each generated tracking entry
          for (const entry of generatedEntries) {
            dataStorage.saveTrackingEntry(entry);
          }
        } catch (err) {
          logger.error('[analyticsManager] mock data seed failed', { error: err });
        }
      }
    }

    const cfg = (() => { try { return analyticsConfig.getConfig(); } catch { return null; } })();
    const minEmotions = cfg?.confidence?.THRESHOLDS?.EMOTION_ENTRIES ?? ANALYTICS_CONFIG.confidence.THRESHOLDS.EMOTION_ENTRIES;
    const minSensory = cfg?.confidence?.THRESHOLDS?.SENSORY_ENTRIES ?? ANALYTICS_CONFIG.confidence.THRESHOLDS.SENSORY_ENTRIES;
    const minTracking = cfg?.confidence?.THRESHOLDS?.TRACKING_ENTRIES ?? ANALYTICS_CONFIG.confidence.THRESHOLDS.TRACKING_ENTRIES;

    const allStudents = dataStorage.getStudents();
    for (const student of allStudents) {
      // Load data via available synchronous APIs
      const tracking = dataStorage.getTrackingEntriesForStudent(student.id);
      const emotions = tracking.flatMap(t => t.emotions ?? []);
      const sensoryInputs = tracking.flatMap(t => t.sensoryInputs ?? []);

      const hasMinimumData =
        (emotions.length) >= minEmotions ||
        (sensoryInputs.length) >= minSensory ||
        (tracking.length) >= minTracking;

      // Ensure profile exists
      analyticsManager.initializeStudentAnalytics(student.id);

      // Best-effort warmups
      if (hasMinimumData) {
        try {
          const analysisDays =
            cfg?.analytics?.ANALYSIS_PERIOD_DAYS ?? ANALYTICS_CONFIG.analytics.ANALYSIS_PERIOD_DAYS;

          if (emotions.length) {
            patternAnalysis.analyzeEmotionPatterns(emotions, analysisDays);
          }
          if (sensoryInputs.length) {
            patternAnalysis.analyzeSensoryPatterns(sensoryInputs, analysisDays);
          }
          if (tracking.length && tracking.length >= (cfg?.analytics?.MIN_TRACKING_FOR_CORRELATION ?? ANALYTICS_CONFIG.analytics.MIN_TRACKING_FOR_CORRELATION)) {
            patternAnalysis.analyzeEnvironmentalCorrelations(tracking);
          }
        } catch {
          /* noop */
        }
      }
    }
  } catch (e) {
    logger.error('[analyticsManager] ensureUniversalAnalyticsInitialization failed', e);
  }
};

/**
 * Defines the analytics profile for a student, tracking configuration and health.
 */
interface StudentAnalyticsProfile {
  studentId: string;
  isInitialized: boolean;
  lastAnalyzedAt: Date | null;
  analyticsConfig: {
    patternAnalysisEnabled: boolean;
    correlationAnalysisEnabled: boolean;
    predictiveInsightsEnabled: boolean;
    anomalyDetectionEnabled: boolean;
    alertSystemEnabled: boolean;
  };
  minimumDataRequirements: {
    emotionEntries: number;
    sensoryEntries: number;
    trackingEntries: number;
  };
  analyticsHealthScore: number;
}

/**
 * Represents the complete set of results from an analytics run.
 */
interface AnalyticsResults {
  patterns: PatternResult[];
  correlations: CorrelationResult[];
  predictiveInsights: PredictiveInsight[];
  anomalies: AnomalyDetection[];
  insights: string[];
  hasMinimumData: boolean;
  confidence: number;
}

type AnalyticsCache = Map<string, { results: AnalyticsResults; timestamp: Date }>;
type AnalyticsProfileMap = Map<string, StudentAnalyticsProfile>;
// #endregion

// #region Utility & Helper Functions (for better SRP)

/**
 * Safely parses analytics profiles from localStorage with validation and error handling.
 * 
 * @function loadProfilesFromStorage
 * @param {string | null} storedProfiles - The stringified profiles from localStorage
 * @returns {AnalyticsProfileMap} A map of valid student profiles, keyed by student ID
 * 
 * @description This function provides robust loading of persisted analytics profiles:
 * 
 * **Validation Process**:
 * - Checks if input is null/empty, returns empty Map if so
 * - Attempts JSON parsing with try/catch for malformed data
 * - Runtime type validation for each profile object
 * - Validates required fields: studentId (string) and isInitialized (boolean)
 * - Converts date strings back to Date objects for lastAnalyzedAt field
 * 
 * **Error Handling**:
 * - Catches and logs JSON parsing errors
 * - Skips invalid profile entries rather than failing completely
 * - Returns empty Map on complete failure to ensure app stability
 * 
 * This defensive approach ensures the analytics system remains functional even
 * if localStorage data becomes corrupted or outdated.
 */
function loadProfilesFromStorage(storedProfiles: string | null): AnalyticsProfileMap {
  const profiles: AnalyticsProfileMap = new Map();
  if (!storedProfiles) return profiles;

  try {
    const data = JSON.parse(storedProfiles);

    // Runtime validation
    const isAnalyticsProfile = (obj: unknown): obj is StudentAnalyticsProfile => {
      const profile = obj as Partial<StudentAnalyticsProfile>;
      return !!(profile && typeof profile.studentId === 'string' && typeof profile.isInitialized === 'boolean');
    };

    Object.entries(data).forEach(([studentId, profile]) => {
      if (isAnalyticsProfile(profile)) {
        profiles.set(studentId, {
          ...profile,
          lastAnalyzedAt: profile.lastAnalyzedAt ? new Date(profile.lastAnalyzedAt) : null,
        });
      }
    });
  } catch (error) {
    logger.error("Error loading analytics profiles:", error);
  }
  return profiles;
}

/**
 * Calculates confidence score based on data availability and recency.
 * 
 * @function calculateConfidence
 * @param {EmotionEntry[]} emotions - Array of emotion entries
 * @param {SensoryEntry[]} sensoryInputs - Array of sensory input entries
 * @param {TrackingEntry[]} trackingEntries - Array of tracking entries
 * @param {AnalyticsConfig['CONFIDENCE']} config - Configuration object with thresholds and weights
 * @returns {number} Confidence score between 0 and 1 (0% to 100%)
 * 
 * @description Uses weighted calculations based on data quantity relative to thresholds,
 * with a recency boost if data is fresh (within configured days).
 * 
 * **Weight Calculation Algorithm**:
 * 1. **Data Quantity Weights**: Each data type contributes based on quantity vs threshold
 *    - Emotion weight = min(emotion_count / EMOTION_THRESHOLD, 1) * EMOTION_WEIGHT
 *    - Sensory weight = min(sensory_count / SENSORY_THRESHOLD, 1) * SENSORY_WEIGHT
 *    - Tracking weight = min(tracking_count / TRACKING_THRESHOLD, 1) * TRACKING_WEIGHT
 * 
 * 2. **Recency Boost**: Additional confidence if last entry is recent
 *    - Checks days since last tracking entry
 *    - If within DAYS_SINCE_LAST_ENTRY threshold, adds RECENCY_BOOST
 *    - Final confidence capped at 1.0 (100%)
 * 
 * The algorithm ensures confidence scales smoothly from 0% (no data) to 100% (abundant recent data).
 */
function calculateConfidence(
  emotions: EmotionEntry[],
  sensoryInputs: SensoryEntry[],
  trackingEntries: TrackingEntry[],
  config: AnalyticsConfig['confidence']
): number {
  const { THRESHOLDS, WEIGHTS } = config;

  const emotionWeight = Math.min(emotions.length / THRESHOLDS.EMOTION_ENTRIES, 1) * WEIGHTS.EMOTION;
  const sensoryWeight = Math.min(sensoryInputs.length / THRESHOLDS.SENSORY_ENTRIES, 1) * WEIGHTS.SENSORY;
  const trackingWeight = Math.min(trackingEntries.length / THRESHOLDS.TRACKING_ENTRIES, 1) * WEIGHTS.TRACKING;

  let confidence = emotionWeight + sensoryWeight + trackingWeight;

  if (trackingEntries.length > 0) {
    const lastEntry = trackingEntries[trackingEntries.length - 1];
    const daysSinceLastEntry = (Date.now() - new Date(lastEntry.timestamp).getTime()) / (1000 * 60 * 60 * 24);
    if (daysSinceLastEntry < THRESHOLDS.DAYS_SINCE_LAST_ENTRY) {
      confidence = Math.min(confidence + WEIGHTS.RECENCY_BOOST, 1);
    }
  }

  return Math.round(confidence * 100) / 100;
}

/**
 * Generates human-readable insights from analytics results.
 * 
 * @function generateInsights
 * @param {Omit<AnalyticsResults, 'insights' | 'confidence'>} results - Analytics results without insights
 * @param {readonly EmotionEntry[]} emotions - Array of emotion entries
 * @param {readonly TrackingEntry[]} trackingEntries - Array of tracking entries
 * @param {AnalyticsConfig['INSIGHTS']} config - Configuration for insight generation
 * @returns {string[]} Array of human-readable insight strings
 * 
 * @description Creates contextual, actionable insights based on analytics data:
 * 
 * **Insight Generation Logic**:
 * 1. **Data Availability Check**: Returns guidance message if no tracking data exists
 * 
 * 2. **Limited Data Warning**: Alerts when sessions < MIN_SESSIONS_FOR_FULL_ANALYTICS
 * 
 * 3. **Pattern Insights**: 
 *    - Filters patterns by HIGH_CONFIDENCE_PATTERN_THRESHOLD
 *    - Limits to MAX_PATTERNS_TO_SHOW
 *    - Formats with pattern description and confidence percentage
 * 
 * 4. **Correlation Insights**:
 *    - Only includes 'high' significance correlations
 *    - Limits to MAX_CORRELATIONS_TO_SHOW
 * 
 * 5. **Predictive Insights**:
 *    - Shows top predictions up to MAX_PREDICTIONS_TO_SHOW
 *    - Includes confidence percentage
 * 
 * 6. **Emotion Trend Analysis**:
 *    - Analyzes recent emotions (RECENT_EMOTION_COUNT)
 *    - Calculates positive emotion rate
 *    - Provides encouragement or suggestions based on thresholds
 * 
 * 7. **Fallback Message**: Ensures array is never empty
 * 
 * All thresholds and limits are configurable for flexibility.
 */
function generateInsights(
  results: Omit<AnalyticsResults, 'insights' | 'confidence'>,
  emotions: readonly EmotionEntry[],
  trackingEntries: readonly TrackingEntry[],
  config: AnalyticsConfig['insights']
): string[] {
    const insights: string[] = [];
    const { patterns, correlations, predictiveInsights } = results;

    if (trackingEntries.length === 0) {
      return ["No tracking data available yet. Start by creating your first tracking session to begin pattern analysis."];
    }

    if (trackingEntries.length < config.MIN_SESSIONS_FOR_FULL_ANALYTICS) {
      insights.push(`Limited data available (${trackingEntries.length} sessions). Analytics will improve as more data is collected.`);
    }

    patterns
      .filter(p => p.confidence > config.HIGH_CONFIDENCE_PATTERN_THRESHOLD)
      .slice(0, config.MAX_PATTERNS_TO_SHOW)
      .forEach(pattern => insights.push(`Pattern detected: ${pattern.description} (${Math.round(pattern.confidence * 100)}% confidence)`));

    correlations
      .filter(c => c.significance === 'high')
      .slice(0, config.MAX_CORRELATIONS_TO_SHOW)
      .forEach(correlation => insights.push(`Strong correlation found: ${correlation.description}`));

    predictiveInsights
      .slice(0, config.MAX_PREDICTIONS_TO_SHOW)
      .forEach(insight => insights.push(`Prediction: ${insight.description} (${Math.round(insight.confidence * 100)}% confidence)`));

    // Add progress insights (positive/negative trends)
    if (emotions.length >= config.RECENT_EMOTION_COUNT) {
        const recentEmotions = emotions.slice(-config.RECENT_EMOTION_COUNT);
        const positiveRate = recentEmotions.filter(e => ANALYTICS_CONFIG.POSITIVE_EMOTIONS.has(e.emotion.toLowerCase())).length / recentEmotions.length;

        if (positiveRate > config.POSITIVE_EMOTION_TREND_THRESHOLD) {
            insights.push(`Positive trend: ${Math.round(positiveRate * 100)}% of recent emotions have been positive.`);
        } else if (positiveRate < config.NEGATIVE_EMOTION_TREND_THRESHOLD) {
            insights.push(`Consider reviewing strategies - only ${Math.round(positiveRate * 100)}% of recent emotions have been positive.`);
        }
    }

    if (insights.length === 0) {
      insights.push("Analytics are active and monitoring patterns. Continue collecting data for more detailed insights.");
    }

    return insights;
}

// #endregion

/**
 * Singleton service managing all analytics operations for the Sensory Tracker application.
 * 
 * @class AnalyticsManagerService
 * @singleton
 * 
 * @description This service orchestrates all analytics-related operations:
 * 
 * **Singleton Pattern Implementation**:
 * - Single instance ensures consistent state across the application
 * - Lazy initialization with getInstance() method
 * - Private constructor prevents direct instantiation
 * - Thread-safe in JavaScript's single-threaded environment
 * 
 * **Core Responsibilities**:
 * 1. **Profile Management**: Maintains analytics profiles for each student
 * 2. **Caching Strategy**: Implements TTL-based caching to optimize performance
 * 3. **Analytics Orchestration**: Coordinates pattern analysis, correlations, and predictions
 * 4. **Data Persistence**: Manages localStorage for profile persistence
 * 
 * **Caching Behavior**:
 * - Cache entries stored with timestamp
 * - TTL (Time To Live) configured via ANALYTICS_CONFIG.CACHE_TTL
 * - Automatic cache invalidation on data updates
 * - Manual cache clearing available per student or globally
 * 
 * **Performance Optimizations**:
 * - Lazy loading of analytics data
 * - Batch processing for multiple students
 * - Graceful error handling with Promise.allSettled
 * 
 * @example
 * const manager = AnalyticsManagerService.getInstance();
 * const analytics = await manager.getStudentAnalytics(student);
 */
class AnalyticsManagerService {
  private static instance: AnalyticsManagerService;
  private analyticsProfiles: AnalyticsProfileMap;
  private analyticsCache: AnalyticsCache = new Map();
  private storage: IDataStorage;

  private constructor(storage: IDataStorage, profiles: AnalyticsProfileMap) {
    this.storage = storage;
    this.analyticsProfiles = profiles;
  }

  /**
   * Retrieves the singleton instance of the AnalyticsManagerService.
   * @param {IDataStorage} [storage=dataStorage] - The data storage dependency.
   * @param {AnalyticsProfileMap} [profiles] - Optional initial profiles to load.
   * @returns {AnalyticsManagerService} The singleton instance.
   */
  static getInstance(
    storage: IDataStorage = dataStorage,
    profiles?: AnalyticsProfileMap
  ): AnalyticsManagerService {
    if (!AnalyticsManagerService.instance) {
      let stored: string | null = null;
      try {
        stored = typeof localStorage !== 'undefined' ? localStorage.getItem('sensoryTracker_analyticsProfiles') : null;
      } catch (e) {
        // environment without localStorage (SSR/tests)
      }
      const initialProfiles = profiles ?? loadProfilesFromStorage(stored);
      AnalyticsManagerService.instance = new AnalyticsManagerService(storage, initialProfiles);
    }
    return AnalyticsManagerService.instance;
  }

  /**
   * Initializes analytics profile for a new student.
   * 
   * @public
   * @method initializeStudentAnalytics
   * @param {string} studentId - Unique identifier for the student
   * @returns {void}
   * 
   * @description Creates and persists an analytics profile for a student if not already initialized.
   * 
   * **Profile Initialization**:
   * - Checks if profile already exists (idempotent operation)
   * - Creates default profile with all analytics features enabled
   * - Sets minimal data requirements (1 entry each for emotions, sensory, tracking)
   * - Initializes health score to 0 (will be calculated on first analysis)
   * - Persists profile to localStorage immediately
   * 
   * **Default Configuration**:
   * - patternAnalysisEnabled: true
   * - correlationAnalysisEnabled: true
   * - predictiveInsightsEnabled: true
   * - anomalyDetectionEnabled: true
   * - alertSystemEnabled: true
   * 
   * This method is automatically called when accessing student analytics,
   * ensuring profiles exist before analysis.
   */
  public initializeStudentAnalytics(studentId: string): void {
    try {
      if (!studentId || typeof studentId !== 'string') {
        logger.warn('[analyticsManager] initializeStudentAnalytics: invalid studentId', { studentId });
        return;
      }

      if (this.analyticsProfiles.has(studentId)) {
        return;
      }

      const profile: StudentAnalyticsProfile = {
        studentId,
        isInitialized: true,
        lastAnalyzedAt: null,
        analyticsConfig: {
          patternAnalysisEnabled: true,
          correlationAnalysisEnabled: true,
          predictiveInsightsEnabled: true,
          anomalyDetectionEnabled: true,
          alertSystemEnabled: true,
        },
        minimumDataRequirements: {
          emotionEntries: 1,
          sensoryEntries: 1,
          trackingEntries: 1,
        },
        analyticsHealthScore: 0,
      };

      this.analyticsProfiles.set(studentId, profile);
      this.saveAnalyticsProfiles();
    } catch (error) {
      logger.error('[analyticsManager] initializeStudentAnalytics failed', { error, studentId });
      // fail-soft
    }
  }

  /**
   * Retrieves comprehensive analytics for a specific student.
   * 
   * @public
   * @async
   * @method getStudentAnalytics
   * @param {Student} student - The student object to analyze
   * @returns {Promise<AnalyticsResults>} Complete analytics results including patterns, correlations, and insights
   * 
   * @description Main entry point for retrieving student analytics with intelligent caching.
   * 
   * **Caching Strategy**:
   * 1. Ensures student profile is initialized
   * 2. Checks cache for existing results
   * 3. Returns cached results if within TTL (Time To Live)
   * 4. Generates fresh analytics if cache miss or expired
   * 5. Updates cache with new results and timestamp
   * 
   * **TTL Behavior**:
   * - Default TTL: Configured in ANALYTICS_CONFIG.CACHE_TTL
   * - Cache validity: timestamp + TTL > current time
   * - Expired entries trigger regeneration
   * 
   * **Side Effects**:
   * - Updates lastAnalyzedAt timestamp in profile
   * - Recalculates and updates health score
   * - Persists updated profile to localStorage
   * 
   * @throws {Error} If analytics generation fails (wrapped from generateAnalytics)
   * 
   * @example
   * const results = await analyticsManager.getStudentAnalytics(student);
   * logger.info(`Confidence: ${results.confidence * 100}%`);
   */
  public async getStudentAnalytics(student: Student): Promise<AnalyticsResults> {
    this.initializeStudentAnalytics(student.id);

    const cached = this.analyticsCache.get(student.id);
    if (cached && (Date.now() - cached.timestamp.getTime()) < (ANALYTICS_CONFIG.analytics as any).CACHE_TTL) {
      return cached.results;
    }

    const results = await this.generateAnalytics(student);
    this.analyticsCache.set(student.id, { results, timestamp: new Date() });

    const profile = this.analyticsProfiles.get(student.id);
    if (profile) {
      profile.lastAnalyzedAt = new Date();
      profile.analyticsHealthScore = this.calculateHealthScore(results);
      this.analyticsProfiles.set(student.id, profile);
      this.saveAnalyticsProfiles();
    }

    return results;
  }

  /**
   * Generates fresh analytics for a student by orchestrating all analysis modules.
   * 
   * @private
   * @async
   * @method generateAnalytics
   * @param {Student} student - The student to analyze
   * @returns {Promise<AnalyticsResults>} Complete analytics results
   * 
   * @description Core analytics generation logic that coordinates multiple analysis types.
   * 
   * **Analysis Pipeline**:
   * 1. **Data Collection**: 
   *    - Retrieves all tracking entries for student
   *    - Extracts emotions and sensory inputs from entries
   *    - Loads student goals for predictive analysis
   * 
   * 2. **Minimum Data Check**:
   *    - Verifies at least one data type has entries
   *    - Skips analysis if no data available
   * 
   * 3. **Pattern Analysis** (if data exists):
   *    - Emotion patterns (if emotions > 0)
   *    - Sensory patterns (if sensory inputs > 0)
   *    - Environmental correlations (if tracking >= MIN_TRACKING_FOR_CORRELATION)
   * 
   * 4. **Enhanced Analysis** (if tracking >= MIN_TRACKING_FOR_ENHANCED):
   *    - Predictive insights using ML models
   *    - Anomaly detection for unusual patterns
   * 
   * 5. **Alert Generation**:
   *    - Triggers alert system for any tracking data
   * 
   * 6. **Insight Generation**:
   *    - Creates human-readable insights
   *    - Calculates overall confidence score
   * 
   * **Configuration**:
   * - Prefers live user-adjustable configuration
   * - Falls back to default constants if config unavailable
   * 
   * **Error Handling**:
   * - Logs detailed errors with student context
   * - Re-throws wrapped error for caller handling
   * 
   * @throws {Error} Analytics generation failed for student
   */
  private async generateAnalytics(student: Student): Promise<AnalyticsResults> {
    const trackingEntries = this.storage.getTrackingEntriesForStudent(student.id);
    const goals = this.storage.getGoalsForStudent(student.id);

    const emotions: EmotionEntry[] = trackingEntries.flatMap(entry => entry.emotions || []);
    const sensoryInputs: SensoryEntry[] = trackingEntries.flatMap(entry => entry.sensoryInputs || []);

    const hasMinimumData = emotions.length > 0 || sensoryInputs.length > 0 || trackingEntries.length > 0;

    const patterns: PatternResult[] = [];
    let correlations: CorrelationResult[] = [];
    let predictiveInsights: PredictiveInsight[] = [];
    let anomalies: AnomalyDetection[] = [];

    if (hasMinimumData) {
        try {
            // Prefer live, user-adjustable configuration; fall back to legacy constants to avoid undefined access.
            const liveConfig = (() => {
              try { return analyticsConfig.getConfig(); } catch { return null; }
            })();
            const ANALYTICS = liveConfig?.analytics ?? ANALYTICS_CONFIG.analytics;

            const analysisDays = ANALYTICS?.ANALYSIS_PERIOD_DAYS ?? ANALYTICS_CONFIG.analytics.ANALYSIS_PERIOD_DAYS;

            if (emotions.length > 0) {
                patterns.push(...patternAnalysis.analyzeEmotionPatterns(emotions, analysisDays));
            }
            if (sensoryInputs.length > 0) {
                patterns.push(...patternAnalysis.analyzeSensoryPatterns(sensoryInputs, analysisDays));
            }
            if (trackingEntries.length >= (ANALYTICS?.MIN_TRACKING_FOR_CORRELATION ?? ANALYTICS_CONFIG.analytics.MIN_TRACKING_FOR_CORRELATION)) {
                correlations = patternAnalysis.analyzeEnvironmentalCorrelations(trackingEntries);
            }
            if (trackingEntries.length >= (ANALYTICS?.MIN_TRACKING_FOR_ENHANCED ?? ANALYTICS_CONFIG.analytics.MIN_TRACKING_FOR_ENHANCED)) {
                predictiveInsights = await enhancedPatternAnalysis.generatePredictiveInsights(emotions, sensoryInputs, trackingEntries, goals);
                anomalies = enhancedPatternAnalysis.detectAnomalies(emotions, sensoryInputs, trackingEntries);
            }
            if (trackingEntries.length > 0) {
                await alertSystem.generateAlertsForStudent(student, emotions, sensoryInputs, trackingEntries);
            }
        } catch (error) {
            logger.error(`Error generating analytics for student ${student.id}:`, error);
            // Re-throw a specific error to let the caller handle it
            throw new Error(`Analytics generation failed for student ${student.id}`);
        }
    }

    const resultsWithoutInsights = { patterns, correlations, predictiveInsights, anomalies, hasMinimumData };

    // Resolve INSIGHTS/CONFIDENCE from live config when available; fall back to legacy constants.
    const liveCfg = (() => { try { return analyticsConfig.getConfig(); } catch { return null; } })();
    const INSIGHTS_CFG = liveCfg?.insights ?? ANALYTICS_CONFIG.insights;
    const CONFIDENCE_CFG = liveCfg?.confidence ?? ANALYTICS_CONFIG.confidence;

    return {
      ...resultsWithoutInsights,
      insights: generateInsights(resultsWithoutInsights, emotions, trackingEntries, INSIGHTS_CFG),
      confidence: calculateConfidence(emotions, sensoryInputs, trackingEntries, CONFIDENCE_CFG),
    };
  }

  /**
   * Calculates an "analytics health score" based on the completeness and confidence of the results.
   * @private
   * @param {AnalyticsResults} results - The results from an analytics run.
   * @returns {number} A score from 0 to 100.
   */
  private calculateHealthScore(results: AnalyticsResults): number {
    const liveCfg = (() => { try { return analyticsConfig.getConfig(); } catch { return null; } })();
    const { WEIGHTS } = (liveCfg?.healthScore ?? ANALYTICS_CONFIG.healthScore);
    let score = 0;

    if (results.patterns.length > 0) score += WEIGHTS.PATTERNS;
    if (results.correlations.length > 0) score += WEIGHTS.CORRELATIONS;
    if (results.predictiveInsights.length > 0) score += WEIGHTS.PREDICTIONS;
    if (results.anomalies.length > 0) score += WEIGHTS.ANOMALIES;
    if (results.hasMinimumData) score += WEIGHTS.MINIMUM_DATA;

    return Math.round(score * results.confidence);
  }

  /**
   * Forces a re-calculation of analytics for a specific student by clearing their cache
   * and re-running the analysis.
   * @param {Student} student - The student to re-analyze.
   */
  public async triggerAnalyticsForStudent(student: Student): Promise<void> {
    try {
      if (!student || !student.id) {
        logger.warn('[analyticsManager] triggerAnalyticsForStudent: invalid student', { student });
        return;
      }
      this.analyticsCache.delete(student.id);
      await this.getStudentAnalytics(student);
    } catch (error) {
      logger.error('[analyticsManager] triggerAnalyticsForStudent failed', { error, studentId: student?.id });
      // fail-soft
    }
  }

  /**
   * Triggers an analytics refresh for all students in the system.
   * Uses Promise.allSettled to ensure that one failed analysis does not stop others.
   */
  public async triggerAnalyticsForAllStudents(): Promise<void> {
    const students = this.storage.getStudents();

    const analyticsPromises = students.map(student => {
      this.initializeStudentAnalytics(student.id);
      return this.triggerAnalyticsForStudent(student);
    });

    const settledResults = await Promise.allSettled(analyticsPromises);

    settledResults.forEach((result, index) => {
      if (result.status === 'rejected') {
        logger.error(`Failed to process analytics for student ${students[index].id}:`, result.reason);
      }
    });
  }

  /**
   * Gets the current analytics status for all students, including health scores and last analysis time.
   * This is useful for displaying a high-level dashboard of the system's state.
   * @returns {Array<object>} An array of status objects for each student.
   */
  public getAnalyticsStatus() {
    const students = this.storage.getStudents();
    return students.map(student => {
      const profile = this.analyticsProfiles.get(student.id);
      const cached = this.analyticsCache.get(student.id);

      return {
        studentId: student.id,
        studentName: student.name,
        isInitialized: profile?.isInitialized ?? false,
        lastAnalyzed: profile?.lastAnalyzedAt ?? null,
        healthScore: profile?.analyticsHealthScore ?? 0,
        hasMinimumData: cached?.results.hasMinimumData ?? false,
      };
    });
  }

  /**
   * Clears the analytics cache.
   * @param {string} [studentId] - If provided, clears the cache for only that student.
   * Otherwise, clears the entire analytics cache.
   */
  public clearCache(studentId?: string): void {
    if (studentId) {
      this.analyticsCache.delete(studentId);
    } else {
      this.analyticsCache.clear();
    }
  }

  /**
   * Saves the current map of analytics profiles to localStorage.
   * @private
   */
  private saveAnalyticsProfiles(): void {
    try {
      const data = Object.fromEntries(this.analyticsProfiles);
      if (typeof localStorage !== 'undefined') {
        localStorage.setItem('sensoryTracker_analyticsProfiles', JSON.stringify(data));
      }
    } catch (error) {
      logger.error('Error saving analytics profiles:', error);
      // fail-soft
    }
  }
}

export const analyticsManager = AnalyticsManagerService.getInstance();

import { TrackingEntry, EmotionEntry, SensoryEntry } from "@/types/student";
import { AnalyticsData } from "@/workers/analytics.worker";
import { subDays, startOfDay, endOfDay } from "date-fns";

interface PrecomputationConfig {
  enabled: boolean;
  maxQueueSize: number;
  batchSize: number;
  idleTimeout: number;
  commonTimeframes: number[]; // days to look back
}

interface PrecomputationTask {
  id: string;
  data: AnalyticsData;
  priority: number;
  timestamp: Date;
}

/**
 * AnalyticsPrecomputationManager handles background pre-computation of common analytics queries
 * to improve performance by having results ready when users need them
 */
export class AnalyticsPrecomputationManager {
  private config: PrecomputationConfig;
  private taskQueue: PrecomputationTask[] = [];
  private isProcessing = false;
  private idleCallbackId: number | null = null;
  private processedTasks = new Set<string>();
  private onAnalyze: (data: AnalyticsData) => void;

  constructor(
    onAnalyze: (data: AnalyticsData) => void,
    config: Partial<PrecomputationConfig> = {}
  ) {
    this.onAnalyze = onAnalyze;
    this.config = {
      enabled: true,
      maxQueueSize: 50,
      batchSize: 5,
      idleTimeout: 5000,
      commonTimeframes: [7, 14, 30], // Last 7, 14, and 30 days
      ...config
    };
  }

  /**
   * Schedule precomputation based on current student data
   */
  schedulePrecomputation(
    allEntries: TrackingEntry[],
    allEmotions: EmotionEntry[],
    allSensoryInputs: SensoryEntry[]
  ): void {
    if (!this.config.enabled || this.isProcessing) return;

    // Clear existing queue
    this.taskQueue = [];

    // Get unique student IDs
    const studentIds = new Set<string>();
    allEntries.forEach(e => e.studentId && studentIds.add(e.studentId));
    allEmotions.forEach(e => e.studentId && studentIds.add(e.studentId));
    allSensoryInputs.forEach(s => s.studentId && studentIds.add(s.studentId));

    // Generate tasks for each student and timeframe
    studentIds.forEach(studentId => {
      this.config.commonTimeframes.forEach(days => {
        const task = this.createPrecomputationTask(
          studentId,
          days,
          allEntries,
          allEmotions,
          allSensoryInputs
        );

        if (task && !this.processedTasks.has(task.id)) {
          this.taskQueue.push(task);
        }
      });
    });

    // Also add combined analytics for all students
    this.config.commonTimeframes.forEach(days => {
      const task = this.createCombinedAnalyticsTask(
        days,
        allEntries,
        allEmotions,
        allSensoryInputs
      );

      if (task && !this.processedTasks.has(task.id)) {
        this.taskQueue.push(task);
      }
    });

    // Sort by priority (smaller timeframes first)
    this.taskQueue.sort((a, b) => a.priority - b.priority);

    // Limit queue size
    if (this.taskQueue.length > this.config.maxQueueSize) {
      this.taskQueue = this.taskQueue.slice(0, this.config.maxQueueSize);
    }

    // Schedule processing during idle time
    this.scheduleIdleProcessing();
  }

  /**
   * Create a precomputation task for a specific student and timeframe
   */
  private createPrecomputationTask(
    studentId: string,
    days: number,
    allEntries: TrackingEntry[],
    allEmotions: EmotionEntry[],
    allSensoryInputs: SensoryEntry[]
  ): PrecomputationTask | null {
    const cutoffDate = subDays(new Date(), days);

    // Filter data for this student and timeframe
    const entries = allEntries.filter(e => 
      e.studentId === studentId && e.timestamp >= cutoffDate
    );
    const emotions = allEmotions.filter(e => 
      e.studentId === studentId && e.timestamp >= cutoffDate
    );
    const sensoryInputs = allSensoryInputs.filter(s => 
      s.studentId === studentId && s.timestamp >= cutoffDate
    );

    // Skip if no data
    if (entries.length === 0 && emotions.length === 0 && sensoryInputs.length === 0) {
      return null;
    }

    const taskId = `student:${studentId}:days:${days}`;

    return {
      id: taskId,
      data: { entries, emotions, sensoryInputs },
      priority: days, // Smaller timeframes have higher priority
      timestamp: new Date()
    };
  }

  /**
   * Create a precomputation task for combined analytics
   */
  private createCombinedAnalyticsTask(
    days: number,
    allEntries: TrackingEntry[],
    allEmotions: EmotionEntry[],
    allSensoryInputs: SensoryEntry[]
  ): PrecomputationTask | null {
    const cutoffDate = subDays(new Date(), days);

    // Filter data for timeframe
    const entries = allEntries.filter(e => e.timestamp >= cutoffDate);
    const emotions = allEmotions.filter(e => e.timestamp >= cutoffDate);
    const sensoryInputs = allSensoryInputs.filter(s => s.timestamp >= cutoffDate);

    // Skip if no data
    if (entries.length === 0 && emotions.length === 0 && sensoryInputs.length === 0) {
      return null;
    }

    const taskId = `combined:days:${days}`;

    return {
      id: taskId,
      data: { entries, emotions, sensoryInputs },
      priority: days + 100, // Combined analytics have lower priority
      timestamp: new Date()
    };
  }

  /**
   * Schedule processing during idle time
   */
  private scheduleIdleProcessing(): void {
    if (this.idleCallbackId !== null) {
      cancelIdleCallback(this.idleCallbackId);
    }

    if ('requestIdleCallback' in window) {
      this.idleCallbackId = requestIdleCallback(
        (deadline) => this.processQueue(deadline),
        { timeout: this.config.idleTimeout }
      );
    } else {
      // Fallback for browsers without requestIdleCallback
      setTimeout(() => this.processQueue(), 1000);
    }
  }

  /**
   * Process queued tasks during idle time
   */
  private processQueue(deadline?: IdleDeadline): void {
    if (this.taskQueue.length === 0) {
      this.isProcessing = false;
      return;
    }

    this.isProcessing = true;
    let processed = 0;

    // Process tasks while we have time
    while (
      this.taskQueue.length > 0 && 
      processed < this.config.batchSize &&
      (!deadline || deadline.timeRemaining() > 10)
    ) {
      const task = this.taskQueue.shift();
      if (task) {
        this.onAnalyze(task.data);
        this.processedTasks.add(task.id);
        processed++;
      }
    }

    // Schedule next batch if there are more tasks
    if (this.taskQueue.length > 0) {
      this.scheduleIdleProcessing();
    } else {
      this.isProcessing = false;
    }
  }

  /**
   * Get common data queries for precomputation
   */
  static getCommonDataQueries(
    entries: TrackingEntry[],
    emotions: EmotionEntry[],
    sensoryInputs: SensoryEntry[]
  ): AnalyticsData[] {
    const queries: AnalyticsData[] = [];
    const now = new Date();

    // Common timeframes
    const timeframes = [
      { days: 7, label: 'week' },
      { days: 14, label: 'twoWeeks' },
      { days: 30, label: 'month' }
    ];

    timeframes.forEach(({ days }) => {
      const cutoffDate = subDays(now, days);

      queries.push({
        entries: entries.filter(e => e.timestamp >= cutoffDate),
        emotions: emotions.filter(e => e.timestamp >= cutoffDate),
        sensoryInputs: sensoryInputs.filter(s => s.timestamp >= cutoffDate)
      });
    });

    // Today's data as half-open interval [startOfDay, startOfNextDay)
    const todayStart = startOfDay(now);
    const todayEndExclusive = new Date(todayStart.getTime() + 24 * 60 * 60 * 1000);
    queries.push({
      entries: entries.filter(e => e.timestamp >= todayStart && e.timestamp < todayEndExclusive),
      emotions: emotions.filter(e => e.timestamp >= todayStart && e.timestamp < todayEndExclusive),
      sensoryInputs: sensoryInputs.filter(s => s.timestamp >= todayStart && s.timestamp < todayEndExclusive)
    });

    // High-activity periods (days with more than average activity)
    const dailyActivity = new Map<string, number>();
    entries.forEach(entry => {
      const dateKey = startOfDay(entry.timestamp).toISOString();
      dailyActivity.set(dateKey, (dailyActivity.get(dateKey) || 0) + 1);
    });

    const avgActivity = Array.from(dailyActivity.values()).reduce((a, b) => a + b, 0) / dailyActivity.size;
    const highActivityDays = Array.from(dailyActivity.entries())
      .filter(([_, count]) => count > avgActivity * 1.5)
      .map(([date]) => new Date(date));

    highActivityDays.slice(0, 3).forEach(date => {
      const dayStart = startOfDay(date);
      const dayEndExclusive = new Date(dayStart.getTime() + 24 * 60 * 60 * 1000);
      queries.push({
        entries: entries.filter(e => e.timestamp >= dayStart && e.timestamp < dayEndExclusive),
        emotions: emotions.filter(e => e.timestamp >= dayStart && e.timestamp < dayEndExclusive),
        sensoryInputs: sensoryInputs.filter(s => s.timestamp >= dayStart && s.timestamp < dayEndExclusive)
      });
    });

    return queries;
  }

  /**
   * Clear processed tasks cache
   */
  clearProcessedTasks(): void {
    this.processedTasks.clear();
  }

  /**
   * Stop all precomputation
   */
  stop(): void {
    this.config.enabled = false;
    this.taskQueue = [];
    if (this.idleCallbackId !== null) {
      cancelIdleCallback(this.idleCallbackId);
      this.idleCallbackId = null;
    }
    this.isProcessing = false;
  }

  /**
   * Resume precomputation
   */
  resume(): void {
    this.config.enabled = true;
  }

  /**
   * Get current status
   */
  getStatus(): {
    enabled: boolean;
    queueSize: number;
    isProcessing: boolean;
    processedCount: number;
  } {
    return {
      enabled: this.config.enabled,
      queueSize: this.taskQueue.length,
      isProcessing: this.isProcessing,
      processedCount: this.processedTasks.size
    };
  }
}

import { EmotionEntry, SensoryEntry } from '@/types/student';
import { bin, extent } from 'vega-transforms';

/**
 * Aggregates emotion and sensory entries by day, calculating the total count
 * and average intensity for each day. This is useful for visualizing trends
 * over time.
 *
 * @param entries - An array of EmotionEntry or SensoryEntry objects. Each object
 *   must have a `timestamp` and `intensity` property.
 * @returns An array of objects, where each object represents a day and contains
 *   the date, the total number of entries for that day, and the average
 *   intensity of those entries.
 *
 * @example
 * const entries = [
 *   { timestamp: '2023-01-01T12:00:00Z', intensity: 5 },
 *   { timestamp: '2023-01-01T18:00:00Z', intensity: 3 },
 *   { timestamp: '2023-01-02T09:00:00Z', intensity: 8 },
 * ];
 * const dailyStats = aggregateEntriesByDay(entries);
 * // dailyStats will be:
 * // [
 * //   { date: '2023-01-01', count: 2, averageIntensity: 4 },
 * //   { date: '2023-01-02', count: 1, averageIntensity: 8 },
 * // ]
 */
export function aggregateEntriesByDay(entries: (EmotionEntry | SensoryEntry)[]) {
  const aggregated = entries.reduce((acc, entry) => {
    const date = new Date(entry.timestamp).toISOString().split('T')[0];
    if (!acc[date]) {
      acc[date] = { count: 0, totalIntensity: 0 };
    }
    acc[date].count++;
    acc[date].totalIntensity += entry.intensity;
    return acc;
  }, {} as Record<string, { count: number; totalIntensity: number }>);

  return Object.entries(aggregated).map(([date, { count, totalIntensity }]) => ({
    date,
    count,
    averageIntensity: totalIntensity / count,
  }));
}

/**
 * Computes intensity bins for a collection of emotion or sensory entries.
 * This function uses the vega-transforms `bin` utility to partition the
 * entries into a specified number of bins based on their intensity levels.
 *
 * @param entries - An array of EmotionEntry or SensoryEntry objects. Each
 *   object must have an `intensity` property.
 * @returns An array of objects, where each object represents an intensity
 *   bin. It includes the bin range as a string (e.g., "0-2") and the
 *   number of entries falling within that bin.
 *
 * @example
 * const entries = [
 *   { intensity: 1 },
 *   { intensity: 2 },
 *   { intensity: 5 },
 *   { intensity: 8 },
 *   { intensity: 9 },
 * ];
 * const intensityBins = computeIntensityBins(entries);
 * // Example output (exact bins may vary based on implementation):
 * // [
 * //   { bin: '0-2', count: 2 },
 * //   { bin: '4-6', count: 1 },
 * //   { bin: '8-10', count: 2 },
 * // ]
 */
export function computeIntensityBins(entries: (EmotionEntry | SensoryEntry)[]) {
  const intensityExtent = extent(entries, (d: EmotionEntry | SensoryEntry) => d.intensity);
  const binned = bin({ extent: intensityExtent, maxbins: 5 })(entries, (d: EmotionEntry | SensoryEntry) => d.intensity);

  return binned.map((bin: any) => ({
    bin: `${bin.x0}-${bin.x1}`,
    count: bin.length,
  }));
}

import { TrackingEntry, EmotionEntry, SensoryEntry, Student } from '@/types/student';
import { z } from 'zod';

// Student schema
const studentSchema = z.object({
  id: z.string(),
  name: z.string(),
  grade: z.string().optional(),
  dateOfBirth: z.string().optional(),
  notes: z.string().optional(),
  iepGoals: z.array(z.any()).optional(),
  createdAt: z.date(),
  lastUpdated: z.date().optional(),
  version: z.number().optional(),
});

// Updated tracking entry schema to match the actual TrackingEntry type
const trackingEntrySchema = z.object({
  id: z.string(),
  studentId: z.string(),
  timestamp: z.union([z.date(), z.string()]).transform((val) => 
    typeof val === 'string' ? new Date(val) : val
  ),
  emotions: z.array(z.any()).optional(),
  sensoryInputs: z.array(z.any()).optional(),
  environmentalData: z.any().optional(),
  notes: z.string().optional(),
});

const emotionEntrySchema = z.object({
  id: z.string(),
  studentId: z.string(),
  timestamp: z.union([z.date(), z.string()]).transform((val) => 
    typeof val === 'string' ? new Date(val) : val
  ),
  emotion: z.string(),
  intensity: z.number(),
  triggers: z.array(z.string()).optional(),
  notes: z.string().optional(),
});

const sensoryEntrySchema = z.object({
  id: z.string(),
  studentId: z.string(),
  timestamp: z.union([z.date(), z.string()]).transform((val) => 
    typeof val === 'string' ? new Date(val) : val
  ),
  type: z.string().optional(),
  sensoryType: z.string().optional(),
  response: z.string().optional(),
  intensity: z.number(),
  notes: z.string().optional(),
});

const trackingDataSchema = z.object({
  entries: z.array(trackingEntrySchema),
  emotions: z.array(emotionEntrySchema),
  sensoryInputs: z.array(sensoryEntrySchema),
});

type ValidationResult = {
  valid: boolean;
  errors: z.ZodError | null;
  cleanedData: {
    entries: TrackingEntry[];
    emotions: EmotionEntry[];
    sensoryInputs: SensoryEntry[];
  };
};

export function validateTrackingData(data: any): ValidationResult {
  const result = trackingDataSchema.safeParse(data);
  if (result.success) {
    return {
      valid: true,
      errors: null,
      cleanedData: result.data,
    };
  } else {
    return {
      valid: false,
      errors: result.error,
      cleanedData: {
        entries: [],
        emotions: [],
        sensoryInputs: [],
      },
    };
  }
}

// Individual validation functions
type SimpleValidationResult = {
  isValid: boolean;
  errors?: string[];
};

export function validateStudent(student: any): SimpleValidationResult {
  const result = studentSchema.safeParse(student);
  return {
    isValid: result.success,
    errors: result.success ? undefined : result.error.errors.map(e => e.message),
  };
}

export function validateEmotionEntry(entry: any): SimpleValidationResult {
  const result = emotionEntrySchema.safeParse(entry);
  return {
    isValid: result.success,
    errors: result.success ? undefined : result.error.errors.map(e => e.message),
  };
}

export function validateSensoryEntry(entry: any): SimpleValidationResult {
  const result = sensoryEntrySchema.safeParse(entry);
  return {
    isValid: result.success,
    errors: result.success ? undefined : result.error.errors.map(e => e.message),
  };
}

export function validateTrackingEntry(entry: any): SimpleValidationResult {
  const result = trackingEntrySchema.safeParse(entry);
  return {
    isValid: result.success,
    errors: result.success ? undefined : result.error.errors.map(e => e.message),
  };
}

/**
 * Diagnostic Logger for Debugging
 * Mr. Debugger's specialized diagnostic tool
 */

import { logger } from './logger';

interface DiagnosticInfo {
  timestamp: Date;
  memoryUsage?: {
    usedJSHeapSize: number;
    totalJSHeapSize: number;
    jsHeapSizeLimit: number;
  };
  activeTimers?: number;
  activeListeners?: number;
  componentName?: string;
  action?: string;
  data?: any;
}

class DiagnosticLogger {
  private static instance: DiagnosticLogger;
  private activeTimers = new Set<number>();
  private activeListeners = new Map<string, number>();
  private diagnosticMode = false;

  private constructor() {
    // Determine initial diagnostic mode from URL or localStorage (opt-in only)
    if (typeof window !== 'undefined') {
      try {
        const urlFlag = new URLSearchParams(window.location.search).get('diag') === '1';
        const lsFlag = (window.localStorage.getItem('diagnostics') || '').toLowerCase() === 'on';
        this.diagnosticMode = Boolean(urlFlag || lsFlag);
      } catch {
        this.diagnosticMode = false;
      }
    }

    // Track performance metrics only when diagnostics are enabled
    if (this.diagnosticMode && typeof window !== 'undefined' && 'performance' in window) {
      this.startPerformanceMonitoring();
    }
  }

  static getInstance(): DiagnosticLogger {
    if (!DiagnosticLogger.instance) {
      DiagnosticLogger.instance = new DiagnosticLogger();
    }
    return DiagnosticLogger.instance;
  }

  private startPerformanceMonitoring() {
    // Monitor memory usage every 5 seconds
    setInterval(() => {
      if (this.diagnosticMode && (performance as any).memory) {
        const memInfo = (performance as any).memory;
        const usedMB = (memInfo.usedJSHeapSize / 1048576).toFixed(2);
        const totalMB = (memInfo.totalJSHeapSize / 1048576).toFixed(2);

        logger.info(`[DIAGNOSTIC] Memory Usage: ${usedMB}MB / ${totalMB}MB`);

        // Warning if memory usage is high
        if (memInfo.usedJSHeapSize / memInfo.jsHeapSizeLimit > 0.9) {
          logger.warn('[DIAGNOSTIC] High memory usage detected!', {
            used: usedMB,
            limit: (memInfo.jsHeapSizeLimit / 1048576).toFixed(2)
          });
        }
      }
    }, 5000);
  }

  public isEnabled() {
    return this.diagnosticMode;
  }

  logComponentMount(componentName: string) {
    if (!this.diagnosticMode) return;

    const info: DiagnosticInfo = {
      timestamp: new Date(),
      componentName,
      action: 'MOUNT',
      activeTimers: this.activeTimers.size,
      activeListeners: Array.from(this.activeListeners.values()).reduce((a, b) => a + b, 0)
    };

    logger.debug('[DIAGNOSTIC] Component Mounted', info);
  }

  logComponentUnmount(componentName: string) {
    if (!this.diagnosticMode) return;

    const info: DiagnosticInfo = {
      timestamp: new Date(),
      componentName,
      action: 'UNMOUNT',
      activeTimers: this.activeTimers.size,
      activeListeners: Array.from(this.activeListeners.values()).reduce((a, b) => a + b, 0)
    };

    logger.debug('[DIAGNOSTIC] Component Unmounted', info);
  }

  logUseEffectCleanup(componentName: string, hasCleanup: boolean) {
    if (!this.diagnosticMode) return;

    logger.debug('[DIAGNOSTIC] UseEffect Cleanup', {
      componentName,
      hasCleanup,
      timestamp: new Date()
    });

    if (!hasCleanup) {
      logger.warn('[DIAGNOSTIC] Missing cleanup in useEffect!', { componentName });
    }
  }

  logWorkerMessage(workerName: string, messageType: string, data?: any) {
    if (!this.diagnosticMode) return;

    logger.debug('[DIAGNOSTIC] Worker Message', {
      workerName,
      messageType,
      timestamp: new Date(),
      dataSize: data ? JSON.stringify(data).length : 0
    });
  }

  logWorkerTimeout(workerName: string, timeout: number) {
    if (!this.diagnosticMode) return;

    logger.error('[DIAGNOSTIC] Worker Timeout!', {
      workerName,
      timeout,
      timestamp: new Date()
    });
  }

  trackTimer(timerId: number) {
    if (!this.diagnosticMode) return;
    this.activeTimers.add(timerId);
    logger.debug('[DIAGNOSTIC] Timer Created', { 
      timerId, 
      activeCount: this.activeTimers.size 
    });
  }

  untrackTimer(timerId: number) {
    if (!this.diagnosticMode) return;
    this.activeTimers.delete(timerId);
    logger.debug('[DIAGNOSTIC] Timer Cleared', { 
      timerId, 
      activeCount: this.activeTimers.size 
    });
  }

  trackEventListener(element: string, event: string) {
    if (!this.diagnosticMode) return;
    const key = `${element}-${event}`;
    const current = this.activeListeners.get(key) || 0;
    this.activeListeners.set(key, current + 1);

    logger.debug('[DIAGNOSTIC] Event Listener Added', {
      element,
      event,
      totalListeners: Array.from(this.activeListeners.values()).reduce((a, b) => a + b, 0)
    });
  }

  untrackEventListener(element: string, event: string) {
    if (!this.diagnosticMode) return;
    const key = `${element}-${event}`;
    const current = this.activeListeners.get(key) || 0;
    if (current > 0) {
      this.activeListeners.set(key, current - 1);
    }

    logger.debug('[DIAGNOSTIC] Event Listener Removed', {
      element,
      event,
      totalListeners: Array.from(this.activeListeners.values()).reduce((a, b) => a + b, 0)
    });
  }

  logAsyncOperation(operationName: string, duration: number, success: boolean) {
    if (!this.diagnosticMode) return;

    const logData = {
      operationName,
      duration,
      success,
      timestamp: new Date()
    };

    if (duration > 3000) {
      logger.warn('[DIAGNOSTIC] Slow async operation detected!', logData);
    } else {
      logger.debug('[DIAGNOSTIC] Async operation completed', logData);
    }
  }

  checkForLeaks() {
    if (!this.diagnosticMode) return;

    const report = {
      activeTimers: this.activeTimers.size,
      activeListeners: Array.from(this.activeListeners.values()).reduce((a, b) => a + b, 0),
      timestamp: new Date()
    };

    if (report.activeTimers > 10) {
      logger.warn('[DIAGNOSTIC] Potential timer leak detected!', report);
    }

    if (report.activeListeners > 50) {
      logger.warn('[DIAGNOSTIC] Potential event listener leak detected!', report);
    }

    return report;
  }

  setDiagnosticMode(enabled: boolean) {
    this.diagnosticMode = enabled;
    logger.info(`[DIAGNOSTIC] Diagnostic mode ${enabled ? 'enabled' : 'disabled'}`);
    // Note: we intentionally do not re-wrap timers dynamically here to avoid breaking existing references
  }
}

export const diagnostics = DiagnosticLogger.getInstance();

// Wrap setTimeout and clearTimeout to track timers ONLY when diagnostics are enabled
if (typeof window !== 'undefined' && diagnostics.isEnabled()) {
  const originalSetTimeout = window.setTimeout;
  const originalClearTimeout = window.clearTimeout;

  window.setTimeout = function(...args: any[]) {
    const timerId = originalSetTimeout.apply(window, args as any);
    diagnostics.trackTimer(timerId as any);
    return timerId;
  } as typeof window.setTimeout;

  window.clearTimeout = function(timerId: number) {
    diagnostics.untrackTimer(timerId);
    return originalClearTimeout.call(window, timerId);
  } as typeof window.clearTimeout;
}

import { EmotionEntry, SensoryEntry, TrackingEntry, Goal } from "@/types/student";
import { isWithinInterval, subDays, format, differenceInDays } from "date-fns";
import { analyticsConfig, AnalyticsConfiguration } from "@/lib/analyticsConfig";
import { mlModels, EmotionPrediction, SensoryPrediction, BaselineCluster } from "@/lib/mlModels";
import { logger } from '@/lib/logger';

export interface PredictiveInsight {
  type: 'prediction' | 'trend' | 'recommendation' | 'risk';
  title: string;
  description: string;
  confidence: number;
  timeframe: string;
  prediction?: {
    value: number;
    trend: 'increasing' | 'decreasing' | 'stable';
    accuracy: number;
  };
  recommendations: string[];
  severity?: 'low' | 'medium' | 'high';
  source?: 'statistical' | 'ml' | 'hybrid';
  mlPrediction?: EmotionPrediction[] | SensoryPrediction;
}

export interface TrendAnalysis {
  metric: string;
  direction: 'increasing' | 'decreasing' | 'stable';
  rate: number; // change per day
  significance: number; // 0-1
  confidence: number;
  forecast: {
    next7Days: number;
    next30Days: number;
    confidence: number;
  };
}

export interface AnomalyDetection {
  timestamp: Date;
  type: 'emotion' | 'sensory' | 'environmental';
  severity: 'low' | 'medium' | 'high';
  description: string;
  deviationScore: number;
  recommendations: string[];
}

export interface CorrelationMatrix {
  factors: string[];
  matrix: number[][];
  significantPairs: Array<{
    factor1: string;
    factor2: string;
    correlation: number;
    pValue: number;
    significance: 'low' | 'moderate' | 'high';
  }>;
}

class EnhancedPatternAnalysisEngine {
  private config: AnalyticsConfiguration;
  private mlModelsInitialized: boolean = false;

  constructor() {
    this.config = analyticsConfig.getConfig();

    // Subscribe to configuration changes
    analyticsConfig.subscribe((newConfig) => {
      this.config = newConfig;
    });

    // Initialize ML models
    this.initializeMLModels();
  }

  private async initializeMLModels(): Promise<void> {
    try {
      await mlModels.init();
      this.mlModelsInitialized = true;
    } catch (error) {
      logger.error('Failed to initialize ML models:', error);
      this.mlModelsInitialized = false;
    }
  }

  // Predictive Analytics with ML Integration
  async generatePredictiveInsights(
    emotions: EmotionEntry[],
    sensoryInputs: SensoryEntry[],
    trackingEntries: TrackingEntry[],
    goals: Goal[] = []
  ): Promise<PredictiveInsight[]> {
    const insights: PredictiveInsight[] = [];

    // Statistical emotional well-being prediction
    const emotionTrend = this.analyzeEmotionTrend(emotions);
    if (emotionTrend && emotionTrend.significance >= this.config.enhancedAnalysis.predictionConfidenceThreshold) {
      const statisticalInsight: PredictiveInsight = {
        type: 'prediction',
        title: 'Emotional Well-being Forecast (Statistical)',
        description: `Based on current trends, emotional intensity is ${emotionTrend.direction}`,
        confidence: emotionTrend.significance,
        timeframe: '7-day forecast',
        prediction: {
          value: emotionTrend.forecast.next7Days,
          trend: emotionTrend.direction,
          accuracy: emotionTrend.confidence
        },
        recommendations: this.getEmotionTrendRecommendations(emotionTrend),
        severity: this.getTrendSeverity(emotionTrend),
        source: 'statistical'
      };
      insights.push(statisticalInsight);
    }

    // ML emotional prediction if available
    if (this.mlModelsInitialized && trackingEntries.length >= 7) {
      try {
        const modelStatus = await mlModels.getModelStatus();
        if (modelStatus.get('emotion-prediction')) {
          const mlEmotionPredictions = await mlModels.predictEmotions(
            trackingEntries.slice(-14), // Use last 14 days for better context
            7
          );

          if (mlEmotionPredictions.length > 0) {
            // Calculate overall trend from ML predictions
            const avgPredictedIntensity = mlEmotionPredictions.reduce((sum, pred) => {
              const emotionSum = Object.values(pred.emotions).reduce((s, v) => s + v, 0);
              return sum + emotionSum / Object.keys(pred.emotions).length;
            }, 0) / mlEmotionPredictions.length;

            const currentAvgIntensity = emotions.slice(-7).reduce((sum, e) => sum + e.intensity, 0) /
              Math.max(emotions.slice(-7).length, 1);

            const mlTrend = avgPredictedIntensity > currentAvgIntensity * 1.1 ? 'increasing' :
                           avgPredictedIntensity < currentAvgIntensity * 0.9 ? 'decreasing' : 'stable';

            insights.push({
              type: 'prediction',
              title: 'Emotional Well-being Forecast (ML)',
              description: `Machine learning predicts emotional patterns will be ${mlTrend}`,
              confidence: mlEmotionPredictions[0].confidence,
              timeframe: '7-day forecast',
              prediction: {
                value: avgPredictedIntensity,
                trend: mlTrend,
                accuracy: mlEmotionPredictions[0].confidence
              },
              recommendations: this.getMLEmotionRecommendations(mlEmotionPredictions, mlTrend),
              severity: mlTrend === 'increasing' && avgPredictedIntensity > 3.5 ? 'high' :
                       mlTrend === 'decreasing' && avgPredictedIntensity < 2 ? 'medium' : 'low',
              source: 'ml',
              mlPrediction: mlEmotionPredictions
            });
          }
        }
      } catch (error) {
        logger.error('ML emotion prediction failed:', error);
      }
    }

    // Statistical sensory regulation prediction
    const sensoryTrend = this.analyzeSensoryTrend(sensoryInputs);
    if (sensoryTrend && sensoryTrend.significance >= this.config.enhancedAnalysis.predictionConfidenceThreshold) {
      insights.push({
        type: 'prediction',
        title: 'Sensory Regulation Forecast (Statistical)',
        description: `Sensory seeking/avoiding patterns show ${sensoryTrend.direction} trend`,
        confidence: sensoryTrend.significance,
        timeframe: '14-day forecast',
        prediction: {
          value: sensoryTrend.forecast.next7Days,
          trend: sensoryTrend.direction,
          accuracy: sensoryTrend.confidence
        },
        recommendations: this.getSensoryTrendRecommendations(sensoryTrend),
        severity: this.getTrendSeverity(sensoryTrend),
        source: 'statistical'
      });
    }

    // ML sensory prediction if available
    if (this.mlModelsInitialized && trackingEntries.length > 0) {
      try {
        const modelStatus = await mlModels.getModelStatus();
        if (modelStatus.get('sensory-response') && trackingEntries[trackingEntries.length - 1].environmentalData) {
          const latestEnvironment = {
            lighting: trackingEntries[trackingEntries.length - 1].environmentalData?.roomConditions?.lighting as 'bright' | 'dim' | 'moderate' || 'moderate',
            noise: (trackingEntries[trackingEntries.length - 1].environmentalData?.roomConditions?.noiseLevel &&
                   trackingEntries[trackingEntries.length - 1].environmentalData.roomConditions.noiseLevel > 70 ? 'loud' :
                   trackingEntries[trackingEntries.length - 1].environmentalData?.roomConditions?.noiseLevel &&
                   trackingEntries[trackingEntries.length - 1].environmentalData.roomConditions.noiseLevel < 40 ? 'quiet' : 'moderate') as 'loud' | 'moderate' | 'quiet',
            temperature: (trackingEntries[trackingEntries.length - 1].environmentalData?.roomConditions?.temperature &&
                        trackingEntries[trackingEntries.length - 1].environmentalData.roomConditions.temperature > 26 ? 'hot' :
                        trackingEntries[trackingEntries.length - 1].environmentalData?.roomConditions?.temperature &&
                        trackingEntries[trackingEntries.length - 1].environmentalData.roomConditions.temperature < 18 ? 'cold' : 'comfortable') as 'hot' | 'cold' | 'comfortable',
            crowded: 'moderate' as const,
            smells: false,
            textures: false
          };

          const mlSensoryPrediction = await mlModels.predictSensoryResponse(
            latestEnvironment,
            new Date()
          );

          if (mlSensoryPrediction) {
            insights.push({
              type: 'prediction',
              title: 'Sensory Response Prediction (ML)',
              description: `Machine learning predicts sensory responses based on current environment`,
              confidence: mlSensoryPrediction.confidence,
              timeframe: 'Current environment',
              recommendations: this.getMLSensoryRecommendations(mlSensoryPrediction),
              severity: mlSensoryPrediction.environmentalTriggers.length > 2 ? 'high' :
                       mlSensoryPrediction.environmentalTriggers.length > 0 ? 'medium' : 'low',
              source: 'ml',
              mlPrediction: mlSensoryPrediction
            });
          }
        }
      } catch (error) {
        logger.error('ML sensory prediction failed:', error);
      }
    }

    // Goal achievement prediction
    goals.forEach(goal => {
      const goalPrediction = this.predictGoalAchievement(goal);
      if (goalPrediction) {
        insights.push(goalPrediction);
      }
    });

    // Risk assessment
    const riskInsights = this.assessRisks(emotions, sensoryInputs, trackingEntries);
    insights.push(...riskInsights);

    return insights;
  }

  // Enhanced Trend Analysis with Statistical Significance
  analyzeTrendsWithStatistics(data: { value: number; timestamp: Date }[]): TrendAnalysis | null {
    if (data.length < this.config.enhancedAnalysis.minSampleSize) return null;

    // Sort by timestamp
    const sortedData = data.sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

    // Calculate linear regression
    const n = sortedData.length;
    const x = sortedData.map((_, i) => i);
    const y = sortedData.map(d => d.value);

    const sumX = x.reduce((a, b) => a + b, 0);
    const sumY = y.reduce((a, b) => a + b, 0);
    const sumXY = x.map((xi, i) => xi * y[i]).reduce((a, b) => a + b, 0);
    const sumXX = x.map(xi => xi * xi).reduce((a, b) => a + b, 0);

    const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
    const intercept = (sumY - slope * sumX) / n;

    // Calculate R-squared for significance
    const yMean = sumY / n;
    const yPred = x.map(xi => (Number.isFinite(slope) ? slope * xi + intercept : intercept));
    const ssRes = y.map((yi, i) => Math.pow(yi - yPred[i], 2)).reduce((a, b) => a + b, 0);
    const ssTot = y.map(yi => Math.pow(yi - yMean, 2)).reduce((a, b) => a + b, 0);
    const rSquared = ssTot === 0 ? 0 : Math.max(0, Math.min(1, 1 - (ssRes / ssTot)));

    // Enhanced confidence calculation
    const timeSpanDays = differenceInDays(
      sortedData[sortedData.length - 1].timestamp,
      sortedData[0].timestamp
    );

    // Guard against zero/negative timespan to avoid NaN/Infinity
    const safeTimeSpanDays = Math.max(1, timeSpanDays || 0);
    const dailyRate = Number.isFinite(slope) ? slope * (n / safeTimeSpanDays) : 0;

    // Multi-factor confidence calculation
    const dataQuality = Math.min(1, n / 30); // Better with more data points
    const timeSpanQuality = Math.min(1, timeSpanDays / 21); // Better with longer timespan
    const patternStrength = Math.max(0, rSquared); // R-squared strength
    const enhancedConfidence = (dataQuality * 0.3 + timeSpanQuality * 0.3 + patternStrength * 0.4);

    const direction = Math.abs(dailyRate) < this.config.enhancedAnalysis.trendThreshold ? 'stable' :
                     dailyRate > 0 ? 'increasing' : 'decreasing';

    return {
      metric: 'Overall Trend',
      direction,
      rate: Number.isFinite(dailyRate) ? dailyRate : 0,
      significance: Number.isFinite(rSquared) ? rSquared : 0,
      confidence: Number.isFinite(enhancedConfidence) ? enhancedConfidence : 0,
      forecast: {
        next7Days: (yPred[yPred.length - 1] ?? 0) + ((Number.isFinite(slope) ? slope : 0) * 7),
        next30Days: (yPred[yPred.length - 1] ?? 0) + ((Number.isFinite(slope) ? slope : 0) * 30),
        confidence: Number.isFinite(enhancedConfidence) ? enhancedConfidence : 0
      }
    };
  }

  // Generate confidence explanation
  generateConfidenceExplanation(
    dataPoints: number,
    timeSpanDays: number,
    rSquared: number,
    confidence: number
  ): { level: 'low' | 'medium' | 'high'; explanation: string; factors: string[] } {
    const factors: string[] = [];
    let explanation = '';
    let level: 'low' | 'medium' | 'high' = 'low';

    if (dataPoints < 10) {
      factors.push(`insufficientData:${dataPoints}:${this.config.enhancedAnalysis.minSampleSize}`);
    }

    if (timeSpanDays < 14) {
      factors.push(`shortTimespan:${timeSpanDays}:21`);
    }

    if (rSquared < 0.3) {
      factors.push(`weakPattern:${rSquared.toFixed(3)}`);
    } else if (rSquared > 0.7) {
      factors.push(`strongPattern:${rSquared.toFixed(3)}`);
    } else {
      factors.push('moderatePattern');
    }

    // Determine overall level and explanation
    if (confidence >= 0.7) {
      level = 'high';
      explanation = rSquared > 0.8 ? 'excellentData' : 'reliableInsight';
    } else if (confidence >= 0.4) {
      level = 'medium';
      explanation = 'emergingTrend';
    } else {
      level = 'low';
      explanation = 'needMoreData';
    }

    return { level, explanation, factors };
  }

  // Anomaly Detection using Statistical Methods
  detectAnomalies(
    emotions: EmotionEntry[],
    sensoryInputs: SensoryEntry[],
    trackingEntries: TrackingEntry[]
  ): AnomalyDetection[] {
    const anomalies: AnomalyDetection[] = [];

    // Emotion intensity anomalies
    const emotionIntensities = emotions.map(e => e.intensity);
    const emotionMean = emotionIntensities.reduce((a, b) => a + b, 0) / emotionIntensities.length;
    const emotionStd = Math.sqrt(
      emotionIntensities.map(x => Math.pow(x - emotionMean, 2)).reduce((a, b) => a + b, 0) / emotionIntensities.length
    );

    // Apply anomaly sensitivity multiplier
    const anomalyThreshold = this.config.enhancedAnalysis.anomalyThreshold *
      (1 / this.config.alertSensitivity.anomalyMultiplier);

    emotions.forEach(emotion => {
      const zScore = Math.abs((emotion.intensity - emotionMean) / emotionStd);
      if (zScore > anomalyThreshold) {
        anomalies.push({
          timestamp: emotion.timestamp,
          type: 'emotion',
          severity: zScore > 3 ? 'high' : zScore > 2.5 ? 'medium' : 'low',
          description: `Unusual ${emotion.emotion} intensity detected (${emotion.intensity}/5)`,
          deviationScore: zScore,
          recommendations: this.getAnomalyRecommendations('emotion', emotion.emotion, zScore)
        });
      }
    });

    // Sensory frequency anomalies
    const dailySensoryCounts = this.groupSensoryByDay(sensoryInputs);
    const counts = Object.values(dailySensoryCounts);
    if (counts.length > 0) {
      const countMean = counts.reduce((a, b) => a + b, 0) / counts.length;
      const countStd = Math.sqrt(
        counts.map(x => Math.pow(x - countMean, 2)).reduce((a, b) => a + b, 0) / counts.length
      );

      Object.entries(dailySensoryCounts).forEach(([date, count]) => {
        const zScore = Math.abs((count - countMean) / countStd);
        if (zScore > anomalyThreshold) {
          anomalies.push({
            timestamp: new Date(date),
            type: 'sensory',
            severity: zScore > 3 ? 'high' : zScore > 2.5 ? 'medium' : 'low',
            description: `Unusual sensory activity level detected (${count} inputs)`,
            deviationScore: zScore,
            recommendations: this.getAnomalyRecommendations('sensory', 'frequency', zScore)
          });
        }
      });
    }

    return anomalies.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  // Comprehensive Correlation Matrix
  generateCorrelationMatrix(trackingEntries: TrackingEntry[]): CorrelationMatrix {
    const factors = [
      'avgEmotionIntensity',
      'positiveEmotionRatio',
      'sensorySeekingRatio',
      'noiseLevel',
      'temperature',
      'lightingQuality'
    ];

    const dataPoints = trackingEntries.map(entry => ({
      avgEmotionIntensity: entry.emotions.length > 0 
        ? entry.emotions.reduce((sum, e) => sum + e.intensity, 0) / entry.emotions.length 
        : 0,
      positiveEmotionRatio: entry.emotions.length > 0
        ? entry.emotions.filter(e => ['happy', 'calm', 'focused', 'excited'].includes(e.emotion.toLowerCase())).length / entry.emotions.length
        : 0,
      sensorySeekingRatio: entry.sensoryInputs.length > 0
        ? entry.sensoryInputs.filter(s => s.response.toLowerCase().includes('seeking')).length / entry.sensoryInputs.length
        : 0,
      noiseLevel: entry.environmentalData?.roomConditions?.noiseLevel || 0,
      temperature: entry.environmentalData?.roomConditions?.temperature || 0,
      lightingQuality: this.convertLightingToNumeric(entry.environmentalData?.roomConditions?.lighting)
    }));

    const matrix: number[][] = [];
    const significantPairs: CorrelationMatrix['significantPairs'] = [];

    factors.forEach((factor1, i) => {
      matrix[i] = [];
      factors.forEach((factor2, j) => {
        const values1 = dataPoints.map(d => d[factor1 as keyof typeof d]).filter(v => v !== undefined);
        const values2 = dataPoints.map(d => d[factor2 as keyof typeof d]).filter(v => v !== undefined);

        const correlation = this.calculatePearsonCorrelation(values1, values2);
        matrix[i][j] = correlation;

        if (i < j && Math.abs(correlation) > 0.3 && values1.length >= this.config.enhancedAnalysis.minSampleSize) {
          const pValue = this.calculatePValue(correlation, values1.length);
          significantPairs.push({
            factor1,
            factor2,
            correlation,
            pValue,
            significance: Math.abs(correlation) > 0.7 ? 'high' : 
                         Math.abs(correlation) > 0.5 ? 'moderate' : 'low'
          });
        }
      });
    });

    return {
      factors,
      matrix,
      significantPairs: significantPairs.sort((a, b) => Math.abs(b.correlation) - Math.abs(a.correlation))
    };
  }

  // Helper Methods
  private analyzeEmotionTrend(emotions: EmotionEntry[]): TrendAnalysis | null {
    const emotionData = emotions.map(e => ({
      value: e.intensity,
      timestamp: e.timestamp
    }));

    return this.analyzeTrendsWithStatistics(emotionData);
  }

  private analyzeSensoryTrend(sensoryInputs: SensoryEntry[]): TrendAnalysis | null {
    // Convert sensory responses to numeric values for trend analysis
    const sensoryData = sensoryInputs.map(s => ({
      value: s.response.toLowerCase().includes('seeking') ? 1 : 
             s.response.toLowerCase().includes('avoiding') ? -1 : 0,
      timestamp: s.timestamp
    }));

    return this.analyzeTrendsWithStatistics(sensoryData);
  }

  private predictGoalAchievement(goal: Goal): PredictiveInsight | null {
    if (!goal.dataPoints || goal.dataPoints.length < 3) return null;

    const progressData = goal.dataPoints.map(dp => ({
      value: dp.value,
      timestamp: dp.timestamp
    }));

    const trend = this.analyzeTrendsWithStatistics(progressData);
    if (!trend) return null;

    const currentProgress = goal.dataPoints[goal.dataPoints.length - 1].value;
    const targetValue = goal.targetValue;
    const remainingProgress = targetValue - currentProgress;
    const estimatedDays = trend.rate > 0 ? remainingProgress / trend.rate : -1;

    return {
      type: 'prediction',
      title: `Goal Achievement Forecast: ${goal.title}`,
      description: estimatedDays > 0 
        ? `Estimated ${Math.ceil(estimatedDays)} days to achieve goal at current pace`
        : 'Goal may require strategy adjustment based on current trend',
      confidence: trend.significance,
      timeframe: 'Goal completion forecast',
      prediction: {
        value: targetValue,
        trend: trend.direction,
        accuracy: trend.significance
      },
      recommendations: this.getGoalRecommendations(goal, trend, estimatedDays),
      severity: estimatedDays < 0 ? 'high' : estimatedDays > 60 ? 'medium' : 'low'
    };
  }

  private assessRisks(
    emotions: EmotionEntry[],
    sensoryInputs: SensoryEntry[],
    trackingEntries: TrackingEntry[]
  ): PredictiveInsight[] {
    const risks: PredictiveInsight[] = [];
    const recentData = {
      emotions: emotions.filter(e => e.timestamp >= subDays(new Date(), this.config.timeWindows.shortTermDays)),
      sensoryInputs: sensoryInputs.filter(s => s.timestamp >= subDays(new Date(), this.config.timeWindows.shortTermDays)),
      trackingEntries: trackingEntries.filter(t => t.timestamp >= subDays(new Date(), this.config.timeWindows.shortTermDays))
    };

    // Apply sensitivity multiplier for risk assessment
    const riskThreshold = this.config.enhancedAnalysis.riskAssessmentThreshold *
      (1 / this.config.alertSensitivity.emotionIntensityMultiplier);

    // High stress accumulation risk - fixed for 1-5 scale
    const highStressCount = recentData.emotions.filter(e =>
      e.intensity >= 4 && ['anxious', 'frustrated', 'overwhelmed', 'angry'].includes(e.emotion.toLowerCase())
    ).length;

    if (highStressCount >= Math.floor(riskThreshold)) {
      risks.push({
        type: 'risk',
        title: 'Stress Accumulation Risk',
        description: `${highStressCount} high-stress incidents in the past 2 weeks`,
        confidence: 0.8,
        timeframe: 'Immediate attention needed',
        recommendations: [
          'Implement immediate stress reduction strategies',
          'Review and adjust current interventions',
          'Consider environmental modifications',
          'Schedule additional support sessions'
        ],
        severity: 'high'
      });
    }

    return risks;
  }

  private groupSensoryByDay(sensoryInputs: SensoryEntry[]): Record<string, number> {
    return sensoryInputs.reduce((acc, input) => {
      const date = format(input.timestamp, 'yyyy-MM-dd');
      acc[date] = (acc[date] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);
  }

  private convertLightingToNumeric(lighting?: string): number {
    const lightingMap: Record<string, number> = {
      'dim': 1,
      'normal': 2,
      'bright': 3,
      'fluorescent': 2.5,
      'natural': 3.5
    };
    return lightingMap[lighting?.toLowerCase() || ''] || 2;
  }

  private calculatePearsonCorrelation(x: number[], y: number[]): number {
    if (x.length !== y.length || x.length === 0) return 0;

    const n = x.length;
    const sumX = x.reduce((a, b) => a + b, 0);
    const sumY = y.reduce((a, b) => a + b, 0);
    const sumXY = x.map((xi, i) => xi * y[i]).reduce((a, b) => a + b, 0);
    const sumXX = x.map(xi => xi * xi).reduce((a, b) => a + b, 0);
    const sumYY = y.map(yi => yi * yi).reduce((a, b) => a + b, 0);

    const numerator = n * sumXY - sumX * sumY;
    const denominator = Math.sqrt((n * sumXX - sumX * sumX) * (n * sumYY - sumY * sumY));

    return denominator === 0 ? 0 : numerator / denominator;
  }

  private calculatePValue(correlation: number, sampleSize: number): number {
    // Simplified p-value calculation for correlation
    if (!Number.isFinite(correlation) || sampleSize <= 2 || !Number.isFinite(sampleSize)) {
      return 1; // non-significant
    }
    const denom = (1 - correlation * correlation);
    if (denom <= 0) return 0; // maximal correlation
    const t = correlation * Math.sqrt((sampleSize - 2) / denom);
    if (!Number.isFinite(t)) return 1;
    const val = 2 * (1 - this.studentTCDF(Math.abs(t), sampleSize - 2));
    return Number.isFinite(val) ? Math.max(0, Math.min(1, val)) : 1;
  }

  private studentTCDF(t: number, df: number): number {
    // Simplified student's t-distribution CDF approximation
    if (!Number.isFinite(t) || df <= 0) return 0.5;
    const x = df / (df + t * t);
    const ib = this.incompleteBeta(Math.max(1e-8, df / 2), 0.5, Math.max(1e-8, Math.min(1 - 1e-8, x)));
    const res = 0.5 + 0.5 * Math.sign(t) * ib;
    return Number.isFinite(res) ? Math.max(0, Math.min(1, res)) : 0.5;
  }

  private incompleteBeta(a: number, b: number, x: number): number {
    // Simplified incomplete beta function approximation with guards
    if (!Number.isFinite(a) || !Number.isFinite(b) || !Number.isFinite(x)) return 0;
    if (x <= 0) return 0;
    if (x >= 1) return 1;
    const num = Math.pow(x, a) * Math.pow(1 - x, b);
    const den = a * this.beta(a, b);
    if (!Number.isFinite(num) || !Number.isFinite(den) || den === 0) return 0;
    return num / den;
  }

  private beta(a: number, b: number): number {
    // Simplified beta function approximation
    const ga = this.gamma(a);
    const gb = this.gamma(b);
    const gab = this.gamma(a + b);
    if (!Number.isFinite(ga) || !Number.isFinite(gb) || !Number.isFinite(gab) || gab === 0) return 1;
    return (ga * gb) / gab;
  }

  private gamma(z: number): number {
    // Simplified gamma function approximation using Stirling's approximation with guards
    if (!Number.isFinite(z)) return 1;
    if (z <= 0) return 1;
    if (z < 1) return this.gamma(z + 1) / z;
    const v = Math.sqrt(2 * Math.PI / z) * Math.pow(z / Math.E, z);
    return Number.isFinite(v) ? v : 1;
  }

  private getEmotionTrendRecommendations(trend: TrendAnalysis): string[] {
    if (trend.direction === 'decreasing') {
      return [
        'Increase positive reinforcement strategies',
        'Review environmental factors that may be contributing to stress',
        'Consider additional sensory support tools',
        'Schedule more frequent check-ins'
      ];
    } else if (trend.direction === 'increasing') {
      return [
        'Continue current successful strategies',
        'Document what is working well',
        'Gradually introduce new challenges',
        'Share progress with student and family'
      ];
    }
    return [
      'Monitor for changes in patterns',
      'Maintain current support level',
      'Be prepared to adjust strategies as needed'
    ];
  }

  private getSensoryTrendRecommendations(trend: TrendAnalysis): string[] {
    if (trend.rate > 0) { // Increasing seeking
      return [
        'Provide more structured sensory breaks',
        'Introduce additional sensory tools',
        'Consider sensory diet adjustments',
        'Monitor for overstimulation'
      ];
    } else if (trend.rate < 0) { // Increasing avoiding
      return [
        'Reduce environmental stimuli',
        'Provide more quiet spaces',
        'Gradually reintroduce sensory experiences',
        'Focus on calming strategies'
      ];
    }
    return [
      'Maintain current sensory support level',
      'Continue monitoring sensory preferences',
      'Be responsive to daily variations'
    ];
  }

  private getGoalRecommendations(goal: Goal, trend: TrendAnalysis, estimatedDays: number): string[] {
    if (estimatedDays < 0) {
      return [
        'Review and adjust goal strategies',
        'Break goal into smaller milestones',
        'Identify and address barriers',
        'Consider modifying timeline or approach'
      ];
    } else if (estimatedDays > 90) {
      return [
        'Increase intervention frequency',
        'Add additional support strategies',
        'Review goal expectations',
        'Provide more immediate reinforcement'
      ];
    }
    return [
      'Continue current approach',
      'Monitor progress regularly',
      'Celebrate milestones reached',
      'Maintain consistent support'
    ];
  }

  private getAnomalyRecommendations(type: string, context: string, severity: number): string[] {
    if (type === 'emotion') {
      return [
        'Investigate potential triggers for this emotional spike',
        'Provide immediate support and coping strategies',
        'Monitor closely for additional unusual patterns',
        'Consider environmental or schedule changes'
      ];
    } else if (type === 'sensory') {
      return [
        'Review sensory environment for unusual factors',
        'Check for changes in routine or schedule',
        'Provide additional sensory regulation support',
        'Monitor for illness or other physical factors'
      ];
    }
    return [
      'Investigate potential causes',
      'Provide additional support',
      'Monitor closely',
      'Document and track patterns'
    ];
  }

  private getTrendSeverity(trend: TrendAnalysis): 'low' | 'medium' | 'high' {
    if (trend.direction === 'decreasing' && trend.significance > 0.7) return 'high';
    if (trend.direction === 'decreasing' && trend.significance > 0.4) return 'medium';
    return 'low';
  }

  private getMLEmotionRecommendations(predictions: EmotionPrediction[], trend: string): string[] {
    const highAnxietyDays = predictions.filter(p => p.emotions.anxious > 7).length;
    const lowPositiveDays = predictions.filter(p => p.emotions.happy < 3 && p.emotions.calm < 3).length;

    const recommendations: string[] = [];

    if (highAnxietyDays >= 3) {
      recommendations.push('ML predicts elevated anxiety - implement proactive calming strategies');
      recommendations.push('Schedule additional check-ins on high-anxiety days');
    }

    if (lowPositiveDays >= 4) {
      recommendations.push('ML indicates low positive emotions upcoming - increase engagement activities');
      recommendations.push('Prepare mood-boosting interventions');
    }

    if (trend === 'increasing') {
      recommendations.push('ML shows increasing emotional intensity - monitor for triggers');
    } else if (trend === 'decreasing') {
      recommendations.push('ML shows decreasing emotional engagement - check for withdrawal signs');
    }

    recommendations.push('Compare ML predictions with actual outcomes to refine models');

    return recommendations;
  }

  private getMLSensoryRecommendations(prediction: SensoryPrediction): string[] {
    const recommendations: string[] = [];

    // Check each sensory modality
    Object.entries(prediction.sensoryResponse).forEach(([sense, response]) => {
      if (response.avoiding > 0.7) {
        recommendations.push(`High ${sense} avoidance predicted - minimize ${sense} stimuli`);
      } else if (response.seeking > 0.7) {
        recommendations.push(`High ${sense} seeking predicted - provide ${sense} input opportunities`);
      }
    });

    // Environmental trigger recommendations
    prediction.environmentalTriggers.forEach(trigger => {
      if (trigger.probability > 0.7) {
        recommendations.push(`High probability of reaction to ${trigger.trigger} - prepare alternatives`);
      }
    });

    return recommendations;
  }

  // Baseline analysis using ML clustering
  async analyzeBaseline(trackingEntries: TrackingEntry[]): Promise<BaselineCluster[]> {
    if (!this.mlModelsInitialized || trackingEntries.length < 10) {
      return [];
    }

    try {
      const clusters = await mlModels.performBaselineClustering(trackingEntries, 3);
      return clusters;
    } catch (error) {
      logger.error('Baseline clustering failed:', error);
      return [];
    }
  }
}

export const enhancedPatternAnalysis = new EnhancedPatternAnalysisEngine();

import { Student, TrackingEntry, EmotionEntry, SensoryEntry, Goal } from "@/types/student";
import { format } from "date-fns";

export interface ExportOptions {
  format: 'pdf' | 'csv' | 'json';
  includeFields: string[];
  dateRange?: {
    start: Date;
    end: Date;
  };
  groupBy?: 'student' | 'date' | 'goal';
  includeCharts?: boolean;
  anonymize?: boolean;
}

export interface BackupData {
  version: string;
  timestamp: Date;
  students: Student[];
  trackingEntries: TrackingEntry[];
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  goals: Goal[];
  metadata: {
    exportedBy: string;
    totalRecords: number;
    dateRange: {
      earliest: Date;
      latest: Date;
    };
  };
}

class ExportSystem {
  private readonly CURRENT_VERSION = '1.0.0';

  // PDF Generation
  async generatePDFReport(
    student: Student,
    data: {
      trackingEntries: TrackingEntry[];
      emotions: EmotionEntry[];
      sensoryInputs: SensoryEntry[];
      goals: Goal[];
    },
    options: ExportOptions
  ): Promise<Blob> {
    // Create a virtual document structure for PDF generation
    const reportContent = this.buildReportContent(student, data, options);

    // For now, create a simple HTML-based report that can be printed to PDF
    const htmlContent = this.generateHTMLReport(reportContent, options);

    // Convert to blob for download
    return new Blob([htmlContent], { type: 'text/html' });
  }

  // CSV Export
  generateCSVExport(
    students: Student[],
    allData: {
      trackingEntries: TrackingEntry[];
      emotions: EmotionEntry[];
      sensoryInputs: SensoryEntry[];
      goals: Goal[];
    },
    options: ExportOptions
  ): string {
    const { includeFields, dateRange, anonymize } = options;
    let csvContent = '';

    if (includeFields.includes('emotions')) {
      const emotionData = this.filterByDateRange(allData.emotions, dateRange);
      csvContent += this.generateEmotionsCSV(emotionData, students, anonymize);
      csvContent += '\n\n';
    }

    if (includeFields.includes('sensoryInputs')) {
      const sensoryData = this.filterByDateRange(allData.sensoryInputs, dateRange);
      csvContent += this.generateSensoryCSV(sensoryData, students, anonymize);
      csvContent += '\n\n';
    }

    if (includeFields.includes('goals')) {
      csvContent += this.generateGoalsCSV(allData.goals, students, anonymize);
      csvContent += '\n\n';
    }

    if (includeFields.includes('trackingEntries')) {
      const trackingData = this.filterByDateRange(allData.trackingEntries, dateRange);
      csvContent += this.generateTrackingCSV(trackingData, students, anonymize);
    }

    return csvContent;
  }

  // JSON Export with Full Data Structure
  generateJSONExport(
    students: Student[],
    allData: {
      trackingEntries: TrackingEntry[];
      emotions: EmotionEntry[];
      sensoryInputs: SensoryEntry[];
      goals: Goal[];
    },
    options: ExportOptions
  ): string {
    const { includeFields, dateRange, anonymize } = options;

    const exportData: any = {
      version: this.CURRENT_VERSION,
      exportDate: new Date().toISOString(),
      options,
      data: {}
    };

    if (includeFields.includes('students')) {
      exportData.data.students = anonymize 
        ? students.map(s => this.anonymizeStudent(s))
        : students;
    }

    if (includeFields.includes('emotions')) {
      const emotionData = this.filterByDateRange(allData.emotions, dateRange);
      exportData.data.emotions = anonymize 
        ? emotionData.map(e => this.anonymizeEmotion(e))
        : emotionData;
    }

    if (includeFields.includes('sensoryInputs')) {
      const sensoryData = this.filterByDateRange(allData.sensoryInputs, dateRange);
      exportData.data.sensoryInputs = anonymize 
        ? sensoryData.map(s => this.anonymizeSensory(s))
        : sensoryData;
    }

    if (includeFields.includes('goals')) {
      exportData.data.goals = anonymize 
        ? allData.goals.map(g => this.anonymizeGoal(g))
        : allData.goals;
    }

    if (includeFields.includes('trackingEntries')) {
      const trackingData = this.filterByDateRange(allData.trackingEntries, dateRange);
      exportData.data.trackingEntries = anonymize 
        ? trackingData.map(t => this.anonymizeTracking(t))
        : trackingData;
    }

    return JSON.stringify(exportData, null, 2);
  }

  // Backup System
  createFullBackup(
    students: Student[],
    allData: {
      trackingEntries: TrackingEntry[];
      emotions: EmotionEntry[];
      sensoryInputs: SensoryEntry[];
      goals: Goal[];
    }
  ): BackupData {
    const dates = [
      ...allData.trackingEntries.map(t => t.timestamp),
      ...allData.emotions.map(e => e.timestamp),
      ...allData.sensoryInputs.map(s => s.timestamp)
    ].sort((a, b) => a.getTime() - b.getTime());

    return {
      version: this.CURRENT_VERSION,
      timestamp: new Date(),
      students,
      trackingEntries: allData.trackingEntries,
      emotions: allData.emotions,
      sensoryInputs: allData.sensoryInputs,
      goals: allData.goals,
      metadata: {
        exportedBy: 'SensoryTracker',
        totalRecords: allData.trackingEntries.length + allData.emotions.length + allData.sensoryInputs.length + allData.goals.length,
        dateRange: {
          earliest: dates[0] || new Date(),
          latest: dates[dates.length - 1] || new Date()
        }
      }
    };
  }

  // Restore from Backup
  async restoreFromBackup(backupData: BackupData): Promise<{
    success: boolean;
    errors: string[];
    imported: {
      students: number;
      trackingEntries: number;
      emotions: number;
      sensoryInputs: number;
      goals: number;
    };
  }> {
    const errors: string[] = [];
    const imported = {
      students: 0,
      trackingEntries: 0,
      emotions: 0,
      sensoryInputs: 0,
      goals: 0
    };

    try {
      // Validate backup version compatibility
      if (!this.isVersionCompatible(backupData.version)) {
        errors.push(`Backup version ${backupData.version} is not compatible with current version ${this.CURRENT_VERSION}`);
        return { success: false, errors, imported };
      }

      // Import data with validation
      const validationResults = await this.validateBackupData(backupData);
      if (validationResults.errors.length > 0) {
        errors.push(...validationResults.errors);
      }

      // Import valid data
      imported.students = validationResults.validStudents.length;
      imported.trackingEntries = validationResults.validTrackingEntries.length;
      imported.emotions = validationResults.validEmotions.length;
      imported.sensoryInputs = validationResults.validSensoryInputs.length;
      imported.goals = validationResults.validGoals.length;

      return {
        success: errors.length === 0,
        errors,
        imported
      };

    } catch (error) {
      errors.push(`Restore failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      return { success: false, errors, imported };
    }
  }

  // Import from CSV
  async importFromCSV(csvContent: string, dataType: 'emotions' | 'sensoryInputs' | 'students'): Promise<{
    success: boolean;
    errors: string[];
    imported: any[];
  }> {
    const errors: string[] = [];
    const imported: any[] = [];

    try {
      const lines = csvContent.split('\n').filter(line => line.trim() !== '');
      if (lines.length < 2) {
        errors.push('CSV file must contain at least a header row and one data row');
        return { success: false, errors, imported };
      }

      const headers = this.parseCSVLine(lines[0]);
      const requiredHeaders = this.getRequiredHeaders(dataType);

      // Validate headers
      const missingHeaders = requiredHeaders.filter(h => !headers.includes(h));
      if (missingHeaders.length > 0) {
        errors.push(`Missing required headers: ${missingHeaders.join(', ')}`);
        return { success: false, errors, imported };
      }

      // Parse data rows
      for (let i = 1; i < lines.length; i++) {
        try {
          const values = this.parseCSVLine(lines[i]);
          if (values.length !== headers.length) {
            errors.push(`Row ${i + 1}: Column count mismatch`);
            continue;
          }

          const rowData = this.parseCSVRowData(headers, values, dataType);
          if (rowData) {
            imported.push(rowData);
          }
        } catch (error) {
          errors.push(`Row ${i + 1}: ${error instanceof Error ? error.message : 'Parse error'}`);
        }
      }

      return {
        success: imported.length > 0,
        errors,
        imported
      };

    } catch (error) {
      errors.push(`Import failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      return { success: false, errors, imported };
    }
  }

