// Input sanitization
export function sanitizeInput(input: string): string {
  return input
    .trim()
    .replace(/[<>]/g, '') // Remove potential HTML tags
    .slice(0, 10000); // Limit length
}

export function sanitizeObject(obj: Record<string, any>): Record<string, any> {
  const sanitized: Record<string, any> = {};

  for (const [key, value] of Object.entries(obj)) {
    if (typeof value === 'string') {
      sanitized[key] = sanitizeInput(value);
    } else if (typeof value === 'object' && value !== null) {
      sanitized[key] = sanitizeObject(value);
    } else {
      sanitized[key] = value;
    }
  }

  return sanitized;
}

// Alternative worker implementation using inline worker for better compatibility
import { AnalyticsData, AnalyticsResults } from '@/workers/analytics.worker';
import { logger } from './logger';

const workerCode = `
  import { logger } from './logger.js';

  self.onmessage = async (e) => {
    const data = e.data;
    // Worker received message

    // Simple response for testing
    const results = {
      patterns: [],
      correlations: [],
      predictiveInsights: [],
      anomalies: [],
      insights: ['Worker is functioning correctly'],
      cacheKey: data.cacheKey
    };

    // Simulate some processing time
    await new Promise(resolve => setTimeout(resolve, 100));

    self.postMessage(results);
  };
`;

export function createInlineWorker(): Worker | null {
  let workerUrl: string | null = null;

  try {
    const blob = new Blob([workerCode], { type: 'application/javascript' });
    workerUrl = URL.createObjectURL(blob);
    const worker = new Worker(workerUrl);

    // Clean up the URL after creating the worker
    URL.revokeObjectURL(workerUrl);

    return worker;
  } catch (error) {
    // Ensure URL is cleaned up even if worker creation fails
    if (workerUrl) {
      URL.revokeObjectURL(workerUrl);
    }
    logger.error('Failed to create inline worker:', error as Error);
    return null;
  }
}

export enum LogLevel {
  DEBUG = 0,
  INFO = 1,
  WARN = 2,
  ERROR = 3,
  NONE = 4
}

interface LoggerConfig {
  level: LogLevel;
  enableConsole: boolean;
  enableRemote: boolean;
  remoteEndpoint?: string;
}

type LogData = Record<string, unknown> | unknown[] | string | number | boolean | null | undefined;

interface ErrorData {
  message: string;
  stack?: string;
  name: string;
  [key: string]: unknown;
}

class Logger {
  private config: LoggerConfig;
  private static instance: Logger;

  private constructor() {
    this.config = {
      level: import.meta.env.PROD ? LogLevel.ERROR : LogLevel.DEBUG,
      enableConsole: !import.meta.env.PROD,
      enableRemote: false
    };
  }

  public static getInstance(): Logger {
    if (!Logger.instance) {
      Logger.instance = new Logger();
    }
    return Logger.instance;
  }

  public init(level: LogLevel) {
    this.config.level = level;
  }

  public setLogLevel(level: LogLevel) {
    this.config.level = level;
  }

  private shouldLog(level: LogLevel): boolean {
    return level >= this.config.level;
  }

  private formatMessage(level: string): string {
    const timestamp = new Date().toISOString();
    return `[${timestamp}] [${level}]`;
  }

  debug(message: string, ...data: LogData[]): void {
    if (this.shouldLog(LogLevel.DEBUG) && this.config.enableConsole) {
      console.log(this.formatMessage('DEBUG'), message, ...data);
    }
  }

  info(message: string, ...data: LogData[]): void {
    if (this.shouldLog(LogLevel.INFO) && this.config.enableConsole) {
      console.info(this.formatMessage('INFO'), message, ...data);
    }
  }

  warn(message: string, ...data: LogData[]): void {
    if (this.shouldLog(LogLevel.WARN) && this.config.enableConsole) {
      console.warn(this.formatMessage('WARN'), message, ...data);
    }
  }

  error(message: string, ...data: (Error | LogData)[]): void {
    if (this.shouldLog(LogLevel.ERROR)) {
      if (this.config.enableConsole) {
        console.error(this.formatMessage('ERROR'), message, ...data);
      }

      const errorData = data.map(d => {
        if (d instanceof Error) {
          return { message: d.message, stack: d.stack, name: d.name };
        }
        return d;
      });

      // Send to remote logging service if enabled
      if (this.config.enableRemote && this.config.remoteEndpoint) {
        this.sendToRemote(message, errorData);
      }
    }
  }

  private async sendToRemote(message: string, data: (LogData | ErrorData)[]): Promise<void> {
    try {
      await fetch(this.config.remoteEndpoint!, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          timestamp: new Date().toISOString(),
          level: 'ERROR',
          message,
          data,
          userAgent: navigator.userAgent,
          url: window.location.href
        })
      });
    } catch (error) {
      // Silently fail to avoid infinite loop
    }
  }

  configure(config: Partial<LoggerConfig>): void {
    this.config = { ...this.config, ...config };
  }
}

export const logger = Logger.getInstance();

