// Generate tracking data for a student
const generateTrackingDataForStudent = (student: Student, scenario: 'emma' | 'lars' | 'astrid'): TrackingEntry[] => {
  const entries: TrackingEntry[] = [];
  const totalDays = Math.floor(Math.random() * 60) + 30; // 30-90 days of data

  for (let i = 0; i < totalDays; i++) {
    // Generate 1-3 entries per day with some days having no entries
    const entriesPerDay = Math.random() > 0.3 ? Math.floor(Math.random() * 3) + 1 : 0;

    for (let j = 0; j < entriesPerDay; j++) {
      const daysAgo = i + (j * 0.3); // Spread entries throughout the day
      entries.push(generateTrackingEntry(student, daysAgo, scenario));
    }
  }

  return entries.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
};

// Generate all mock data
export const generateAllMockData = (): { students: Student[]; trackingEntries: TrackingEntry[] } => {
  const students = generateMockStudents();
  const trackingEntries: TrackingEntry[] = [];

  const scenarios: Array<'emma' | 'lars' | 'astrid'> = ['emma', 'lars', 'astrid'];

  students.forEach((student, index) => {
    const scenario = scenarios[index];
    const studentEntries = generateTrackingDataForStudent(student, scenario);
    trackingEntries.push(...studentEntries);
  });

  return { students, trackingEntries };
};

export function loadMockDataToStorage(): void {
  try {
    // Clear only existing mock data first
    clearMockDataFromStorage();

    const { students, trackingEntries } = generateAllMockData();

    // Save students
    students.forEach(student => {
      dataStorage.saveStudent(student);
    });

    // Save tracking entries
    trackingEntries.forEach(entry => {
      dataStorage.saveTrackingEntry(entry);
    });
  } catch (error) {
    logger.error('Failed to load mock data:', error);
    throw new Error('Failed to initialize mock data');
  }
}

export function clearMockDataFromStorage(): void {
  try {
    const allStudents = dataStorage.getStudents();
    const allEntries = dataStorage.getTrackingEntries();

    // Filter out mock data
    const nonMockStudents = allStudents.filter(student => !student.id.startsWith('mock_'));
    const nonMockEntries = allEntries.filter(entry => !entry.studentId.startsWith('mock_'));

    // Clear all data and re-save only non-mock data
    dataStorage.clearAllData();

    // Restore non-mock data
    nonMockStudents.forEach(student => dataStorage.saveStudent(student));
    nonMockEntries.forEach(entry => dataStorage.saveTrackingEntry(entry));
  } catch (error) {
    logger.error('Failed to clear mock data:', error);
    throw new Error('Failed to clear mock data');
  }
}

import * as tf from '@tensorflow/tfjs';
import { logger } from '@/lib/logger';

/**
 * Configuration options for model drift detection
 * 
 * @interface DriftConfig
 * @description Defines parameters used to configure the drift detection algorithm.
 * This interface allows customization of detection sensitivity and methodology.
 */
export interface DriftConfig {
  /**
   * The threshold value above which drift is considered detected
   * @description A value between 0 and 1, where higher values require more significant drift for detection
   * @default 0.3
   */
  threshold: number;

  /**
   * The method used for calculating drift
   * @description Specifies the statistical method for measuring distribution divergence
   * @example 'kl_divergence', 'js_divergence', 'wasserstein', 'chi_squared'
   */
  method: 'kl_divergence' | 'js_divergence' | 'wasserstein' | 'chi_squared';

  /**
   * Number of bins to use for histogram-based methods
   * @description Used when discretizing continuous data for divergence calculations
   * @default 50
   */
  bins?: number;

  /**
   * Whether to normalize the data before drift detection
   * @description Normalizing can help when comparing datasets with different scales
   * @default true
   */
  normalize?: boolean;

  /**
   * Confidence level for statistical tests (if applicable)
   * @description Used for methods that perform hypothesis testing
   * @default 0.95
   */
  confidenceLevel?: number;
}

/**
 * Result of a drift detection analysis
 * 
 * @interface DriftResult
 * @description Contains the outcome of drift detection including the divergence score,
 * detection status, timestamp, and optional detailed information about the analysis.
 */
export interface DriftResult {
  /**
   * The calculated divergence score between distributions
   * @description A numerical measure of how different the new data distribution is
   * from the reference distribution. Higher values indicate greater drift.
   */
  divergenceScore: number;

  /**
   * Whether drift has been detected based on the configured threshold
   * @description True if divergenceScore exceeds the configured threshold
   */
  isDriftDetected: boolean;

  /**
   * The timestamp when the drift detection was performed
   * @description Records when this analysis was conducted for tracking drift over time
   */
  timestamp: Date;

  /**
   * Optional detailed information about the drift detection
   * @description May include information about which features contributed most to drift,
   * statistical test results, or other diagnostic information
   */
  details?: string;
}

/**
 * Model Drift Detector class for monitoring distribution shifts in model inputs
 * 
 * @class ModelDriftDetector
 * @description Implements various statistical methods to detect when the distribution
 * of new data significantly differs from a reference distribution. This is crucial
 * for identifying when a model may need retraining or when data quality issues arise.
 * 
 * @example
 * ```typescript
 * const detector = new ModelDriftDetector(config);
 * const result = await detector.detect(newDataTensor, referenceDataTensor);
 * if (result.isDriftDetected) {
 *   logger.info(`Drift detected with score: ${result.divergenceScore}`);
 * }
 * ```
 */
export class ModelDriftDetector {
  private config: DriftConfig;

  /**
   * Creates an instance of ModelDriftDetector
   *
   * @constructor
   * @param {DriftConfig} config - Configuration options for drift detection
   */
  constructor(config: DriftConfig) {
    this.config = {
      bins: 50,
      normalize: true,
      confidenceLevel: 0.95,
      ...config
    };
  }

  /**
   * Detects drift between new data and reference data distributions
   *
   * @method detect
   * @async
   * @param {tf.Tensor} newData - The new data tensor to check for drift
   * @param {tf.Tensor} referenceData - The reference data tensor representing the expected distribution
   * @returns {Promise<DriftResult>} A promise that resolves to a DriftResult object containing
   * the divergence score, detection status, timestamp, and optional details
   *
   * @description This method compares the statistical distributions of the new data
   * against the reference data using KL divergence. The implementation handles
   * data preprocessing, distribution calculation, divergence measurement, and threshold
   * comparison to determine if significant drift has occurred.
   *
   * The KL divergence is calculated as: KL(P||Q) =  P(i) * log(P(i)/Q(i))
   * where P is the new data distribution and Q is the reference distribution.
   */
  public async detect(newData: tf.Tensor, referenceData: tf.Tensor): Promise<DriftResult> {
    try {
      // Validate inputs
      if (!newData || !referenceData) {
        throw new Error('Both newData and referenceData tensors are required');
      }

      // Check for empty tensors
      const newDataSize = newData.size;
      const referenceDataSize = referenceData.size;

      if (newDataSize === 0 || referenceDataSize === 0) {
        throw new Error('Cannot detect drift on empty tensors');
      }

      // Flatten tensors to 1D for distribution calculation
      const flatNewData = newData.flatten();
      const flatReferenceData = referenceData.flatten();

      // Normalize data if configured
      let processedNewData: tf.Tensor1D = flatNewData;
      let processedReferenceData: tf.Tensor1D = flatReferenceData;

      if (this.config.normalize) {
        processedNewData = await this.normalizeData(flatNewData) as tf.Tensor1D;
        processedReferenceData = await this.normalizeData(flatReferenceData) as tf.Tensor1D;
      }

      // Calculate distributions using histogram binning
      const [newDataDist, refDataDist] = await this.calculateDistributions(
        processedNewData,
        processedReferenceData
      );

      // Calculate KL divergence
      const divergenceScore = await this.calculateKLDivergence(newDataDist, refDataDist);

      // Determine if drift is detected
      const isDriftDetected = divergenceScore > this.config.threshold;

      // Clean up tensors
      flatNewData.dispose();
      flatReferenceData.dispose();
      if (this.config.normalize) {
        processedNewData.dispose();
        processedReferenceData.dispose();
      }
      newDataDist.dispose();
      refDataDist.dispose();

      // Create result
      const result: DriftResult = {
        divergenceScore,
        isDriftDetected,
        timestamp: new Date(),
        details: `KL divergence: ${divergenceScore.toFixed(4)}, Threshold: ${this.config.threshold}, Drift detected: ${isDriftDetected ? 'Yes' : 'No'}`
      };

      return result;
    } catch (error) {
      throw new Error(`Drift detection failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Normalizes data to have zero mean and unit variance
   *
   * @private
   * @param {tf.Tensor} data - The tensor to normalize
   * @returns {Promise<tf.Tensor>} The normalized tensor
   */
  private async normalizeData(data: tf.Tensor): Promise<tf.Tensor> {
    const mean = data.mean();
    const std = tf.moments(data).variance.sqrt();

    // Add small epsilon to avoid division by zero
    const epsilon = 1e-10;
    const normalizedData = data.sub(mean).div(std.add(epsilon));

    mean.dispose();
    std.dispose();

    return normalizedData;
  }

  /**
   * Calculates probability distributions for both datasets using histogram binning
   *
   * @private
   * @param {tf.Tensor} newData - The new data tensor
   * @param {tf.Tensor} referenceData - The reference data tensor
   * @returns {Promise<[tf.Tensor, tf.Tensor]>} Tuple of probability distributions
   */
  private async calculateDistributions(
    newData: tf.Tensor,
    referenceData: tf.Tensor
  ): Promise<[tf.Tensor, tf.Tensor]> {
    const bins = this.config.bins || 50;

    // Combine data to get consistent bin edges
    const combinedData = tf.concat([newData, referenceData]);
    const minVal = await combinedData.min().array() as number;
    const maxVal = await combinedData.max().array() as number;
    combinedData.dispose();

    // Create bin edges
    const binWidth = (maxVal - minVal) / bins;
    const binEdges = Array.from({ length: bins + 1 }, (_, i) => minVal + i * binWidth);

    // Calculate histograms
    const newDataHist = await this.calculateHistogram(newData, binEdges);
    const refDataHist = await this.calculateHistogram(referenceData, binEdges);

    // Convert to probability distributions
    const newDataDist = this.histogramToProbability(newDataHist);
    const refDataDist = this.histogramToProbability(refDataHist);

    newDataHist.dispose();
    refDataHist.dispose();

    return [newDataDist, refDataDist];
  }

  /**
   * Calculates histogram for given data and bin edges
   *
   * @private
   * @param {tf.Tensor} data - The data tensor
   * @param {number[]} binEdges - Array of bin edge values
   * @returns {Promise<tf.Tensor>} The histogram counts
   */
  private async calculateHistogram(data: tf.Tensor, binEdges: number[]): Promise<tf.Tensor> {
    const dataArray = await data.array() as number[];
    const bins = binEdges.length - 1;
    const histogram = new Float32Array(bins);

    // Count values in each bin
    for (const value of dataArray) {
      let binIndex = Math.floor((value - binEdges[0]) / (binEdges[binEdges.length - 1] - binEdges[0]) * bins);
      binIndex = Math.max(0, Math.min(bins - 1, binIndex));
      histogram[binIndex]++;
    }

    return tf.tensor1d(histogram);
  }

  /**
   * Converts histogram counts to probability distribution
   *
   * @private
   * @param {tf.Tensor} histogram - The histogram counts
   * @returns {tf.Tensor} The probability distribution
   */
  private histogramToProbability(histogram: tf.Tensor): tf.Tensor {
    const sum = histogram.sum();
    // Add small epsilon to avoid division by zero
    const epsilon = 1e-10;
    return histogram.div(sum.add(epsilon));
  }

  /**
   * Calculates KL divergence between two probability distributions
   * KL(P||Q) =  P(i) * log(P(i)/Q(i))
   *
   * @private
   * @param {tf.Tensor} p - The new data distribution (P)
   * @param {tf.Tensor} q - The reference distribution (Q)
   * @returns {Promise<number>} The KL divergence value
   */
  private async calculateKLDivergence(p: tf.Tensor, q: tf.Tensor): Promise<number> {
    // Add small epsilon to avoid log(0) and division by zero
    const epsilon = 1e-10;

    // Calculate KL divergence: P(i) * log(P(i)/Q(i))
    const pSafe = p.add(epsilon);
    const qSafe = q.add(epsilon);

    const ratio = pSafe.div(qSafe);
    const logRatio = ratio.log();
    const klTerms = pSafe.mul(logRatio);
    const klDivergence = klTerms.sum();

    const result = await klDivergence.array() as number;

    // Clean up intermediate tensors
    pSafe.dispose();
    qSafe.dispose();
    ratio.dispose();
    logRatio.dispose();
    klTerms.dispose();
    klDivergence.dispose();

    return result;
  }
}

import { EmotionEntry, SensoryEntry, EnvironmentalEntry, TrackingEntry } from "@/types/student";
import { isWithinInterval, subDays, startOfDay, endOfDay } from "date-fns";
import { analyticsConfig, AnalyticsConfiguration } from "@/lib/analyticsConfig";

export interface PatternResult {
  type: 'emotion' | 'sensory' | 'environmental' | 'correlation';
  pattern: string;
  confidence: number; // 0-1
  frequency: number;
  description: string;
  recommendations?: string[];
  dataPoints: number;
  timeframe: string;
}

export interface CorrelationResult {
  factor1: string;
  factor2: string;
  correlation: number; // -1 to 1
  significance: 'low' | 'moderate' | 'high';
  description: string;
  recommendations?: string[];
}

export interface TriggerAlert {
  id: string;
  type: 'concern' | 'improvement' | 'pattern';
  severity: 'low' | 'medium' | 'high';
  title: string;
  description: string;
  recommendations: string[];
  timestamp: Date;
  studentId: string;
  dataPoints: number;
}

class PatternAnalysisEngine {
  private config: AnalyticsConfiguration;

  constructor() {
    this.config = analyticsConfig.getConfig();

    // Subscribe to configuration changes
    analyticsConfig.subscribe((newConfig) => {
      this.config = newConfig;
    });
  }

  analyzeEmotionPatterns(emotions: EmotionEntry[], timeframeDays?: number): PatternResult[] {
    const actualTimeframe = timeframeDays || this.config.timeWindows.defaultAnalysisDays;

    if (emotions.length < this.config.patternAnalysis.minDataPoints) return [];

    const patterns: PatternResult[] = [];
    const cutoffDate = subDays(new Date(), actualTimeframe);
    const recentEmotions = emotions.filter(e => e.timestamp >= cutoffDate);

    // Apply sensitivity multipliers based on alert sensitivity level
    const intensityThreshold = this.config.patternAnalysis.highIntensityThreshold *
      (1 / this.config.alertSensitivity.emotionIntensityMultiplier);
    const frequencyThreshold = this.config.patternAnalysis.concernFrequencyThreshold *
      (1 / this.config.alertSensitivity.frequencyMultiplier);

    // Analyze high-intensity negative emotions
    const highIntensityNegative = recentEmotions.filter(e =>
      e.intensity >= intensityThreshold &&
      ['anxious', 'frustrated', 'angry', 'overwhelmed', 'sad'].includes(e.emotion.toLowerCase())
    );

    // Also analyze moderate intensity patterns for better detection
    const moderateIntensityNegative = recentEmotions.filter(e =>
      e.intensity >= 3 &&
      ['anxious', 'frustrated', 'angry', 'overwhelmed', 'sad'].includes(e.emotion.toLowerCase())
    );

    if (highIntensityNegative.length / recentEmotions.length > frequencyThreshold) {
      patterns.push({
        type: 'emotion',
        pattern: 'high-intensity-negative',
        confidence: Math.min(highIntensityNegative.length / recentEmotions.length, 1),
        frequency: highIntensityNegative.length,
        description: `High-intensity negative emotions detected in ${Math.round((highIntensityNegative.length / recentEmotions.length) * 100)}% of recent sessions`,
        recommendations: [
          'Consider implementing calming strategies before intense activities',
          'Monitor environmental triggers that may contribute to stress',
          'Discuss coping mechanisms with student'
        ],
        dataPoints: recentEmotions.length,
        timeframe: `${actualTimeframe} days`
      });
    }

    // Analyze emotion consistency
    const emotionCounts = recentEmotions.reduce((acc, e) => {
      acc[e.emotion.toLowerCase()] = (acc[e.emotion.toLowerCase()] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    // Optimized: Find max without sorting entire array
    let dominantEmotion: [string, number] | undefined;
    let maxCount = 0;
    for (const [emotion, count] of Object.entries(emotionCounts)) {
      if (count > maxCount) {
        maxCount = count;
        dominantEmotion = [emotion, count];
      }
    }

    if (dominantEmotion && dominantEmotion[1] / recentEmotions.length > this.config.patternAnalysis.emotionConsistencyThreshold) {
      patterns.push({
        type: 'emotion',
        pattern: 'consistent-emotion',
        confidence: dominantEmotion[1] / recentEmotions.length,
        frequency: dominantEmotion[1],
        description: `Consistent ${dominantEmotion[0]} emotion pattern detected`,
        recommendations: this.getEmotionRecommendations(dominantEmotion[0]),
        dataPoints: recentEmotions.length,
        timeframe: `${actualTimeframe} days`
      });
    }

    // Add moderate negative emotion pattern if high intensity doesn't trigger
    if (highIntensityNegative.length === 0 && moderateIntensityNegative.length / recentEmotions.length > this.config.patternAnalysis.moderateNegativeThreshold) {
      patterns.push({
        type: 'emotion',
        pattern: 'moderate-negative-trend',
        confidence: moderateIntensityNegative.length / recentEmotions.length,
        frequency: moderateIntensityNegative.length,
        description: `Moderate negative emotions detected in ${Math.round((moderateIntensityNegative.length / recentEmotions.length) * 100)}% of recent sessions`,
        recommendations: [
          'Monitor for potential stress escalation',
          'Implement preventive calming strategies',
          'Consider environmental adjustments'
        ],
        dataPoints: recentEmotions.length,
        timeframe: `${actualTimeframe} days`
      });
    }

    return patterns;
  }

  analyzeSensoryPatterns(sensoryInputs: SensoryEntry[], timeframeDays?: number): PatternResult[] {
    const actualTimeframe = timeframeDays || this.config.timeWindows.defaultAnalysisDays;

    if (sensoryInputs.length < this.config.patternAnalysis.minDataPoints) return [];

    const patterns: PatternResult[] = [];
    const cutoffDate = subDays(new Date(), actualTimeframe);
    const recentSensory = sensoryInputs.filter(s => s.timestamp >= cutoffDate);

    // Analyze sensory seeking vs avoiding patterns
    const seekingBehaviors = recentSensory.filter(s => 
      s.response.toLowerCase().includes('seeking') || 
      s.response.toLowerCase().includes('craving')
    );

    const avoidingBehaviors = recentSensory.filter(s => 
      s.response.toLowerCase().includes('avoiding') || 
      s.response.toLowerCase().includes('covering')
    );

    if (seekingBehaviors.length > avoidingBehaviors.length * 2) {
      patterns.push({
        type: 'sensory',
        pattern: 'sensory-seeking',
        confidence: seekingBehaviors.length / recentSensory.length,
        frequency: seekingBehaviors.length,
        description: 'Strong sensory-seeking pattern identified',
        recommendations: [
          'Provide scheduled sensory breaks',
          'Offer fidget tools and movement opportunities',
          'Consider sensory-rich learning activities'
        ],
        dataPoints: recentSensory.length,
        timeframe: `${actualTimeframe} days`
      });
    } else if (avoidingBehaviors.length > seekingBehaviors.length * 2) {
      patterns.push({
        type: 'sensory',
        pattern: 'sensory-avoiding',
        confidence: avoidingBehaviors.length / recentSensory.length,
        frequency: avoidingBehaviors.length,
        description: 'Strong sensory-avoiding pattern identified',
        recommendations: [
          'Provide quiet, low-stimulation spaces',
          'Use noise-canceling headphones when appropriate',
          'Gradually introduce sensory experiences'
        ],
        dataPoints: recentSensory.length,
        timeframe: `${actualTimeframe} days`
      });
    }

    return patterns;
  }

  analyzeEnvironmentalCorrelations(trackingEntries: TrackingEntry[]): CorrelationResult[] {
    if (trackingEntries.length < this.config.patternAnalysis.minDataPoints) return [];

    const correlations: CorrelationResult[] = [];

    // Analyze noise level vs emotion intensity correlation
    const noiseEmotionData = trackingEntries
      .filter(entry => entry.environmentalData?.roomConditions?.noiseLevel && entry.emotions.length > 0)
      .map(entry => ({
        noise: entry.environmentalData!.roomConditions!.noiseLevel,
        avgEmotionIntensity: entry.emotions.reduce((sum, e) => sum + e.intensity, 0) / entry.emotions.length
      }));

    if (noiseEmotionData.length >= this.config.patternAnalysis.minDataPoints) {
      const correlation = this.calculateCorrelation(
        noiseEmotionData.map(d => d.noise),
        noiseEmotionData.map(d => d.avgEmotionIntensity)
      );

      if (Math.abs(correlation) > this.config.patternAnalysis.correlationThreshold) {
        correlations.push({
          factor1: 'Noise Level',
          factor2: 'Emotion Intensity',
          correlation,
          significance: this.getSignificance(Math.abs(correlation)),
          description: correlation > 0 
            ? 'Higher noise levels correlate with more intense emotions'
            : 'Lower noise levels correlate with more intense emotions',
          recommendations: correlation > 0 
            ? ['Consider noise reduction strategies', 'Provide quiet spaces during intense activities']
            : ['Monitor for overstimulation in quiet environments']
        });
      }
    }

    // Analyze lighting vs positive emotions
    const lightingEmotionData = trackingEntries
      .filter(entry => entry.environmentalData?.roomConditions?.lighting && entry.emotions.length > 0)
      .map(entry => ({
        lighting: entry.environmentalData!.roomConditions!.lighting,
        positiveEmotions: entry.emotions.filter(e => 
          ['happy', 'calm', 'focused', 'excited', 'content'].includes(e.emotion.toLowerCase())
        ).length / entry.emotions.length
      }));

    // Group lighting data for analysis
    const lightingGroups = lightingEmotionData.reduce((acc, d) => {
      if (!acc[d.lighting]) acc[d.lighting] = [];
      acc[d.lighting].push(d.positiveEmotions);
      return acc;
    }, {} as Record<string, number[]>);

    // Find best lighting condition
    const lightingAverages = Object.entries(lightingGroups)
      .map(([lighting, values]) => ({
        lighting,
        average: values.reduce((sum, v) => sum + v, 0) / values.length,
        count: values.length
      }))
      .filter(l => l.count >= 3)
      .sort((a, b) => b.average - a.average);

    if (lightingAverages.length > 1) {
      const best = lightingAverages[0];
      const worst = lightingAverages[lightingAverages.length - 1];

      if (best.average - worst.average > 0.2) {
        correlations.push({
          factor1: 'Lighting Conditions',
          factor2: 'Positive Emotions',
          correlation: 0.5, // Categorical correlation estimate
          significance: 'moderate',
          description: `${best.lighting} lighting shows highest positive emotion rates (${Math.round(best.average * 100)}%)`,
          recommendations: [
            `Optimize for ${best.lighting} lighting when possible`,
            `Minimize exposure to ${worst.lighting} lighting during challenging activities`
          ]
        });
      }
    }

    return correlations;
  }

  generateTriggerAlerts(
    emotions: EmotionEntry[], 
    sensoryInputs: SensoryEntry[], 
    trackingEntries: TrackingEntry[],
    studentId: string
  ): TriggerAlert[] {
    const alerts: TriggerAlert[] = [];

    // Check for recent concerning patterns
    const recentCutoff = subDays(new Date(), this.config.timeWindows.recentDataDays);
    const recentEmotions = emotions.filter(e => e.timestamp >= recentCutoff);
    const recentEntries = trackingEntries.filter(e => e.timestamp >= recentCutoff);

    // Apply sensitivity multipliers
    const intensityThreshold = this.config.patternAnalysis.highIntensityThreshold *
      (1 / this.config.alertSensitivity.emotionIntensityMultiplier);

    // High stress pattern alert
    const highStressEmotions = recentEmotions.filter(e =>
      e.intensity >= intensityThreshold &&
      ['anxious', 'frustrated', 'overwhelmed', 'angry'].includes(e.emotion.toLowerCase())
    );

    if (highStressEmotions.length >= 2) {
      alerts.push({
        id: ((globalThis as unknown as { crypto?: { randomUUID?: () => string } })?.crypto?.randomUUID?.()) ||
            `alert-${Math.random().toString(36).slice(2)}-${Date.now()}`,
        type: 'concern',
        severity: 'high',
        title: 'High Stress Pattern Detected',
        description: `${highStressEmotions.length} high-intensity stress responses recorded in the past week`,
        recommendations: [
          'Schedule a check-in with the student',
          'Review current stressors and triggers',
          'Implement additional calming strategies',
          'Consider environmental modifications'
        ],
        timestamp: new Date(),
        studentId,
        dataPoints: recentEmotions.length
      });
    }

    // Positive progress alert - fixed for 1-5 scale
    const positiveEmotions = recentEmotions.filter(e => 
      ['happy', 'calm', 'focused', 'proud', 'content'].includes(e.emotion.toLowerCase()) &&
      e.intensity >= 4
    );

    if (positiveEmotions.length >= 3 && recentEmotions.length >= 5) {
      alerts.push({
        id: ((globalThis as unknown as { crypto?: { randomUUID?: () => string } })?.crypto?.randomUUID?.()) ||
            `alert-${Math.random().toString(36).slice(2)}-${Date.now()}`,
        type: 'improvement',
        severity: 'low',
        title: 'Positive Progress Noted',
        description: `Strong positive emotional responses observed in ${Math.round((positiveEmotions.length / recentEmotions.length) * 100)}% of recent sessions`,
        recommendations: [
          'Continue current successful strategies',
          'Document what is working well',
          'Consider sharing success with student and family'
        ],
        timestamp: new Date(),
        studentId,
        dataPoints: recentEmotions.length
      });
    }

    // Environmental pattern alert
    const environmentalPatterns = this.analyzeEnvironmentalCorrelations(recentEntries);
    environmentalPatterns.forEach(pattern => {
      if (pattern.significance === 'high' && Math.abs(pattern.correlation) > 0.6) {
        alerts.push({
          id: ((globalThis as unknown as { crypto?: { randomUUID?: () => string } })?.crypto?.randomUUID?.()) ||
              `alert-${Math.random().toString(36).slice(2)}-${Date.now()}`,
          type: 'pattern',
          severity: 'medium',
          title: 'Environmental Pattern Identified',
          description: pattern.description,
          recommendations: pattern.recommendations || [],
          timestamp: new Date(),
          studentId,
          dataPoints: recentEntries.length
        });
      }
    });

    return alerts;
  }

  private calculateCorrelation(x: number[], y: number[]): number {
    const n = x.length;
    if (n === 0 || n !== y.length) return 0;

    const sumX = x.reduce((a, b) => a + b, 0);
    const sumY = y.reduce((a, b) => a + b, 0);
    const sumXY = x.map((xi, i) => xi * y[i]).reduce((a, b) => a + b, 0);
    const sumXX = x.map(xi => xi * xi).reduce((a, b) => a + b, 0);
    const sumYY = y.map(yi => yi * yi).reduce((a, b) => a + b, 0);

    const numerator = n * sumXY - sumX * sumY;
    const denominator = Math.sqrt((n * sumXX - sumX * sumX) * (n * sumYY - sumY * sumY));

    return denominator === 0 ? 0 : numerator / denominator;
  }

  private getSignificance(correlation: number): 'low' | 'moderate' | 'high' {
    // Adjust thresholds based on configuration
    const lowThreshold = this.config.patternAnalysis.correlationThreshold;
    const highThreshold = this.config.patternAnalysis.correlationThreshold * 2;

    if (correlation < lowThreshold) return 'low';
    if (correlation < highThreshold) return 'moderate';
    return 'high';
  }

  private getEmotionRecommendations(emotion: string): string[] {
    const recommendations: Record<string, string[]> = {
      'anxious': [
        'Introduce mindfulness and breathing exercises',
        'Create predictable routines and schedules',
        'Provide advance notice of changes'
      ],
      'frustrated': [
        'Break tasks into smaller, manageable steps',
        'Offer choice and control opportunities',
        'Teach problem-solving strategies'
      ],
      'happy': [
        'Continue activities that promote positive engagement',
        'Document successful strategies for future use',
        'Build on current strengths'
      ],
      'calm': [
        'Maintain current supportive environment',
        'Use as a baseline for comparison',
        'Gradually introduce new challenges'
      ]
    };

    return recommendations[emotion.toLowerCase()] || [
      'Monitor patterns and adjust strategies as needed',
      'Consult with support team for specialized approaches'
    ];
  }
}

export const patternAnalysis = new PatternAnalysisEngine();

/**
 * Utility functions for managing localStorage
 */
import { logger } from '@/lib/logger';

export const storageUtils = {
  /**
   * Check available storage space
   */
  getStorageInfo(): { used: number; available: boolean } {
    let used = 0;
    for (const key in localStorage) {
      if (localStorage.hasOwnProperty(key)) {
        used += localStorage[key].length + key.length;
      }
    }
    return {
      used,
      available: used < 5000000 // 5MB approximate limit
    };
  },

  /**
   * Clear old tracking data to free up space
   */
  clearOldTrackingData(daysToKeep: number = 30): void {
    try {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - daysToKeep);

      // Clear old tracking entries
      const entriesKey = 'sensoryTracker_entries';
      const entriesData = localStorage.getItem(entriesKey);
      if (entriesData) {
        const entries = JSON.parse(entriesData);
        const filteredEntries = entries.filter((entry: { timestamp: string | Date }) => {
          const entryDate = new Date(entry.timestamp);
          return entryDate > cutoffDate;
        });
        localStorage.setItem(entriesKey, JSON.stringify(filteredEntries));
      }

      // Clear old alerts
      const alertsKey = 'sensoryTracker_alerts';
      const alertsData = localStorage.getItem(alertsKey);
      if (alertsData) {
        const alerts = JSON.parse(alertsData);
        const filteredAlerts = alerts.filter((alert: { timestamp: string | Date }) => {
          const alertDate = new Date(alert.timestamp);
          return alertDate > cutoffDate;
        });
        localStorage.setItem(alertsKey, JSON.stringify(filteredAlerts));
      }
    } catch (error) {
      logger.error('Error clearing old data:', error);
    }
  },

  /**
   * Compress data before storing
   */
  compressData(data: unknown): string {
    // Remove unnecessary whitespace from JSON
    return JSON.stringify(data);
  },

  /**
   * Safe storage with quota handling
   */
  safeSetItem(key: string, value: string): void {
    try {
      localStorage.setItem(key, value);
    } catch (e) {
      if (e instanceof DOMException && (
        e.code === 22 || // Quota exceeded
        e.code === 1014 || // NS_ERROR_DOM_QUOTA_REACHED (Firefox)
        e.name === 'QuotaExceededError' ||
        e.name === 'NS_ERROR_DOM_QUOTA_REACHED'
      )) {
        // Try to clear old data and retry
        this.clearOldTrackingData();

        try {
          localStorage.setItem(key, value);
        } catch (retryError) {
          // If still failing, clear all non-essential data
          this.clearNonEssentialData();
          localStorage.setItem(key, value);
        }
      } else {
        throw e;
      }
    }
  },

  /**
   * Clear non-essential data when storage is full
   */
  clearNonEssentialData(): void {
    const essentialKeys = [
      'sensoryTracker_students',
      'sensoryTracker_goals',
      'sensoryTracker_dataVersion'
    ];

    for (const key in localStorage) {
      if (localStorage.hasOwnProperty(key) && 
          key.startsWith('sensoryTracker_') && 
          !essentialKeys.includes(key)) {
        localStorage.removeItem(key);
      }
    }
  }
};

