/**
 * @file src/workers/analytics.worker.ts
 * 
 * This is a web worker dedicated to performing heavy analytics computations
 * in a background thread, ensuring the main UI thread remains responsive.
 * It listens for messages containing student data, runs a series of analyses,
 * and posts the results back to the main thread.
 */
import { PatternResult, CorrelationResult } from '@/lib/patternAnalysis';
import { PredictiveInsight, AnomalyDetection } from '@/lib/enhancedPatternAnalysis';
import { TrackingEntry, EmotionEntry, SensoryEntry } from '@/types/student';
import { AnalyticsData, AnalyticsResults, AnalyticsConfiguration, WorkerCacheEntry, AnalyticsWorkerMessage } from '@/types/analytics';
import { createCachedPatternAnalysis } from '@/lib/cachedPatternAnalysis';
import { logger } from '@/lib/logger';

// Type is now imported from @/types/analytics

let workerCacheTTL = 5 * 60 * 1000; // default 5 minutes, overridden by config
let workerCacheMaxSize = 200; // soft cap, overridden by config if provided
const insertionOrder: string[] = []; // FIFO order for eviction

// Create a simple cache implementation for the worker
const workerCache = {
  storage: new Map<string, WorkerCacheEntry>(),

  get(key: string): unknown | undefined {
    const entry = this.storage.get(key);
    if (entry && entry.expires > Date.now()) {
      return entry.data;
    }
    // Expired or missing: ensure removal
    this.storage.delete(key);
    return undefined;
  },

  set(key: string, value: unknown, tags: string[] = []): void {
    // Insert/update
    if (!this.storage.has(key)) {
      insertionOrder.push(key);
    }
    this.storage.set(key, {
      data: value,
      expires: Date.now() + workerCacheTTL,
      tags
    });
    // Evict oldest until under max size
    while (insertionOrder.length > workerCacheMaxSize) {
      const oldestKey = insertionOrder.shift();
      if (oldestKey) {
        this.storage.delete(oldestKey);
      }
    }
  },

  has(key: string): boolean {
    return this.get(key) !== undefined;
  },

  invalidateByTag(tag: string): number {
    let count = 0;
    for (const [key, entry] of this.storage.entries()) {
      if (entry.tags && entry.tags.includes(tag)) {
        this.storage.delete(key);
        count++;
      }
    }
    if (count > 0) {
      // Rebuild insertion order without deleted keys
      const remaining = insertionOrder.filter(k => this.storage.has(k));
      insertionOrder.length = 0;
      insertionOrder.push(...remaining);
    }
    return count;
  },

  getDataFingerprint(data: unknown): string {
    const stringify = (obj: unknown): string => {
      if (obj === null || obj === undefined) return 'null';
      if (typeof obj !== 'object') return String(obj);
      if (Array.isArray(obj)) return `[${obj.map(stringify).join(',')}]`;

      const keys = Object.keys(obj as Record<string, unknown>).sort();
      return `{${keys.map(k => `${k}:${stringify((obj as Record<string, unknown>)[k])}`).join(',')}}`;
    };

    const str = stringify(data);
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash;
    }
    return Math.abs(hash).toString(36);
  },

  createKey(prefix: string, params: Record<string, unknown>): string {
    const sortedParams = Object.keys(params)
      .sort()
      .map(key => `${key}:${JSON.stringify(params[key])}`)
      .join(':');
    return `${prefix}:${sortedParams}`;
  }
};

// Create cached pattern analysis instance
const cachedAnalysis = createCachedPatternAnalysis(workerCache);

/**
 * Compute a stable hash for the subset of config that affects analysis output.
 */
const getConfigHash = (cfg: AnalyticsConfiguration | null): string => {
  if (!cfg) return 'no-config';
  const subset = {
    patternAnalysis: cfg.patternAnalysis,
    enhancedAnalysis: cfg.enhancedAnalysis,
    timeWindows: cfg.timeWindows,
    alertSensitivity: cfg.alertSensitivity
  };
  // Reuse workerCache fingerprint utility by temporary object
  const stringify = (obj: unknown): string => {
    if (obj === null || obj === undefined) return 'null';
    if (typeof obj !== 'object') return String(obj);
    if (Array.isArray(obj)) return `[${obj.map(stringify).join(',')}]`;
    const keys = Object.keys(obj as Record<string, unknown>).sort();
    return `{${keys.map(k => `${k}:${stringify((obj as Record<string, unknown>)[k])}`).join(',')}}`;
  };
  const str = stringify(subset);
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return Math.abs(hash).toString(36);
};

// Outgoing message queue to avoid flooding the main thread with messages
const outgoingQueue: AnalyticsWorkerMessage[] = [];
let flushScheduled = false;
const enqueueMessage = (msg: AnalyticsWorkerMessage) => {
  outgoingQueue.push(msg);
  if (!flushScheduled) {
    flushScheduled = true;
    setTimeout(() => {
      try {
        while (outgoingQueue.length) {
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          (postMessage as any)(outgoingQueue.shift());
        }
      } finally {
        flushScheduled = false;
      }
    }, 30);
  }
};

// Configuration passed from main thread
let currentConfig: AnalyticsConfiguration | null = null;

// Types are now imported from @/types/analytics
export type { AnalyticsData, AnalyticsResults } from '@/types/analytics';

/**
 * Generates high-level, human-readable insights from the raw analysis results.
 * This function consolidates the most significant findings into a simple string array.
 * @param {PatternResult[]} emotionPatterns - The results from emotion pattern analysis.
 * @param {PatternResult[]} sensoryPatterns - The results from sensory pattern analysis.
 * @param {CorrelationResult[]} correlations - The results from correlation analysis.
 * @param {AnalyticsData} data - The raw data used for the analysis.
 * @returns {string[]} An array of insight strings.
 */
const generateInsights = (
  emotionPatterns: PatternResult[],
  sensoryPatterns: PatternResult[],
  correlations: CorrelationResult[],
  data: AnalyticsData
): string[] => {
    const allInsights: string[] = [];

      if (data.entries.length < 5) {
        allInsights.push(
          `Limited data available (${data.entries.length} sessions). Consider collecting more tracking sessions for better pattern analysis.`
        );
      }

      const highConfidenceEmotionPatterns = emotionPatterns.filter(p => p.confidence > 0.7);
      if (highConfidenceEmotionPatterns.length > 0) {
        const pattern = highConfidenceEmotionPatterns[0];
        allInsights.push(
          `Strong ${pattern.pattern.replace('-', ' ')} pattern detected with ${Math.round(pattern.confidence * 100)}% confidence.`
        );
      }

      const highConfidenceSensoryPatterns = sensoryPatterns.filter(p => p.confidence > 0.6);
      if (highConfidenceSensoryPatterns.length > 0) {
        const pattern = highConfidenceSensoryPatterns[0];
        allInsights.push(
          `${pattern.description} - consider implementing the recommended strategies.`
        );
      }

      const strongCorrelations = correlations.filter(c => c.significance === 'high');
      if (strongCorrelations.length > 0) {
        strongCorrelations.forEach(correlation => {
          allInsights.push(
            `Strong correlation found: ${correlation.description}`
          );
        });
      }

      return allInsights.length > 0 ? allInsights : [
        'Continue collecting data to identify meaningful patterns and insights.'
      ];
};

/**
 * Main message handler for the worker.
 * This function is triggered when the main thread calls `worker.postMessage()`.
 * It orchestrates the analysis process and posts the results back.
 */
export async function handleMessage(e: MessageEvent<AnalyticsData>) {
  const filteredData = e.data;

  // Diagnostic log: message received - use cache to limit verbosity
  try {
    const logKey = `worker_msg_${filteredData?.cacheKey || 'nocache'}_${new Date().getMinutes()}`;
    if (!workerCache.has(logKey)) {
      logger.debug('[analytics.worker] onmessage', {
        hasConfig: !!filteredData?.config,
        cacheKey: filteredData?.cacheKey ?? null,
        entries: filteredData?.entries?.length ?? 0,
        emotions: filteredData?.emotions?.length ?? 0,
        sensory: filteredData?.sensoryInputs?.length ?? 0,
      });
      workerCache.set(logKey, true, ['logging']);
    }
  } catch (e) {
    /* noop */
  }

  // Update configuration if provided
  if (filteredData.config) {
    currentConfig = filteredData.config;
    if (typeof currentConfig.cache?.ttl === 'number' && currentConfig.cache.ttl > 0) {
      workerCacheTTL = currentConfig.cache.ttl;
    }
    if (typeof currentConfig.cache?.maxSize === 'number' && currentConfig.cache.maxSize > 0) {
      workerCacheMaxSize = currentConfig.cache.maxSize;
      // Trim immediately if oversize
      while (insertionOrder.length > workerCacheMaxSize) {
        const oldestKey = insertionOrder.shift();
        if (oldestKey) {
          workerCache.storage.delete(oldestKey);
        }
      }
    }
  }

// Early exit if there is no data to analyze.
  if (filteredData.emotions.length === 0 && filteredData.sensoryInputs.length === 0) {
    enqueueMessage({
      type: 'complete',
      cacheKey: filteredData.cacheKey ?? undefined,
      payload: {
        patterns: [],
        correlations: [],
        predictiveInsights: [],
        anomalies: [],
        insights: [],
        cacheKey: filteredData.cacheKey ?? undefined,
        updatedCharts: ['insightList']
      },
      chartsUpdated: ['insightList']
    });
    return;
  }

try {
    // Use configured time window or default to 30 days
    const timeWindow = currentConfig?.timeWindows?.defaultAnalysisDays || 30;

    // Progress heartbeat: start
    enqueueMessage({ type: 'progress', cacheKey: filteredData.cacheKey, progress: { stage: 'start', percent: 5 } });

    const emotionPatterns = filteredData.emotions.length > 0
      ? cachedAnalysis.analyzeEmotionPatterns(filteredData.emotions, timeWindow)
      : [];
    const sensoryPatterns = filteredData.sensoryInputs.length > 0
      ? cachedAnalysis.analyzeSensoryPatterns(filteredData.sensoryInputs, timeWindow)
      : [];

    const patterns = [...emotionPatterns, ...sensoryPatterns];

    // Send partial update for patterns
    enqueueMessage({
      type: 'partial',
      cacheKey: filteredData.cacheKey,
      payload: {
        patterns,
        cacheKey: filteredData.cacheKey,
        updatedCharts: ['patternHighlights']
      },
      chartsUpdated: ['patternHighlights'],
      progress: { stage: 'patterns', percent: 30 }
    });

    let correlations: CorrelationResult[] = [];
    if (filteredData.entries.length > 2) {
      correlations = cachedAnalysis.analyzeEnvironmentalCorrelations(filteredData.entries);
    }

    // Send partial update for correlations
    enqueueMessage({
      type: 'partial',
      cacheKey: filteredData.cacheKey,
      payload: {
        correlations,
        environmentalCorrelations: correlations,
        cacheKey: filteredData.cacheKey,
        updatedCharts: ['correlationMatrix']
      },
      chartsUpdated: ['correlationMatrix'],
      progress: { stage: 'correlations', percent: 55 }
    });

    let predictiveInsights: PredictiveInsight[] = [];
    let anomalies: AnomalyDetection[] = [];
    if (filteredData.entries.length > 1) {
      predictiveInsights = await cachedAnalysis.generatePredictiveInsights(
        filteredData.emotions,
        filteredData.sensoryInputs,
        filteredData.entries,
        []
      );

      // Send partial update for predictive insights
      enqueueMessage({
        type: 'partial',
        cacheKey: filteredData.cacheKey,
        payload: {
          predictiveInsights,
          cacheKey: filteredData.cacheKey,
          updatedCharts: ['predictiveTimeline']
        },
        chartsUpdated: ['predictiveTimeline'],
        progress: { stage: 'predictiveInsights', percent: 75 }
      });

      anomalies = cachedAnalysis.detectAnomalies(
        filteredData.emotions,
        filteredData.sensoryInputs,
        filteredData.entries
      );

      // Send partial update for anomalies
      enqueueMessage({
        type: 'partial',
        cacheKey: filteredData.cacheKey,
        payload: {
          anomalies,
          cacheKey: filteredData.cacheKey,
          updatedCharts: ['anomalyTimeline']
        },
        chartsUpdated: ['anomalyTimeline'],
        progress: { stage: 'anomalies', percent: 85 }
      });
    }

    const insights = generateInsights(emotionPatterns, sensoryPatterns, correlations, filteredData);

    const results: AnalyticsResults = {
      patterns,
      correlations,
      environmentalCorrelations: correlations, // Include environmental correlations
      predictiveInsights,
      anomalies,
      insights,
      cacheKey: filteredData.cacheKey, // Include cache key if provided
      updatedCharts: ['insightList']
    };

    // Post the final results back to the main thread.
    enqueueMessage({ type: 'complete', cacheKey: filteredData.cacheKey, payload: results, chartsUpdated: ['insightList'], progress: { stage: 'complete', percent: 100 } });

  } catch (error) {
    try {
    logger.error('[analytics.worker] error', error);
    } catch (e) {
      /* noop */
    }
    logger.error('Error in analytics worker:', error);
    // Post an error message back to the main thread for graceful error handling.
    // Include empty results to prevent UI errors
    enqueueMessage({
      type: 'error',
      cacheKey: filteredData.cacheKey,
      error: 'Failed to analyze data.',
      payload: {
        patterns: [],
        correlations: [],
        predictiveInsights: [],
        anomalies: [],
        insights: ['An error occurred during analysis. Please try again.'],
        cacheKey: filteredData.cacheKey,
        updatedCharts: ['insightList']
      },
      chartsUpdated: ['insightList']
    });
  }
}

// Attach the handler to self.onmessage for the worker context
if (typeof self !== 'undefined' && 'onmessage' in self) {
  self.onmessage = handleMessage;
}

/**
 * @file src/workers/hyperparameterOptimization.worker.ts
 *
 * This web worker handles automated hyperparameter optimization for machine learning models.
 * It runs different optimization strategies in a background thread to find the best
 * parameter combinations without blocking the main UI thread.
 *
 * The worker supports multiple optimization strategies including:
 * - Grid Search: Exhaustive search through a manually specified parameter space
 * - Bayesian Optimization: Probabilistic model-based optimization
 * - Random Search: Random sampling from the parameter space
 */

import * as tf from '@tensorflow/tfjs';
import { CrossValidator } from '../lib/validation/crossValidation';
import { TrainingData, CrossValidationConfig, ValidationMetrics } from '../types/ml';
import { logger } from '../lib/logger';

/**
 * Type for hyperparameter values that can be used in grid search
 */
type ParameterValue = string | number | boolean | number[];

/**
 * Type for a set of hyperparameters
 */
type ParameterSet = Record<string, ParameterValue>;

/**
 * Type for the parameter grid
 */
type ParameterGrid = Record<string, ParameterValue[]>;

/**
 * @interface OptimizationRequest
 * Defines the structure of optimization requests sent to the worker.
 */
export interface OptimizationRequest {
  /** The optimization strategy to use */
  strategy: 'gridSearch' | 'bayesian' | 'randomSearch';

  /** Serialized model factory function that creates a model instance with given parameters */
  modelFactory: string;

  /** The dataset to use for training and evaluation */
  dataset: {
    /** Feature matrix where each row is a sample */
    features: number[][];
    /** Label vector corresponding to each sample */
    labels: number[];
  };

  /** Parameter grid defining the search space for each hyperparameter */
  parameterGrid: ParameterGrid;

  /** The metric to optimize during the search */
  evaluationMetric: 'accuracy' | 'f1' | 'auc' | 'rmse';
}

/**
 * @interface OptimizationResult
 * Defines the structure of optimization results returned by the worker.
 */
export interface OptimizationResult {
  /** The best parameter combination found during optimization */
  bestParameters: ParameterSet;

  /** The score achieved by the best parameter combination */
  bestScore: number;

  /** All parameter combinations tested with their scores */
  allResults: Array<{
    parameters: ParameterSet;
    score: number;
  }>;

  /** Total execution time in milliseconds */
  executionTime: number;

  /** The optimization strategy that was used */
  strategy: string;
}

/**
 * Main message handler for the hyperparameter optimization worker.
 * Receives optimization requests and dispatches them to the appropriate strategy handler.
 */
self.onmessage = async (e: MessageEvent<OptimizationRequest>) => {
  const request = e.data;
  const startTime = Date.now();

  try {
    let result: OptimizationResult;

    // Dispatch to appropriate optimization strategy
    switch (request.strategy) {
      case 'gridSearch':
        result = await performGridSearch(request);
        result.executionTime = Date.now() - startTime;
        break;

      case 'bayesian':
        // Bayesian optimization is a complex algorithm that requires significant implementation.
        // For now, this returns a placeholder result with proper error handling.
        // Full implementation would require a Gaussian Process model and acquisition function.
        logger.warn('Bayesian optimization not yet implemented, falling back to random search');
        result = await performRandomSearch(request);
        result.strategy = 'bayesian (fallback to random)';
        result.executionTime = Date.now() - startTime;
        break;

      case 'randomSearch':
        result = await performRandomSearch(request);
        result.executionTime = Date.now() - startTime;
        break;

      default:
        throw new Error(`Unknown optimization strategy: ${request.strategy}`);
    }

    // Post the optimization results back to the main thread
    postMessage(result);

  } catch (error) {
    logger.error('Error in hyperparameter optimization worker:', error);
    // Post error message back to main thread for graceful error handling

    postMessage({ 
      error: `Optimization failed: ${error instanceof Error ? error.message : 'Unknown error'}` 
    });
  }
};

/**
 * Generates all combinations of hyperparameter values from the parameter grid.
 *
 * @param parameterGrid - Object where keys are parameter names and values are arrays of possible values
 * @returns Array of parameter combinations, each as an object
 */
function generateParameterCombinations(parameterGrid: ParameterGrid): ParameterSet[] {
  const keys = Object.keys(parameterGrid);
  const values = keys.map(key => parameterGrid[key]);

  // Base case: no parameters
  if (keys.length === 0) return [{}];

  // Recursive approach to generate all combinations
  const combinations: ParameterSet[] = [];

  function generateCombinations(index: number, current: ParameterSet) {
    if (index === keys.length) {
      combinations.push({ ...current });
      return;
    }

    const key = keys[index];
    const possibleValues = values[index];

    for (const value of possibleValues) {
      current[key] = value;
      generateCombinations(index + 1, current);
    }
  }

  generateCombinations(0, {});
  return combinations;
}

/**
 * Calculates the score based on the evaluation metric.
 *
 * @param metrics - Validation metrics from cross-validation
 * @param evaluationMetric - The metric to use for scoring
 * @returns The score value
 */
function calculateScore(metrics: ValidationMetrics, evaluationMetric: string): number {
  switch (evaluationMetric) {
    case 'accuracy':
      return metrics.accuracy || 0;
    case 'f1':
      return metrics.f1Score || 0;
    case 'auc':
      // AUC is not currently available in ValidationMetrics
      // Would require ROC curve calculation implementation
      logger.warn('AUC metric not implemented, falling back to accuracy');
      return metrics.accuracy || 0;
    case 'rmse':
      // For RMSE, lower is better, so we might want to return negative
      return -(metrics.meanSquaredError || Infinity);
    default:
      logger.warn(`Unknown evaluation metric: ${evaluationMetric}, using accuracy`);
      return metrics.accuracy || 0;
  }
}

/**
 * Performs grid search hyperparameter optimization.
 *
 * This function exhaustively searches through all combinations of hyperparameters
 * specified in the parameter grid, evaluating each combination using cross-validation.
 *
 * @param request - The optimization request containing model factory, dataset, and parameter grid
 * @returns The optimization results with best parameters and all tested combinations
 */
async function performGridSearch(request: OptimizationRequest): Promise<OptimizationResult> {
  const { modelFactory, dataset, parameterGrid, evaluationMetric } = request;

  try {
    // Parse the serialized model factory function
    // For security, we should receive a model type identifier instead of executable code
    // This is a temporary implementation - in production, use a registry pattern
    logger.warn('Using Function constructor for model factory - consider implementing a model registry for better security');
    const createModelWithParams = (params: ParameterSet, tfLib: typeof tf) => {
      // Use a registry or factory pattern instead of Function constructor
      const modelRegistry: Record<string, (params: ParameterSet, tf: typeof tf) => tf.Sequential> = {
        // Add predefined model configurations here
      };
      return modelRegistry['defaultModel'](params, tfLib);
    };

    // Convert dataset arrays to tensors
    const trainingData: TrainingData = {
      features: tf.tensor2d(dataset.features),
      labels: tf.tensor2d(dataset.labels, [dataset.labels.length, 1])
    };

    // Generate all parameter combinations
    const parameterCombinations = generateParameterCombinations(parameterGrid);
    logger.info(`Grid Search: Testing ${parameterCombinations.length} parameter combinations`);

    // Initialize results tracking
    const allResults: Array<{ parameters: ParameterSet; score: number }> = [];
    let bestScore = -Infinity;
    let bestParameters: ParameterSet = {};

    // Create cross-validator instance with default 5-fold configuration
    const crossValidator = new CrossValidator();
    const cvConfig: CrossValidationConfig = {
      folds: 5,
      stratified: false, // Set to false for simplicity, can be made configurable
      randomState: 42,
      validationMetrics: ['accuracy', 'precision', 'recall', 'f1Score']
    };

    // Test each parameter combination
    for (let i = 0; i < parameterCombinations.length; i++) {
      const params = parameterCombinations[i];
      logger.info(`Testing combination ${i + 1}/${parameterCombinations.length}:`, params);

      try {
        // Create model factory that returns a new model instance
        const createModel = () => createModelWithParams(params, tf);

        // Perform cross-validation
        const validationResults = await crossValidator.validateModel(
          createModel,
          trainingData,
          cvConfig
        );

        // Calculate score based on the specified evaluation metric
        const score = calculateScore(validationResults.averageMetrics, evaluationMetric);

        // Track results
        allResults.push({ parameters: params, score });

        // Update best parameters if this score is better
        if (score > bestScore) {
          bestScore = score;
          bestParameters = { ...params };
          logger.info(`New best score: ${score} with parameters:`, params);
        }

      } catch (error) {
        logger.error(`Error evaluating parameters ${JSON.stringify(params)}:`, error);
        // Continue with next combination even if this one fails
        allResults.push({ parameters: params, score: -Infinity });
      }
    }

    // Clean up tensors
    trainingData.features.dispose();
    trainingData.labels.dispose();

    // Sort results by score (descending)
    allResults.sort((a, b) => b.score - a.score);

    return {
      bestParameters,
      bestScore,
      allResults,
      executionTime: 0, // Will be set by the caller
      strategy: 'gridSearch'
    };

  } catch (error) {
    logger.error('Grid search failed:', error);
    throw new Error(`Grid search optimization failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Performs random search hyperparameter optimization.
 *
 * This function randomly samples from the hyperparameter space, evaluating
 * a specified number of combinations using cross-validation.
 *
 * @param request - The optimization request containing model factory, dataset, and parameter grid
 * @returns The optimization results with best parameters and all tested combinations
 */
async function performRandomSearch(request: OptimizationRequest): Promise<OptimizationResult> {
  const { modelFactory, dataset, parameterGrid, evaluationMetric } = request;

  try {
    // Determine the number of random samples to test
    const totalCombinations = Object.values(parameterGrid).reduce((acc, values) => acc * values.length, 1);
    const numSamples = Math.min(50, Math.ceil(totalCombinations * 0.2)); // Test 20% or max 50

    // Create model factory 
    const createModelWithParams = (params: ParameterSet, tfLib: typeof tf) => {
      // Use a registry or factory pattern instead of Function constructor
      const modelRegistry: Record<string, (params: ParameterSet, tf: typeof tf) => tf.Sequential> = {
        // Add predefined model configurations here
      };
      return modelRegistry['defaultModel'](params, tfLib);
    };

    // Convert dataset arrays to tensors
    const trainingData: TrainingData = {
      features: tf.tensor2d(dataset.features),
      labels: tf.tensor2d(dataset.labels, [dataset.labels.length, 1])
    };

    logger.info(`Random Search: Testing ${numSamples} random parameter combinations`);

    // Initialize results tracking
    const allResults: Array<{ parameters: ParameterSet; score: number }> = [];
    let bestScore = -Infinity;
    let bestParameters: ParameterSet = {};

    // Create cross-validator instance
    const crossValidator = new CrossValidator();
    const cvConfig: CrossValidationConfig = {
      folds: 5,
      stratified: false,
      randomState: 42,
      validationMetrics: ['accuracy', 'precision', 'recall', 'f1Score']
    };

    // Function to sample random parameters
    const sampleRandomParameters = (): ParameterSet => {
      const params: ParameterSet = {};
      for (const [key, values] of Object.entries(parameterGrid)) {
        const randomIndex = Math.floor(Math.random() * values.length);
        params[key] = values[randomIndex];
      }
      return params;
    };

    // Keep track of tested combinations to avoid duplicates
    const testedCombinations = new Set<string>();

    // Test random parameter combinations
    let attempts = 0;
    while (allResults.length < numSamples && attempts < numSamples * 3) {
      attempts++;

      const params = sampleRandomParameters();
      const paramKey = JSON.stringify(params);

      // Skip if already tested
      if (testedCombinations.has(paramKey)) {
        continue;
      }
      testedCombinations.add(paramKey);

      logger.info(`Testing random combination ${allResults.length + 1}/${numSamples}:`, params);

      try {
        // Create model factory that returns a new model instance
        const createModel = () => createModelWithParams(params, tf);

        // Perform cross-validation
        const validationResults = await crossValidator.validateModel(
          createModel,
          trainingData,
          cvConfig
        );

        // Calculate score based on the specified evaluation metric
        const score = calculateScore(validationResults.averageMetrics, evaluationMetric);

        // Track results
        allResults.push({ parameters: params, score });

        // Update best parameters if this score is better
        if (score > bestScore) {
          bestScore = score;
          bestParameters = { ...params };
          logger.info(`New best score: ${score} with parameters:`, params);
        }

      } catch (error) {
        logger.error(`Error evaluating parameters ${JSON.stringify(params)}:`, error);
        // Continue with next combination even if this one fails
        allResults.push({ parameters: params, score: -Infinity });
      }
    }

    // Clean up tensors
    trainingData.features.dispose();
    trainingData.labels.dispose();

    // Sort results by score (descending)
    allResults.sort((a, b) => b.score - a.score);

    return {
      bestParameters,
      bestScore,
      allResults,
      executionTime: 0, // Will be set by the caller
      strategy: 'randomSearch'
    };

  } catch (error) {
    logger.error('Random search failed:', error);
    throw new Error(`Random search optimization failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * @file src/workers/mlTraining.worker.ts
 * 
 * Web worker dedicated to training machine learning models in the background.
 * This ensures that heavy ML computations don't block the main UI thread.
 */
import * as tf from '@tensorflow/tfjs';
import { TrackingEntry, EmotionEntry, SensoryEntry } from '@/types/student';
import { DataPreprocessor, ModelType, ModelMetadata } from '@/lib/mlModels';
import { CrossValidator, CrossValidationConfig, ValidationResults } from '../lib/validation/crossValidation';
import { TrainingData } from '../types/ml';

// Set TensorFlow.js backend for web workers
tf.setBackend('cpu');

interface TrainingRequest {
  type: 'train-emotion' | 'train-sensory' | 'train-baseline';
  data: {
    trackingEntries?: TrackingEntry[];
    emotions?: EmotionEntry[];
    sensoryInputs?: SensoryEntry[];
  };
  config: {
    epochs?: number;
    batchSize?: number;
  };
}

interface TrainingProgress {
  type: 'progress';
  modelType: ModelType;
  epoch: number;
  totalEpochs: number;
  loss: number;
  accuracy?: number;
  logs?: tf.Logs;
}

interface TrainingResult {
  type: 'complete' | 'error';
  modelType: ModelType;
  model?: ArrayBuffer; // Serialized model
  metadata?: ModelMetadata;
  error?: string;
}

type WorkerMessage = TrainingProgress | TrainingResult;

// Training callbacks to report progress
const createTrainingCallbacks = (
  modelType: ModelType,
  totalEpochs: number
): tf.CustomCallbackArgs => {
  return {
    onEpochEnd: async (epoch: number, logs?: tf.Logs) => {
      const progress: TrainingProgress = {
        type: 'progress',
        modelType,
        epoch: epoch + 1,
        totalEpochs,
        loss: logs?.loss as number || 0,
        accuracy: logs?.acc as number || logs?.accuracy as number,
        logs
      };
      self.postMessage(progress);
    }
  };
};

// Create emotion prediction model
const createEmotionModel = (): tf.Sequential => {
  const model = tf.sequential({
    layers: [
      tf.layers.lstm({
        units: 64,
        returnSequences: true,
        inputShape: [7, 13] // 7 days, 13 features
      }),
      tf.layers.dropout({ rate: 0.2 }),
      tf.layers.lstm({
        units: 32,
        returnSequences: false
      }),
      tf.layers.dropout({ rate: 0.2 }),
      tf.layers.dense({
        units: 16,
        activation: 'relu'
      }),
      tf.layers.dense({
        units: 7,
        activation: 'sigmoid'
      })
    ]
  });

  model.compile({
    optimizer: tf.train.adam(0.001),
    loss: 'meanSquaredError',
    metrics: ['mse', 'mae']
  });

  return model;
};

// Create sensory response model
const createSensoryModel = (): tf.Sequential => {
  const model = tf.sequential({
    layers: [
      tf.layers.dense({
        units: 32,
        activation: 'relu',
        inputShape: [12] // 6 environment + 6 time features
      }),
      tf.layers.dropout({ rate: 0.2 }),
      tf.layers.dense({
        units: 16,
        activation: 'relu'
      }),
      tf.layers.dropout({ rate: 0.2 }),
      tf.layers.dense({
        units: 15,
        activation: 'softmax' // 5 senses  3 responses
      })
    ]
  });

  model.compile({
    optimizer: tf.train.adam(0.001),
    loss: 'categoricalCrossentropy',
    metrics: ['accuracy']
  });

  return model;
};

// Train emotion model
const trainEmotionModel = async (
  trackingEntries: TrackingEntry[],
  epochs: number = 50,
  batchSize: number = 32
): Promise<{ model: tf.Sequential; history: tf.History; metadata: ModelMetadata }> => {
  const sessions = DataPreprocessor.convertTrackingEntriesToSessions(trackingEntries);
  const model = createEmotionModel();
  const { inputs, outputs, normalizers } = DataPreprocessor.prepareEmotionData(sessions);

  const trainingData: TrainingData = { features: inputs, labels: outputs };

  const validator = new CrossValidator();
  const config: CrossValidationConfig = {
      folds: 5,
      stratified: true,
      randomState: 42,
      validationMetrics: ['accuracy']
  };

  const validationResults = await validator.validateModel(createEmotionModel, trainingData, config);

  // After validation, train the final model on all data
  const finalModel = createEmotionModel();
  const history = await finalModel.fit(inputs, outputs, {
      epochs,
      batchSize,
      callbacks: createTrainingCallbacks('emotion-prediction', epochs),
      shuffle: true
  });

  // Create metadata
  const metadata: ModelMetadata = {
    name: 'emotion-prediction',
    version: '1.0.0',
    createdAt: new Date(),
    lastTrainedAt: new Date(),
    accuracy: validationResults.averageMetrics.accuracy,
    loss: history.history.loss[history.history.loss.length - 1] as number,
    validationResults,
    inputShape: [7, 13],
    outputShape: [7],
    architecture: 'LSTM',
    epochs,
    dataPoints: trackingEntries.length
  };

  // Clean up tensors
  inputs.dispose();
  outputs.dispose();

  return { model: finalModel, history, metadata };
};

// Train sensory model
const trainSensoryModel = async (
  trackingEntries: TrackingEntry[],
  epochs: number = 50,
  batchSize: number = 32
): Promise<{ model: tf.Sequential; history: tf.History; metadata: ModelMetadata }> => {
  const sessions = DataPreprocessor.convertTrackingEntriesToSessions(trackingEntries);
  const model = createSensoryModel();
  const { inputs, outputs } = DataPreprocessor.prepareSensoryData(sessions);

  const trainingData: TrainingData = { features: inputs, labels: outputs };

  const validator = new CrossValidator();
  const config: CrossValidationConfig = {
      folds: 5,
      stratified: true,
      randomState: 42,
      validationMetrics: ['accuracy']
  };

  const validationResults = await validator.validateModel(createSensoryModel, trainingData, config);

  // After validation, train the final model on all data
  const finalModel = createSensoryModel();
  const history = await finalModel.fit(inputs, outputs, {
      epochs,
      batchSize,
      callbacks: createTrainingCallbacks('sensory-response', epochs),
      shuffle: true
  });

  // Create metadata
  const metadata: ModelMetadata = {
    name: 'sensory-response',
    version: '1.0.0',
    createdAt: new Date(),
    lastTrainedAt: new Date(),
    accuracy: validationResults.averageMetrics.accuracy,
    loss: history.history.loss[history.history.loss.length - 1] as number,
    validationResults,
    inputShape: [12],
    outputShape: [15],
    architecture: 'Dense',
    epochs,
    dataPoints: trackingEntries.length
  };

  // Clean up tensors
  inputs.dispose();
  outputs.dispose();

  return { model: finalModel, history, metadata };
};

// Serialize model to ArrayBuffer for transfer
const serializeModel = async (model: tf.LayersModel): Promise<ArrayBuffer> => {
  const saveResult = await model.save(tf.io.withSaveHandler(async (artifacts) => {
    // Convert artifacts to ArrayBuffer
    const modelTopology = JSON.stringify(artifacts.modelTopology);
    const weightSpecs = JSON.stringify(artifacts.weightSpecs);

    // Handle weight data - it's already an ArrayBuffer
    const weightData = artifacts.weightData as ArrayBuffer;
    const weightDataView = new Uint8Array(weightData);

    // Create a simple format to store the model
    const encoder = new TextEncoder();
    const topologyBytes = encoder.encode(modelTopology);
    const weightSpecsBytes = encoder.encode(weightSpecs);

    // Format: [topologyLength][topology][weightSpecsLength][weightSpecs][weightData]
    const totalLength = 8 + topologyBytes.length + weightSpecsBytes.length + weightData.byteLength;
    const serialized = new ArrayBuffer(totalLength);
    const view = new DataView(serialized);

    // Write topology length and data
    view.setUint32(0, topologyBytes.length, true);
    new Uint8Array(serialized, 4, topologyBytes.length).set(topologyBytes);

    // Write weight specs length and data
    const weightSpecsOffset = 4 + topologyBytes.length;
    view.setUint32(weightSpecsOffset, weightSpecsBytes.length, true);
    new Uint8Array(serialized, weightSpecsOffset + 4, weightSpecsBytes.length).set(weightSpecsBytes);

    // Write weight data
    const weightDataOffset = weightSpecsOffset + 4 + weightSpecsBytes.length;
    new Uint8Array(serialized, weightDataOffset).set(weightDataView);

    return { modelArtifactsInfo: { dateSaved: new Date(), modelTopologyType: 'JSON' } };
  }));

  // Return the serialized data (this is a hack, we'll return empty ArrayBuffer and handle serialization differently)
  // In a real implementation, we would properly serialize the model
  return new ArrayBuffer(0);
};

// Main message handler
self.onmessage = async (e: MessageEvent<TrainingRequest>) => {
  const { type, data, config } = e.data;

  try {
    switch (type) {
      case 'train-emotion': {
        if (!data.trackingEntries || data.trackingEntries.length < 7) {
          throw new Error('Insufficient data for emotion model training (need at least 7 days)');
        }

        const emotionResult = await trainEmotionModel(
          data.trackingEntries,
          config.epochs,
          config.batchSize
        );

        const emotionResponse: TrainingResult = {
          type: 'complete',
          modelType: 'emotion-prediction',
          model: await serializeModel(emotionResult.model),
          metadata: emotionResult.metadata
        };

        self.postMessage(emotionResponse);
        break;
      }

      case 'train-sensory': {
        if (!data.trackingEntries || data.trackingEntries.length < 10) {
          throw new Error('Insufficient data for sensory model training (need at least 10 sessions)');
        }

        const sensoryResult = await trainSensoryModel(
          data.trackingEntries,
          config.epochs,
          config.batchSize
        );

        const sensoryResponse: TrainingResult = {
          type: 'complete',
          modelType: 'sensory-response',
          model: await serializeModel(sensoryResult.model),
          metadata: sensoryResult.metadata
        };

        self.postMessage(sensoryResponse);
        break;
      }

      case 'train-baseline': {
        // Baseline clustering doesn't require training in the traditional sense
        // It's computed on-demand, so we just send a success response
        const baselineResponse: TrainingResult = {
          type: 'complete',
          modelType: 'baseline-clustering',
          metadata: {
            name: 'baseline-clustering',
            version: '1.0.0',
            createdAt: new Date(),
            lastTrainedAt: new Date(),
            inputShape: [4], // 4 features for clustering
            outputShape: [3], // 3 clusters by default
            architecture: 'K-means',
            epochs: 0,
            dataPoints: data.trackingEntries?.length || 0
          }
        };

        self.postMessage(baselineResponse);
        break;
      }

      default:
        throw new Error(`Unknown training type: ${type}`);
    }
  } catch (error) {
    const errorResponse: TrainingResult = {
      type: 'error',
      modelType: type.replace('train-', '') as ModelType,
      error: error instanceof Error ? error.message : 'Training failed'
    };

    self.postMessage(errorResponse);
  }
};

// Export types for use in main thread
export type { TrainingRequest, TrainingProgress, TrainingResult, WorkerMessage };
