import React, { useState, useEffect, useCallback } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { analyticsConfig } from '@/lib/analyticsConfig';
import { useAnalyticsWorker } from '@/hooks/useAnalyticsWorker';
import { CheckCircle, XCircle, RefreshCw, Settings } from 'lucide-react';

interface TestResult {
  name: string;
  status: 'pass' | 'fail' | 'pending';
  message: string;
}

const AnalyticsConfigTest: React.FC = () => {
  const [testResults, setTestResults] = useState<TestResult[]>([]);
  const [currentConfig, setCurrentConfig] = useState(analyticsConfig.getConfig());
  const { cacheSize, clearCache } = useAnalyticsWorker();

  useEffect(() => {
    // Subscribe to configuration changes
    const unsubscribe = analyticsConfig.subscribe((newConfig) => {
      setCurrentConfig(newConfig);
    });

    return unsubscribe;
  }, []);

  const runTests = async () => {
    const results: TestResult[] = [];

    // Test 1: Configuration Persistence
    try {
      const originalConfig = analyticsConfig.getConfig();
      analyticsConfig.updateConfig({
        patternAnalysis: {
          ...originalConfig.patternAnalysis,
          minDataPoints: 7
        }
      });

      // Simulate page reload by creating new instance
      const newConfigManager = analyticsConfig;
      const loadedConfig = newConfigManager.getConfig();

      if (loadedConfig.patternAnalysis.minDataPoints === 7) {
        results.push({
          name: 'Configuration Persistence',
          status: 'pass',
          message: 'Configuration saved and loaded from localStorage successfully'
        });
      } else {
        results.push({
          name: 'Configuration Persistence',
          status: 'fail',
          message: 'Configuration not persisted correctly'
        });
      }

      // Reset to original
      analyticsConfig.updateConfig(originalConfig);
    } catch (error) {
      results.push({
        name: 'Configuration Persistence',
        status: 'fail',
        message: `Error: ${error}`
      });
    }

    // Test 2: Preset Configuration
    try {
      analyticsConfig.setPreset('sensitive');
      const sensitiveConfig = analyticsConfig.getConfig();

      if (sensitiveConfig.alertSensitivity.level === 'high' &&
          sensitiveConfig.patternAnalysis.minDataPoints === 2) {
        results.push({
          name: 'Preset Configuration',
          status: 'pass',
          message: 'Sensitive preset applied correctly'
        });
      } else {
        results.push({
          name: 'Preset Configuration',
          status: 'fail',
          message: 'Preset not applied correctly'
        });
      }

      // Reset to balanced
      analyticsConfig.setPreset('balanced');
    } catch (error) {
      results.push({
        name: 'Preset Configuration',
        status: 'fail',
        message: `Error: ${error}`
      });
    }

    // Test 3: Cache Invalidation on Config Change
    try {
      const initialCacheSize = cacheSize;

      // Update config with invalidateOnConfigChange = true
      analyticsConfig.updateConfig({
        cache: {
          ...currentConfig.cache,
          invalidateOnConfigChange: true
        }
      });

      // Change a threshold to trigger cache invalidation
      analyticsConfig.updateConfig({
        patternAnalysis: {
          ...currentConfig.patternAnalysis,
          minDataPoints: 5
        }
      });

      // Give the event loop a tick for cache invalidation without setTimeout
      await new Promise<void>((resolve) => requestAnimationFrame(() => resolve()));

      results.push({
        name: 'Cache Invalidation',
        status: 'pass',
        message: `Cache invalidation working (was ${initialCacheSize}, now ${cacheSize})`
      });
    } catch (error) {
      results.push({
        name: 'Cache Invalidation',
        status: 'fail',
        message: `Error: ${error}`
      });
    }

    // Test 4: Configuration Export/Import
    try {
      const exported = analyticsConfig.exportConfig();
      const parsed = JSON.parse(exported);

      if (parsed.patternAnalysis && parsed.alertSensitivity && parsed.cache) {
        // Modify and import
        parsed.patternAnalysis.minDataPoints = 10;
        const importSuccess = analyticsConfig.importConfig(JSON.stringify(parsed));

        if (importSuccess && analyticsConfig.getConfig().patternAnalysis.minDataPoints === 10) {
          results.push({
            name: 'Configuration Export/Import',
            status: 'pass',
            message: 'Configuration exported and imported successfully'
          });
        } else {
          results.push({
            name: 'Configuration Export/Import',
            status: 'fail',
            message: 'Import failed or values not updated'
          });
        }
      } else {
        results.push({
          name: 'Configuration Export/Import',
          status: 'fail',
          message: 'Export format invalid'
        });
      }

      // Reset to defaults
      analyticsConfig.resetToDefaults();
    } catch (error) {
      results.push({
        name: 'Configuration Export/Import',
        status: 'fail',
        message: `Error: ${error}`
      });
    }

    // Test 5: Sensitivity Multipliers
    try {
      analyticsConfig.updateConfig({
        alertSensitivity: {
          level: 'high',
          emotionIntensityMultiplier: 1.5,
          frequencyMultiplier: 1.5,
          anomalyMultiplier: 1.5
        }
      });

      const config = analyticsConfig.getConfig();
      if (config.alertSensitivity.emotionIntensityMultiplier === 1.5) {
        results.push({
          name: 'Sensitivity Multipliers',
          status: 'pass',
          message: 'Sensitivity multipliers applied correctly'
        });
      } else {
        results.push({
          name: 'Sensitivity Multipliers',
          status: 'fail',
          message: 'Multipliers not set correctly'
        });
      }

      // Reset
      analyticsConfig.resetToDefaults();
    } catch (error) {
      results.push({
        name: 'Sensitivity Multipliers',
        status: 'fail',
        message: `Error: ${error}`
      });
    }

    setTestResults(results);
  };

  const getStatusIcon = useCallback((status: TestResult['status']): React.ReactElement => {
    switch (status) {
      case 'pass':
        return <CheckCircle className="h-5 w-5 text-green-600" />;
      case 'fail':
        return <XCircle className="h-5 w-5 text-red-600" />;
      default:
        return <RefreshCw className="h-5 w-5 text-gray-400 animate-spin" />;
    }
  }, []);

  const getStatusColor = useCallback((status: TestResult['status']): 'default' | 'destructive' | 'secondary' | 'outline' => {
    switch (status) {
      case 'pass':
        return 'default';
      case 'fail':
        return 'destructive';
      default:
        return 'secondary';
    }
  }, []);

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Settings className="h-5 w-5" />
          Analytics Configuration Tests
        </CardTitle>
        <CardDescription>
          Verify that configuration changes and cache invalidation work correctly
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <p className="text-sm text-muted-foreground">Current Configuration</p>
            <div className="flex gap-2">
              <Badge variant="outline">
                Alert Level: {currentConfig.alertSensitivity.level}
              </Badge>
              <Badge variant="outline">
                Min Data Points: {currentConfig.patternAnalysis.minDataPoints}
              </Badge>
              <Badge variant="outline">
                Cache Size: {cacheSize}
              </Badge>
            </div>
          </div>
          <Button onClick={runTests} className="flex items-center gap-2">
            <RefreshCw className="h-4 w-4" />
            Run Tests
          </Button>
        </div>

        {testResults.length > 0 && (
          <div className="space-y-2">
            <h4 className="text-sm font-medium">Test Results</h4>
            {testResults.map((result) => (
              <div
                key={`${result.name}-${result.status}`}
                className="flex items-start gap-3 p-3 rounded-lg bg-muted/50"
              >
                {getStatusIcon(result.status)}
                <div className="flex-1">
                  <p className="font-medium">{result.name}</p>
                  <p className="text-sm text-muted-foreground">{result.message}</p>
                </div>
                <Badge variant={getStatusColor(result.status) as "default" | "secondary" | "destructive" | "outline"}>
                  {result.status.toUpperCase()}
                </Badge>
              </div>
            ))}
          </div>
        )}

        <div className="pt-4 border-t">
          <p className="text-sm text-muted-foreground mb-2">Quick Actions</p>
          <div className="flex flex-wrap gap-2">
            <Button
              size="sm"
              variant="outline"
              onClick={() => analyticsConfig.setPreset('conservative')}
            >
              Set Conservative
            </Button>
            <Button
              size="sm"
              variant="outline"
              onClick={() => analyticsConfig.setPreset('sensitive')}
            >
              Set Sensitive
            </Button>
            <Button
              size="sm"
              variant="outline"
              onClick={() => analyticsConfig.resetToDefaults()}
            >
              Reset to Defaults
            </Button>
            <Button
              size="sm"
              variant="outline"
              onClick={clearCache}
            >
              Clear Cache
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export { AnalyticsConfigTest };

import React, { useState, useEffect } from 'react';
import {
  Settings,
  Save,
  Download,
  Upload,
  RotateCcw,
  Info,
  AlertTriangle,
  Shield,
  Zap,
  Brain,
  RefreshCw,
  Activity,
  Loader2
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Slider } from '@/components/ui/slider';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { toast } from 'sonner';
import { analyticsConfig, AnalyticsConfiguration, PRESET_CONFIGS } from '@/lib/analyticsConfig';
import { mlModels, ModelMetadata, ModelType } from '@/lib/mlModels';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { logger } from '@/lib/logger';
import { useTranslation } from '@/hooks/useTranslation';

interface AnalyticsSettingsProps {
  onConfigChange?: (config: AnalyticsConfiguration) => void;
  onClose?: () => void;
}

export const AnalyticsSettings: React.FC<AnalyticsSettingsProps> = ({
  onConfigChange,
  onClose
}) => {
  const { tAnalytics } = useTranslation();
  const [config, setConfig] = useState<AnalyticsConfiguration>(analyticsConfig.getConfig());
  const [selectedPreset, setSelectedPreset] = useState<string>('balanced');
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [modelStatus, setModelStatus] = useState<Map<ModelType, ModelMetadata | null>>(new Map());
  const [isTraining, setIsTraining] = useState<ModelType | null>(null);
  const [mlEnabled, setMlEnabled] = useState(true);
  const [isLoadingModels, setIsLoadingModels] = useState(true);
  const [isDeletingModel, setIsDeletingModel] = useState<ModelType | null>(null);

  useEffect(() => {
    // Subscribe to configuration changes
    const unsubscribe = analyticsConfig.subscribe((newConfig) => {
      setConfig(newConfig);
      if (onConfigChange) {
        onConfigChange(newConfig);
      }
    });

    // Load ML model status
    loadModelStatus();

    return unsubscribe;
  }, [onConfigChange]);

  const loadModelStatus = async () => {
    setIsLoadingModels(true);
    try {
      await mlModels.init();
      const status = await mlModels.getModelStatus();
      setModelStatus(status);
    } catch (error) {
      logger.error('Failed to load ML model status', { error });
      toast.error(String(tAnalytics('settings.toasts.loadModelsFailed')));
    } finally {
      setIsLoadingModels(false);
    }
  };

  const handleSliderChange = (path: string[], value: number[]) => {
    const newConfig = { ...config };
    let current: Record<string, unknown> = newConfig as unknown as Record<string, unknown>;

    for (let i = 0; i < path.length - 1; i++) {
      current = current[path[i]] as Record<string, unknown>;
    }

    current[path[path.length - 1]] = value[0];
    setConfig(newConfig);
    setHasUnsavedChanges(true);
  };

  const handleSensitivityChange = (value: string) => {
    const newConfig = { ...config };
    newConfig.alertSensitivity.level = value as 'low' | 'medium' | 'high';
    // Do not mutate multipliers here; users can choose presets that carry multipliers
    setConfig(newConfig);
    setHasUnsavedChanges(true);
  };

  const handlePresetSelect = (preset: keyof typeof PRESET_CONFIGS) => {
    setSelectedPreset(preset);
    analyticsConfig.setPreset(preset);
    setHasUnsavedChanges(false);
    toast.success(String(tAnalytics('settings.toasts.presetApplied', { name: PRESET_CONFIGS[preset].name })));
  };

  const handleSave = () => {
    analyticsConfig.updateConfig(config);
    setHasUnsavedChanges(false);
    toast.success(String(tAnalytics('settings.toasts.configUpdated')));
  };

  const handleReset = () => {
    analyticsConfig.resetToDefaults();
    setSelectedPreset('balanced');
    setHasUnsavedChanges(false);
    toast.success(String(tAnalytics('settings.toasts.resetToDefaults')));
  };

  const handleExport = () => {
    const configString = analyticsConfig.exportConfig();
    const blob = new Blob([configString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'analytics-config.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    toast.success(String(tAnalytics('settings.toasts.exported')));
  };

  const handleImport = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const content = e.target?.result as string;
        if (analyticsConfig.importConfig(content)) {
          setHasUnsavedChanges(false);
          toast.success(String(tAnalytics('settings.toasts.imported')));
        } else {
          toast.error(String(tAnalytics('settings.toasts.invalidConfig')));
        }
      } catch (_error) {
        toast.error(String(tAnalytics('settings.toasts.importFailed')));
      }
    };
    reader.readAsText(file);
  };

  // Track a pending training timeout id for cleanup
  const [trainingRequested, setTrainingRequested] = useState<ModelType | null>(null);

  useEffect(() => {
    if (!trainingRequested) return;
    let cancelled = false;
    const id = window.setTimeout(async () => {
      if (cancelled) return;
      setIsTraining(null);
      await loadModelStatus();
      toast.success(String(tAnalytics('settings.ml.modelUpdated', { model: formatModelType(trainingRequested) })));
      setTrainingRequested(null);
    }, 3000);
    return () => {
      cancelled = true;
      clearTimeout(id);
    };
  }, [trainingRequested]);

  const handleModelRetrain = async (modelType: ModelType) => {
    setIsTraining(modelType);
    toast(String(tAnalytics('settings.ml.trainingBackground', { model: formatModelType(modelType) })));
    // Defer the actual completion via effect-managed timeout
    setTrainingRequested(modelType);
  };

  const handleDeleteModel = async (modelType: ModelType) => {
    setIsDeletingModel(modelType);
    try {
      await mlModels.deleteModel(modelType);
      await loadModelStatus();

      toast.success(String(tAnalytics('settings.ml.modelDeleted', { model: formatModelType(modelType) })));
    } catch (_error) {
      toast.error(String(tAnalytics('settings.ml.deleteFailed')));
    } finally {
      setIsDeletingModel(null);
    }
  };

  const formatModelType = (type: ModelType): string => {
    return type.split('-').map(word =>
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const getModelIcon = (type: ModelType) => {
    switch (type) {
      case 'emotion-prediction':
        return <Brain className="h-4 w-4" />;
      case 'sensory-response':
        return <Activity className="h-4 w-4" />;
      case 'baseline-clustering':
        return <Shield className="h-4 w-4" />;
      default:
        return <Settings className="h-4 w-4" />;
    }
  };

  const renderTooltip = (content: string) => (
    <TooltipProvider>
      <Tooltip>
        <TooltipTrigger asChild>
          <Info className="h-4 w-4 ml-2 text-muted-foreground cursor-help" />
        </TooltipTrigger>
        <TooltipContent>
          <p className="max-w-xs">{content}</p>
        </TooltipContent>
      </Tooltip>
    </TooltipProvider>
  );

  return (
    <Dialog open onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Settings className="h-5 w-5" />
            {String(tAnalytics('settings.title'))}
          </DialogTitle>
          <DialogDescription>
            {String(tAnalytics('settings.subtitle'))}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Preset Configurations */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">{String(tAnalytics('settings.presets.title'))}</CardTitle>
              <CardDescription>
                {String(tAnalytics('settings.presets.description'))}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <RadioGroup value={selectedPreset} onValueChange={(value) => handlePresetSelect(value as keyof typeof PRESET_CONFIGS)}>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <Label 
                    htmlFor="conservative" 
                    className="flex flex-col space-y-2 cursor-pointer border rounded-lg p-4 hover:bg-muted/50"
                  >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="conservative" id="conservative" />
                      <Shield className="h-4 w-4 text-blue-500" />
                      <span className="font-medium">{String(tAnalytics('settings.presets.conservative'))}</span>
                    </div>
                    <p className="text-sm text-muted-foreground">
                      {String(tAnalytics('settings.presets.conservativeDesc'))}
                    </p>
                  </Label>

                  <Label 
                    htmlFor="balanced" 
                    className="flex flex-col space-y-2 cursor-pointer border rounded-lg p-4 hover:bg-muted/50"
                  >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="balanced" id="balanced" />
                      <Settings className="h-4 w-4 text-green-500" />
                      <span className="font-medium">{String(tAnalytics('settings.presets.balanced'))}</span>
                    </div>
                    <p className="text-sm text-muted-foreground">
                      {String(tAnalytics('settings.presets.balancedDesc'))}
                    </p>
                  </Label>

                  <Label 
                    htmlFor="sensitive" 
                    className="flex flex-col space-y-2 cursor-pointer border rounded-lg p-4 hover:bg-muted/50"
                  >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="sensitive" id="sensitive" />
                      <Zap className="h-4 w-4 text-yellow-500" />
                      <span className="font-medium">{String(tAnalytics('settings.presets.sensitive'))}</span>
                    </div>
                    <p className="text-sm text-muted-foreground">
                      {String(tAnalytics('settings.presets.sensitiveDesc'))}
                    </p>
                  </Label>
                </div>
              </RadioGroup>
            </CardContent>
          </Card>

          {/* Detailed Settings Tabs */}
          <Tabs defaultValue="thresholds" className="w-full">
              <TabsList className="grid w-full grid-cols-5">
              <TabsTrigger value="thresholds">{String(tAnalytics('settings.tabs.thresholds'))}</TabsTrigger>
              <TabsTrigger value="sensitivity">{String(tAnalytics('settings.tabs.sensitivity'))}</TabsTrigger>
              <TabsTrigger value="timewindows">{String(tAnalytics('settings.tabs.timeWindows'))}</TabsTrigger>
              <TabsTrigger value="mlmodels">{String(tAnalytics('settings.tabs.mlModels'))}</TabsTrigger>
              <TabsTrigger value="advanced">{String(tAnalytics('settings.tabs.advanced'))}</TabsTrigger>
            </TabsList>

            <TabsContent value="thresholds" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="text-base">{String(tAnalytics('settings.thresholds.title'))}</CardTitle>
                  <CardDescription>
                    {String(tAnalytics('settings.thresholds.description'))}
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Label htmlFor="minDataPoints">{String(tAnalytics('settings.thresholds.minDataPoints'))}</Label>
                      {renderTooltip(String(tAnalytics('settings.tooltips.minDataPoints')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="minDataPoints"
                        value={[config.patternAnalysis.minDataPoints]}
                        onValueChange={(value) => handleSliderChange(['patternAnalysis', 'minDataPoints'], value)}
                        min={1}
                        max={10}
                        step={1}
                        className="flex-1"
                      />
                      <span className="w-12 text-right">{config.patternAnalysis.minDataPoints}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Label htmlFor="correlationThreshold">{String(tAnalytics('settings.thresholds.correlationThreshold'))}</Label>
                      {renderTooltip(String(tAnalytics('settings.tooltips.correlationThreshold')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="correlationThreshold"
                        value={[config.patternAnalysis.correlationThreshold]}
                        onValueChange={(value) => handleSliderChange(['patternAnalysis', 'correlationThreshold'], value)}
                        min={0.1}
                        max={0.9}
                        step={0.05}
                        className="flex-1"
                      />
                      <span className="w-12 text-right">{config.patternAnalysis.correlationThreshold.toFixed(2)}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Label htmlFor="concernFrequency">{String(tAnalytics('settings.thresholds.concernFrequency'))}</Label>
                      {renderTooltip(String(tAnalytics('settings.tooltips.concernFrequency')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="concernFrequency"
                        value={[config.patternAnalysis.concernFrequencyThreshold * 100]}
                        onValueChange={(value) => handleSliderChange(['patternAnalysis', 'concernFrequencyThreshold'], [value[0] / 100])}
                        min={10}
                        max={50}
                        step={5}
                        className="flex-1"
                      />
                      <span className="w-12 text-right">{(config.patternAnalysis.concernFrequencyThreshold * 100).toFixed(0)}%</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-base">{String(tAnalytics('settings.thresholds.enhancedTitle'))}</CardTitle>
                  <CardDescription>
                    {String(tAnalytics('settings.thresholds.enhancedDescription'))}
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Label htmlFor="anomalyThreshold">{String(tAnalytics('settings.thresholds.anomalySensitivity'))}</Label>
                      {renderTooltip(String(tAnalytics('settings.tooltips.anomalySensitivity')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="anomalyThreshold"
                        value={[config.enhancedAnalysis.anomalyThreshold]}
                        onValueChange={(value) => handleSliderChange(['enhancedAnalysis', 'anomalyThreshold'], value)}
                        min={1}
                        max={3}
                        step={0.25}
                        className="flex-1"
                      />
                      <span className="w-12 text-right">{config.enhancedAnalysis.anomalyThreshold.toFixed(2)}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Label htmlFor="minSampleSize">{String(tAnalytics('settings.thresholds.minSampleSize'))}</Label>
                      {renderTooltip(String(tAnalytics('settings.tooltips.minSampleSize')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="minSampleSize"
                        value={[config.enhancedAnalysis.minSampleSize]}
                        onValueChange={(value) => handleSliderChange(['enhancedAnalysis', 'minSampleSize'], value)}
                        min={3}
                        max={15}
                        step={1}
                        className="flex-1"
                      />
                      <span className="w-12 text-right">{config.enhancedAnalysis.minSampleSize}</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="sensitivity" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="text-base">{String(tAnalytics('settings.sensitivity.title'))}</CardTitle>
                  <CardDescription>
                    {String(tAnalytics('settings.sensitivity.description'))}
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <RadioGroup 
                    value={config.alertSensitivity.level} 
                    onValueChange={handleSensitivityChange}
                  >
                    <div className="space-y-4">
                      <Label className="flex items-start space-x-3 cursor-pointer">
