                      </Label>

                      <Label className="flex items-start space-x-3 cursor-pointer">
                        <RadioGroupItem value="medium" className="mt-1" />
                        <div>
                          <p className="font-medium">{String(tAnalytics('settings.sensitivity.medium.title'))}</p>
                          <p className="text-sm text-muted-foreground">
                            {String(tAnalytics('settings.sensitivity.medium.desc'))}
                          </p>
                        </div>
                      </Label>

                      <Label className="flex items-start space-x-3 cursor-pointer">
                        <RadioGroupItem value="high" className="mt-1" />
                        <div>
                          <p className="font-medium">{String(tAnalytics('settings.sensitivity.high.title'))}</p>
                          <p className="text-sm text-muted-foreground">
                            {String(tAnalytics('settings.sensitivity.high.desc'))}
