                      <div>{String(tAnalytics('settings.sensitivity.emotion'))}: {config.alertSensitivity.emotionIntensityMultiplier}x</div>
                      <div>{String(tAnalytics('settings.sensitivity.frequency'))}: {config.alertSensitivity.frequencyMultiplier}x</div>
                      <div>{String(tAnalytics('settings.sensitivity.anomaly'))}: {config.alertSensitivity.anomalyMultiplier}x</div>
