                      {renderTooltip(String(tAnalytics('settings.tooltips.recentData')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="recentData"
                        value={[config.timeWindows.recentDataDays]}
                        onValueChange={(value) => handleSliderChange(['timeWindows', 'recentDataDays'], value)}
                        min={3}
                        max={14}
                        step={1}
                        className="flex-1"
                      />
                      <span className="w-16 text-right">{config.timeWindows.recentDataDays} {String(tAnalytics('confidence.calculation.days'))}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Label htmlFor="longTerm">{String(tAnalytics('settings.timewindows.longTerm'))}</Label>
                      {renderTooltip(String(tAnalytics('settings.tooltips.longTerm')))}
                    </div>
                    <div className="flex items-center gap-4">
                      <Slider
                        id="longTerm"
                        value={[config.timeWindows.longTermDays]}
                        onValueChange={(value) => handleSliderChange(['timeWindows', 'longTermDays'], value)}
                        min={30}
                        max={180}
                        step={30}
                        className="flex-1"
                      />
                      <span className="w-16 text-right">{config.timeWindows.longTermDays} {String(tAnalytics('confidence.calculation.days'))}</span>
