import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@/components/ui/collapsible';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useTranslation } from '@/hooks/useTranslation';
import { 
  ChevronDown, 
  ChevronUp, 
  Calculator, 
  TrendingUp, 
  Database, 
  Calendar,
  AlertCircle,
  CheckCircle,
  Target,
  BookOpen,
  Info
} from 'lucide-react';

interface DetailedConfidenceExplanationProps {
  confidence: number;
  dataPoints: number;
  timeSpanDays: number;
  rSquared: number;
  minDataPoints?: number;
  recommendedTimeSpan?: number;
}

export const DetailedConfidenceExplanation: React.FC<DetailedConfidenceExplanationProps> = ({
  confidence,
  dataPoints,
  timeSpanDays,
  rSquared,
  minDataPoints = 10,
  recommendedTimeSpan = 21
}) => {
  const { tAnalytics } = useTranslation();
  const [isExpanded, setIsExpanded] = useState(false);

  const getConfidenceLevel = (): 'low' | 'medium' | 'high' => {
    if (confidence >= 0.7) return 'high';
    if (confidence >= 0.4) return 'medium';
    return 'low';
  };

  const getDataQualityScore = () => {
    const dataScore = Math.min(dataPoints / minDataPoints, 1) * 100;
    const timeScore = Math.min(timeSpanDays / recommendedTimeSpan, 1) * 100;
    return {
      dataScore: Math.round(dataScore),
      timeScore: Math.round(timeScore),
      overallScore: Math.round((dataScore + timeScore) / 2)
    };
  };

  const getPatternStrength = () => {
    if (rSquared >= 0.7) return { level: 'strong', score: Math.round(rSquared * 100) };
    if (rSquared >= 0.4) return { level: 'moderate', score: Math.round(rSquared * 100) };
    return { level: 'weak', score: Math.round(rSquared * 100) };
  };

  const getConfidenceFactors = () => {
    const factors = [];

    if (dataPoints < minDataPoints) {
      factors.push({
        type: 'warning',
        title: String(tAnalytics('confidence.factors.insufficientData.title')),
        description: String(tAnalytics('confidence.factors.insufficientData.description', { 
          current: dataPoints, 
          needed: minDataPoints 
        })),
        impact: 'high',
        actionable: true
      });
    } else {
      factors.push({
        type: 'success',
        title: String(tAnalytics('confidence.factors.sufficientData.title')),
        description: String(tAnalytics('confidence.factors.sufficientData.description', { 
          count: dataPoints 
        })),
        impact: 'positive',
        actionable: false
      });
    }

    if (timeSpanDays < recommendedTimeSpan) {
      factors.push({
        type: 'warning',
        title: String(tAnalytics('confidence.factors.shortTimespan.title')),
        description: String(tAnalytics('confidence.factors.shortTimespan.description', { 
          current: timeSpanDays, 
          recommended: recommendedTimeSpan 
        })),
        impact: 'medium',
        actionable: true
      });
    } else {
      factors.push({
        type: 'success',
        title: String(tAnalytics('confidence.factors.adequateTimespan.title')),
        description: String(tAnalytics('confidence.factors.adequateTimespan.description', { 
          days: timeSpanDays 
        })),
        impact: 'positive',
        actionable: false
      });
    }

    const patternStrength = getPatternStrength();
    if (patternStrength.level === 'weak') {
      factors.push({
        type: 'info',
        title: String(tAnalytics('confidence.factors.weakPattern.title')),
        description: String(tAnalytics('confidence.factors.weakPattern.description', { 
          rsquared: rSquared.toFixed(3),
          percentage: patternStrength.score 
        })),
        impact: 'medium',
        actionable: true
      });
    } else {
      factors.push({
        type: 'success',
        title: String(tAnalytics(`confidence.factors.${patternStrength.level}Pattern.title`)),
        description: String(tAnalytics(`confidence.factors.${patternStrength.level}Pattern.description`, { 
          rsquared: rSquared.toFixed(3),
          percentage: patternStrength.score 
        })),
        impact: 'positive',
        actionable: false
      });
    }

    return factors;
  };

  const getActionableRecommendations = () => {
    const recommendations = [];

    if (dataPoints < minDataPoints) {
      recommendations.push(String(tAnalytics('confidence.recommendations.collectMoreData', { 
        needed: minDataPoints - dataPoints 
      })));
    }

    if (timeSpanDays < recommendedTimeSpan) {
      recommendations.push(String(tAnalytics('confidence.recommendations.extendTimespan', { 
        additional: recommendedTimeSpan - timeSpanDays 
      })));
    }

    if (rSquared < 0.4) {
      recommendations.push(String(tAnalytics('confidence.recommendations.improveDataConsistency')));
      recommendations.push(String(tAnalytics('confidence.recommendations.documentContext')));
    }

    return recommendations;
  };

  const renderCalculationBreakdown = () => {
    const qualityScores = getDataQualityScore();
    const patternStrength = getPatternStrength();

    return (
      <div className="space-y-4">
        <div className="text-sm font-medium">{String(tAnalytics('confidence.calculation.title'))}</div>

        <div className="space-y-3">
          <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
            <div className="flex items-center gap-2">
              <Database className="h-4 w-4" />
              <span className="text-sm">{String(tAnalytics('confidence.calculation.dataQuantity'))}</span>
            </div>
            <div className="text-right">
              <div className="text-sm font-medium">{qualityScores.dataScore}%</div>
              <div className="text-xs text-muted-foreground">
                {dataPoints}/{minDataPoints} {String(tAnalytics('confidence.calculation.dataPoints'))}
              </div>
            </div>
          </div>

          <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
            <div className="flex items-center gap-2">
              <Calendar className="h-4 w-4" />
              <span className="text-sm">{String(tAnalytics('confidence.calculation.timeSpan'))}</span>
            </div>
            <div className="text-right">
              <div className="text-sm font-medium">{qualityScores.timeScore}%</div>
              <div className="text-xs text-muted-foreground">
                {timeSpanDays}/{recommendedTimeSpan} {String(tAnalytics('confidence.calculation.days'))}
              </div>
            </div>
          </div>

          <div className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
            <div className="flex items-center gap-2">
              <TrendingUp className="h-4 w-4" />
              <span className="text-sm">{String(tAnalytics('confidence.calculation.patternStrength'))}</span>
            </div>
            <div className="text-right">
              <div className="text-sm font-medium">{patternStrength.score}%</div>
              <div className="text-xs text-muted-foreground">
                R = {rSquared.toFixed(3)}
              </div>
            </div>
          </div>
        </div>

        <div className="border-t pt-3">
          <div className="flex items-center justify-between p-3 bg-primary/10 rounded-lg">
            <div className="flex items-center gap-2">
              <Calculator className="h-4 w-4" />
              <span className="text-sm font-medium">{String(tAnalytics('confidence.calculation.overall'))}</span>
            </div>
            <div className="text-right">
              <div className="text-lg font-bold">{Math.round(confidence * 100)}%</div>
              <div className="text-xs text-muted-foreground">
                {String(tAnalytics(`confidence.${getConfidenceLevel()}`))}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const renderEducationalContent = () => {
    return (
      <div className="space-y-4">
        <div className="text-sm font-medium">{String(tAnalytics('confidence.education.title'))}</div>

        <div className="space-y-3">
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm flex items-center gap-2">
                <Info className="h-4 w-4" />
                {String(tAnalytics('confidence.education.whatIsConfidence.title'))}
              </CardTitle>
            </CardHeader>
            <CardContent className="pt-0 text-sm text-muted-foreground">
              {String(tAnalytics('confidence.education.whatIsConfidence.content'))}
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm flex items-center gap-2">
                <Target className="h-4 w-4" />
                {String(tAnalytics('confidence.education.whyItMatters.title'))}
              </CardTitle>
            </CardHeader>
            <CardContent className="pt-0 text-sm text-muted-foreground">
              {String(tAnalytics('confidence.education.whyItMatters.content'))}
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm flex items-center gap-2">
                <BookOpen className="h-4 w-4" />
                {String(tAnalytics('confidence.education.interpretation.title'))}
              </CardTitle>
            </CardHeader>
            <CardContent className="pt-0 space-y-2">
              <div className="text-xs">
                <Badge variant="outline" className="text-emerald-600 border-emerald-200 mr-2">
                  70-100%
                </Badge>
                <span className="text-muted-foreground">
                  {String(tAnalytics('confidence.education.interpretation.high'))}
                </span>
              </div>
              <div className="text-xs">
                <Badge variant="outline" className="text-amber-600 border-amber-200 mr-2">
                  40-69%
                </Badge>
                <span className="text-muted-foreground">
                  {String(tAnalytics('confidence.education.interpretation.medium'))}
                </span>
              </div>
              <div className="text-xs">
                <Badge variant="outline" className="text-orange-600 border-orange-200 mr-2">
                  0-39%
                </Badge>
                <span className="text-muted-foreground">
                  {String(tAnalytics('confidence.education.interpretation.low'))}
                </span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  };

  const confidenceFactors = getConfidenceFactors();
  const recommendations = getActionableRecommendations();
  const qualityScores = getDataQualityScore();

  return (
    <Card className="w-full">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg flex items-center gap-2">
            <Calculator className="h-5 w-5" />
            {String(tAnalytics('confidence.detailed.title'))}
          </CardTitle>
          <div className="flex items-center gap-2">
            <Badge 
              variant="outline" 
              className={
                getConfidenceLevel() === 'high' ? 'text-emerald-600 border-emerald-200' :
                getConfidenceLevel() === 'medium' ? 'text-amber-600 border-amber-200' :
                'text-orange-600 border-orange-200'
              }
            >
              {Math.round(confidence * 100)}% {String(tAnalytics(`confidence.${getConfidenceLevel()}`))}
            </Badge>
            <Collapsible open={isExpanded} onOpenChange={setIsExpanded}>
              <CollapsibleTrigger asChild>
                <Button variant="ghost" size="sm">
                  {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                </Button>
              </CollapsibleTrigger>
            </Collapsible>
          </div>
        </div>
      </CardHeader>

      <Collapsible open={isExpanded} onOpenChange={setIsExpanded}>
        <CollapsibleContent>
          <CardContent>
            <Tabs defaultValue="overview" className="w-full">
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="overview">{String(tAnalytics('confidence.tabs.overview'))}</TabsTrigger>
                <TabsTrigger value="calculation">{String(tAnalytics('confidence.tabs.calculation'))}</TabsTrigger>
                <TabsTrigger value="factors">{String(tAnalytics('confidence.tabs.factors'))}</TabsTrigger>
                <TabsTrigger value="education">{String(tAnalytics('confidence.tabs.education'))}</TabsTrigger>
              </TabsList>

              <TabsContent value="overview" className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <Card>
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">{String(tAnalytics('confidence.overview.dataQuality'))}</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <Progress value={qualityScores.overallScore} className="mb-2" />
                      <div className="text-xs text-muted-foreground">
                        {qualityScores.overallScore}% {String(tAnalytics('confidence.overview.complete'))}
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">{String(tAnalytics('confidence.overview.timespan'))}</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="text-lg font-bold">{timeSpanDays}</div>
                      <div className="text-xs text-muted-foreground">
                        {String(tAnalytics('confidence.overview.daysOfData'))}
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">{String(tAnalytics('confidence.overview.patterns'))}</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="text-lg font-bold">{getPatternStrength().score}%</div>
                      <div className="text-xs text-muted-foreground">
                        {String(tAnalytics(`confidence.overview.${getPatternStrength().level}Pattern`))}
                      </div>
                    </CardContent>
                  </Card>
                </div>

                {recommendations.length > 0 && (
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-sm">{String(tAnalytics('confidence.overview.recommendations'))}</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <ul className="space-y-2">
                        {recommendations.map((rec, index) => (
                          <li key={index} className="flex items-start gap-2 text-sm">
                            <Target className="h-4 w-4 mt-0.5 text-primary" />
                            <span>{rec}</span>
                          </li>
                        ))}
                      </ul>
                    </CardContent>
                  </Card>
                )}
              </TabsContent>

              <TabsContent value="calculation">
                {renderCalculationBreakdown()}
              </TabsContent>

              <TabsContent value="factors" className="space-y-4">
                {confidenceFactors.map((factor, index) => (
                  <Card key={index}>
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm flex items-center gap-2">
                        {factor.type === 'success' ? (
                          <CheckCircle className="h-4 w-4 text-emerald-600" />
                        ) : factor.type === 'warning' ? (
                          <AlertCircle className="h-4 w-4 text-amber-600" />
                        ) : (
                          <Info className="h-4 w-4 text-blue-600" />
                        )}
                        {factor.title}
                      </CardTitle>
                    </CardHeader>
                    <CardContent className="pt-0">
                      <p className="text-sm text-muted-foreground">{factor.description}</p>
                    </CardContent>
                  </Card>
                ))}
              </TabsContent>

              <TabsContent value="education">
                {renderEducationalContent()}
              </TabsContent>
            </Tabs>
          </CardContent>
        </CollapsibleContent>
      </Collapsible>
    </Card>
  );
};

import { useEffect, useRef, useState } from 'react';

interface CapturedError {
  message: string;
  details?: string;
  timestamp: number;
}

export const DevErrorBanner = () => {
  const [lastError, setLastError] = useState<CapturedError | null>(null);
  const [errorCount, setErrorCount] = useState(0);
  const [isCollapsed, setIsCollapsed] = useState(true);
  const [isHidden, setIsHidden] = useState(false);
  const originalConsoleError = useRef<typeof console.error | null>(null);

  useEffect(() => {
    // Intercept console.error
    originalConsoleError.current = console.error;
    console.error = (...args: unknown[]) => {
      try {
        const msg = args.map(a => {
          if (a instanceof Error) return `${a.name}: ${a.message}`;
          if (typeof a === 'object') return JSON.stringify(a);
          return String(a);
        }).join(' ');
        setLastError({ message: msg, timestamp: Date.now() });
        setErrorCount(c => c + 1);
      } catch {}
      // Always forward to the original console
      originalConsoleError.current?.apply(console, args as []);
    };

    const onWindowError = (e: ErrorEvent) => {
      setLastError({ message: e.message || 'Unhandled error', details: e.error?.stack, timestamp: Date.now() });
      setErrorCount(c => c + 1);
    };
    const onUnhandledRejection = (e: PromiseRejectionEvent) => {
      const reason = (e as any).reason;
      const msg = reason?.message || String(reason) || 'Unhandled promise rejection';
      const stack = reason?.stack ? String(reason.stack) : undefined;
      setLastError({ message: msg, details: stack, timestamp: Date.now() });
      setErrorCount(c => c + 1);
    };

    // eslint-disable-next-line no-restricted-syntax -- Added within useEffect with proper cleanup
    window.addEventListener('error', onWindowError);
    // eslint-disable-next-line no-restricted-syntax -- Added within useEffect with proper cleanup
    window.addEventListener('unhandledrejection', onUnhandledRejection);

    return () => {
      // Restore console.error
      if (originalConsoleError.current) {
        console.error = originalConsoleError.current;
      }
      window.removeEventListener('error', onWindowError);
      window.removeEventListener('unhandledrejection', onUnhandledRejection);
    };
  }, []);

  if (isHidden || !lastError) return null;

  return (
    <div className="fixed top-0 left-0 right-0 z-[1000]">
      <div className="mx-auto max-w-6xl m-2 rounded-md border border-destructive/40 bg-destructive/10 backdrop-blur px-3 py-2 text-sm">
        <div className="flex items-start justify-between gap-3">
          <div className="min-w-0">
            <div className="font-medium text-destructive">Dev error captured ({errorCount})</div>
            <div className="mt-1 truncate text-foreground/90" title={lastError.message}>{lastError.message}</div>
            {!isCollapsed && lastError.details && (
              <pre className="mt-2 max-h-40 overflow-auto whitespace-pre-wrap rounded bg-background/60 p-2 text-xs text-muted-foreground">{lastError.details}</pre>
            )}
          </div>
          <div className="shrink-0 space-x-2">
            <button
              type="button"
              className="rounded border border-border/60 bg-background px-2 py-1 text-xs hover:bg-accent"
              onClick={() => setIsCollapsed(c => !c)}
            >
              {isCollapsed ? 'Expand' : 'Collapse'}
            </button>
            <button
              type="button"
              className="rounded border border-border/60 bg-background px-2 py-1 text-xs hover:bg-accent"
              onClick={() => setIsHidden(true)}
            >
              Dismiss
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { EmotionEntry } from "@/types/student";
import { Heart, Frown, Angry, Smile, Zap, Sun } from "lucide-react";
import { useTranslation } from "@/hooks/useTranslation";
import { logger } from '@/lib/logger';

interface EmotionTrackerProps {
  onEmotionAdd: (emotion: Omit<EmotionEntry, 'id' | 'timestamp'>) => void;
  studentId: string;
}

const emotions = [
  { type: 'happy' as const, icon: Smile, color: 'emotion-happy' },
  { type: 'calm' as const, icon: Heart, color: 'emotion-calm' },
  { type: 'excited' as const, icon: Zap, color: 'emotion-excited' },
  { type: 'sad' as const, icon: Frown, color: 'emotion-sad' },
  { type: 'anxious' as const, icon: Sun, color: 'emotion-anxious' },
  { type: 'angry' as const, icon: Angry, color: 'emotion-angry' },
];

// Sub-emotions for each primary emotion
const subEmotions: Record<string, string[]> = {
  happy: ['joyful', 'content', 'cheerful', 'pleased', 'delighted'],
  calm: ['peaceful', 'relaxed', 'serene', 'tranquil', 'centered'],
  excited: ['energetic', 'enthusiastic', 'thrilled', 'eager', 'animated'],
  sad: ['disappointed', 'lonely', 'grieving', 'melancholy', 'dejected'],
  anxious: ['worried', 'nervous', 'fearful', 'stressed', 'panicked'],
  angry: ['frustrated', 'irritated', 'furious', 'annoyed', 'resentful']
};

/**
 * EmotionTracker Component
 * 
 * Provides an accessible interface for tracking and recording student emotions
 * with intensity levels, triggers, and contextual information.
 * 
 * @component
 * @param {EmotionTrackerProps} props - Component props
 * @param {Function} props.onEmotionAdd - Callback when emotion is added
 * @param {string} props.studentId - ID of the student being tracked
 */
export const EmotionTracker = ({ onEmotionAdd, studentId }: EmotionTrackerProps) => {
  const { tTracking, tCommon } = useTranslation();
  const [selectedEmotion, setSelectedEmotion] = useState<string>('');
  const [selectedSubEmotion, setSelectedSubEmotion] = useState<string>('');
  const [intensity, setIntensity] = useState<number>(3);
  const [duration, setDuration] = useState<number>(0);
  const [escalationPattern, setEscalationPattern] = useState<'sudden' | 'gradual' | 'unknown'>('unknown');
  const [notes, setNotes] = useState('');
  const [triggers, setTriggers] = useState<string[]>([]);
  const [newTrigger, setNewTrigger] = useState('');

  const handleAddTrigger = () => {
    if (newTrigger.trim() && !triggers.includes(newTrigger.trim())) {
      setTriggers([...triggers, newTrigger.trim()]);
      setNewTrigger('');
    }
  };

  const handleRemoveTrigger = (trigger: string) => {
    setTriggers(triggers.filter(t => t !== trigger));
  };

  const handleSubmit = () => {
    if (!selectedEmotion) {
      logger.warn('Attempted to submit emotion without selection');
      return;
    }

    onEmotionAdd({
      studentId,
      emotion: selectedEmotion as EmotionEntry['emotion'],
      subEmotion: selectedSubEmotion || undefined,
      intensity: intensity as EmotionEntry['intensity'],
      duration: duration > 0 ? duration : undefined,
      escalationPattern: escalationPattern !== 'unknown' ? escalationPattern : undefined,
      notes: notes.trim() || undefined,
      triggers: triggers.length > 0 ? triggers : undefined,
    });

    // Reset form
    setSelectedEmotion('');
    setSelectedSubEmotion('');
    setIntensity(3);
    setDuration(0);
    setEscalationPattern('unknown');
    setNotes('');
    setTriggers([]);
  };

  return (
    <Card className="font-dyslexia bg-gradient-card border-0">
      <CardHeader>
        <CardTitle className="text-xl text-foreground">{String(tTracking('emotions.title'))}</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Emotion Selection */}
        <div>
          <h3 className="text-sm font-medium text-foreground mb-3">{String(tTracking('emotions.selectEmotion'))}</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {emotions.map((emotion) => {
              const Icon = emotion.icon;
              return (
                <Button
                  key={emotion.type}
                  variant={selectedEmotion === emotion.type ? "default" : "outline"}
                  className={`h-20 flex-col gap-2 font-dyslexia hover-lift press-scale transition-all duration-300 ${
                    selectedEmotion === emotion.type 
                      ? 'bg-gradient-primary shadow-glow animate-bounce-in' 
                      : 'hover:scale-105 animate-fade-in hover:shadow-soft'
                  }`}
                  onClick={() => setSelectedEmotion(emotion.type)}
                  aria-label={`Select ${String(tTracking(`emotions.types.${emotion.type}`))}`}
                  aria-pressed={selectedEmotion === emotion.type}
                >
                  <Icon className="h-6 w-6 transform transition-transform duration-200 hover:scale-110" />
                  <span className="text-sm">{String(tTracking(`emotions.types.${emotion.type}`))}</span>
                </Button>
              );
            })}
          </div>
        </div>

        {/* Sub-emotion Selection */}
        {selectedEmotion && subEmotions[selectedEmotion] && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">Specific Feeling (Optional)</h3>
            <div className="flex flex-wrap gap-2">
              {subEmotions[selectedEmotion].map((subEmotion) => (
                <Badge
                  key={subEmotion}
                  variant={selectedSubEmotion === subEmotion ? "default" : "outline"}
                  className="cursor-pointer font-dyslexia hover-lift transition-all duration-200"
                  onClick={() => setSelectedSubEmotion(selectedSubEmotion === subEmotion ? '' : subEmotion)}
                  role="button"
                  tabIndex={0}
                  aria-pressed={selectedSubEmotion === subEmotion}
                  aria-label={`Select ${subEmotion}`}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      setSelectedSubEmotion(selectedSubEmotion === subEmotion ? '' : subEmotion);
                    }
                  }}
                >
                  {subEmotion}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Intensity Scale */}
        {selectedEmotion && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">
              {String(tTracking('emotions.intensity'))}: {intensity}/5
            </h3>
            <div className="flex gap-2">
              {[1, 2, 3, 4, 5].map((level) => (
                <Button
                  key={level}
                  variant={intensity === level ? "default" : "outline"}
                  size="sm"
                  className={`w-12 h-12 rounded-full font-dyslexia hover-lift press-scale transition-all duration-200 hover:shadow-soft ${
                    intensity === level ? 'bg-gradient-primary animate-bounce-in' : ''
                  }`}
                  onClick={() => setIntensity(level)}
                  title={String(tTracking(`emotions.intensityLevels.${level}`))}
                  aria-label={`Intensity level ${level}`}
                  aria-pressed={intensity === level}
                >
                  {level}
                </Button>
              ))}
            </div>
          </div>
        )}

        {/* Duration */}
        {selectedEmotion && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">Duration (minutes)</h3>
            <div className="flex gap-2">
              <input
                type="number"
                value={duration}
                onChange={(e) => {
                  const value = e.target.value;
                  // Allow empty string for better UX while typing
                  if (value === '') {
                    setDuration(0);
                    return;
                  }
                  // Parse and validate the number
                  const parsed = parseInt(value, 10);
                  if (!isNaN(parsed)) {
                    setDuration(Math.max(0, Math.min(999, parsed))); // Cap at 999 minutes
                  }
                }}
                placeholder="How long did it last?"
                className="w-32 px-3 py-2 border border-border rounded-lg font-dyslexia bg-input focus:ring-2 focus:ring-ring focus:border-transparent"
                min="0"
                max="999"
                aria-label="Duration in minutes"
                aria-describedby="duration-help"
              />
              <div className="flex gap-1">
                {[5, 15, 30, 60].map((minutes) => (
                  <Button
                    key={minutes}
                    variant="outline"
                    size="sm"
                    onClick={() => setDuration(minutes)}
                    className="font-dyslexia"
                  >
                    {minutes < 60 ? `${minutes}m` : '1h'}
                  </Button>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Escalation Pattern */}
        {selectedEmotion && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">How did it develop?</h3>
            <div className="flex gap-2">
              <Button
                variant={escalationPattern === 'sudden' ? "default" : "outline"}
                size="sm"
                onClick={() => setEscalationPattern('sudden')}
                className="font-dyslexia"
              >
                Sudden
              </Button>
              <Button
                variant={escalationPattern === 'gradual' ? "default" : "outline"}
                size="sm"
                onClick={() => setEscalationPattern('gradual')}
                className="font-dyslexia"
              >
                Gradual
              </Button>
              <Button
                variant={escalationPattern === 'unknown' ? "default" : "outline"}
                size="sm"
                onClick={() => setEscalationPattern('unknown')}
                className="font-dyslexia"
              >
                Unknown
              </Button>
            </div>
          </div>
        )}

        {/* Triggers */}
        <div>
          <h3 className="text-sm font-medium text-foreground mb-3">Utlsere (Valgfritt)</h3>
          <div className="flex gap-2 mb-2">
            <Label htmlFor="emotion-new-trigger" className="sr-only">
              {String(tTracking('emotions.addTrigger'))}
            </Label>
            <input
              id="emotion-new-trigger"
              type="text"
              value={newTrigger}
              onChange={(e) => setNewTrigger(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddTrigger()}
              placeholder="Legg til en utlser..."
              className="flex-1 px-3 py-2 border border-border rounded-lg font-dyslexia bg-input focus:ring-2 focus:ring-ring focus:border-transparent"
            />
            <Button onClick={handleAddTrigger} size="sm" variant="outline">
              {String(tCommon('buttons.add'))}
            </Button>
          </div>
          <div className="flex flex-wrap gap-2">
            {triggers.map((trigger) => (
              <Badge
                key={trigger}
                variant="secondary"
                className="font-dyslexia cursor-pointer"
                onClick={() => handleRemoveTrigger(trigger)}
                role="button"
                tabIndex={0}
                aria-label={`Remove trigger ${trigger}`}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault();
                    handleRemoveTrigger(trigger);
                  }
                }}
              >
                {trigger} 
              </Badge>
            ))}
          </div>
        </div>

        {/* Notes */}
        <div>
          <h3 className="text-sm font-medium text-foreground mb-3">{String(tTracking('emotions.notes'))}</h3>
          <Label htmlFor="emotion-notes" className="sr-only">
            {String(tTracking('emotions.notes'))}
          </Label>
          <Textarea
            id="emotion-notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            placeholder="Ytterligere observasjoner..."
            className="font-dyslexia bg-input border-border focus:ring-ring"
            rows={3}
          />
        </div>

        <Button 
          onClick={handleSubmit}
          disabled={!selectedEmotion}
          className="w-full font-dyslexia bg-gradient-primary hover:opacity-90 transition-all duration-200"
        >
          {String(tTracking('emotions.addEmotion'))}
        </Button>
      </CardContent>
    </Card>
  );
};

