import React, { memo, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { TrackingEntry } from '@/types/student';
import { useDataPagination } from '@/hooks/useDataPagination';
import { ChevronsLeft, ChevronLeft, ChevronRight, ChevronsRight } from 'lucide-react';
import { PageButton } from '@/components/ui/PageButton';

interface PaginatedSessionsListProps {
  sessions: TrackingEntry[];
  title?: string;
}

/**
 * PaginatedSessionsList Component
 * 
 * Displays a paginated list of tracking entries (sessions) with detailed information
 * including emotions, sensory inputs, and environmental data.
 * 
 * @component
 * @param {PaginatedSessionsListProps} props - Component props
 * @param {TrackingEntry[]} props.sessions - Array of tracking entries to display
 * @param {string} [props.title="Sessions in Selected Period"] - Title for the sessions list
 * @returns {React.ReactElement} Rendered component
 */
export const PaginatedSessionsList: React.FC<PaginatedSessionsListProps> = memo(({
  sessions,
  title = "Sessions in Selected Period"
}) => {
  const {
    paginatedData,
    currentPage,
    pageSize,
    totalItems,
    totalPages,
    goToPage,
    goToNextPage,
    goToPreviousPage,
    goToFirstPage,
    goToLastPage,
    changePageSize,
    hasNextPage,
    hasPreviousPage,
    startIndex,
    endIndex,
    isEmpty,
    isLoading,  // Added from new hook implementation
  } = useDataPagination(sessions, { initialPageSize: 10 });

  const handlePageSizeChange = useCallback((value: string) => {
    changePageSize(parseInt(value));
  }, [changePageSize]);

  if (isEmpty && !isLoading) {
    return (
      <Card className="bg-gradient-card border-0 shadow-soft">
        <CardContent className="py-16 text-center text-muted-foreground">
          <p>No sessions available in the selected period.</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-gradient-card border-0 shadow-soft">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span>{title}</span>
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <span>Showing {startIndex}-{endIndex} of {totalItems}</span>
            <Select
              value={pageSize.toString()}
              onValueChange={handlePageSizeChange}
            >
              <SelectTrigger className="w-20 h-8">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="5">5</SelectItem>
                <SelectItem value="10">10</SelectItem>
                <SelectItem value="20">20</SelectItem>
                <SelectItem value="50">50</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {isLoading ? (
          <div className="text-center p-8 text-muted-foreground">
            Loading sessions...
          </div>
        ) : (
          <div>
            {/* Sessions List */}
            <div className="space-y-4">
              {paginatedData.map((entry) => (
                <div key={entry.id} className="border-l-4 border-primary pl-4 py-2 hover:bg-muted/50 transition-colors rounded-r">
                  <div className="flex items-center justify-between mb-1">
                    <span className="font-medium text-foreground">
                      {entry.timestamp.toLocaleDateString()} at {entry.timestamp.toLocaleTimeString()}
                    </span>
                    <div className="flex gap-2">
                      <Badge variant="outline" className="text-xs">
                        {entry.emotions.length} emotions
                      </Badge>
                      <Badge variant="outline" className="text-xs">
                        {entry.sensoryInputs.length} sensory
                      </Badge>
                      {entry.environmentalData && (
                        <Badge variant="outline" className="text-xs">
                          Environmental data
                        </Badge>
                      )}
                    </div>
                  </div>
                  {entry.generalNotes && (
                    <p className="text-sm text-muted-foreground mt-1 line-clamp-2">
                      {entry.generalNotes}
                    </p>
                  )}
                  {entry.emotions.length > 0 && (
                    <div className="flex flex-wrap gap-1 mt-2">
                      {entry.emotions.slice(0, 3).map((emotion, idx) => (
                        <Badge key={idx} variant="secondary" className="text-xs">
                          {emotion.emotion} ({emotion.intensity})
                        </Badge>
                      ))}
                      {entry.emotions.length > 3 && (
                        <Badge variant="outline" className="text-xs">
                          +{entry.emotions.length - 3} more
                        </Badge>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* Pagination Controls */}
            {totalPages > 1 && (
              <div className="flex items-center justify-between pt-4 border-t">
                <div className="text-sm text-muted-foreground">
                  Page {currentPage} of {totalPages}
                </div>

                <div className="flex items-center gap-1">
                  <Button
                    variant="ghost"
                    size="icon"
                    aria-label="First page"
                    onClick={goToFirstPage}
                    disabled={!hasPreviousPage}
                  >
                    <ChevronsLeft className="h-4 w-4" />
                  </Button>

                  <Button
                    variant="ghost"
                    size="icon"
                    aria-label="Previous page"
                    onClick={goToPreviousPage}
                    disabled={!hasPreviousPage}
                  >
                    <ChevronLeft className="h-4 w-4" />
                  </Button>

                  {/* Page number buttons */}
                  {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                    let pageNum;
                    if (totalPages <= 5) {
                      pageNum = i + 1;
                    } else if (currentPage <= 3) {
                      pageNum = i + 1;
                    } else if (currentPage >= totalPages - 2) {
                      pageNum = totalPages - 4 + i;
                    } else {
                      pageNum = currentPage - 2 + i;
                    }

                    return (
                      <PageButton
                        key={pageNum}
                        pageNum={pageNum}
                        isCurrent={currentPage === pageNum}
                        onClick={goToPage}
                      />
                    );
                  })}

                  <Button
                    variant="ghost"
                    size="icon"
                    aria-label="Next page"
                    onClick={goToNextPage}
                    disabled={!hasNextPage}
                  >
                    <ChevronRight className="h-4 w-4" />
                  </Button>

                  <Button
                    variant="ghost"
                    size="icon"
                    aria-label="Last page"
                    onClick={goToLastPage}
                    disabled={!hasNextPage}
                  >
                    <ChevronsRight className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
});

// Add display name for React DevTools and debugging
PaginatedSessionsList.displayName = 'PaginatedSessionsList';

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { AlertTriangle, TrendingUp, Calendar, Target } from 'lucide-react';
import { useTranslation } from '@/hooks/useTranslation';

interface PatternDetectionEmptyStateProps {
  dataPoints: number;
  daysWithData: number;
  onCollectData?: () => void;
  className?: string;
}

export const PatternDetectionEmptyState = ({
  dataPoints,
  daysWithData,
  onCollectData,
  className
}: PatternDetectionEmptyStateProps) => {
  const { tCommon } = useTranslation();

  const getGuidanceMessage = () => {
    if (dataPoints === 0) {
      return {
        title: "Ingen data registrert enn",
        description: "Start med  registrere flelser og sensoriske opplevelser for  oppdage mnstre.",
        actionText: "Registrer frste oppfring",
        icon: AlertTriangle,
        color: "text-orange-600"
      };
    }

    if (dataPoints < 3) {
      return {
        title: "Trenger mer data for mnstergjenkjenning",
        description: `Du har ${dataPoints} datapunkter. Registrer minst ${3 - dataPoints} til for  begynne  se mnstre.`,
        actionText: "Fortsett  registrere data",
        icon: Target,
        color: "text-blue-600"
      };
    }

    if (daysWithData < 7) {
      return {
        title: "Trenger data over lengre tid",
        description: `Du har ${daysWithData} dager med data. Samle data over minst 7 dager for  identifisere trender.`,
        actionText: "Fortsett daglig registrering",
        icon: Calendar,
        color: "text-purple-600"
      };
    }

    return {
      title: "Ingen tydelige mnstre oppdaget enn",
      description: "Data er tilgjengelig, men ingen sterke mnstre er funnet. Dette kan vre positivt - det kan bety stabil tilstand.",
      actionText: "Fortsett  overvke",
      icon: TrendingUp,
      color: "text-green-600"
    };
  };

  const guidance = getGuidanceMessage();
  const Icon = guidance.icon;

  const requirements = [
    { label: "Minimum datapunkter", current: dataPoints, target: 3, met: dataPoints >= 3 },
    { label: "Dager med data", current: daysWithData, target: 7, met: daysWithData >= 7 },
    { label: "Regelmessighet", current: "Variabel", target: "Daglig", met: false }
  ];

  return (
    <Card className={className}>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Icon className={`h-5 w-5 ${guidance.color}`} />
          {guidance.title}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <p className="text-muted-foreground">
          {guidance.description}
        </p>

        {/* Requirements Progress */}
        <div className="space-y-3">
          <h4 className="text-sm font-medium">Krav for mnstergjenkjenning:</h4>
          {requirements.map((req, index) => (
            <div key={index} className="flex items-center justify-between py-2 px-3 rounded-lg bg-muted/50">
              <span className="text-sm">{req.label}</span>
              <div className="flex items-center gap-2">
                <span className="text-sm text-muted-foreground">
                  {req.current} / {req.target}
                </span>
                <div className={`w-3 h-3 rounded-full ${req.met ? 'bg-green-500' : 'bg-muted-foreground/30'}`} />
              </div>
            </div>
          ))}
        </div>

        {/* Quick Tips */}
        <div className="p-4 bg-blue-50 dark:bg-blue-950/20 rounded-lg">
          <h4 className="text-sm font-medium text-blue-900 dark:text-blue-100 mb-2">
            Tips for bedre mnstergjenkjenning:
          </h4>
          <ul className="text-sm text-blue-800 dark:text-blue-200 space-y-1">
            <li> Registrer data p samme tid hver dag</li>
            <li> Inkluder bde flelser og sensoriske opplevelser</li>
            <li> Legg merke til miljfaktorer (sty, lys, aktivitet)</li>
            <li> Vr konsistent i minst 2-3 uker</li>
          </ul>
        </div>

        {/* Action Button */}
        {onCollectData && (
          <Button 
            onClick={onCollectData}
            className="w-full bg-gradient-primary hover:opacity-90"
          >
            <TrendingUp className="h-4 w-4 mr-2" />
            {guidance.actionText}
          </Button>
        )}
      </CardContent>
    </Card>
  );
};

