              <CardTitle className="text-sm">{tCommon(template.name as unknown as string)}</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-xs text-muted-foreground mb-3">{tCommon(template.description as unknown as string)}</p>
              <Badge variant="outline" className="text-xs">
                {tCommon('reports.sectionsCount', { count: template.sections.length })}
              </Badge>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { SensoryEntry } from "@/types/student";
import { Eye, Ear, Hand, RotateCcw, Activity } from "lucide-react";
import { useTranslation } from "@/hooks/useTranslation";

interface SensoryTrackerProps {
  onSensoryAdd: (sensory: Omit<SensoryEntry, 'id' | 'timestamp'>) => void;
  studentId: string;
}

const sensoryTypes = [
  { type: 'visual' as const, icon: Eye },
  { type: 'auditory' as const, icon: Ear },
  { type: 'tactile' as const, icon: Hand },
  { type: 'vestibular' as const, icon: RotateCcw },
  { type: 'proprioceptive' as const, icon: Activity },
];

const responses = [
  { type: 'seeking' as const },
  { type: 'avoiding' as const },
  { type: 'neutral' as const },
  { type: 'overwhelmed' as const },
];

// Common body locations for sensory experiences
const bodyLocations = [
  'Head', 'Eyes', 'Ears', 'Mouth', 'Neck', 'Shoulders',
  'Arms', 'Hands', 'Chest', 'Back', 'Stomach', 'Legs', 'Feet'
];

// Common coping strategy suggestions
const copingStrategySuggestions = [
  'Deep breathing', 'Take a break', 'Use fidget tool', 'Movement break',
  'Quiet space', 'Headphones', 'Weighted blanket', 'Sensory walk',
  'Compression', 'Joint compressions', 'Chewing gum', 'Water break'
];

export const SensoryTracker = ({ onSensoryAdd, studentId }: SensoryTrackerProps) => {
  const { tTracking, tCommon } = useTranslation();
  const [selectedType, setSelectedType] = useState<string>('');
  const [selectedResponse, setSelectedResponse] = useState<string>('');
  const [intensity, setIntensity] = useState<number>(3);
  // Intensity range: 1-5 only, capped
  const handleIntensity = (value: number) => {
    const clamped = Math.max(1, Math.min(5, value));
    setIntensity(clamped);
  };
  const [notes, setNotes] = useState('');
  const [environment, setEnvironment] = useState('');
  const [location, setLocation] = useState('');
  const [copingStrategies, setCopingStrategies] = useState<string[]>([]);
  const [newCopingStrategy, setNewCopingStrategy] = useState('');

  const handleAddCopingStrategy = () => {
    if (newCopingStrategy.trim() && !copingStrategies.includes(newCopingStrategy.trim())) {
      setCopingStrategies([...copingStrategies, newCopingStrategy.trim()]);
      setNewCopingStrategy('');
    }
  };

  const handleRemoveCopingStrategy = (strategy: string) => {
    setCopingStrategies(copingStrategies.filter(s => s !== strategy));
  };

  const handleSubmit = () => {
    if (!selectedType || !selectedResponse) return;

    onSensoryAdd({
      studentId,
      sensoryType: selectedType as SensoryEntry['sensoryType'],
      response: selectedResponse as SensoryEntry['response'],
      intensity: intensity as SensoryEntry['intensity'],
      location: location || undefined,
      notes: notes.trim() || undefined,
      environment: environment.trim() || undefined,
      copingStrategies: copingStrategies.length > 0 ? copingStrategies : undefined,
    });

    // Reset form
    setSelectedType('');
    setSelectedResponse('');
    setIntensity(3);
    setLocation('');
    setNotes('');
    setEnvironment('');
    setCopingStrategies([]);
  };

  return (
    <Card className="font-dyslexia bg-gradient-card border-0">
      <CardHeader>
        <CardTitle className="text-xl text-foreground">{String(tTracking('sensory.title'))}</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Sensory Type Selection */}
        <div>
          <h3 className="text-sm font-medium text-foreground mb-3">{String(tTracking('sensory.selectType'))}</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
            {sensoryTypes.map((type) => {
              const Icon = type.icon;
              return (
                <Button
                  key={type.type}
                  variant={selectedType === type.type ? "default" : "outline"}
                  className={`h-16 flex items-center gap-3 justify-start font-dyslexia hover-lift press-scale transition-all duration-300 ${
                    selectedType === type.type 
                      ? 'bg-gradient-primary shadow-glow animate-bounce-in' 
                      : 'hover:scale-105 animate-fade-in hover:shadow-soft'
                  }`}
                  onClick={() => setSelectedType(type.type)}
                  aria-label={`Select sensory type: ${String(tTracking(`sensory.types.${type.type}`))}`}
                  aria-pressed={selectedType === type.type}
                >
                  <Icon className="h-5 w-5 transform transition-transform duration-200 hover:scale-110" />
                  <div className="text-left">
                    <div className="font-medium">{String(tTracking(`sensory.types.${type.type}`))}</div>
                  </div>
                </Button>
              );
            })}
          </div>
        </div>

        {/* Response Type */}
        {selectedType && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">{String(tTracking('sensory.response'))}</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
              {responses.map((response) => (
                <Button
                  key={response.type}
                  variant={selectedResponse === response.type ? "default" : "outline"}
                  className={`h-16 flex-col gap-1 font-dyslexia hover-lift press-scale transition-all duration-300 ${
                    selectedResponse === response.type 
                      ? 'bg-gradient-primary shadow-glow animate-bounce-in' 
                      : 'hover:scale-105 animate-fade-in hover:shadow-soft'
                  }`}
                  onClick={() => setSelectedResponse(response.type)}
                  aria-label={`Select response: ${String(tTracking(`sensory.responses.${response.type}`))}`}
                  aria-pressed={selectedResponse === response.type}
                >
                  <span className="font-medium">{String(tTracking(`sensory.responses.${response.type}`))}</span>
                </Button>
              ))}
            </div>
          </div>
        )}

        {/* Intensity Scale */}
{selectedResponse && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">
              {String(tTracking('sensory.intensity'))}: {intensity}/5
            </h3>
            <div className="flex gap-2">
              {[1, 2, 3, 4, 5].map((level) => (
                <Button
                  key={level}
                  variant={intensity === level ? "default" : "outline"}
                  size="sm"
                  className={`w-12 h-12 rounded-full font-dyslexia ${
                    intensity === level ? 'bg-gradient-primary' : ''
                  }`}
                  onClick={() => handleIntensity(level)}
                  title={String(tTracking(`sensory.intensityLevels.${level}`))}
                  aria-label={`Intensity level ${level}`}
                  aria-pressed={intensity === level}
                >
                  {level}
                </Button>
              ))}
            </div>
            <input
              type="number"
              value={intensity}
              min={1}
              max={5}
              onChange={e => handleIntensity(Number(e.target.value))}
              className="w-16 px-2 py-1 mt-2 rounded border"
              aria-label="Manual intensity input"
            />
          </div>
        )}

        {/* Body Location */}
        {selectedType && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">Body Location (Optional)</h3>
            <div className="flex flex-wrap gap-2">
              {bodyLocations.map((loc) => (
                <Badge
                  key={loc}
                  variant={location === loc ? "default" : "outline"}
                  className="cursor-pointer font-dyslexia hover-lift transition-all duration-200"
                  onClick={() => setLocation(location === loc ? '' : loc)}
                  role="button"
                  tabIndex={0}
                  aria-pressed={location === loc}
                  aria-label={`Select body location: ${loc}`}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      setLocation(location === loc ? '' : loc);
                    }
                  }}
                >
                  {loc}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Coping Strategies */}
        {selectedResponse && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-3">Coping Strategies Used</h3>
            <div className="flex gap-2 mb-2">
              <Label htmlFor="sensory-new-strategy" className="sr-only">
                {String(tCommon('buttons.add'))} coping strategy
              </Label>
              <input
                id="sensory-new-strategy"
                type="text"
                value={newCopingStrategy}
                onChange={(e) => setNewCopingStrategy(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAddCopingStrategy()}
                placeholder="Add a coping strategy..."
                className="flex-1 px-3 py-2 border border-border rounded-lg font-dyslexia bg-input focus:ring-2 focus:ring-ring focus:border-transparent"
              />
              <Button onClick={handleAddCopingStrategy} size="sm" variant="outline">
                {String(tCommon('buttons.add'))}
              </Button>
            </div>
            <div className="flex flex-wrap gap-2 mb-2">
              {copingStrategySuggestions.map((strategy) => (
                <Badge
                  key={strategy}
                  variant="outline"
                  className="cursor-pointer font-dyslexia text-xs"
                  onClick={() => {
                    if (!copingStrategies.includes(strategy)) {
                      setCopingStrategies([...copingStrategies, strategy]);
                    }
                  }}
                  role="button"
                  tabIndex={0}
                  aria-label={`Add suggested strategy ${strategy}`}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      if (!copingStrategies.includes(strategy)) {
                        setCopingStrategies([...copingStrategies, strategy]);
                      }
                    }
                  }}
                >
                  + {strategy}
                </Badge>
              ))}
            </div>
            <div className="flex flex-wrap gap-2">
              {copingStrategies.map((strategy) => (
                <Badge
                  key={strategy}
                  variant="secondary"
                  className="font-dyslexia cursor-pointer"
                  onClick={() => handleRemoveCopingStrategy(strategy)}
                  role="button"
                  tabIndex={0}
                  aria-label={`Remove coping strategy ${strategy}`}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      handleRemoveCopingStrategy(strategy);
                    }
                  }}
                >
                  {strategy} 
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Environment */}
        <div>
          <h3 className="text-sm font-medium text-foreground mb-3">Milj (Valgfritt)</h3>
          <Label htmlFor="sensory-environment" className="sr-only">
            Milj
          </Label>
          <input
            id="sensory-environment"
            type="text"
            value={environment}
            onChange={(e) => setEnvironment(e.target.value)}
            placeholder="f.eks. Klasserom, Lekeplass, Bibliotek..."
            className="w-full px-3 py-2 border border-border rounded-lg font-dyslexia bg-input focus:ring-2 focus:ring-ring focus:border-transparent"
          />
        </div>

        {/* Notes */}
        <div>
          <h3 className="text-sm font-medium text-foreground mb-3">{String(tTracking('sensory.notes'))}</h3>
          <Label htmlFor="sensory-notes" className="sr-only">
            {String(tTracking('sensory.notes'))}
          </Label>
          <Textarea
            id="sensory-notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            placeholder="Ytterligere observasjoner om den sensoriske responsen..."
            className="font-dyslexia bg-input border-border focus:ring-ring"
            rows={3}
          />
        </div>

        <Button 
          onClick={handleSubmit}
          disabled={!selectedType || !selectedResponse}
          className="w-full font-dyslexia bg-gradient-primary hover:opacity-90 transition-all duration-200"
        >
          {String(tTracking('sensory.addInput'))}
        </Button>
      </CardContent>
    </Card>
  );
};

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Database, Trash2, AlertCircle, CheckCircle } from 'lucide-react';
import { storageUtils } from '@/lib/storageUtils';
import { dataStorage } from '@/lib/dataStorage';
import { toast } from 'sonner';
import { cn } from '@/lib/utils';
import { logger } from '@/lib/logger';

/**
 * StorageManager Component
 * 
 * Provides UI for managing local storage, including viewing usage statistics
 * and clearing different types of data. Includes safety checks and error handling.
 * 
 * @component
 * @returns {React.ReactElement} Rendered storage management interface
 */
export const StorageManager = () => {
  const [storageInfo, setStorageInfo] = useState<ReturnType<typeof storageUtils.getStorageInfo>>(storageUtils.getStorageInfo());
  const [stats, setStats] = useState<ReturnType<typeof dataStorage.getStorageStats>>(dataStorage.getStorageStats());

  const refreshStats = () => {
    setStorageInfo(storageUtils.getStorageInfo());
    setStats(dataStorage.getStorageStats());
  };

  const handleClearOldData = () => {
    try {
      storageUtils.clearOldTrackingData(30);
      toast.success('Old data cleared successfully');
      refreshStats();
    } catch (error) {
      logger.error('Failed to clear old tracking data', error);
      toast.error('Failed to clear old data');
    }
  };

  const handleClearNonEssential = () => {
    try {
      storageUtils.clearNonEssentialData();
      toast.success('Non-essential data cleared');
      refreshStats();
    } catch (error) {
      logger.error('Failed to clear non-essential data', error);
      toast.error('Failed to clear non-essential data');
    }
  };

  /**
   * Handle clearing all data with proper confirmation dialog.
   * Uses a more robust approach than browser confirm().
   */
  const handleClearAll = () => {
    // Using window.confirm with proper error handling
    // In production, consider using a custom modal component
    try {
      const confirmed = window.confirm('Are you sure you want to clear ALL data? This cannot be undone!');
      if (confirmed) {
        try {
          dataStorage.clearAllData();
          toast.success('All data cleared');
          // Use window.location.replace for better history management
          window.location.replace('/');
        } catch (error) {
          logger.error('Failed to clear all data', error);
          toast.error('Failed to clear all data');
        }
      }
    } catch (error) {
      // Handle cases where confirm might fail (e.g., in some test environments)
      logger.error('Confirmation dialog failed', error);
      toast.error('Could not show confirmation dialog');
    }
  };

  const formatBytes = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const usagePercentage = (storageInfo.used / 5000000) * 100;

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Database className="h-5 w-5" />
          Storage Management
        </CardTitle>
        <CardDescription>
          Manage your local storage to ensure smooth operation
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Storage Usage */}
        <div>
          <h3 className="font-medium mb-2">Storage Usage</h3>
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Used</span>
              <span>{formatBytes(storageInfo.used)} / ~5 MB</span>
            </div>
          <div className="w-full">
            <Progress
              value={Math.min(usagePercentage, 100)}
              className="h-2"
            />
          </div>
          </div>
        </div>

        {/* Storage Stats */}
        <div>
          <h3 className="font-medium mb-2">Data Statistics</h3>
          <div className="grid grid-cols-2 gap-2 text-sm">
            <div>Students: {stats.studentsCount}</div>
            <div>Entries: {stats.entriesCount}</div>
            <div>Goals: {stats.goalsCount}</div>
            <div>Alerts: {stats.alertsCount}</div>
          </div>
        </div>

        {/* Warnings */}
        {usagePercentage > 70 && (
          <Alert className={cn(
            usagePercentage > 90 ? 'border-red-500' : 'border-yellow-500'
          )}>
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              {usagePercentage > 90
                ? 'Storage is almost full! Clear some data to prevent errors.'
                : 'Storage usage is getting high. Consider clearing old data.'}
            </AlertDescription>
          </Alert>
        )}

        {/* Actions */}
        <div className="space-y-2">
          <Button
            variant="outline"
            onClick={handleClearOldData}
            className="w-full justify-start"
          >
            <Trash2 className="h-4 w-4 mr-2" />
            Clear data older than 30 days
          </Button>
          <Button
            variant="outline"
            onClick={handleClearNonEssential}
            className="w-full justify-start"
          >
            <Trash2 className="h-4 w-4 mr-2" />
            Clear non-essential data
          </Button>
          <Button
            variant="destructive"
            onClick={handleClearAll}
            className="w-full justify-start"
          >
            <Trash2 className="h-4 w-4 mr-2" />
            Clear ALL data (irreversible)
          </Button>
        </div>

        {storageInfo.available && (
          <Alert className="border-green-500">
            <CheckCircle className="h-4 w-4" />
            <AlertDescription>
              Storage is healthy with sufficient space available.
            </AlertDescription>
          </Alert>
        )}
      </CardContent>
    </Card>
  );
};

