  }>;
  onTimeRangeChange?: (start: Date, end: Date) => void;
  realtime?: boolean;
}

interface TimelineEvent {
  id: string;
  timestamp: Date;
  type: 'emotion' | 'sensory' | 'tracking' | 'anomaly';
  label: string;
  value?: number;
  color: string;
  metadata?: Record<string, string | number>;
}

interface DataStream {
  id: string;
  label: string;
  color: string;
  data: Array<{ timestamp: Date; value: number }>;
  visible: boolean;
  yScale: [number, number];
}

export const TimelineVisualization: React.FC<TimelineVisualizationProps> = ({
  emotions,
  sensoryInputs,
  trackingEntries,
  anomalies = [],
  onTimeRangeChange,
  realtime = false
}) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 400 });

  // Timer refs for proper cleanup
  const resizeTimeoutRef = useRef<number | null>(null);
  const resizeRafRef = useRef<number | null>(null);
  const playbackIntervalRef = useRef<number | null>(null);
  const realtimeIntervalRef = useRef<number | null>(null);

  // Timeline state
  const [timeRange, setTimeRange] = useState<[Date, Date]>(() => {
    const now = new Date();
    return [subDays(now, 7), now];
  });
  const [zoomLevel, setZoomLevel] = useState(1);
  const [panOffset, setPanOffset] = useState(0);
  const [brushSelection, setBrushSelection] = useState<[Date, Date] | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [hoveredEvent, setHoveredEvent] = useState<TimelineEvent | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const dragStartDateRef = useRef<Date | null>(null);

  // Data stream visibility
  const [streamVisibility, setStreamVisibility] = useState({
    emotions: true,
    sensory: true,
    anomalies: true
  });

  // Calculate dimensions on mount and resize with throttling
  useEffect(() => {
    if (!containerRef.current) return;

    const updateDimensions = () => {
      if (!containerRef.current) return;
      const { width, height } = containerRef.current.getBoundingClientRect();
      setDimensions({ width: width - 40, height: height - 200 });
    };

    // Throttled resize handler to prevent excessive re-renders
    const throttledUpdateDimensions = () => {
      if (resizeTimeoutRef.current !== null) {
        window.clearTimeout(resizeTimeoutRef.current);
        resizeTimeoutRef.current = null;
      }
      resizeTimeoutRef.current = window.setTimeout(() => {
        if (resizeRafRef.current !== null) {
          cancelAnimationFrame(resizeRafRef.current);
        }
        resizeRafRef.current = requestAnimationFrame(updateDimensions);
      }, 100);
    };

    // Initial sync
    updateDimensions();

    // Use AbortController for clean listener removal
    const ac = new AbortController();
    window.addEventListener('resize', throttledUpdateDimensions, { passive: true, signal: ac.signal });

    return () => {
      try { ac.abort(); } catch {}
      if (resizeTimeoutRef.current !== null) {
        window.clearTimeout(resizeTimeoutRef.current);
        resizeTimeoutRef.current = null;
      }
      if (resizeRafRef.current !== null) {
        cancelAnimationFrame(resizeRafRef.current);
        resizeRafRef.current = null;
      }
    };
  }, []);

  // Process data into timeline events
  const timelineEvents = useMemo((): TimelineEvent[] => {
    const events: TimelineEvent[] = [];

    // Process emotions
    if (streamVisibility.emotions) {
      emotions.forEach((emotion, i) => {
        events.push({
          id: `emotion-${i}`,
          timestamp: emotion.timestamp,
          type: 'emotion',
          label: emotion.emotion,
          value: emotion.intensity,
          color: '#10B981',
          metadata: {
            intensity: emotion.intensity,
            triggers: emotion.triggers?.join(', ') || 'None'
          }
        });
      });
    }

    // Process sensory inputs
    if (streamVisibility.sensory) {
      sensoryInputs.forEach((input, i) => {
        events.push({
          id: `sensory-${i}`,
          timestamp: input.timestamp,
          type: 'sensory',
          label: `${input.sensoryType}: ${input.response}`,
          color: '#3B82F6',
          metadata: {
            type: input.sensoryType,
            response: input.response
          }
        });
      });
    }

    // Process anomalies
    if (streamVisibility.anomalies) {
      anomalies.forEach((anomaly, i) => {
        events.push({
          id: `anomaly-${i}`,
          timestamp: anomaly.timestamp,
          type: 'anomaly',
          label: anomaly.type,
          color: anomaly.severity === 'high' ? '#EF4444' : 
                 anomaly.severity === 'medium' ? '#F59E0B' : '#FCD34D'
        });
      });
    }

    return events.filter(event => 
      isWithinInterval(event.timestamp, { start: timeRange[0], end: timeRange[1] })
    );
  }, [emotions, sensoryInputs, anomalies, timeRange, streamVisibility]);

  // Process data streams with optimized calculations
  const dataStreams = useMemo((): DataStream[] => {
    const streams: DataStream[] = [];

    // Pre-filter emotions once instead of multiple times
    const filteredEmotions = emotions.filter(e => 
      isWithinInterval(e.timestamp, { start: timeRange[0], end: timeRange[1] })
    );

    // Emotion intensity stream
    const emotionData = filteredEmotions
      .map(e => ({ timestamp: e.timestamp, value: e.intensity }))
      .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

    if (emotionData.length > 0) {
      streams.push({
        id: 'emotion-intensity',
        label: 'Emotion Intensity',
        color: '#10B981',
        data: emotionData,
        visible: streamVisibility.emotions,
        yScale: [0, 10]
      });
    }

    // Sensory response stream (aggregated by hour)
    const sensoryByHour = new Map<string, { seeking: number; avoiding: number; neutral: number }>();
    sensoryInputs
      .filter(s => isWithinInterval(s.timestamp, { start: timeRange[0], end: timeRange[1] }))
      .forEach(input => {
        const hourKey = format(input.timestamp, 'yyyy-MM-dd HH:00');
        if (!sensoryByHour.has(hourKey)) {
          sensoryByHour.set(hourKey, { seeking: 0, avoiding: 0, neutral: 0 });
        }
        const hourData = sensoryByHour.get(hourKey);
        if (!hourData) {
          // This shouldn't happen, but add safety check
          logger.warn('Unexpected missing hour data', { hourKey });
          return;
        }
        if (input.response?.includes('seeking')) hourData.seeking++;
        else if (input.response?.includes('avoiding')) hourData.avoiding++;
        else hourData.neutral++;
      });

    const sensoryData = Array.from(sensoryByHour.entries())
      .map(([hourKey, data]) => ({
        timestamp: new Date(hourKey),
        value: data.seeking - data.avoiding // Net sensory response
      }))
      .sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

    if (sensoryData.length > 0) {
      // Optimize min/max calculation - single pass instead of nested map
      let minValue = Infinity;
      let maxValue = -Infinity;
      for (const d of sensoryData) {
        if (d.value < minValue) minValue = d.value;
        if (d.value > maxValue) maxValue = d.value;
      }
      streams.push({
        id: 'sensory-response',
        label: 'Sensory Response (Seeking - Avoiding)',
        color: '#3B82F6',
        data: sensoryData,
        visible: streamVisibility.sensory,
        yScale: [minValue - 1, maxValue + 1]
      });
    }

    return streams;
  }, [emotions, sensoryInputs, timeRange, streamVisibility]);

  // Timeline calculations
  const timeScale = useCallback((date: Date): number => {
    const totalDuration = timeRange[1].getTime() - timeRange[0].getTime();
    const offset = date.getTime() - timeRange[0].getTime();
    return (offset / totalDuration) * dimensions.width * zoomLevel + panOffset;
  }, [timeRange, dimensions.width, zoomLevel, panOffset]);

  const inverseTimeScale = useCallback((x: number): Date => {
    const adjustedX = (x - panOffset) / zoomLevel;
    const totalDuration = timeRange[1].getTime() - timeRange[0].getTime();
    const offset = (adjustedX / dimensions.width) * totalDuration;
    return new Date(timeRange[0].getTime() + offset);
  }, [timeRange, dimensions.width, zoomLevel, panOffset]);

  // Handle zoom
  const handleZoom = (delta: number) => {
    const newZoom = Math.max(0.5, Math.min(10, zoomLevel + delta));
    setZoomLevel(newZoom);
  };

  // Handle pan
  const handlePan = (delta: number) => {
    setPanOffset(prev => prev + delta);
  };

  // Handle brush selection
  const handleMouseDown = useCallback((e: React.MouseEvent<SVGElement>) => {
    if (!svgRef.current) return;
    const rect = svgRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const startDate = inverseTimeScale(x);

    dragStartDateRef.current = startDate;
    setIsDragging(true);
  }, [inverseTimeScale]);

  // Handle mouse events for brush selection with proper cleanup and throttling
  useEffect(() => {
    if (!isDragging || !dragStartDateRef.current || !svgRef.current) return;

    const ac = new AbortController();
    const rect = svgRef.current.getBoundingClientRect();
    const startDate = dragStartDateRef.current;

    // Throttle mouse move events to prevent excessive state updates
    let rafId: number | null = null;
    const handleMouseMove = (e: MouseEvent) => {
      if (rafId !== null) return;
      rafId = requestAnimationFrame(() => {
        const currentX = e.clientX - rect.left;
        const currentDate = inverseTimeScale(currentX);
        setBrushSelection([
          startDate < currentDate ? startDate : currentDate,
          startDate < currentDate ? currentDate : startDate
        ]);
        rafId = null;
      });
    };

    const handleMouseUp = () => {
      if (brushSelection && onTimeRangeChange) {
        onTimeRangeChange(brushSelection[0], brushSelection[1]);
      }
      setIsDragging(false);
      dragStartDateRef.current = null;
    };

    // eslint-disable-next-line no-restricted-syntax -- listeners scoped by AbortController and cleaned via abort
    document.addEventListener('mousemove', handleMouseMove, { signal: ac.signal });
    // eslint-disable-next-line no-restricted-syntax -- listeners scoped by AbortController and cleaned via abort
    document.addEventListener('mouseup', handleMouseUp, { signal: ac.signal });

    // Cleanup function using AbortController
    return () => {
      if (rafId !== null) cancelAnimationFrame(rafId);
      try { ac.abort(); } catch {}
    };
  }, [isDragging, brushSelection, onTimeRangeChange, inverseTimeScale]);

  // Playback animation with proper cleanup
  const stopPlayback = useCallback(() => {
    if (playbackIntervalRef.current !== null) {
      clearInterval(playbackIntervalRef.current);
      playbackIntervalRef.current = null;
    }
  }, []);

  useEffect(() => {
    if (!isPlaying) {
      stopPlayback();
      return;
    }

    const tick = () => {
      setTimeRange(prev => {
        const duration = prev[1].getTime() - prev[0].getTime();
        const step = (duration / 100) * playbackSpeed;
        return [
          new Date(prev[0].getTime() + step),
          new Date(prev[1].getTime() + step)
        ];
      });
    };

    // Respect page visibility to reduce background CPU usage
    const onVisibility = () => {
      if (document.visibilityState !== 'visible') {
        stopPlayback();
      } else if (isPlaying && playbackIntervalRef.current === null) {
        playbackIntervalRef.current = window.setInterval(tick, 100);
      }
    };

    // eslint-disable-next-line no-restricted-syntax -- Listener added within effect and removed in cleanup
    document.addEventListener('visibilitychange', onVisibility);
    playbackIntervalRef.current = window.setInterval(tick, 100);

    return () => {
      document.removeEventListener('visibilitychange', onVisibility);
      stopPlayback();
    };
  }, [isPlaying, playbackSpeed, stopPlayback]);

  // Real-time updates with proper cleanup
  const stopRealtime = useCallback(() => {
    if (realtimeIntervalRef.current !== null) {
      clearInterval(realtimeIntervalRef.current);
      realtimeIntervalRef.current = null;
    }
  }, []);

  useEffect(() => {
    if (!realtime) {
      stopRealtime();
      return;
    }

    const tick = () => {
      const now = new Date();
      setTimeRange(prev => {
        const duration = prev[1].getTime() - prev[0].getTime();
        return [
          new Date(now.getTime() - duration),
          now
        ];
      });
    };

    realtimeIntervalRef.current = window.setInterval(tick, 5000); // Update every 5 seconds

    return () => {
      stopRealtime();
    };
  }, [realtime, stopRealtime]);

  // Render timeline grid
  const renderGrid = () => {
    const gridLines = [];
    const labels = [];
    const startTime = timeRange[0].getTime();
    const endTime = timeRange[1].getTime();
    const duration = endTime - startTime;

    // Determine appropriate time interval based on zoom
    let interval: number;
    let formatStr: string;
    if (duration < 3600000) { // Less than 1 hour
      interval = 300000; // 5 minutes
      formatStr = 'HH:mm';
    } else if (duration < 86400000) { // Less than 1 day
      interval = 3600000; // 1 hour
      formatStr = 'HH:mm';
    } else if (duration < 604800000) { // Less than 1 week
      interval = 86400000; // 1 day
      formatStr = 'MMM dd';
    } else {
      interval = 604800000; // 1 week
      formatStr = 'MMM dd';
    }

    for (let time = startTime; time <= endTime; time += interval) {
      const x = timeScale(new Date(time));
      if (x >= 0 && x <= dimensions.width) {
        gridLines.push(
          <line
            key={`grid-${time}`}
            x1={x}
            y1={0}
            x2={x}
            y2={dimensions.height}
            stroke="#e5e7eb"
            strokeDasharray="2,2"
          />
        );
        labels.push(
          <text
            key={`label-${time}`}
            x={x}
            y={dimensions.height - 5}
            textAnchor="middle"
            fontSize="12"
            fill="#6b7280"
          >
            {format(new Date(time), formatStr)}
          </text>
        );
      }
    }

    return { gridLines, labels };
  };

  // Render data streams
  const renderDataStreams = () => {
    return dataStreams.filter(stream => stream.visible).map(stream => {
      const yScale = (value: number) => {
        const [min, max] = stream.yScale;
        const normalized = (value - min) / (max - min);
        return dimensions.height * 0.8 - normalized * (dimensions.height * 0.6);
      };

      const pathData = stream.data
        .map((point, i) => {
          const x = timeScale(point.timestamp);
          const y = yScale(point.value);
          return `${i === 0 ? 'M' : 'L'} ${x} ${y}`;
        })
        .join(' ');

      return (
        <g key={stream.id}>
          <path
            d={pathData}
            fill="none"
            stroke={stream.color}
            strokeWidth="2"
            opacity="0.8"
          />
          {stream.data.map((point, i) => {
            const x = timeScale(point.timestamp);
            const y = yScale(point.value);
            if (x >= 0 && x <= dimensions.width) {
              return (
                <circle
                  key={`${stream.id}-${i}`}
                  cx={x}
                  cy={y}
                  r="3"
                  fill={stream.color}
                  className="cursor-pointer hover:r-5"
                  onMouseEnter={() => setHoveredEvent({
                    id: `${stream.id}-${i}`,
                    timestamp: point.timestamp,
                    type: 'tracking',
                    label: `${stream.label}: ${point.value.toFixed(1)}`,
                    value: point.value,
                    color: stream.color
                  })}
                  onMouseLeave={() => setHoveredEvent(null)}
                />
              );
            }
            return null;
          })}
        </g>
      );
    });
  };

  // Render events
  const renderEvents = () => {
    return timelineEvents.map(event => {
      const x = timeScale(event.timestamp);
      if (x < 0 || x > dimensions.width) return null;

      const getIcon = () => {
        switch (event.type) {
          case 'emotion': return <Brain className="h-4 w-4" />;
          case 'sensory': return <Eye className="h-4 w-4" />;
          case 'anomaly': return <AlertCircle className="h-4 w-4" />;
          default: return <Activity className="h-4 w-4" />;
        }
      };

      return (
        <g key={event.id}>
          <line
            x1={x}
            y1={dimensions.height * 0.2}
            x2={x}
            y2={dimensions.height * 0.8}
            stroke={event.color}
            strokeWidth="1"
            opacity="0.3"
          />
          <circle
            cx={x}
            cy={dimensions.height * 0.2}
            r="8"
            fill={event.color}
            className="cursor-pointer"
            onMouseEnter={() => setHoveredEvent(event)}
            onMouseLeave={() => setHoveredEvent(null)}
          />
        </g>
      );
    });
  };

  // Render brush selection
  const renderBrushSelection = () => {
    if (!brushSelection) return null;

    const x1 = timeScale(brushSelection[0]);
    const x2 = timeScale(brushSelection[1]);

    return (
      <rect
        x={Math.min(x1, x2)}
        y={0}
        width={Math.abs(x2 - x1)}
        height={dimensions.height}
        fill="hsl(var(--primary))"
        opacity="0.1"
        stroke="hsl(var(--primary))"
        strokeWidth="2"
        strokeDasharray="4,2"
      />
    );
  };

  const { gridLines, labels } = useMemo(() => renderGrid(), [timeScale, dimensions.width, dimensions.height, timeRange]);

  const renderedDataStreams = useMemo(() => renderDataStreams(), [dataStreams, timeScale, dimensions.height]);

  return (
    <Card className="w-full">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Clock className="h-5 w-5" />
            Timeline Visualization
          </CardTitle>
          <div className="flex items-center gap-2">
            {realtime && (
              <Badge variant="default" className="animate-pulse">
                Live
              </Badge>
            )}
            <Button
              size="icon"
              variant="ghost"
              aria-label="Reset view"
              title="Reset view"
              onClick={() => {
                setZoomLevel(1);
                setPanOffset(0);
                setBrushSelection(null);
              }}
            >
              <RotateCcw className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {/* Controls */}
        <div className="mb-4 space-y-4">
          <div className="flex items-center gap-4">
            {/* Zoom controls */}
            <div className="flex items-center gap-2">
              <Button
                size="icon"
                variant="ghost"
                aria-label="Zoom out"
                title="Zoom out"
                onClick={() => handleZoom(-0.5)}
                disabled={zoomLevel <= 0.5}
              >
                <ZoomOut className="h-4 w-4" />
              </Button>
              <span className="text-sm font-medium w-16 text-center">
                {Math.round(zoomLevel * 100)}%
              </span>
              <Button
                size="icon"
                variant="ghost"
                aria-label="Zoom in"
                title="Zoom in"
                onClick={() => handleZoom(0.5)}
                disabled={zoomLevel >= 10}
              >
                <ZoomIn className="h-4 w-4" />
              </Button>
            </div>

            {/* Pan controls */}
            <div className="flex items-center gap-2">
              <Button
                size="icon"
                variant="ghost"
                aria-label="Pan left"
                title="Pan left"
                onClick={() => handlePan(50)}
              >
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <Button
                size="icon"
                variant="ghost"
                aria-label="Pan right"
                title="Pan right"
                onClick={() => handlePan(-50)}
              >
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>

            {/* Playback controls */}
            <div className="flex items-center gap-2">
              <Button
                size="icon"
                variant={isPlaying ? 'default' : 'ghost'}
                aria-label={isPlaying ? 'Pause playback' : 'Start playback'}
                title={isPlaying ? 'Pause playback' : 'Start playback'}
                onClick={() => setIsPlaying(!isPlaying)}
              >
                {isPlaying ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
              </Button>
              <Slider
                value={[playbackSpeed]}
                onValueChange={([value]) => setPlaybackSpeed(value)}
                min={0.25}
                max={4}
                step={0.25}
                className="w-24"
              />
              <span className="text-sm text-muted-foreground">
                {playbackSpeed}x
              </span>
            </div>

            {/* Stream visibility toggles */}
            <div className="flex items-center gap-2 ml-auto">
              <Toggle
                size="sm"
                pressed={streamVisibility.emotions}
                onPressedChange={(pressed) => 
                  setStreamVisibility(prev => ({ ...prev, emotions: pressed }))
                }
              >
                <Brain className="h-4 w-4 mr-1" />
                Emotions
              </Toggle>
              <Toggle
                size="sm"
                pressed={streamVisibility.sensory}
                onPressedChange={(pressed) => 
                  setStreamVisibility(prev => ({ ...prev, sensory: pressed }))
                }
              >
                <Eye className="h-4 w-4 mr-1" />
                Sensory
              </Toggle>
              <Toggle
                size="sm"
                pressed={streamVisibility.anomalies}
                onPressedChange={(pressed) => 
                  setStreamVisibility(prev => ({ ...prev, anomalies: pressed }))
                }
              >
                <AlertCircle className="h-4 w-4 mr-1" />
                Anomalies
              </Toggle>
            </div>
          </div>

          {/* Time range display */}
          <div className="flex items-center justify-between text-sm text-muted-foreground">
            <span>{format(timeRange[0], 'MMM dd, yyyy HH:mm')}</span>
            <span className="font-medium">
              {differenceInMinutes(timeRange[1], timeRange[0])} minutes
            </span>
            <span>{format(timeRange[1], 'MMM dd, yyyy HH:mm')}</span>
          </div>
        </div>

        {/* Timeline visualization */}
        <div ref={containerRef} className="relative bg-gray-50 rounded-lg overflow-hidden">
          <svg
            ref={svgRef}
            width={dimensions.width}
            height={dimensions.height}
            className="cursor-crosshair"
            onMouseDown={handleMouseDown}
          >
            {/* Grid lines */}
            {gridLines}

            {/* Data streams */}
            {renderedDataStreams}

            {/* Events */}
            {renderEvents()}

            {/* Brush selection */}
            {renderBrushSelection()}

            {/* Time labels */}
            {labels}

            {/* Current time indicator (for realtime mode) */}
            {realtime && (
              <line
                x1={timeScale(new Date())}
                y1={0}
                x2={timeScale(new Date())}
                y2={dimensions.height}
                stroke="#ef4444"
                strokeWidth="2"
                strokeDasharray="4,2"
              />
            )}
          </svg>

          {/* Tooltip */}
          {hoveredEvent && (
            <div
              className={cn(
                "absolute bg-background border rounded-lg shadow-lg p-3 pointer-events-none",
                "top-5"
              )}
              style={{
                left: `${Math.min(timeScale(hoveredEvent.timestamp), dimensions.width - 200)}px`
              }}
            >
              <div className="flex items-center gap-2 mb-1">
                <div 
                  className={cn("w-3 h-3 rounded-full")}
                  style={{ backgroundColor: hoveredEvent.color }}
                />
                <span className="font-medium capitalize">{hoveredEvent.type}</span>
              </div>
              <p className="text-sm">{hoveredEvent.label}</p>
              <p className="text-xs text-muted-foreground">
                {format(hoveredEvent.timestamp, 'MMM dd, yyyy HH:mm:ss')}
              </p>
              {hoveredEvent.metadata && (
                <div className="mt-1 pt-1 border-t">
                  {Object.entries(hoveredEvent.metadata).map(([key, value]) => (
                    <p key={key} className="text-xs text-muted-foreground">
                      {key}: {value}
                    </p>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Legend */}
          <div className="absolute bottom-4 left-4 bg-background/90 backdrop-blur-sm rounded-lg p-3 shadow-lg">
            <h4 className="font-medium text-sm mb-2">Data Streams</h4>
            <div className="space-y-1">
              {dataStreams.filter(s => s.visible).map(stream => (
                <div key={stream.id} className="flex items-center gap-2">
                  <div 
                    className={cn("w-3 h-3 rounded-full")}
                    style={{ backgroundColor: stream.color }}
                  />
                  <span className="text-xs">{stream.label}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Stats */}
          <div className="absolute top-4 right-4 bg-background/90 backdrop-blur-sm rounded-lg p-3 shadow-lg">
            <div className="text-xs space-y-1">
              <p>Events: {timelineEvents.length}</p>
              <p>Zoom: {Math.round(zoomLevel * 100)}%</p>
              {brushSelection && (
                <p>Selection: {differenceInMinutes(brushSelection[1], brushSelection[0])} min</p>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

import { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { RefreshCw, CheckCircle, AlertCircle, Users } from 'lucide-react';
import { universalAnalyticsInitializer } from '@/lib/universalAnalyticsInitializer';
import { analyticsManager } from '@/lib/analyticsManager';
import { logger } from '@/lib/logger';

interface AnalyticsStatusData {
  isInitialized: boolean;
  studentsWithAnalytics: number;
  totalStudents: number;
  studentStatuses: Array<{
    studentId: string;
    studentName: string;
    isInitialized: boolean;
    hasMinimumData: boolean;
    healthScore: number;
  }>;
}

export const UniversalAnalyticsStatus = () => {
  const [status, setStatus] = useState<AnalyticsStatusData | null>(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  // Ensure universal initialization on mount to cover all students, including new and mock
  useEffect(() => {
    // Lazy-load to avoid import cycles
    import('@/lib/analyticsManager')
      .then(mod => mod.ensureUniversalAnalyticsInitialization?.())
      .catch(() => {/* noop */});
  }, []);

  const loadStatus = async () => {
    try {
      const initStatus = universalAnalyticsInitializer.getInitializationStatus();
      const analyticsStatuses = analyticsManager.getAnalyticsStatus();

      setStatus({
        isInitialized: initStatus.isInitialized,
        studentsWithAnalytics: initStatus.studentsWithAnalytics,
        totalStudents: initStatus.totalStudents,
        studentStatuses: analyticsStatuses
      });
    } catch (error) {
      logger.error('Error loading analytics status', { error });
    }
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      await universalAnalyticsInitializer.forceReinitialization();
      await loadStatus();
    } catch (error) {
      logger.error('Error refreshing analytics', { error });
    } finally {
      setIsRefreshing(false);
    }
  };

  useEffect(() => {
    loadStatus();

    // Auto-refresh every 30 seconds
    // eslint-disable-next-line no-restricted-syntax -- Interval created within effect and cleared on cleanup
    const interval = setInterval(loadStatus, 30000);
    return () => clearInterval(interval);
  }, []);

  if (!status) {
    return (
      <Card className="bg-gradient-card border-0 shadow-medium">
        <CardContent className="p-6">
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary"></div>
            <span className="ml-2 text-muted-foreground">Loading analytics status...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  const allStudentsInitialized = status.studentsWithAnalytics === status.totalStudents && status.totalStudents > 0;
  const hasStudentsWithData = status.studentStatuses.some(s => s.hasMinimumData);

  return (
    <Card className="bg-gradient-card border-0 shadow-medium">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Users className="h-5 w-5" />
            Universal Analytics Status
          </CardTitle>
          <Button
            variant="ghost"
            size="icon"
            aria-label="Refresh analytics status"
            title="Refresh analytics status"
            onClick={handleRefresh}
            disabled={isRefreshing}
            className="h-8"
          >
            <RefreshCw className={`h-4 w-4 ${isRefreshing ? 'animate-spin' : ''}`} />
          </Button>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Overall Status */}
        <div className="flex items-center justify-between p-3 bg-background/50 rounded-lg">
          <div className="flex items-center gap-2">
            {allStudentsInitialized ? (
              <CheckCircle className="h-5 w-5 text-green-500" />
            ) : (
              <AlertCircle className="h-5 w-5 text-yellow-500" />
            )}
            <span className="font-medium">
              System Status
            </span>
          </div>
          <Badge variant={allStudentsInitialized ? "default" : "secondary"}>
            {allStudentsInitialized ? 'Fully Initialized' : 'Initializing'}
          </Badge>
        </div>

        {/* Statistics */}
        <div className="grid grid-cols-2 gap-4">
          <div className="text-center p-3 bg-primary/5 rounded-lg">
            <div className="text-2xl font-bold text-primary">
              {status.studentsWithAnalytics}
            </div>
            <div className="text-sm text-muted-foreground">
              Students with Analytics
            </div>
          </div>

          <div className="text-center p-3 bg-secondary/5 rounded-lg">
            <div className="text-2xl font-bold text-secondary">
              {status.studentStatuses.filter(s => s.hasMinimumData).length}
            </div>
            <div className="text-sm text-muted-foreground">
              Students with Data
            </div>
          </div>
        </div>

        {/* Student Details */}
        {status.studentStatuses.length > 0 && (
          <div className="space-y-2">
            <h4 className="font-medium text-sm text-muted-foreground">Student Details:</h4>
            {status.studentStatuses.map(student => (
              <div key={student.studentId} className="flex items-center justify-between p-2 bg-background/30 rounded">
                <div className="flex items-center gap-2">
                  <div className={`w-2 h-2 rounded-full ${
                    student.hasMinimumData ? 'bg-green-500' : 
                    student.isInitialized ? 'bg-yellow-500' : 'bg-gray-400'
                  }`} />
                  <span className="text-sm font-medium">{student.studentName}</span>
                </div>
                <div className="flex items-center gap-2">
                  <Badge variant="outline" className="text-xs">
                    Health: {student.healthScore}%
                  </Badge>
                  {student.hasMinimumData && (
                    <Badge variant="default" className="text-xs">
                      Pattern Detection Active
                    </Badge>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Success Message */}
        {allStudentsInitialized && hasStudentsWithData && (
          <div className="p-3 bg-green-50 border border-green-200 rounded-lg">
            <div className="flex items-center gap-2 text-green-800">
              <CheckCircle className="h-4 w-4" />
              <span className="text-sm font-medium">
                 Universal pattern detection is active for all students!
              </span>
            </div>
          </div>
        )}

        {/* Instructions */}
        {status.totalStudents === 0 && (
          <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="text-blue-800 text-sm">
              Add your first student to see universal analytics in action. Pattern detection will start immediately!
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

import React, { useState, useRef, useMemo, useCallback, useEffect } from "react";
import { ScrollArea } from "@/components/ui/scroll-area";
import { cn } from "@/lib/utils";

/**
 * @fileoverview VirtualScrollArea - A performance-optimized virtual scrolling component
 * 
 * Virtual scrolling is a technique where only the visible items (plus a small buffer)
 * are rendered in the DOM, while maintaining the illusion of a complete scrollable list.
 * This dramatically improves performance when dealing with large datasets.
 * 
 * ## Benefits:
 * - **Reduced Memory Usage**: Only visible items are kept in the DOM
 * - **Faster Initial Render**: Rendering 20 items vs 10,000 items
 * - **Improved Scroll Performance**: Less DOM manipulation during scrolling
 * - **Scalability**: Can handle datasets with millions of items
 * 
 * ## Implementation Details:
 * The component uses a "window" rendering technique:
 * 1. Calculates which items should be visible based on scroll position
 * 2. Renders only those items plus an overscan buffer
 * 3. Uses CSS transforms to position items correctly
 * 4. Maintains a virtual height to preserve scrollbar behavior
 * 
 * ## Performance Considerations:
 * - Items must have a fixed height for accurate positioning
 * - The overscan buffer helps prevent flickering during fast scrolling
 * - Using React.memo on renderItem can further improve performance
 * 
 * @module components/VirtualScrollArea
 */

/**
 * A virtualized scroll area component for efficiently rendering long lists.
 *
 * This component enhances performance by rendering only the items currently visible
 * within the viewport, plus a specified number of "overscan" items before and after
 * the visible range. This approach minimizes the number of DOM elements, leading to
 * faster rendering and lower memory usage, which is especially beneficial for
 * applications displaying large datasets.
 *
 * It is built on top of the `@/components/ui/scroll-area` component and handles
 * the logic for calculating which items to display as the user scrolls.
 *
 * @example
 * // Basic usage with simple string items
 * ```tsx
 * const items = Array.from({ length: 1000 }, (_, i) => `Item ${i + 1}`);
 *
 * const MyListComponent = () => (
 *   <VirtualScrollArea
 *     items={items}
 *     itemHeight={40}
 *     containerHeight={400}
 *     renderItem={(item, index) => (
 *       <div key={index} className="leading-10 px-2.5">
 *         {item}
 *       </div>
 *     )}
 *     className="border rounded-md"
 *   />
 * );
 * ```
 * 
 * @example
 * // Usage with complex objects and memoized render function
 * ```tsx
 * interface User {
 *   id: string;
 *   name: string;
 *   email: string;
 *   avatar: string;
 * }
 * 
 * const users: User[] = fetchUsers(); // Your data source
 * 
 * const UserListItem = React.memo<{ user: User; index: number }>(({ user, index }) => (
 *   <div className="flex items-center p-2 hover:bg-gray-50">
 *     <img src={user.avatar} alt={user.name} className="w-10 h-10 rounded-full mr-3" />
 *     <div>
 *       <div className="font-semibold">{user.name}</div>
 *       <div className="text-sm text-gray-500">{user.email}</div>
 *     </div>
 *   </div>
 * ));
 * 
 * const UserList = () => (
 *   <VirtualScrollArea
 *     items={users}
 *     itemHeight={60}
 *     containerHeight={600}
 *     renderItem={(user, index) => <UserListItem key={user.id} user={user} index={index} />}
 *     overscan={10} // Increase overscan for smoother scrolling
 *   />
 * );
 * ```
 * 
 * @example
 * // Usage with dynamic container height
 * ```tsx
 * const DynamicHeightList = () => {
 *   const [containerHeight, setContainerHeight] = useState(0);
 *   const containerRef = useRef<HTMLDivElement>(null);
 * 
 *   useEffect(() => {
 *     const updateHeight = () => {
 *       if (containerRef.current) {
 *         setContainerHeight(containerRef.current.clientHeight);
 *       }
 *     };
 * 
 *     updateHeight();
 *     window.addEventListener('resize', updateHeight);
 *     return () => window.removeEventListener('resize', updateHeight);
 *   }, []);
 * 
 *   return (
 *     <div ref={containerRef} className="h-full">
 *       {containerHeight > 0 && (
 *         <VirtualScrollArea
 *           items={items}
 *           itemHeight={50}
 *           containerHeight={containerHeight}
 *           renderItem={(item, index) => <ItemComponent item={item} />}
 *         />
 *       )}
 *     </div>
 *   );
 * };
 * ```
 */
interface VirtualScrollAreaProps<T> {
  /**
   * The array of items to be rendered in the list.
   * Can be of any type, as long as `renderItem` can handle it.
   * @type {T[]}
   */
  items: T[];
  /**
   * The fixed height of each item in the list, in pixels.
   * This is crucial for calculating the position of virtualized items.
   * @type {number}
   */
  itemHeight: number;
  /**
   * The total height of the scrollable container, in pixels.
   * @type {number}
   */
  containerHeight: number;
  /**
   * A function that takes an item and its index and returns a React node to be rendered.
   * This function is called for each visible item.
   * @param {T} item - The item data to render.
   * @param {number} index - The index of the item in the original `items` array.
   * @returns {React.ReactNode} The JSX element to render for the item.
   */
  renderItem: (item: T, index: number) => React.ReactNode;
  /**
   * Optional CSS class name(s) to apply to the root `ScrollArea` component.
   * @type {string}
   * @default ""
   */
  className?: string;
  /**
   * The number of items to render outside the visible viewport, both above and below.
   * A larger overscan can reduce flickering on fast scrolls at the cost of rendering more elements.
   * @type {number}
   * @default 5
   */
  overscan?: number;
}

/**
 * Renders a virtualized scroll area for efficiently displaying long lists.
 *
 * @template T - The type of items in the list.
 * @param {VirtualScrollAreaProps<T>} props - The props for the component.
 * @returns {React.ReactElement} The rendered virtual scroll area component.
 */
export function VirtualScrollArea<T>({
  items,
  itemHeight,
  containerHeight,
  renderItem,
  className = "",
  overscan = 5,
}: VirtualScrollAreaProps<T>) {
  const [scrollTop, setScrollTop] = useState(0);
  const scrollElementRef = useRef<HTMLDivElement>(null);
  const scrollTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const lastScrollTopRef = useRef(0);

  // Clamp totalHeight to 0 (no negative wrapper heights when items is empty)
  const totalHeight = Math.max(0, items.length * itemHeight);

  /**
   * Calculate visible range based on scroll position.
   * Memoized to prevent unnecessary recalculations.
   * 
   * @returns {Object} Object containing start and end indices of visible items
   */
  const visibleRange = useMemo(() => {
    const start = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
    const end = Math.min(
      items.length - 1,
      Math.floor((scrollTop + containerHeight) / itemHeight) + overscan
    );
    return { start, end };
  }, [scrollTop, itemHeight, containerHeight, items.length, overscan]);

  /**
   * Extract visible items from the full items array.
   * Memoized to prevent unnecessary array operations.
   * 
   * @returns {T[]} Array of items currently visible (plus overscan)
   */
  const visibleItems = useMemo(() => {
    return items.slice(visibleRange.start, visibleRange.end + 1);
  }, [items, visibleRange]);

  /**
   * Throttled scroll handler to prevent excessive state updates.
   * Uses requestAnimationFrame for smooth scrolling performance.
   * 
   * @param {React.UIEvent<HTMLDivElement>} event - The scroll event
   */
  const handleScroll = useCallback((event: React.UIEvent<HTMLDivElement>) => {
    const currentScrollTop = event.currentTarget.scrollTop;

    // Clear existing timeout
    if (scrollTimeoutRef.current) {
      clearTimeout(scrollTimeoutRef.current);
    }

    // Throttle scroll updates using requestAnimationFrame
    // eslint-disable-next-line no-restricted-syntax -- Timeout used for throttling and cleared on cleanup
    scrollTimeoutRef.current = setTimeout(() => {
      // Only update if scroll position actually changed
      if (Math.abs(currentScrollTop - lastScrollTopRef.current) > 1) {
        lastScrollTopRef.current = currentScrollTop;
        setScrollTop(currentScrollTop);
      }
    }, 16); // ~60fps throttling
  }, []);

  /**
   * Cleanup effect to clear timeouts on unmount.
   * Prevents memory leaks from pending timeouts.
   */
  useEffect(() => {
    return () => {
      if (scrollTimeoutRef.current) {
        clearTimeout(scrollTimeoutRef.current);
      }
    };
  }, []);

  // Generate dynamic classes for container height using Tailwind's arbitrary values
  const containerHeightClass = containerHeight ? `h-[${containerHeight}px]` : 'h-full';

  // Calculate dynamic positioning classes using Tailwind's arbitrary values
  const totalHeightClass = `h-[${totalHeight}px]`;
  const translateYClass = `translate-y-[${visibleRange.start * itemHeight}px]`;
  const itemHeightClass = `h-[${itemHeight}px]`;

  return (
    <ScrollArea 
      className={cn(className, "relative", containerHeightClass)}
    >
      <div 
        ref={scrollElementRef}
        onScroll={handleScroll}
        className={cn("overflow-auto", containerHeightClass)}
      >
        <div className={cn("relative", totalHeightClass)}>
          <div className={cn("relative", translateYClass)}>
            {visibleItems.map((item, index) => (
              <div
                key={visibleRange.start + index}
                className={cn("relative", itemHeightClass)}
              >
                {renderItem(item, visibleRange.start + index)}
              </div>
            ))}
          </div>
        </div>
      </div>
    </ScrollArea>
  );
}

