interface PatternAnalysisViewProps {
  patterns: PatternResult[];
  predictiveInsights: PredictiveInsight[];
  anomalies: AnomalyDetection[];
  isAnalyzing: boolean;
  highlightState: HighlightState;
  handleHighlight: (type: HighlightState['type'], id: string) => void;
  filteredData: {
    emotions: { timestamp: Date; intensity: number }[];
    sensoryInputs: { timestamp: Date }[];
  };
}

export const PatternAnalysisView: React.FC<PatternAnalysisViewProps> = ({
  patterns,
  predictiveInsights,
  anomalies,
  isAnalyzing,
  highlightState,
  handleHighlight,
  filteredData,
}) => {
  const getPatternIcon = (type: string) => {
    switch (type.toLowerCase()) {
      case 'emotion': return <Brain className="h-4 w-4" />;
      case 'sensory': return <Eye className="h-4 w-4" />;
      case 'environmental': return <Thermometer className="h-4 w-4" />;
      default: return <Activity className="h-4 w-4" />;
    }
  };

  const getConfidenceColor = (confidence: number) => {
    if (confidence >= 0.8) return 'text-green-600 bg-green-50 border-green-200';
    if (confidence >= 0.6) return 'text-yellow-600 bg-yellow-50 border-yellow-200';
    return 'text-red-600 bg-red-50 border-red-200';
  };

  if (isAnalyzing) {
    return (
      <div aria-label="Loading chart data" className="h-[400px] w-full">
        <div className="h-full w-full animate-pulse rounded-md border border-border/50 bg-muted/20" />
      </div>
    );
  }

  if (patterns.length === 0 && predictiveInsights.length === 0) {
    return (
      <div className="flex items-center justify-center h-64 text-muted-foreground">
        <div className="text-center">
          <Zap className="h-16 w-16 mx-auto mb-4 opacity-50" />
          <p>No patterns detected yet</p>
          <p className="text-sm">Need more data for pattern analysis</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {patterns.length > 0 && (
        <div className="space-y-4">
          <h3 className="text-lg font-semibold flex items-center gap-2">
            <Target className="h-5 w-5" />
            Detected Patterns ({patterns.length})
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {patterns.map((pattern, index) => (
              <Card
                key={index}
                className={cn(
                  "bg-gradient-card cursor-pointer transition-all",
                  highlightState.type === 'emotion' && pattern.type === 'emotion' && "ring-2 ring-primary"
                )}
                onClick={() => handleHighlight('emotion', `pattern-${index}`)}
              >
                <CardContent className="p-4">
                  <div className="flex items-start gap-3">
                    {getPatternIcon(pattern.type)}
                    <div className="flex-1">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium capitalize">{pattern.type} Pattern</h4>
                        <Badge className={getConfidenceColor(pattern.confidence)}>
                          {Math.round(pattern.confidence * 100)}% confident
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mb-2">
                        Frequency: {pattern.frequency} occurrences
                      </p>
                      {(pattern.recommendations?.length ?? 0) > 0 && (
                        <div className="space-y-1">
                          <p className="text-sm font-medium">Recommendations:</p>
                          {(pattern.recommendations ?? []).slice(0, 2).map((rec, i) => (
                            <p key={i} className="text-xs text-muted-foreground flex items-start gap-1">
                              <Lightbulb className="h-3 w-3 mt-0.5 flex-shrink-0" />
                              {rec}
                            </p>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {predictiveInsights.length > 0 && (
        <div className="space-y-4">
          <h3 className="text-lg font-semibold flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Predictive Insights ({predictiveInsights.length})
          </h3>
          <div className="space-y-4">
            {predictiveInsights.map((insight, index) => (
              <Card key={index} className="bg-gradient-card">
                <CardContent className="p-4">
                  <div className="flex items-start gap-3">
                    <div className="flex-shrink-0">
                      {insight.severity === 'high' ? (
                        <AlertTriangle className="h-5 w-5 text-red-500" />
                      ) : insight.severity === 'medium' ? (
                        <Clock className="h-5 w-5 text-yellow-500" />
                      ) : (
                        <CheckCircle className="h-5 w-5 text-green-500" />
                      )}
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium">{insight.title}</h4>
                        <Badge variant="outline">
                          {Math.round(insight.confidence * 100)}% confidence
                        </Badge>
                      </div>

                      <p className="text-sm text-muted-foreground mb-2">
                        {insight.description}
                      </p>

                      {insight.prediction && (
                        <div className="mb-2">
                          <p className="text-sm font-medium mb-1">Prediction:</p>
                          <div className="flex items-center gap-2 text-sm">
                            {insight.prediction.trend === 'increasing' ? (
                              <TrendingUp className="h-4 w-4 text-green-500" />
                            ) : insight.prediction.trend === 'decreasing' ? (
                              <TrendingDown className="h-4 w-4 text-red-500" />
                            ) : (
                              <Activity className="h-4 w-4 text-blue-500" />
                            )}
                            <span className="capitalize">{insight.prediction.trend}</span>
                            <ConfidenceIndicator
                              confidence={insight.prediction.accuracy}
                              dataPoints={filteredData.emotions.length + filteredData.sensoryInputs.length}
                              timeSpanDays={filteredData.emotions.length > 0 && filteredData.emotions[0] ?
                                Math.abs(differenceInDays(new Date(), filteredData.emotions[0].timestamp)) : 0}
                              rSquared={insight.prediction.accuracy}
                              className="ml-1"
                            />
                          </div>
                        </div>
                      )}

                      {insight.recommendations.length > 0 && (
                        <div className="space-y-1">
                          <p className="text-sm font-medium">Recommendations:</p>
                          {insight.recommendations.slice(0, 3).map((rec, i) => (
                            <p key={i} className="text-xs text-muted-foreground flex items-start gap-1">
                              <Lightbulb className="h-3 w-3 mt-0.5 flex-shrink-0" />
                              {rec}
                            </p>
                          ))}
                        </div>
                      )}

                      {insight.severity && (
                        <div className="mt-2 pt-2 border-t border-border">
                          <p className="text-xs text-muted-foreground flex items-center gap-1">
                            <Shield className="h-3 w-3" />
                            Severity: <span className="font-medium capitalize">{insight.severity}</span>
                          </p>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {anomalies.length > 0 && (
        <div className="space-y-4">
          <h3 className="text-lg font-semibold flex items-center gap-2">
            <AlertTriangle className="h-5 w-5" />
            Detected Anomalies ({anomalies.length})
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {anomalies.map((anomaly, index) => (
              <Card
                key={index}
                className={cn(
                  "bg-gradient-card border-orange-200 cursor-pointer",
                  highlightState.type === 'anomaly' && highlightState.id === `anomaly-${index}` && "ring-2 ring-orange-500"
                )}
                onClick={() => handleHighlight('anomaly', `anomaly-${index}`)}
              >
                <CardContent className="p-4">
                  <div className="flex items-start gap-3">
                    <AlertTriangle className="h-4 w-4 text-orange-500 flex-shrink-0 mt-0.5" />
                    <div className="flex-1">
                      <h4 className="font-medium mb-1">{anomaly.type} Anomaly</h4>
                      <p className="text-sm text-muted-foreground mb-2">
                        Severity: <span className="font-medium capitalize">{anomaly.severity}</span>
                      </p>
                      <p className="text-xs text-muted-foreground">
                        {format(anomaly.timestamp, 'MMM dd, yyyy')}
                      </p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Student, TrackingEntry, EmotionEntry, SensoryEntry } from '@/types/student';
import { TrendingUp, AlertTriangle, Target, Lightbulb } from 'lucide-react';

type AnalysisBundle = {
  patterns?: Array<{ pattern?: string; description?: string; confidence?: number; type?: string; dataPoints?: number }>;
  predictiveInsights?: Array<{ title?: string; description?: string; confidence?: number; timeframe?: string }>;
  anomalies?: Array<{ description: string; timestamp: Date; severity: 'low'|'medium'|'high' }>;
  correlationMatrix?: { significantPairs?: Array<{ factor1: string; factor2: string; correlation: number; significance: string }> } | null;
  isAnalyzing?: boolean;
};

interface Props {
  student: Student;
  filteredData: {
    entries: TrackingEntry[];
    emotions: EmotionEntry[];
    sensoryInputs: SensoryEntry[];
  };
  analysis: AnalysisBundle;
  activePreset?: string | null;
  onCreateGoal?: () => void;
  onAddIntervention?: () => void;
  onScheduleBreak?: () => void;
  onJumpToTracking?: () => void;
  className?: string;
}

const scoreToBadge = (p?: number) => {
  if (!p && p !== 0) return <Badge variant="outline">n/a</Badge>;
  const pct = Math.round(p * 100);
  const variant = p >= 0.7 ? 'default' : p >= 0.4 ? 'secondary' : 'destructive';
  return <Badge variant={variant}>{pct}%</Badge>;
};

export const TeacherInsightsPanel: React.FC<Props> = ({
  student,
  filteredData,
  analysis,
  activePreset,
  onCreateGoal,
  onAddIntervention,
  onScheduleBreak,
  onJumpToTracking,
  className = ''
}) => {
  const sessions = filteredData?.entries?.length ?? 0;
  const emotions = filteredData?.emotions?.length ?? 0;

  const limited = sessions < 10 || new Set(filteredData.entries.map(e => e.timestamp.toDateString())).size < 7;
  const pairs = analysis.correlationMatrix?.significantPairs || [];
  const topCorr = pairs[0];
  const topPattern = (analysis.patterns || []).sort((a,b) => (b.confidence ?? 0) - (a.confidence ?? 0))[0];
  const anomaly = (analysis.anomalies || [])[0];

  return (
    <Card className={`w-full md:w-[360px] lg:w-[400px] bg-card/60 border border-border/60 ${className}`}>
      <CardHeader>
        <CardTitle className="text-lg flex items-center gap-2">
          <Lightbulb className="h-5 w-5" />
          Insights for {student.name}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">

        {activePreset && (
          <div className="text-sm text-muted-foreground">
            Youre viewing: <span className="font-medium">{activePreset.replaceAll('_',' ')}</span>
          </div>
        )}

        {limited && (
          <div className="p-3 rounded-lg bg-muted/40 text-sm">
            Limited data  results may change as more sessions are recorded. Try tracking for at least 7 days and 10 sessions for stronger findings.
          </div>
        )}

        {topPattern && (
          <div className="p-3 rounded-lg border">
            <div className="flex items-center justify-between">
              <div className="font-medium flex items-center gap-2">
                <TrendingUp className="h-4 w-4" />
                Pattern detected
              </div>
              {scoreToBadge(topPattern.confidence)}
            </div>
            <div className="text-sm mt-1">{topPattern.description || topPattern.pattern}</div>
          </div>
        )}

        {topCorr && (
          <div className="p-3 rounded-lg border">
            <div className="font-medium">Correlation</div>
            <div className="text-sm mt-1">{topCorr.factor1}  {topCorr.factor2}</div>
            <div className="text-xs text-muted-foreground">r = {topCorr.correlation.toFixed(2)} ({topCorr.significance})</div>
          </div>
        )}

        {anomaly && (
          <div className="p-3 rounded-lg border">
            <div className="flex items-center gap-2 font-medium">
              <AlertTriangle className="h-4 w-4 text-amber-600" /> Recent Anomaly
            </div>
            <div className="text-sm mt-1">{anomaly.description}</div>
          </div>
        )}

        <div className="pt-2 grid grid-cols-2 gap-2">
          <Button variant="outline" size="sm" onClick={onCreateGoal}><Target className="h-4 w-4 mr-1" />Create Goal</Button>
          <Button variant="outline" size="sm" onClick={onAddIntervention}>Add Intervention</Button>
          <Button variant="outline" size="sm" onClick={onScheduleBreak}>Schedule Break</Button>
          <Button variant="outline" size="sm" onClick={onJumpToTracking}>Track Now</Button>
        </div>

        <div className="text-xs text-muted-foreground pt-2">
          Data: {sessions} sessions, {emotions} emotions
        </div>
      </CardContent>
    </Card>
  );
};

