const ErrorFallback = () => (
  <Card className="border-destructive/20">
    <CardHeader>
      <CardTitle className="flex items-center gap-2 text-destructive">
        <AlertCircle className="h-5 w-5" />
        Failed to load Interactive Visualization
      </CardTitle>
    </CardHeader>
    <CardContent>
