import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { EmotionEntry, SensoryEntry, TrackingEntry, Student } from '@/types/student';
import { enhancedPatternAnalysis, PredictiveInsight, AnomalyDetection as Anomaly } from '@/lib/enhancedPatternAnalysis';
import { patternAnalysis, PatternResult as Pattern } from '@/lib/patternAnalysis';
import { differenceInDays, format, subDays } from 'date-fns';
import {
  Brain,
  TrendingUp,
  TrendingDown,
  Eye,
  Lightbulb,
  Target,
  AlertTriangle,
  CheckCircle,
  Activity,
  BarChart3,
  Heart,
  Shield
} from 'lucide-react';
import { useOptimizedMemo, useAsyncMemo } from '@/hooks/useOptimizedMemo';
import { OptimizedAnimatedCounter } from './OptimizedAnimatedCounter';

const useDashboardMetrics = (filteredData) => {
  return useOptimizedMemo(() => {
    const totalDays = differenceInDays(new Date(), filteredData.emotions[0]?.timestamp || new Date());
    const avgDailyEmotions = filteredData.emotions.length / Math.max(1, totalDays);
    const avgEmotionIntensity = filteredData.emotions.reduce((sum, e) => sum + e.intensity, 0) / Math.max(1, filteredData.emotions.length);
    const recentWeek = filteredData.emotions.filter(e => e.timestamp >= subDays(new Date(), 7));
    const recentAvgIntensity = recentWeek.reduce((sum, e) => sum + e.intensity, 0) / Math.max(1, recentWeek.length);
    const intensityTrend = recentAvgIntensity - avgEmotionIntensity;
    return {
      dataConsistency: Math.min(100, (avgDailyEmotions / 3) * 100),
      emotionalStability: Math.max(0, 100 - (avgEmotionIntensity * 20)),
      intensityTrend,
      totalDataPoints: filteredData.emotions.length + filteredData.sensoryInputs.length
    };
  }, [filteredData], 'progressMetrics');
};

interface EnhancedPersonalizedInsightsProps {
  student: Student;
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  trackingEntries: TrackingEntry[];
}

export const OptimizedEnhancedPersonalizedInsights: React.FC<EnhancedPersonalizedInsightsProps> = ({
  student,
  emotions,
  sensoryInputs,
  trackingEntries
}) => {
  const [selectedTimeframe, setSelectedTimeframe] = useState<'7d' | '30d' | '90d'>('30d');

  const filteredData = useOptimizedMemo(() => {
    const cutoff = subDays(new Date(), selectedTimeframe === '7d' ? 7 : selectedTimeframe === '30d' ? 30 : 90);
    return {
      emotions: emotions.filter(e => e.timestamp >= cutoff),
      sensoryInputs: sensoryInputs.filter(s => s.timestamp >= cutoff),
      trackingEntries: trackingEntries.filter(t => t.timestamp >= cutoff)
    };
  }, [emotions, sensoryInputs, trackingEntries, selectedTimeframe], 'filteredData');

  const { data: insights, loading: insightsLoading } = useAsyncMemo<{
    predictiveInsights: PredictiveInsight[];
    anomalies: Anomaly[];
    emotionPatterns: Pattern[];
    sensoryPatterns: Pattern[];
  } | null>(async () => {
    if (filteredData.emotions.length === 0 && filteredData.sensoryInputs.length === 0) {
      return null;
    }
    const [predictiveInsights, anomalies] = await Promise.all([
      enhancedPatternAnalysis.generatePredictiveInsights(filteredData.emotions, filteredData.sensoryInputs, filteredData.trackingEntries),
      enhancedPatternAnalysis.detectAnomalies(filteredData.emotions, filteredData.sensoryInputs, filteredData.trackingEntries)
    ]);
    return {
      predictiveInsights,
      anomalies,
      emotionPatterns: patternAnalysis.analyzeEmotionPatterns(filteredData.emotions),
      sensoryPatterns: patternAnalysis.analyzeSensoryPatterns(filteredData.sensoryInputs)
    };
  }, [filteredData], null);

  const personalStrengths = useOptimizedMemo(() => {
    const strengths = [];
    const positiveEmotions = filteredData.emotions.filter(e =>
      ['happy', 'calm', 'focused', 'proud', 'excited'].includes(e.emotion.toLowerCase())
    );
    if (positiveEmotions.length > filteredData.emotions.length * 0.6) {
      strengths.push({
        title: 'Emotional Resilience',
        description: `${student.name} maintains positive emotions ${Math.round(positiveEmotions.length / filteredData.emotions.length * 100)}% of the time`,
        icon: Heart,
        confidence: 0.8
      });
    }
    const consistentSensory = filteredData.sensoryInputs.filter(s =>
      s.response.toLowerCase().includes('appropriate') ||
      s.response.toLowerCase().includes('comfortable')
    );
    if (consistentSensory.length > filteredData.sensoryInputs.length * 0.5) {
      strengths.push({
        title: 'Sensory Self-Regulation',
        description: `Shows good sensory processing in ${Math.round(consistentSensory.length / filteredData.sensoryInputs.length * 100)}% of situations`,
        icon: Shield,
        confidence: 0.7
      });
    }
    return strengths;
  }, [filteredData, student.name], 'personalStrengths');

  const growthOpportunities = useOptimizedMemo(() => {
    const opportunities = [];
    const challengingEmotions = filteredData.emotions.filter(e =>
      e.intensity >= 4 && ['anxious', 'frustrated', 'overwhelmed'].includes(e.emotion.toLowerCase())
    );
    if (challengingEmotions.length > 0) {
      opportunities.push({
        title: 'Stress Management',
        description: `Opportunities to develop coping strategies for high-intensity emotions`,
        recommendations: [
          'Practice deep breathing exercises',
          'Implement sensory breaks',
          'Create calm-down strategies'
        ],
        priority: 'high'
      });
    }
    const sensorySeekingCount = filteredData.sensoryInputs.filter(s =>
      s.response.toLowerCase().includes('seeking')
    ).length;
    if (sensorySeekingCount > filteredData.sensoryInputs.length * 0.7) {
      opportunities.push({
        title: 'Sensory Regulation',
        description: `High sensory seeking behavior suggests need for structured sensory input`,
        recommendations: [
          'Schedule regular sensory breaks',
          'Provide fidget tools',
          'Create sensory-rich learning activities'
        ],
        priority: 'medium'
      });
    }
    return opportunities;
  }, [filteredData], 'growthOpportunities');

  const progressMetrics = useDashboardMetrics(filteredData);

  if (insightsLoading) {
    return <div>Loading insights...</div>
  }

  if (!insights) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lightbulb className="h-5 w-5" />
            Personalized Insights for {student.name}
          </CardTitle>
        </CardHeader>
        <CardContent className="text-center py-8">
          <Activity className="h-16 w-16 mx-auto mb-4 opacity-50 text-muted-foreground" />
          <p className="text-muted-foreground">Start tracking emotions and sensory inputs to generate personalized insights</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Lightbulb className="h-5 w-5 text-primary" />
              Personalized Insights for {student.name}
            </CardTitle>
            <div className="flex gap-2">
              {(['7d', '30d', '90d'] as const).map((timeframe) => (
                <Button
                  key={timeframe}
                  variant={selectedTimeframe === timeframe ? 'default' : 'outline'}
                  size="sm"
                  onClick={() => setSelectedTimeframe(timeframe)}
                >
                  {timeframe === '7d' ? '7 days' : timeframe === '30d' ? '30 days' : '90 days'}
                </Button>
              ))}
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary"><OptimizedAnimatedCounter value={progressMetrics.totalDataPoints} /></div>
              <div className="text-sm text-muted-foreground">Total Data Points</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary"><OptimizedAnimatedCounter value={Math.round(progressMetrics.dataConsistency)} />%</div>
              <div className="text-sm text-muted-foreground">Data Consistency</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary"><OptimizedAnimatedCounter value={Math.round(progressMetrics.emotionalStability)} />%</div>
              <div className="text-sm text-muted-foreground">Emotional Stability</div>
            </div>
            <div className="text-center">
              <div className="flex items-center justify-center gap-1">
                <span className="text-2xl font-bold text-primary">
                  {progressMetrics.intensityTrend > 0 ? '+' : ''}{progressMetrics.intensityTrend.toFixed(1)}
                </span>
                {progressMetrics.intensityTrend > 0 ? 
                  <TrendingUp className="h-4 w-4 text-red-500" /> : 
                  <TrendingDown className="h-4 w-4 text-green-500" />
                }
              </div>
              <div className="text-sm text-muted-foreground">Intensity Trend</div>
            </div>
          </div>
        </CardContent>
      </Card>

      <Tabs defaultValue="insights" className="w-full">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="insights">AI Insights</TabsTrigger>
          <TabsTrigger value="strengths">Strengths</TabsTrigger>
          <TabsTrigger value="growth">Growth Areas</TabsTrigger>
          <TabsTrigger value="patterns">Patterns</TabsTrigger>
          <TabsTrigger value="anomalies">Alerts</TabsTrigger>
        </TabsList>

        <TabsContent value="insights" className="space-y-4">
          {insights.predictiveInsights.length > 0 ? (
            insights.predictiveInsights.map((insight, index) => (
              <Card key={index} className="border-l-4 border-l-primary">
                <CardContent className="pt-4">
                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-3 flex-1">
                      <div className="p-2 rounded-full bg-primary/10">
                        {insight.type === 'prediction' ? <Target className="h-4 w-4 text-primary" /> :
                         insight.type === 'trend' ? <TrendingUp className="h-4 w-4 text-primary" /> :
                         insight.type === 'risk' ? <AlertTriangle className="h-4 w-4 text-orange-500" /> :
                         <Lightbulb className="h-4 w-4 text-primary" />}
                      </div>
                      <div className="flex-1">
                        <h4 className="font-semibold text-foreground">{insight.title}</h4>
                        <p className="text-sm text-muted-foreground mt-1">{insight.description}</p>
                        {insight.prediction && (
                          <div className="mt-2 p-2 bg-muted/50 rounded-lg">
                            <div className="text-sm font-medium">
                              Forecast: {insight.prediction.value.toFixed(1)} 
                              <Badge variant="outline" className="ml-2">
                                {insight.prediction.trend}
                              </Badge>
                            </div>
                          </div>
                        )}
                        {insight.recommendations.length > 0 && (
                          <div className="mt-3">
                            <h5 className="text-sm font-medium mb-2">Recommendations:</h5>
                            <ul className="text-sm text-muted-foreground space-y-1">
                              {insight.recommendations.map((rec, recIndex) => (
                                <li key={recIndex} className="flex items-start gap-2">
                                  <CheckCircle className="h-3 w-3 mt-0.5 text-green-500 flex-shrink-0" />
                                  <span>{rec}</span>
                                </li>
                              ))}
                            </ul>
                          </div>
                        )}
                      </div>
                    </div>
                    <div className="text-right">
                      <Badge 
                        variant={insight.confidence > 0.7 ? 'default' : 'outline'}
                        className={
                          insight.confidence > 0.7 ? 'bg-green-100 text-green-800' :
                          insight.confidence > 0.4 ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }
                      >
                       <OptimizedAnimatedCounter value={Math.round(insight.confidence * 100)} />% confidence
                      </Badge>
                      <div className="text-xs text-muted-foreground mt-1">
                        {insight.timeframe}
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Brain className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Continue collecting data to generate predictive insights</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="strengths" className="space-y-4">
          {personalStrengths.length > 0 ? (
            personalStrengths.map((strength, index) => (
              <Card key={index} className="border-l-4 border-l-green-500">
                <CardContent className="pt-4">
                  <div className="flex items-start gap-3">
                    <div className="p-2 rounded-full bg-green-100">
                      <strength.icon className="h-4 w-4 text-green-600" />
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-foreground">{strength.title}</h4>
                      <p className="text-sm text-muted-foreground mt-1">{strength.description}</p>
                      <div className="mt-2">
                        <div className="flex items-center gap-2">
                          <span className="text-xs text-muted-foreground">Strength Level:</span>
                          <Progress value={strength.confidence * 100} className="w-24 h-2" />
                          <span className="text-xs font-medium"><OptimizedAnimatedCounter value={Math.round(strength.confidence * 100)} />%</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Heart className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Strengths will be identified as patterns emerge from data collection</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="growth" className="space-y-4">
          {growthOpportunities.length > 0 ? (
            growthOpportunities.map((opportunity, index) => (
              <Card key={index} className={`border-l-4 ${
                opportunity.priority === 'high' ? 'border-l-orange-500' :
                opportunity.priority === 'medium' ? 'border-l-yellow-500' :
                'border-l-blue-500'
              }`}>
                <CardContent className="pt-4">
                  <div className="flex items-start gap-3">
                    <div className={`p-2 rounded-full ${
                      opportunity.priority === 'high' ? 'bg-orange-100' :
                      opportunity.priority === 'medium' ? 'bg-yellow-100' :
                      'bg-blue-100'
                    }`}>
                      <Target className={`h-4 w-4 ${
                        opportunity.priority === 'high' ? 'text-orange-600' :
                        opportunity.priority === 'medium' ? 'text-yellow-600' :
                        'text-blue-600'
                      }`} />
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <h4 className="font-semibold text-foreground">{opportunity.title}</h4>
                        <Badge variant={
                          opportunity.priority === 'high' ? 'destructive' :
                          opportunity.priority === 'medium' ? 'default' :
                          'secondary'
                        }>
                          {opportunity.priority} priority
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mt-1">{opportunity.description}</p>
                      {opportunity.recommendations.length > 0 && (
                        <div className="mt-3">
                          <h5 className="text-sm font-medium mb-2">Action Steps:</h5>
                          <ul className="text-sm text-muted-foreground space-y-1">
                            {opportunity.recommendations.map((rec, recIndex) => (
                              <li key={recIndex} className="flex items-start gap-2">
                                <span className="text-primary"></span>
                                <span>{rec}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Target className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Growth opportunities will be identified based on data patterns</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="patterns" className="space-y-4">
          {(insights.emotionPatterns.length > 0 || insights.sensoryPatterns.length > 0) ? (
            <>
              {insights.emotionPatterns.map((pattern, index) => (
                <Card key={`emotion-${index}`} className="border-l-4 border-l-purple-500">
                  <CardContent className="pt-4">
                    <div className="flex items-start gap-3">
                      <Brain className="h-5 w-5 text-purple-500 mt-0.5" />
                      <div className="flex-1">
                        <h4 className="font-semibold">Emotional Pattern: {pattern.pattern.replace('-', ' ')}</h4>
                        <p className="text-sm text-muted-foreground mt-1">{pattern.description}</p>
                        <div className="flex items-center gap-4 mt-2">
                          <Badge variant="outline">
                           <OptimizedAnimatedCounter value={Math.round(pattern.confidence * 100)} />% confidence
                          </Badge>
                          <span className="text-xs text-muted-foreground">
                            {pattern.dataPoints} data points
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
              {insights.sensoryPatterns.map((pattern, index) => (
                <Card key={`sensory-${index}`} className="border-l-4 border-l-cyan-500">
                  <CardContent className="pt-4">
                    <div className="flex items-start gap-3">
                      <Eye className="h-5 w-5 text-cyan-500 mt-0.5" />
                      <div className="flex-1">
                        <h4 className="font-semibold">Sensory Pattern: {pattern.pattern.replace('-', ' ')}</h4>
                        <p className="text-sm text-muted-foreground mt-1">{pattern.description}</p>
                        <div className="flex items-center gap-4 mt-2">
                          <Badge variant="outline">
                           <OptimizedAnimatedCounter value={Math.round(pattern.confidence * 100)} />% confidence
                          </Badge>
                          <span className="text-xs text-muted-foreground">
                            {pattern.dataPoints} data points
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </>
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <BarChart3 className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>Patterns will emerge as more data is collected</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="anomalies" className="space-y-4">
          {insights.anomalies.length > 0 ? (
            insights.anomalies.map((anomaly, index) => (
              <Card key={index} className={`border-l-4 ${
                anomaly.severity === 'high' ? 'border-l-red-500' :
                anomaly.severity === 'medium' ? 'border-l-orange-500' :
                'border-l-yellow-500'
              }`}>
                <CardContent className="pt-4">
                  <div className="flex items-start gap-3">
                    <AlertTriangle className={`h-5 w-5 mt-0.5 ${
                      anomaly.severity === 'high' ? 'text-red-500' :
                      anomaly.severity === 'medium' ? 'text-orange-500' :
                      'text-yellow-500'
                    }`} />
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <h4 className="font-semibold">{anomaly.description}</h4>
                        <Badge variant={
                          anomaly.severity === 'high' ? 'destructive' :
                          anomaly.severity === 'medium' ? 'default' :
                          'secondary'
                        }>
                          {anomaly.severity} severity
                        </Badge>
                      </div>
                      <p className="text-xs text-muted-foreground mt-1">
                        {format(anomaly.timestamp, 'MMM dd, yyyy - HH:mm')}
                      </p>
                      {anomaly.recommendations.length > 0 && (
                        <div className="mt-3">
                          <h5 className="text-sm font-medium mb-2">Recommended Actions:</h5>
                          <ul className="text-sm text-muted-foreground space-y-1">
                            {anomaly.recommendations.map((rec, recIndex) => (
                              <li key={recIndex} className="flex items-start gap-2">
                                <span className="text-primary"></span>
                                <span>{rec}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <Shield className="h-16 w-16 mx-auto mb-4 opacity-50" />
              <p>No significant anomalies detected in recent data</p>
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
};

import React, { memo } from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { User, Activity } from "lucide-react";
import { Student } from "@/types/student";

interface StudentCardProps {
  student: Student;
  onView: (student: Student) => void;
  onTrack: (student: Student) => void;
}

/**
 * Optimized StudentCard component with React.memo to prevent unnecessary re-renders
 * Only re-renders when student prop or callbacks change
 */
export const OptimizedStudentCard = memo(({ student, onView, onTrack }: StudentCardProps) => {
  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase();
  };

  return (
    <Card className="overflow-hidden hover:shadow-lg transition-shadow duration-200">
      <CardContent className="p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
              <span className="text-primary font-semibold">{getInitials(student.name)}</span>
            </div>
            <div>
              <h3 className="font-semibold text-foreground">{student.name}</h3>
              <p className="text-sm text-muted-foreground">Grade {student.grade}</p>
            </div>
          </div>
        </div>

        <div className="flex gap-2">
          <Button
            variant="default"
            size="sm"
            onClick={() => onView(student)}
            className="flex-1"
          >
            <User className="h-4 w-4 mr-2" />
            View Profile
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => onTrack(student)}
            className="flex-1"
          >
            <Activity className="h-4 w-4 mr-2" />
            Track
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}, (prevProps, nextProps) => {
  // Custom comparison function for better performance
  // Only re-render if student data actually changed
  return (
    prevProps.student.id === nextProps.student.id &&
    prevProps.student.name === nextProps.student.name &&
    prevProps.student.grade === nextProps.student.grade &&
    prevProps.onView === nextProps.onView &&
    prevProps.onTrack === nextProps.onTrack
  );
});

OptimizedStudentCard.displayName = 'OptimizedStudentCard';

import React, { memo, useMemo, useCallback } from 'react';
import { VirtualScrollArea } from './VirtualScrollArea';
import { PremiumStudentCard } from '../ui/PremiumStudentCard';
import { Student } from '@/types/student';

interface OptimizedStudentListProps {
  students: Student[];
  onViewStudent: (studentId: string) => void;
  onTrackStudent: (studentId: string) => void;
  onDeleteStudent: (studentId: string) => void;
  containerHeight?: number;
  itemHeight?: number;
}

const StudentCardMemo = memo(PremiumStudentCard);

export const OptimizedStudentList: React.FC<OptimizedStudentListProps> = memo(({
  students,
  onViewStudent,
  onTrackStudent,
  onDeleteStudent,
  containerHeight = 600,
  itemHeight = 180
}) => {
  const renderStudent = useCallback(
    (student: Student, index: number) => (
      <StudentCardMemo
        key={student.id}
        student={student}
        index={index}
        onView={() => onViewStudent(student.id)}
        onTrack={() => onTrackStudent(student.id)}
        onDelete={() => onDeleteStudent(student.id)}
      />
    ), [onViewStudent, onTrackStudent, onDeleteStudent]
  );

  if (students.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">No students found</p>
      </div>
    );
  }

  // Use virtual scrolling for large lists
  if (students.length > 20) {
    return (
      <VirtualScrollArea
        items={students}
        itemHeight={itemHeight}
        containerHeight={containerHeight}
        renderItem={renderStudent}
        className="w-full"
        overscan={8} // Increased overscan for smoother scrolling with larger student cards
      />
    );
  }

  // For smaller lists, render directly
  return (
    <div className="space-y-4">
      {students.map((student, index) => renderStudent(student, index))}
    </div>
  );
});

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@/components/ui/collapsible';
import { PaginatedSessionsList } from '@/components/PaginatedSessionsList';
import { DataRequirementsCalculator } from '@/components/DataRequirementsCalculator';
import { DataQualityFeedback } from '@/components/DataQualityFeedback';
import { DataCollectionRoadmap } from '@/components/DataCollectionRoadmap';
import { AnalyticsStatusIndicator } from '@/components/AnalyticsStatusIndicator';
import { IS_PROD } from '@/lib/env';
import { DateRangeSelector, TimeRange } from '@/components/DateRangeSelector';
import { Student, TrackingEntry, EmotionEntry, SensoryEntry, Insights } from '@/types/student';
import { useTranslation } from '@/hooks/useTranslation';
import { Plus, Calendar, BarChart3, TrendingUp, ChevronDown, Info, Settings } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { cn } from '@/lib/utils';

/**
 * @interface DashboardSectionProps
 * Props for the DashboardSection component.
 * 
 * @property {Student} student - The student object.
 * @property {TrackingEntry[]} trackingEntries - All tracking entries for the student.
 * @property {object} filteredData - Data filtered by the selected date range.
 * @property {TimeRange} selectedRange - The currently selected time range for filtering.
 * @property {(range: TimeRange) => void} onRangeChange - Callback to handle changes to the date range.
 * @property {Insights | null} insights - The AI-generated insights for the student.
 * @property {boolean} isLoadingInsights - Flag indicating if insights are currently being loaded.
 */
interface DashboardSectionProps {
  student: Student;
  trackingEntries: TrackingEntry[];
  filteredData: {
    entries: TrackingEntry[];
    emotions: EmotionEntry[];
    sensoryInputs: SensoryEntry[];
  };
  selectedRange: TimeRange;
  onRangeChange: (range: TimeRange) => void;
  insights: Insights | null;
  isLoadingInsights: boolean;
}

export function DashboardSection({ 
  student, 
  trackingEntries, 
  filteredData, 
  selectedRange, 
  onRangeChange,
  insights,
  isLoadingInsights, 
}: DashboardSectionProps) {
  const { tStudent, tCommon } = useTranslation();
  const navigate = useNavigate();
  const [showAdvancedDetails, setShowAdvancedDetails] = useState(false);

  // Calculate stats for the selected period
  const stats = {
    totalSessions: filteredData.entries.length,
    totalEmotions: filteredData.emotions.length,
    totalSensoryInputs: filteredData.sensoryInputs.length
  };

  // Calculate simple data quality score
  const dataQualityScore = Math.min(
    100,
    Math.round(
      ((stats.totalSessions * 0.4) + (stats.totalEmotions * 0.3) + (stats.totalSensoryInputs * 0.3)) / 10 * 100
    )
  );

  const getQualityStatus = (score: number) => {
    if (score >= 80) return { label: 'Utmerket', color: 'bg-green-500', variant: 'default' as const };
    if (score >= 60) return { label: 'God', color: 'bg-blue-500', variant: 'secondary' as const };
    if (score >= 40) return { label: 'Moderat', color: 'bg-yellow-500', variant: 'outline' as const };
    return { label: 'Trenger mer data', color: 'bg-red-500', variant: 'destructive' as const };
  };

  const qualityStatus = getQualityStatus(dataQualityScore);

  return (
    <div className="space-y-6">
      {/* Header Actions */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Oversikt</h2>
          <p className="text-muted-foreground">
            Sammendrag av {student.name}s data og aktivitet
          </p>
        </div>
        <Button
          onClick={() => navigate(`/track/${student.id}`)}
          className="bg-gradient-primary hover:opacity-90"
        >
          <Plus className="h-4 w-4 mr-2" />
          {String(tStudent('interface.newSession'))}
        </Button>
      </div>

      {/* Analytics Status */}
      <Card className="bg-gradient-card border-0 shadow-soft">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <BarChart3 className="h-5 w-5" />
            Analysestatus
          </CardTitle>
        </CardHeader>
        <CardContent>
          <AnalyticsStatusIndicator studentId={student.id} showDetails={!IS_PROD} />
        </CardContent>
      </Card>

      {/* Date Range Selector */}
      <Card className="bg-gradient-card border-0 shadow-soft">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            {String(tStudent('interface.dataAnalysisPeriod'))}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <DateRangeSelector 
            selectedRange={selectedRange}
            onRangeChange={onRangeChange}
          />
        </CardContent>
      </Card>

      {/* Summary Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card className="bg-gradient-card border-0 shadow-soft">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  {String(tStudent('interface.sessionsInPeriod'))}
                </p>
                <p className="text-2xl font-bold">{stats.totalSessions}</p>
              </div>
              <Badge variant="secondary">{String(tStudent('interface.totalSessions'))}</Badge>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gradient-card border-0 shadow-soft">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  {String(tStudent('interface.emotionsTracked'))}
                </p>
                <p className="text-2xl font-bold">{stats.totalEmotions}</p>
              </div>
              <Badge variant="secondary">{String(tStudent('interface.totalEmotions'))}</Badge>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gradient-card border-0 shadow-soft">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  {String(tStudent('interface.sensoryInputs'))}
                </p>
                <p className="text-2xl font-bold">{stats.totalSensoryInputs}</p>
              </div>
              <Badge variant="secondary">{String(tStudent('interface.totalSensoryInputs'))}</Badge>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Simple Data Quality Overview */}
      <Card className="bg-gradient-card border-0 shadow-soft">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Info className="h-5 w-5" />
            Datakvalitet
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-between mb-4">
            <div>
              <p className="text-sm text-muted-foreground">Samlet kvalitetsscore</p>
              <p className="text-3xl font-bold">{dataQualityScore}%</p>
            </div>
            <Badge variant={qualityStatus.variant} className="ml-2">
              {qualityStatus.label}
            </Badge>
          </div>

          <div className="bg-muted rounded-full h-2 mb-4 overflow-hidden">
            <div 
              className={cn(
                "bg-primary h-2 rounded-full transition-all duration-500",
                "w-full origin-left"
              )}
              style={{ transform: `scaleX(${dataQualityScore / 100})` }}
            />
          </div>

          <Collapsible open={showAdvancedDetails} onOpenChange={setShowAdvancedDetails}>
            <CollapsibleTrigger asChild>
              <Button variant="ghost" className="w-full justify-between p-0 h-auto">
                <span className="text-sm text-muted-foreground">
                  {showAdvancedDetails ? 'Skjul detaljer' : 'Vis detaljert analyse'}
                </span>
                <ChevronDown className={cn(
                  "h-4 w-4 transition-transform",
                  showAdvancedDetails && "rotate-180"
                )} />
              </Button>
            </CollapsibleTrigger>

            <CollapsibleContent className="space-y-6 mt-6">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <DataRequirementsCalculator
                  entries={filteredData.entries}
                  emotions={filteredData.emotions}
                  sensoryInputs={filteredData.sensoryInputs}
                />
                <DataQualityFeedback
                  entries={filteredData.entries}
                  emotions={filteredData.emotions}
                  sensoryInputs={filteredData.sensoryInputs}
                />
              </div>

              <DataCollectionRoadmap
                entries={filteredData.entries}
                emotions={filteredData.emotions}
                sensoryInputs={filteredData.sensoryInputs}
              />
            </CollapsibleContent>
          </Collapsible>
        </CardContent>
      </Card>

      {/* AI Insights */}
      {isLoadingInsights ? (
        <Card className="bg-gradient-card border-0 shadow-soft">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5 animate-pulse" />
              AI-genererte innsikter
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="p-3 bg-accent/20 rounded-lg animate-pulse h-12 w-full" />
              <div className="p-3 bg-accent/20 rounded-lg animate-pulse h-12 w-full" />
            </div>
          </CardContent>
        </Card>
      ) : insights && insights.suggestions && insights.suggestions.length > 0 && (
        <Card className="bg-gradient-card border-0 shadow-soft">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5" />
              AI-genererte innsikter
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {insights.suggestions.slice(0, 3).map((suggestion: string, index: number) => (
                <div key={index} className="p-3 bg-accent/20 rounded-lg">
                  <p className="text-sm">{suggestion}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Recent Sessions */}
      {filteredData.entries.length > 0 && (
        <div>
          <h3 className="text-lg font-semibold mb-4">Nylige kter</h3>
          <PaginatedSessionsList sessions={filteredData.entries} />
        </div>
      )}
    </div>
  );
}

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { AdvancedSearch } from '@/components/AdvancedSearch';
import { QuickEntryTemplates } from '@/components/QuickEntryTemplates';
import { PeriodComparison } from '@/components/PeriodComparison';
import { Student, TrackingEntry, EmotionEntry, SensoryEntry, Goal } from '@/types/student';
import { useTranslation } from '@/hooks/useTranslation';
import { Search, Zap, Calendar } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

interface ToolsSectionProps {
  student: Student;
  trackingEntries: TrackingEntry[];
  emotions: EmotionEntry[];
  sensoryInputs: SensoryEntry[];
  goals: Goal[];
  activeToolSection: string;
  onToolSectionChange: (section: string) => void;
  onSearchResults?: (results: any) => void;
}

export function ToolsSection({ 
  student, 
  trackingEntries, 
  emotions, 
  sensoryInputs, 
  goals,
  activeToolSection,
  onToolSectionChange,
  onSearchResults 
}: ToolsSectionProps) {
  const { tStudent } = useTranslation();
  const navigate = useNavigate();

  const handleQuickTemplateApply = (template: any) => {
    navigate(`/track/${student.id}`, { 
      state: { 
        prefilledData: template 
      } 
    });
  };

  const toolSections = [
    {
      id: 'search',
      title: tStudent('interface.advancedSearch'),
      icon: Search,
      component: (
        <AdvancedSearch
          students={[student]}
          trackingEntries={trackingEntries}
          emotions={emotions}
          sensoryInputs={sensoryInputs}
          goals={goals}
          onResultsChange={onSearchResults}
        />
      )
    },
    {
      id: 'templates',
      title: tStudent('interface.quickTemplates'),
      icon: Zap,
      component: (
        <QuickEntryTemplates
          studentId={student.id}
          onApplyTemplate={handleQuickTemplateApply}
        />
      )
    },
    {
      id: 'compare',
      title: 'Periodesammenligning',
      icon: Calendar,
      component: (
        <PeriodComparison
          emotions={emotions}
          sensoryInputs={sensoryInputs}
          currentRange={{ start: new Date(), end: new Date(), label: "Current" }}
        />
      )
    }
  ];

  const activeSection = toolSections.find(section => section.id === activeToolSection);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h2 className="text-2xl font-bold">Verkty</h2>
        <p className="text-muted-foreground">
          Avanserte verkty for sk, maler og sammenligning
        </p>
      </div>

      {/* Tool Navigation */}
      <div className="flex flex-wrap gap-2">
        {toolSections.map((section) => (
          <button
            key={section.id}
            onClick={() => onToolSectionChange(section.id)}
            className={`flex items-center gap-2 px-4 py-2 rounded-lg border transition-colors ${
              activeToolSection === section.id
                ? 'bg-accent text-accent-foreground border-accent'
                : 'bg-background hover:bg-accent/50 border-border'
            }`}
          >
            <section.icon className="h-4 w-4" />
            {String(section.title)}
          </button>
        ))}
      </div>

      {/* Active Tool Section */}
      {activeSection && (
        <Card className="bg-gradient-card border-0 shadow-soft">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <activeSection.icon className="h-5 w-5" />
              {String(activeSection.title)}
            </CardTitle>
          </CardHeader>
          <CardContent>
            {activeSection.component}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Separator } from '@/components/ui/separator';
import { Download, Upload, Save, RotateCcw, Info } from 'lucide-react';
import { toast } from 'sonner';
import { analyticsConfig, AnalyticsConfiguration } from '@/lib/analyticsConfig';
import { useTranslation } from '@/hooks/useTranslation';
import { logger } from '@/lib/logger';

interface AnalyticsConfigProps {
  className?: string;
  onChange?: (config: AnalyticsConfiguration) => void;
}

interface FieldError {
  fieldId: string;
  message: string;
}

function useConfigState(): [AnalyticsConfiguration, React.Dispatch<React.SetStateAction<AnalyticsConfiguration>>] {
  const [cfg, setCfg] = useState<AnalyticsConfiguration>(() => analyticsConfig.getConfig());
  useEffect(() => {
    const unsub = analyticsConfig.subscribe((next) => setCfg(next));
    return unsub;
  }, []);
  return [cfg, setCfg];
}

export const AnalyticsConfig: React.FC<AnalyticsConfigProps> = ({ className, onChange }) => {
  const { tSettings, tCommon } = useTranslation();
  const [config, setConfig] = useConfigState();
  const [errors, setErrors] = useState<FieldError[]>([]);
  const notifiedRef = useRef(false);
  const firstInvalidRef = useRef<HTMLInputElement | null>(null);
  const fileInputRef = useRef<HTMLInputElement | null>(null);
  const liveRegionRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    onChange?.(config);
  }, [config, onChange]);

  const onNumberChange = useCallback((path: [keyof AnalyticsConfiguration, ...string[]], value: number) => {
    setConfig((prev) => {
      const next: AnalyticsConfiguration = JSON.parse(JSON.stringify(prev));
      // Narrowed typed navigation using index signatures where applicable
      let current: unknown = next as unknown;
      for (let i = 0; i < path.length - 1; i++) {
        const key = path[i];
        if (current && typeof current === 'object' && key in (current as Record<string, unknown>)) {
          current = (current as Record<string, unknown>)[key as string];
        }
      }
      const lastKey = path[path.length - 1] as string;
      if (current && typeof current === 'object') {
        (current as Record<string, unknown>)[lastKey] = value;
      }
      return next;
    });
  }, [setConfig]);

  const onRadioChange = useCallback((value: 'low' | 'medium' | 'high') => {
    setConfig((prev) => ({
      ...prev,
      alertSensitivity: { ...prev.alertSensitivity, level: value },
    }));
  }, [setConfig]);

  const validate = useCallback((cfg: AnalyticsConfiguration): FieldError[] => {
    const errs: FieldError[] = [];
    const push = (fieldId: string, message: string) => errs.push({ fieldId, message });

    if (!Number.isFinite(cfg.patternAnalysis.minDataPoints) || cfg.patternAnalysis.minDataPoints < 1) {
      push('pattern-minDataPoints', tSettings('validation.minDataPoints', { defaultValue: 'Minimum data points must be >= 1' }));
    }
    if (cfg.patternAnalysis.correlationThreshold < 0 || cfg.patternAnalysis.correlationThreshold > 1) {
      push('pattern-correlationThreshold', tSettings('validation.correlationThreshold', { defaultValue: 'Correlation threshold must be between 0 and 1' }));
    }
    if (cfg.enhancedAnalysis.anomalyThreshold < 0.1 || cfg.enhancedAnalysis.anomalyThreshold > 5) {
      push('enhanced-anomalyThreshold', tSettings('validation.anomalyThreshold', { defaultValue: 'Anomaly threshold should be between 0.1 and 5' }));
    }
    if (cfg.timeWindows.defaultAnalysisDays < 1 || cfg.timeWindows.defaultAnalysisDays > 365) {
      push('time-defaultAnalysisDays', tSettings('validation.defaultAnalysisDays', { defaultValue: 'Default analysis window must be between 1 and 365 days' }));
    }
    if (cfg.cache.ttl < 0) {
      push('cache-ttl', tSettings('validation.cacheTtl', { defaultValue: 'Cache TTL must be >= 0 ms' }));
    }
    return errs;
  }, [tSettings]);

  const handleSave = useCallback(() => {
    const errs = validate(config);
    setErrors(errs);

    if (errs.length) {
      if (!notifiedRef.current) {
        toast.error(tSettings('validation.title', { defaultValue: 'Please fix the highlighted fields' }));
        notifiedRef.current = true;
      }
      // Focus first invalid
      setTimeout(() => firstInvalidRef.current?.focus(), 0);
      // Announce in live region
      if (liveRegionRef.current) {
        liveRegionRef.current.textContent = errs[0]?.message ?? '';
      }
      return;
    }

    analyticsConfig.updateConfig(config);
    notifiedRef.current = false;
    toast.success(tSettings('save.success', { defaultValue: 'Analytics configuration saved' }));
  }, [config, tSettings, validate]);

  const handleReset = useCallback(() => {
    analyticsConfig.resetToDefaults();
    setErrors([]);
    notifiedRef.current = false;
    toast.success(tSettings('reset.success', { defaultValue: 'Settings reset to defaults' }));
  }, [tSettings]);

  const exportConfig = useCallback(() => {
    try {
      const configString = analyticsConfig.exportConfig();
      const blob = new Blob([configString], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'analytics-config.json';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
      toast.success(tSettings('export.success', { defaultValue: 'Configuration exported' }));
    } catch (error) {
      logger.error('Failed to export analytics config', error instanceof Error ? error : new Error(String(error)));
      toast.error(tSettings('export.error', { defaultValue: 'Failed to export configuration' }));
    }
  }, [tSettings]);

  const importConfig = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (ev) => {
      try {
        const content = ev.target?.result as string;
        if (analyticsConfig.importConfig(content)) {
          setErrors([]);
          notifiedRef.current = false;
          toast.success(tSettings('import.success', { defaultValue: 'Configuration imported' }));
        } else {
          toast.error(tSettings('import.invalid', { defaultValue: 'Invalid configuration file' }));
        }
      } catch (err) {
        logger.error('Failed reading imported config', err instanceof Error ? err : new Error(String(err)));
        toast.error(tSettings('import.error', { defaultValue: 'Failed to read configuration file' }));
      } finally {
        if (fileInputRef.current) fileInputRef.current.value = '';
      }
    };
    reader.readAsText(file);
  }, [tSettings]);

  const errorFor = useCallback((id: string) => errors.find((e) => e.fieldId === id)?.message, [errors]);

  const Preview = useMemo(() => {
    const level = config.alertSensitivity.level;
    const days = config.timeWindows.defaultAnalysisDays;
    const corr = config.patternAnalysis.correlationThreshold;
    const anomaly = config.enhancedAnalysis.anomalyThreshold;
    return (
      <Card aria-live="polite" className="bg-card border shadow-soft">
        <CardHeader className="pb-2">
          <CardTitle className="text-base">{tSettings('preview.title', { defaultValue: 'Configuration preview' })}</CardTitle>
          <CardDescription>{tSettings('preview.subtitle', { defaultValue: 'A quick summary of key analytics behaviors' })}</CardDescription>
        </CardHeader>
        <CardContent className="pt-0">
          <ul className="text-sm space-y-1">
            <li className="flex items-center gap-2"><span className="font-medium">{tSettings('preview.sensitivity', { defaultValue: 'Alert sensitivity' })}:</span> <span className="capitalize">{level}</span></li>
            <li className="flex items-center gap-2"><span className="font-medium">{tSettings('preview.window', { defaultValue: 'Default analysis window' })}:</span> {days} {tCommon('units.days', { defaultValue: 'days' })}</li>
            <li className="flex items-center gap-2"><span className="font-medium">{tSettings('preview.correlation', { defaultValue: 'Correlation threshold' })}:</span> {corr}</li>
            <li className="flex items-center gap-2"><span className="font-medium">{tSettings('preview.anomaly', { defaultValue: 'Anomaly threshold' })}:</span> {anomaly}</li>
          </ul>
        </CardContent>
      </Card>
    );
  }, [config, tSettings, tCommon]);

  return (
    <section className={className} aria-labelledby="analytics-config-heading" role="region">
      <a href="#analytics-config-main" className="sr-only focus:not-sr-only focus:absolute focus:top-2 focus:left-2 focus:z-50 bg-primary text-primary-foreground px-3 py-1 rounded">{tCommon('a11y.skip_to_content', { defaultValue: 'Skip to content' })}</a>

      <h2 id="analytics-config-heading" className="text-xl font-semibold text-foreground">{tSettings('analytics.title', { defaultValue: 'Analytics configuration' })}</h2>

      <div aria-live="polite" aria-atomic="true" className="sr-only" ref={liveRegionRef} />

      <div id="analytics-config-main" className="mt-4 grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">{tSettings('sections.thresholds', { defaultValue: 'Thresholds & windows' })}</CardTitle>
              <CardDescription>{tSettings('sections.thresholds_desc', { defaultValue: 'Tune analysis requirements and windows' })}</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="pattern-minDataPoints" className="block mb-1">{tSettings('fields.minDataPoints', { defaultValue: 'Minimum data points' })}</Label>
                  <Input
                    id="pattern-minDataPoints"
                    type="number"
                    inputMode="numeric"
                    aria-describedby="help-minDataPoints"
                    aria-invalid={Boolean(errorFor('pattern-minDataPoints'))}
                    ref={(el) => { if (errorFor('pattern-minDataPoints')) firstInvalidRef.current = el; }}
                    className="w-full"
                    value={config.patternAnalysis.minDataPoints}
                    onChange={(e) => onNumberChange(['patternAnalysis', 'minDataPoints'], Number(e.target.value))}
                  />
                  <p id="help-minDataPoints" className="mt-1 text-xs text-muted-foreground flex items-center gap-1">
                    <Info className="h-3 w-3" />
                    {errorFor('pattern-minDataPoints') || tSettings('help.minDataPoints', { defaultValue: 'Minimum entries required to run analysis' })}
                  </p>
                </div>

                <div>
                  <Label htmlFor="pattern-correlationThreshold" className="block mb-1">{tSettings('fields.correlationThreshold', { defaultValue: 'Correlation threshold' })}</Label>
                  <Input
                    id="pattern-correlationThreshold"
                    type="number"
                    step="0.05"
                    min="0"
                    max="1"
                    inputMode="decimal"
                    aria-describedby="help-correlationThreshold"
                    aria-invalid={Boolean(errorFor('pattern-correlationThreshold'))}
                    ref={(el) => { if (errorFor('pattern-correlationThreshold')) firstInvalidRef.current = el; }}
                    className="w-full"
                    value={config.patternAnalysis.correlationThreshold}
                    onChange={(e) => onNumberChange(['patternAnalysis', 'correlationThreshold'], Number(e.target.value))}
                  />
                  <p id="help-correlationThreshold" className="mt-1 text-xs text-muted-foreground flex items-center gap-1">
                    <Info className="h-3 w-3" />
