import React, { useMemo } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { 
  Clock, 
  Pause, 
  Play, 
  Save, 
  X, 
  AlertCircle,
  CheckCircle,
  Activity,
  TrendingUp
} from 'lucide-react';
import { useTracking } from '@/contexts/TrackingContext';
import { formatDuration } from 'date-fns';
import { cn } from '@/lib/utils';

interface SessionIndicatorProps {
  className?: string;
  compact?: boolean;
  showControls?: boolean;
}

/**
 * SessionIndicator Component
 * Displays current session status, quality metrics, and controls
 */
export const SessionIndicator: React.FC<SessionIndicatorProps> = ({
  className,
  compact = false,
  showControls = true,
}) => {
  const {
    currentSession,
    pauseSession,
    resumeSession,
    saveSession,
    discardSession,
    getDataQuality,
  } = useTracking();

  const quality = useMemo(() => getDataQuality(), [getDataQuality]);

  const sessionDuration = useMemo(() => {
    if (!currentSession) return '00:00';
    const duration = Date.now() - currentSession.startTime.getTime();
    const minutes = Math.floor(duration / 60000);
    const seconds = Math.floor((duration % 60000) / 1000);
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  }, [currentSession]);

  if (!currentSession) {
    return null;
  }

  const getStatusColor = () => {
    if (currentSession.isPaused) return 'bg-yellow-500';
    if (quality.completeness >= 70) return 'bg-green-500';
    if (quality.completeness >= 40) return 'bg-blue-500';
    return 'bg-gray-500';
  };

  const getQualityIcon = () => {
    if (quality.completeness >= 70) return <CheckCircle className="h-4 w-4" />;
    if (quality.completeness >= 40) return <Activity className="h-4 w-4" />;
    return <AlertCircle className="h-4 w-4" />;
  };

  if (compact) {
    return (
      <div className={cn('flex items-center gap-2 p-2 rounded-lg bg-background/80 backdrop-blur', className)}>
        <div className={cn('w-2 h-2 rounded-full animate-pulse', getStatusColor())} />
        <span className="text-sm font-medium">{sessionDuration}</span>
        <Badge variant="outline" className="text-xs">
          {quality.emotionCount} emotions
        </Badge>
        <Badge variant="outline" className="text-xs">
          {quality.sensoryCount} sensory
        </Badge>
        {showControls && (
          <div className="flex gap-1 ml-auto">
            {currentSession.isPaused ? (
              <Button size="icon" variant="ghost" onClick={resumeSession} className="h-6 w-6">
                <Play className="h-3 w-3" />
              </Button>
            ) : (
              <Button size="icon" variant="ghost" onClick={pauseSession} className="h-6 w-6">
                <Pause className="h-3 w-3" />
              </Button>
            )}
            <Button size="icon" variant="ghost" onClick={saveSession} className="h-6 w-6">
              <Save className="h-3 w-3" />
            </Button>
          </div>
        )}
      </div>
    );
  }

  return (
    <Card className={cn('bg-gradient-card border-0 shadow-soft', className)}>
      <CardContent className="p-4">
        {/* Session Header */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <div className={cn('w-3 h-3 rounded-full animate-pulse', getStatusColor())} />
            <div>
              <h3 className="font-semibold text-sm">Active Session</h3>
              <p className="text-xs text-muted-foreground">
                {currentSession.isPaused ? 'Paused' : 'Recording'}
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Clock className="h-4 w-4 text-muted-foreground" />
            <span className="font-mono text-sm font-medium">{sessionDuration}</span>
          </div>
        </div>

        {/* Quality Metrics */}
        <div className="space-y-3 mb-4">
          <div>
            <div className="flex items-center justify-between mb-1">
              <span className="text-xs text-muted-foreground">Data Quality</span>
              <div className="flex items-center gap-1">
                {getQualityIcon()}
                <span className="text-xs font-medium">{quality.completeness}%</span>
              </div>
            </div>
            <Progress value={quality.completeness} className="h-2" />
          </div>

          <div className="grid grid-cols-3 gap-2 text-center">
            <div className="p-2 rounded bg-muted/50">
              <p className="text-lg font-semibold">{quality.emotionCount}</p>
              <p className="text-xs text-muted-foreground">Emotions</p>
            </div>
            <div className="p-2 rounded bg-muted/50">
              <p className="text-lg font-semibold">{quality.sensoryCount}</p>
              <p className="text-xs text-muted-foreground">Sensory</p>
            </div>
            <div className="p-2 rounded bg-muted/50">
              <p className="text-lg font-semibold">
                {quality.hasEnvironmental ? '' : ''}
              </p>
              <p className="text-xs text-muted-foreground">Environment</p>
            </div>
          </div>
        </div>

        {/* Last Saved Indicator */}
        {quality.lastSaved && (
          <div className="flex items-center gap-2 mb-3 text-xs text-muted-foreground">
            <CheckCircle className="h-3 w-3 text-green-500" />
            <span>
              Last saved {new Date(quality.lastSaved).toLocaleTimeString()}
            </span>
          </div>
        )}

        {/* Session Controls */}
        {showControls && (
          <div className="flex gap-2">
            {currentSession.isPaused ? (
              <Button
                size="sm"
                variant="outline"
                onClick={resumeSession}
                className="flex-1"
              >
                <Play className="h-4 w-4 mr-2" />
                Resume
              </Button>
            ) : (
              <Button
                size="sm"
                variant="outline"
                onClick={pauseSession}
                className="flex-1"
              >
                <Pause className="h-4 w-4 mr-2" />
                Pause
              </Button>
            )}

            <Button
              size="sm"
              variant="default"
              onClick={saveSession}
              className="flex-1"
              disabled={quality.completeness < 20}
            >
              <Save className="h-4 w-4 mr-2" />
              Save
            </Button>

            <Button
              size="sm"
              variant="ghost"
              onClick={discardSession}
              className="px-2"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        )}

        {/* Quality Warnings */}
        {quality.completeness < 20 && !currentSession.isPaused && (
          <div className="mt-3 p-2 rounded bg-yellow-500/10 border border-yellow-500/20">
            <p className="text-xs text-yellow-600 dark:text-yellow-400">
              Add more data to improve session quality
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

import React, { useState, useEffect, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { 
  Clock, 
  RefreshCw, 
  Trash2, 
  Save,
  AlertTriangle,
  User,
  Calendar,
  Activity
} from 'lucide-react';
import { useTracking } from '@/contexts/TrackingContext';
import { sessionManager } from '@/lib/sessionManager';
import { formatDistanceToNow } from 'date-fns';
import { cn } from '@/lib/utils';

interface SessionRecoveryProps {
  studentId?: string;
  className?: string;
  autoRecover?: boolean;
}

/**
 * SessionRecovery Component
 * Handles recovery of abandoned or interrupted sessions
 */
export const SessionRecovery: React.FC<SessionRecoveryProps> = ({
  studentId,
  className,
  autoRecover = false,
}) => {
  const { recoverSession, sessions } = useTracking();
  const [recoverableSessions, setRecoverableSessions] = useState<any[]>([]);
  const [isRecovering, setIsRecovering] = useState(false);
  const [selectedSession, setSelectedSession] = useState<string | null>(null);

  const handleRecover = useCallback(async (sessionId: string) => {
    setIsRecovering(true);
    setSelectedSession(sessionId);

    try {
      recoverSession(sessionId);
      // Remove from recoverable list
      setRecoverableSessions(prev => 
        prev.filter(s => s.sessionId !== sessionId)
      );
    } catch (error) {
      console.error('Failed to recover session:', error);
    } finally {
      setIsRecovering(false);
      setSelectedSession(null);
    }
  }, [recoverSession]);

  const checkSessions = useCallback(() => {
    const recovered = sessionManager.recoverSessions();
    const filtered = studentId 
      ? recovered.filter(s => s.studentId === studentId)
      : recovered;
    setRecoverableSessions(filtered);

    if (autoRecover && filtered.length === 1) {
      handleRecover(filtered[0].sessionId);
    }
  }, [studentId, autoRecover, handleRecover]);

  useEffect(() => {
    checkSessions();
  }, [checkSessions]);



  const handleDiscard = (sessionId: string) => {
    sessionManager.abandonSession(sessionId);
    setRecoverableSessions(prev => 
      prev.filter(s => s.sessionId !== sessionId)
    );
  };

  const handleDiscardAll = () => {
    recoverableSessions.forEach(session => {
      sessionManager.abandonSession(session.sessionId);
    });
    setRecoverableSessions([]);
  };

  if (recoverableSessions.length === 0) {
    return null;
  }

  // Compact alert for single recoverable session
  if (recoverableSessions.length === 1) {
    const session = recoverableSessions[0];
    const age = formatDistanceToNow(new Date(session.metadata.startTime), { addSuffix: true });

    return (
      <Alert className={cn('border-yellow-500/50 bg-yellow-500/10', className)}>
        <AlertTriangle className="h-4 w-4" />
        <AlertTitle>Unsaved Session Found</AlertTitle>
        <AlertDescription className="mt-2">
          <p className="text-sm mb-3">
            You have an incomplete session from {age} with {session.metadata.dataPoints} data points.
          </p>
          <div className="flex gap-2">
            <Button
              size="sm"
              variant="default"
              onClick={() => handleRecover(session.sessionId)}
              disabled={isRecovering}
            >
              {isRecovering ? (
                <>
                  <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                  Recovering...
                </>
              ) : (
                <>
                  <RefreshCw className="h-4 w-4 mr-2" />
                  Resume Session
                </>
              )}
            </Button>
            <Button
              size="sm"
              variant="ghost"
              onClick={() => handleDiscard(session.sessionId)}
            >
              <Trash2 className="h-4 w-4 mr-2" />
              Discard
            </Button>
          </div>
        </AlertDescription>
      </Alert>
    );
  }

  // Full card for multiple recoverable sessions
  return (
    <Card className={cn('bg-gradient-card border-0 shadow-soft', className)}>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <AlertTriangle className="h-5 w-5 text-yellow-500" />
            <span>Recoverable Sessions</span>
          </div>
          <Badge variant="outline">{recoverableSessions.length}</Badge>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <p className="text-sm text-muted-foreground mb-4">
          Found {recoverableSessions.length} incomplete session(s) that can be recovered.
        </p>

        <div className="space-y-3 mb-4 max-h-64 overflow-y-auto">
          {recoverableSessions.map((session) => {
            const age = formatDistanceToNow(new Date(session.metadata.startTime), { addSuffix: true });
            const quality = session.metadata.quality;

            return (
              <div
                key={session.sessionId}
                className={cn(
                  'p-3 rounded-lg border transition-colors',
                  selectedSession === session.sessionId 
                    ? 'border-primary bg-primary/5' 
                    : 'border-border hover:bg-muted/50'
                )}
              >
                <div className="flex items-start justify-between mb-2">
                  <div className="flex items-center gap-2">
                    <Clock className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm font-medium">Started {age}</span>
                  </div>
                  <Badge 
                    variant={session.metadata.status === 'paused' ? 'secondary' : 'outline'}
                    className="text-xs"
                  >
                    {session.metadata.status}
                  </Badge>
                </div>

                <div className="grid grid-cols-3 gap-2 text-xs mb-3">
                  <div className="flex items-center gap-1">
                    <Activity className="h-3 w-3 text-muted-foreground" />
                    <span>{session.metadata.dataPoints} data points</span>
                  </div>
                  <div className="flex items-center gap-1">
                    <Clock className="h-3 w-3 text-muted-foreground" />
                    <span>{Math.floor(session.metadata.duration / 60000)}m</span>
                  </div>
                  <div className="flex items-center gap-1">
                    <span className="text-muted-foreground">Quality:</span>
                    <span className="font-medium">{quality.score}%</span>
                  </div>
                </div>

                {/* Data summary */}
                <div className="flex gap-2 mb-3">
                  {session.data.emotions.length > 0 && (
                    <Badge variant="outline" className="text-xs">
                      {session.data.emotions.length} emotions
                    </Badge>
                  )}
                  {session.data.sensoryInputs.length > 0 && (
                    <Badge variant="outline" className="text-xs">
                      {session.data.sensoryInputs.length} sensory
                    </Badge>
                  )}
                  {session.data.environmentalData && (
                    <Badge variant="outline" className="text-xs">
                      Environmental
                    </Badge>
                  )}
                </div>

                <div className="flex gap-2">
                  <Button
                    size="sm"
                    variant="default"
                    onClick={() => handleRecover(session.sessionId)}
                    disabled={isRecovering && selectedSession === session.sessionId}
                    className="flex-1"
                  >
                    {isRecovering && selectedSession === session.sessionId ? (
                      <>
                        <RefreshCw className="h-3 w-3 mr-1 animate-spin" />
                        Recovering...
                      </>
                    ) : (
                      <>
                        <RefreshCw className="h-3 w-3 mr-1" />
                        Resume
                      </>
                    )}
                  </Button>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => handleDiscard(session.sessionId)}
                  >
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            );
          })}
        </div>

        <div className="flex justify-end pt-3 border-t">
          <Button
            size="sm"
            variant="ghost"
            onClick={handleDiscardAll}
            className="text-destructive"
          >
            <Trash2 className="h-4 w-4 mr-2" />
            Discard All
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

import React from 'react';
import { Link } from 'react-router-dom';
import { cn } from '@/lib/utils';

export interface BreadcrumbItem {
  label: string;
  href?: string;
  current?: boolean;
}

interface BreadcrumbsProps {
  items: BreadcrumbItem[];
  className?: string;
}

export const Breadcrumbs: React.FC<BreadcrumbsProps> = ({ items, className }) => {
  if (!items || items.length === 0) return null;
  return (
    <nav aria-label="Breadcrumb" className={cn('text-xs text-muted-foreground', className)}>
      <ol className="flex items-center gap-1">
        {items.map((item, idx) => {
          const isLast = idx === items.length - 1;
          const content = item.href && !item.current && !isLast ? (
            <Link to={item.href} className="hover:underline">
              {item.label}
            </Link>
          ) : (
            <span className={cn(item.current || isLast ? 'text-foreground' : undefined)} aria-current={item.current || isLast ? 'page' : undefined}>
              {item.label}
            </span>
          );
          return (
            <li key={`${item.label}-${idx}`} className="flex items-center gap-1">
              {content}
              {!isLast && <span aria-hidden="true">/</span>}
            </li>
          );
        })}
      </ol>
    </nav>
  );
};

