  it('should call postMessage with the correct data when runAnalysis is called', async () => {
    const { result } = renderHook(() => useAnalyticsWorker());
    const testData = { entries: [], emotions: [], sensoryInputs: [] };

    // Allow the init effect to run
    await new Promise(r => setTimeout(r, 0));

    // Wait until the mocked worker has been constructed
    const mod: any = await import('@/workers/analytics.worker?worker');
    let tries = 0;
    while (!mod.__getLastWorker() && tries < 20) {
      await new Promise(r => setTimeout(r, 5));
      tries++;
    }
    // Mark worker as ready so queued tasks flush immediately
    const worker = mod.__getLastWorker();
    worker.onmessage({ data: { type: 'progress', progress: { stage: 'ready', percent: 1 } } });

    await act(async () => {
      await result.current.runAnalysis(testData);
    });

    await waitFor(() => {
      expect(mockPostMessage).toHaveBeenCalled();
    });
    // We now send a typed Insights/Compute task envelope to the worker
    expect(mockPostMessage).toHaveBeenCalledWith(expect.objectContaining({
      type: 'Insights/Compute',
      payload: expect.any(Object),
      cacheKey: expect.any(String),
      ttlSeconds: expect.any(Number),
    }));
    expect(result.current.error).toBeNull();
  });

  it('should update state on successful analysis', async () => {
    const { result } = renderHook(() => useAnalyticsWorker());
    const testResults = { patterns: [], correlations: [], environmentalCorrelations: [], insights: [] };

    // Wait for worker
    const mod: any = await import('@/workers/analytics.worker?worker');
    let worker = mod.__getLastWorker();
    let tries = 0;
    while (!worker && tries < 20) {
      await new Promise(r => setTimeout(r, 5));
      worker = mod.__getLastWorker();
      tries++;
    }

    // Simulate ready then complete message
    await act(async () => {
      worker.onmessage({ data: { type: 'progress', progress: { stage: 'ready', percent: 1 } } });
      worker.onmessage({ data: { type: 'complete', payload: testResults } });
    });

    await waitFor(() => {
      expect(result.current.results).toEqual(testResults);
      expect(result.current.isAnalyzing).toBe(false);
      expect(result.current.error).toBeNull();
    });
  });

  it('should update state on analysis error', async () => {
    const { result } = renderHook(() => useAnalyticsWorker());
    const testError = { message: 'Test Error' } as any;

    // Simulate an error message from the mocked worker
    const mod: any = await import('@/workers/analytics.worker?worker');
    const worker = mod.__getLastWorker();
    await act(async () => {
      worker.onmessage({ data: { type: 'progress', progress: { stage: 'ready', percent: 1 } } });
      worker.onmessage({ data: { type: 'error', error: testError } });
    });

    await waitFor(() => {
      expect(result.current.results).toBeNull();
      expect(result.current.isAnalyzing).toBe(false);
      expect(result.current.error).toEqual(testError);
    });
  });

  it('should terminate the worker on unmount', async () => {
    const { unmount } = renderHook(() => useAnalyticsWorker());

    // Wait for worker
    const mod: any = await import('@/workers/analytics.worker?worker');
    let tries = 0;
    while (!mod.__getLastWorker() && tries < 10) {
      await new Promise(r => setTimeout(r, 0));
      tries++;
    }

    await act(async () => {
      unmount();
    });

    await waitFor(() => {
      expect(mockTerminate).toHaveBeenCalled();
    });
  });
});

// Cache key computation is centralized via buildInsightsCacheKey

/**
 * @file src/hooks/useAnalyticsWorker.ts
 * 
 * This hook encapsulates the logic for interacting with the analytics web worker.
 * It simplifies the process of creating, communicating with, and terminating the worker,
 * providing a clean, React-friendly interface for components to offload heavy computations.
 * Now enhanced with performance caching to avoid redundant calculations.
 */
import { useState, useEffect, useRef, useCallback } from 'react';
import { AnalyticsData, AnalyticsResults, AnalyticsWorkerMessage } from '@/types/analytics';
import { usePerformanceCache } from './usePerformanceCache';
import { analyticsConfig } from '@/lib/analyticsConfig';
import { getValidatedConfig, validateAnalyticsRuntimeConfig } from '@/lib/analyticsConfigValidation';
import { buildInsightsCacheKey, buildInsightsTask } from '@/lib/analyticsManager';
import { logger } from '@/lib/logger';
import { toast } from '@/hooks/use-toast';
import { diagnostics } from '@/lib/diagnostics';
import { analyticsWorkerFallback } from '@/lib/analyticsWorkerFallback';
import { POC_MODE, DISABLE_ANALYTICS_WORKER } from '@/lib/env';
import { i18n } from '@/i18n';

// -----------------------------------------------------------------------------
// Module-level singleton to prevent duplicate workers across multiple consumers
// -----------------------------------------------------------------------------
interface WorkerSingleton {
  worker: Worker | null;
  refs: number;
  ready: boolean;
  circuitUntil: number; // epoch ms; when > now, do not attempt worker usage
}

const singleton: WorkerSingleton = {
  worker: null,
  refs: 0,
  ready: false,
  circuitUntil: 0,
};

function nowMs(): number { return Date.now(); }
function isCircuitOpen(): boolean { return nowMs() < singleton.circuitUntil; }
function openCircuit(ms: number): void { singleton.circuitUntil = nowMs() + Math.max(0, ms); }

// Simple once-per-key dedupe for user-facing notifications
const onceWindow = new Map<string, number>();
function doOnce(key: string, ttlMs: number, fn: () => void) {
  const now = nowMs();
  const last = onceWindow.get(key) ?? 0;
  if (now - last > ttlMs) {
    onceWindow.set(key, now);
    try { fn(); } catch { /* noop */ }
  }
}

// Namespace-safe translator usable outside React hooks
function tNs(ns: string, key: string, defaultValue?: string): string {
  try {
    return i18n.t(key, { ns, defaultValue }) as unknown as string;
  } catch {
    return defaultValue ?? key;
  }
}

// Queue tasks until worker signals ready
const pendingTasks: MessageEvent['data'][] = [];

function flushQueuedTasks(): void {
  if (!singleton.worker || !singleton.ready) return;
  while (pendingTasks.length) {
    try {
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      (singleton.worker as any).postMessage(pendingTasks.shift());
    } catch (e) {
      // If posting fails, stop flushing to avoid spin
      break;
    }
  }
}

async function ensureWorkerInitialized(): Promise<Worker | null> {
  if (POC_MODE || DISABLE_ANALYTICS_WORKER) return null;
  if (singleton.worker) return singleton.worker;
  if (isCircuitOpen()) return null;

  try {
    const mod = await import('@/workers/analytics.worker?worker');
    const WorkerCtor = (mod as unknown as { default: { new(): Worker } }).default;
    const worker = new WorkerCtor();

    // Attach handlers once at creation time
    worker.onmessage = (event: MessageEvent<AnalyticsWorkerMessage>) => {
      // Any message implies liveness; mark ready when first progress received or on any first message
      if (!singleton.ready) {
        const msg = event.data as AnalyticsWorkerMessage | undefined;
        if (!msg || msg.type === 'progress' || msg.type === 'partial' || msg.type === 'complete' || msg.type === 'error') {
          singleton.ready = true;
          flushQueuedTasks();
        }
      }
    };

    worker.onerror = (error: ErrorEvent) => {
      logger.error('[useAnalyticsWorker] Worker runtime error, switching to fallback', error);
      try {
        // Detach base listeners to prevent memory leaks
        worker.onmessage = null as any;
        worker.onerror = null as any;
      } catch { /* noop */ }
      try { worker.terminate(); } catch { /* noop */ }
      singleton.worker = null;
      singleton.ready = false;
      // Clear pending tasks to avoid memory retention
      while (pendingTasks.length) { pendingTasks.pop(); }
      // Open circuit for 60s to avoid thrash
      openCircuit(60_000);
      // One-time user-facing toast per cooldown window (localized without React hooks)
      doOnce('analytics_worker_failure', 60_000, () => {
        toast({
          title: tNs('analytics', 'worker.fallbackTitle', 'Analytics running in fallback mode'),
          description: tNs('analytics', 'worker.fallbackDescription', 'Background worker failed. Using safe fallback to keep the UI responsive.'),
        });
      });
    };

    singleton.worker = worker;
    singleton.ready = false; // will flip true on first onmessage
    logger.info('[useAnalyticsWorker] Analytics worker initialized successfully');
    return worker;
  } catch (error) {
    logger.error('[useAnalyticsWorker] Failed to initialize worker', error as Error);
    singleton.worker = null;
    // Shorter circuit on init failure
    openCircuit(15_000);
    return null;
  }
}

function retainWorker(): void {
  singleton.refs += 1;
}

function releaseWorker(): void {
  singleton.refs = Math.max(0, singleton.refs - 1);
  if (singleton.refs === 0 && singleton.worker) {
    try {
      // Remove base handlers to prevent memory leaks
      singleton.worker.onmessage = null as unknown as (this: Worker, ev: MessageEvent) => any;
      singleton.worker.onerror = null as unknown as (this: Worker, ev: ErrorEvent) => any;
    } catch { /* noop */ }
    try {
      logger.debug('[useAnalyticsWorker] Terminating analytics worker');
      singleton.worker.terminate();
    } catch { /* noop */ }
    singleton.worker = null;
    singleton.ready = false;
    // Clear any queued tasks to avoid memory growth on long-lived sessions
    while (pendingTasks.length) { pendingTasks.pop(); }
  }
}

// Define CacheStats type if not imported from usePerformanceCache
interface CacheStats {
  hits: number;
  misses: number;
  sets: number;
  evictions: number;
  invalidations: number;
  hitRate: number;
  size: number;
  memoryUsage?: number;
}

interface CachedAnalyticsWorkerOptions {
  cacheTTL?: number;
  enableCacheStats?: boolean;
  precomputeOnIdle?: boolean;
  // Optional injection for tests or advanced cache control
  extractTagsFromData?: (data: AnalyticsData | AnalyticsResults) => string[];
}

interface UseAnalyticsWorkerReturn {
  results: AnalyticsResults | null;
  isAnalyzing: boolean;
  error: string | null;
  runAnalysis: (data: AnalyticsData) => Promise<void>;
  precomputeCommonAnalytics: (dataProvider: () => AnalyticsData[]) => void;
  invalidateCacheForStudent: (studentId: string) => void;
  clearCache: () => void;
  cacheStats: CacheStats | null;
  cacheSize: number;
}

/**
 * @hook useAnalyticsWorker
 * 
 * A custom hook to manage the analytics web worker with integrated caching.
 * 
 * @param options Configuration options for caching and precomputation
 * @returns {object} An object containing:
 *  - `results`: The latest analysis results received from the worker or cache.
 *  - `isAnalyzing`: A boolean flag indicating if an analysis is currently in progress.
 *  - `error`: Any error message returned from the worker.
 *  - `runAnalysis`: A function to trigger a new analysis by posting data to the worker.
 *  - `cacheStats`: Performance statistics about cache usage (if enabled).
 *  - `clearCache`: Function to clear the analytics cache.
 *  - `invalidateCache`: Function to invalidate cache entries by tag or pattern.
 */
export const useAnalyticsWorker = (options: CachedAnalyticsWorkerOptions = {}): UseAnalyticsWorkerReturn => {
  // Resolve defaults from runtime analyticsConfig with safe fallbacks
  const liveCfgRaw = (() => { try { return analyticsConfig.getConfig(); } catch { return null; } })();
  const { config: liveCfg } = validateAnalyticsRuntimeConfig(liveCfgRaw ?? undefined);
  const resolvedTtl = typeof options.cacheTTL === 'number'
    ? options.cacheTTL
    : (liveCfg?.cache?.ttl ?? 300_000); // default 300s in ms
  const {
    cacheTTL = resolvedTtl,
    enableCacheStats = false,
    precomputeOnIdle = false
  } = options;

  const workerRef = useRef<Worker | null>(null);
  const watchdogRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const [results, setResults] = useState<AnalyticsResults | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const idleCallbackRef = useRef<number | null>(null);
  const precomputeTimeoutRef = useRef<number | null>(null);
  const staggerTimeoutIdsRef = useRef<Set<number>>(new Set());
  const [isWorkerReady, setIsWorkerReady] = useState<boolean>(false);

  // Per-instance cleanup registry to avoid global state issues
  const instanceCleanupsRef = useRef<Set<() => void>>(new Set());
  const addInstanceCleanup = useCallback((fn: () => void) => {
    instanceCleanupsRef.current.add(fn);
  }, []);
  const runAndClearInstanceCleanups = useCallback(() => {
    for (const fn of instanceCleanupsRef.current) {
      try { fn(); } catch { /* noop */ }
    }
    instanceCleanupsRef.current.clear();
  }, []);

  // Initialize performance cache with appropriate settings
  const cache = usePerformanceCache<AnalyticsResults>({
    maxSize: (liveCfg?.cache?.maxSize ?? 50), // Use runtime config; fallback to 50
    ttl: cacheTTL,
    enableStats: enableCacheStats,
    versioning: true // Enable versioning to invalidate on data structure changes
  });

  /**
   * Extracts tags from analytics data for cache invalidation
   * Allow override via options for testing
   */
  const defaultExtractTagsFromData = useCallback((data: AnalyticsData | AnalyticsResults): string[] => {
    const tags: string[] = ['analytics'];

    // Add student-specific tags if available
    if ('entries' in data && (data as any).entries?.length > 0) {
      const studentIds = Array.from(new Set((data as any).entries.map((e: any) => e.studentId)));
      tags.push(...studentIds.map(id => `student-${id}`));
    }

    // Add date-based tags for time-sensitive invalidation
    const now = new Date();
    tags.push(`analytics-${now.getFullYear()}-${now.getMonth() + 1}`);

    return tags;
  }, []);
  const extractTagsFn = options.extractTagsFromData ?? defaultExtractTagsFromData;

  useEffect(() => {
    let isMounted = true; // Race condition guard

    const init = async () => {
      if (POC_MODE || DISABLE_ANALYTICS_WORKER) {
        workerRef.current = null;
        setIsWorkerReady(false);
        // Inform user once per minute if worker is disabled via flag
        if (DISABLE_ANALYTICS_WORKER) {
          doOnce('analytics_worker_disabled', 60_000, () => {
            toast({
              title: tNs('analytics', 'worker.disabledTitle', 'Analytics worker disabled'),
              description: tNs('analytics', 'worker.disabledDescription', 'Running analytics without a background worker (debug mode).'),
            });
          });
        }
        return;
      }
      if (isCircuitOpen()) {
        workerRef.current = null;
        setIsWorkerReady(false);
        return;
      }
      retainWorker();
      const worker = await ensureWorkerInitialized();
      if (!isMounted) {
        releaseWorker();
        return;
      }
      workerRef.current = worker;
      setIsWorkerReady(!!(singleton.ready && worker));

      // Attach per-hook message listener to consume results
      if (worker) {
        const onMessage = (event: MessageEvent<AnalyticsWorkerMessage>) => {
          const data = event.data as AnalyticsWorkerMessage | undefined;
          if (!data) return;
          // Any inbound message means the worker is alive
          if (!singleton.ready) {
            singleton.ready = true;
            flushQueuedTasks();
          }

          // Reset watchdog on any message
          if (watchdogRef.current) {
            clearTimeout(watchdogRef.current);
            watchdogRef.current = null;
          }

          // Handle message types
          try {
            switch (data.type) {
              case 'partial':
                // For now, do not merge partials into results to avoid flicker; could add later
                break;
              case 'complete':
                setResults(data.payload as unknown as AnalyticsResults);
                setIsAnalyzing(false);
                setError(null);
                break;
              case 'error':
                setIsAnalyzing(false);
                setError(typeof (data as any).error === 'string' ? (data as any).error : 'Analytics worker error');
                // Provide minimal safe results to prevent UI crashes per rules
                setResults((prev) => prev ?? ({
                  patterns: [], correlations: [], environmentalCorrelations: [], predictiveInsights: [], anomalies: [], insights: ['Analytics temporarily unavailable.']
                } as AnalyticsResults));
                break;
              case 'progress':
                // No-op: used for readiness/heartbeat
                break;
              default:
                break;
            }
          } catch (e) {
            logger.error('[useAnalyticsWorker] Failed handling worker message', e as Error);
          }
        };

        const onMessageError = (evt: MessageEvent) => {
          logger.error('[useAnalyticsWorker] messageerror from analytics worker', evt);
        };

        // eslint-disable-next-line no-restricted-syntax -- listeners are attached within effect and removed via instance cleanup
        worker.addEventListener('message', onMessage as EventListener);
        // eslint-disable-next-line no-restricted-syntax -- listeners are attached within effect and removed via instance cleanup
        worker.addEventListener('messageerror', onMessageError as EventListener);

        // Cleanup these listeners if this hook unmounts while worker persists
        addInstanceCleanup(() => {
          try {
            worker.removeEventListener('message', onMessage as EventListener);
            worker.removeEventListener('messageerror', onMessageError as EventListener);
          } catch { /* noop */ }
        });
      }
    };

    init();

    return () => {
      isMounted = false;
      // Local cleanup only; singleton handles actual termination when refs reach 0
      if (idleCallbackRef.current) {
        cancelIdleCallback(idleCallbackRef.current);
        idleCallbackRef.current = null;
      }
      if (watchdogRef.current) {
        clearTimeout(watchdogRef.current);
        watchdogRef.current = null;
      }
      // Run per-instance cleanup fns (like removing event listeners)
      runAndClearInstanceCleanups();
      releaseWorker();
    };
  // Stable on mount; do not re-init on cache identity churn
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /**
   * Creates a cache key based on the analytics data using centralized helper
   */
  const createCacheKey = useCallback((data: AnalyticsData): string => {
    // Map AnalyticsData to ComputeInsightsInputs structure expected by cache key builder
    const inputs = {
      entries: data.entries,
      emotions: data.emotions,
      sensoryInputs: data.sensoryInputs,
      // goals are not available at this layer; omit for key purposes
    } as unknown as { entries: unknown[]; emotions: unknown[]; sensoryInputs: unknown[] };

    // Use live runtime config to ensure keys align across app and worker
    const cfg = getValidatedConfig();

    return buildInsightsCacheKey(inputs as any, { config: cfg });
  }, []);

  /**
   * Sends data to the worker to start a new analysis, checking cache first.
   * @param {AnalyticsData} data - The data to be analyzed.
   */
  const runAnalysis = useCallback(async (data: AnalyticsData) => {
    const cacheKey = createCacheKey(data);

    // Check cache first
    const cachedResult = cache.get(cacheKey);
    if (cachedResult) {
      // Reduce logging verbosity - only log on first hit per key
      if (!cache.get(`_logged_${cacheKey}`)) {
        try {
          logger.debug('[useAnalyticsWorker] cache hit', { cacheKey });
          cache.set(`_logged_${cacheKey}`, true, ['logging'], 60000); // Log once per minute max
        } catch (err) {
          /* noop */
        }
      }
      setResults(cachedResult);
      setError(null);
      return;
    }

    // If no worker available, use fallback
    if (!workerRef.current || DISABLE_ANALYTICS_WORKER) {
      // Only log fallback mode once per session
      if (!cache.get('_logged_fallback_mode')) {
        logger.debug('[useAnalyticsWorker] No worker available, using fallback');
        cache.set('_logged_fallback_mode', true, ['logging'], 3600000); // Log once per hour
      }
      setIsAnalyzing(true);
      setError(null);

      try {
        const results = await analyticsWorkerFallback.processAnalytics(data);
        setResults(results);
        // Cache the results
        const tags = extractTagsFn(data);
        cache.set(cacheKey, results, tags);
      } catch (error) {
        logger.error('[useAnalyticsWorker] Fallback failed', error);
        setError('Analytics processing failed. Please try again.');
        // Set minimal results to prevent UI crash
        setResults({
          patterns: [],
          correlations: [],
          environmentalCorrelations: [],
          predictiveInsights: [],
          anomalies: [],
          insights: ['Analytics temporarily unavailable.']
        });
      } finally {
        setIsAnalyzing(false);
      }
      return;
    }

    // If not in cache, proceed with worker analysis
    setIsAnalyzing(true);
    setError(null);
    setResults(null);

    // Start watchdog timeout to prevent indefinite spinner
    if (watchdogRef.current) {
      clearTimeout(watchdogRef.current);
      watchdogRef.current = null;
    }
    // Determine timeout from config if available; fallback to 15s minimum 3s
    const cfg = getValidatedConfig();
    // Clamp watchdog timeout to a sane upper bound to avoid indefinite spinners.
    // Use config ttl as a hint but never exceed 20s; keep a 5s lower bound.
    const hint = cfg?.cache?.ttl ?? 15000;
    const timeoutMs = Math.min(20000, Math.max(5000, hint));
    // eslint-disable-next-line no-restricted-syntax -- watchdog is tracked in ref and cleared on cleanup
    watchdogRef.current = setTimeout(async () => {
      try {
        logger.error('[useAnalyticsWorker] watchdog timeout: worker did not respond, attempting fallback');
      } catch {
        /* noop */
      }
      diagnostics.logWorkerTimeout('analytics', timeoutMs);

      // Clear any idle callbacks to prevent orphaned timers
      if (idleCallbackRef.current) {
        cancelIdleCallback(idleCallbackRef.current);
        idleCallbackRef.current = null;
      }

      // Terminate unresponsive worker
      if (workerRef.current) {
        try {
          workerRef.current.onmessage = null as any;
          workerRef.current.onerror = null as any;
        } catch { /* noop */ }
        try { workerRef.current.terminate(); } catch { /* noop */ }
        workerRef.current = null;
      }

      // Attempt fallback processing
      try {
        const fallbackResults = await analyticsWorkerFallback.processAnalytics(data);
        setResults(fallbackResults);
        const tags = extractTagsFn(data);
        cache.set(cacheKey, fallbackResults, tags);
        setError('Worker timeout - results computed using fallback mode.');
      } catch (fallbackError) {
        logger.error('[useAnalyticsWorker] Fallback failed after watchdog timeout', fallbackError);
        setError('Analytics processing failed. Please try again.');
        // Set minimal results to prevent UI crash
        setResults({
          patterns: [],
          correlations: [],
          environmentalCorrelations: [],
          predictiveInsights: [],
          anomalies: [],
          insights: ['Analytics temporarily unavailable.']
        });
      } finally {
        setIsAnalyzing(false);
      }
    }, timeoutMs);

    // Get current configuration (validated)
    const config = getValidatedConfig();

    // Rate limit worker posting logs
    const logKey = `_logged_worker_post_${new Date().getMinutes()}`;
    if (!cache.get(logKey)) {
      try {
        logger.debug('[useAnalyticsWorker] posting to worker (runAnalysis)', { hasConfig: !!config, cacheKey });
        cache.set(logKey, true, ['logging'], 60000); // Log at most once per minute
      } catch {
        /* noop */
      }
    }

    // Send data to worker with cache key and configuration
    try {
      // Build typed Insights task with centralized key, ttl, and tags
      const inputs = {
        entries: data.entries,
        emotions: data.emotions,
        sensoryInputs: data.sensoryInputs,
      } as const;
      const task = buildInsightsTask(inputs, {
        config,
        // Propagate tags derived from inputs so worker-side caches can invalidate by tag
        tags: extractTagsFn(data),
      });

      // Rate limit WORKER_MESSAGE logs
      const workerLogKey = `_logged_worker_message_${cacheKey}_${new Date().getMinutes()}`;
      if (!cache.get(workerLogKey)) {
        logger.debug('[WORKER_MESSAGE] Sending Insights/Compute task to analytics worker', {
          cacheKey: task.cacheKey,
          ttlSeconds: task.ttlSeconds,
          tagCount: task.tags?.length ?? 0,
          emotionsCount: data.emotions?.length || 0,
          sensoryInputsCount: data.sensoryInputs?.length || 0,
          entriesCount: data.entries?.length || 0
        });
        cache.set(workerLogKey, true, ['logging'], 60000); // Log once per minute per cache key
      }

      // If worker not available or circuit is open, queue or fallback
      if (!workerRef.current || isCircuitOpen()) {
        // If no worker at all, route to fallback immediately (do not queue unbounded)
        if (!singleton.worker) {
          throw new Error('Worker unavailable or circuit open');
        }
      }

      // If worker exists but is not ready yet, queue the task
      if (workerRef.current && !singleton.ready) {
        pendingTasks.push(task as unknown as MessageEvent['data']);
      } else if (workerRef.current) {
        workerRef.current.postMessage(task);
      } else {
        throw new Error('Worker reference missing');
      }
    } catch (postErr) {
      logger.error('[WORKER_MESSAGE] Failed to post message to worker, falling back to sync', { error: postErr });
      if (watchdogRef.current) {
        clearTimeout(watchdogRef.current);
        watchdogRef.current = null;
      }

      // Fallback to synchronous processing
      try {
        const fallbackResults = await analyticsWorkerFallback.processAnalytics(data);
        setResults(fallbackResults);
        const tags = extractTagsFn(data);
        cache.set(cacheKey, fallbackResults, tags);
        setError(null);
      } catch (fallbackError) {
        logger.error('[useAnalyticsWorker] Fallback processing failed after worker post error', fallbackError);
        setError('Analytics processing failed.');
      } finally {
        setIsAnalyzing(false);
      }
    }
  }, [cache, createCacheKey, extractTagsFn]);

  /**
   * Pre-compute analytics for common queries during idle time
   */
  const precomputeCommonAnalytics = useCallback((dataProvider: () => AnalyticsData[]) => {
    if (!precomputeOnIdle || !workerRef.current) return;

    const precompute = () => {
      const commonDataSets = dataProvider();
      const config = getValidatedConfig();

      commonDataSets.forEach((data, index) => {
        // Stagger the precomputation to avoid blocking
        // eslint-disable-next-line no-restricted-syntax -- timer tracked and cleared via instance cleanup below
        const tId = window.setTimeout(() => {
          const cacheKey = createCacheKey(data);

          // Only compute if not already cached
          if (!cache.has(cacheKey)) {
            try {
        logger.debug('[useAnalyticsWorker] posting Insights/Compute task (precompute)', { hasConfig: !!config, cacheKey, idx: index });
            } catch {
              /* noop */
            }
            const task = buildInsightsTask({
              entries: data.entries,
              emotions: data.emotions,
              sensoryInputs: data.sensoryInputs,
            }, { config, tags: extractTagsFn(data) });
            workerRef.current?.postMessage(task);
          }
          staggerTimeoutIdsRef.current.delete(tId);
        }, index * 100); // 100ms between each precomputation
        staggerTimeoutIdsRef.current.add(tId);
      });
    };

    // Use requestIdleCallback for precomputation
    if ('requestIdleCallback' in window) {
      idleCallbackRef.current = requestIdleCallback(precompute, { timeout: 5000 });
    } else {
      // Fallback for browsers without requestIdleCallback
      // eslint-disable-next-line no-restricted-syntax -- timer tracked in ref and cleared on cleanup
      precomputeTimeoutRef.current = window.setTimeout(precompute, 2000);
    }
    // Ensure any scheduled precompute timers are cleared if this hook unmounts
    addInstanceCleanup(() => {
      if (precomputeTimeoutRef.current) {
        clearTimeout(precomputeTimeoutRef.current);
        precomputeTimeoutRef.current = null;
      }
      for (const id of staggerTimeoutIdsRef.current) {
        try { clearTimeout(id); } catch { /* noop */ }
      }
      staggerTimeoutIdsRef.current.clear();
    });
  }, [precomputeOnIdle, cache, createCacheKey, extractTagsFn, addInstanceCleanup]);

  /**
   * Invalidate cache entries for a specific student
   */
  const invalidateCacheForStudent = useCallback((studentId: string) => {
    cache.invalidateByTag(`student-${studentId}`);
  }, [cache]);

  /**
   * Invalidate all analytics cache entries
   */
  const clearCache = useCallback(() => {
    cache.clear();
  }, [cache]);

  /**
   * Subscribe to configuration changes to invalidate cache
   */
  useEffect(() => {
    const unsubscribe = analyticsConfig.subscribe((newConfig) => {
      if (newConfig.cache.invalidateOnConfigChange) {
        clearCache();
      }
    });

    return unsubscribe;
  }, [clearCache]);

  /**
   * Get current cache statistics
   */
  const getCacheStats = useCallback(() => {
    return cache.stats;
  }, [cache]);

  return {
    results,
    isAnalyzing,
    error,
    runAnalysis,
    precomputeCommonAnalytics,
    invalidateCacheForStudent,
    clearCache,
    cacheStats: getCacheStats(),
    cacheSize: cache.size
  };
};

import { useState, useCallback, useRef, useEffect } from 'react';
import { handleError } from '@/lib/errorHandler';
import { SensoryCompassError, ErrorType } from '@/types/errors';

export interface AsyncState<T> {
  data: T | null;
  loading: boolean;
  error: Error | null;
  isSuccess: boolean;
  isError: boolean;
  isIdle: boolean;
}

export interface UseAsyncStateOptions {
  onSuccess?: <T>(data: T) => void;
  onError?: (error: Error) => void;
  retryCount?: number;
  retryDelay?: number;
  showErrorToast?: boolean;
}

export function useAsyncState<T = unknown>(
  initialData: T | null = null,
  options: UseAsyncStateOptions = {}
): {
  state: AsyncState<T>;
  execute: (asyncFunction: () => Promise<T>) => Promise<T | null>;
  reset: () => void;
  setData: (data: T | null) => void;
  setError: (error: Error | null) => void;
} {
  const [state, setState] = useState<AsyncState<T>>({
    data: initialData,
    loading: false,
    error: null,
    isSuccess: false,
    isError: false,
    isIdle: true,
  });

  const isMountedRef = useRef(true);
  const { onSuccess, onError, retryCount = 0, retryDelay = 1000, showErrorToast = true } = options;

  useEffect(() => {
    return () => {
      isMountedRef.current = false;
    };
  }, []);

  const execute = useCallback(
    async (asyncFunction: () => Promise<T>): Promise<T | null> => {
      if (!isMountedRef.current) return null;

      setState({
        data: state.data,
        loading: true,
        error: null,
        isSuccess: false,
        isError: false,
        isIdle: false,
      });

      let attempts = 0;
      let lastError: Error | null = null;

      while (attempts <= retryCount) {
        try {
          const result = await asyncFunction();

          if (!isMountedRef.current) return null;

          setState({
            data: result,
            loading: false,
            error: null,
            isSuccess: true,
            isError: false,
            isIdle: false,
          });

          onSuccess?.(result);
          return result;
        } catch (error) {
          lastError = error instanceof Error ? error : new Error(String(error));
          attempts++;

          if (attempts <= retryCount) {
            // Wait before retrying
            await new Promise(resolve => setTimeout(resolve, retryDelay * attempts));
          }
        }
      }

      // All attempts failed
      if (!isMountedRef.current) return null;

      const finalError = lastError || new SensoryCompassError(
        ErrorType.UNKNOWN_ERROR,
        'Async operation failed'
      );

      setState({
        data: null,
        loading: false,
        error: finalError,
        isSuccess: false,
        isError: true,
        isIdle: false,
      });

      onError?.(finalError);

      if (showErrorToast) {
        await handleError(finalError, { showToast: true, throwError: false });
      }

      return null;
    },
    [state.data, onSuccess, onError, retryCount, retryDelay, showErrorToast]
  );

  const reset = useCallback(() => {
    setState({
      data: initialData,
      loading: false,
      error: null,
      isSuccess: false,
      isError: false,
      isIdle: true,
    });
  }, [initialData]);

  const setData = useCallback((data: T | null) => {
    setState(prev => ({
      ...prev,
      data,
      isSuccess: data !== null,
      isError: false,
      error: null,
    }));
  }, []);

  const setError = useCallback((error: Error | null) => {
    setState(prev => ({
      ...prev,
      error,
      isError: error !== null,
      isSuccess: false,
      loading: false,
    }));
  }, []);

  return {
    state,
    execute,
    reset,
    setData,
    setError,
  };
}

// Convenience hook for mutations (POST, PUT, DELETE)
export function useAsyncMutation<TData = unknown, TVariables = unknown>(
  mutationFn: (variables: TVariables) => Promise<TData>,
  options: UseAsyncStateOptions = {}
) {
  const { state, execute, reset, setData, setError } = useAsyncState<TData>(null, options);

  const mutate = useCallback(
    async (variables: TVariables) => {
      return execute(() => mutationFn(variables));
    },
    [execute, mutationFn]
  );

  return {
    ...state,
    mutate,
    reset,
    setData,
    setError,
  };
}

// Convenience hook for queries (GET)
export function useAsyncQuery<T = unknown>(
  queryFn: () => Promise<T>,
  options: UseAsyncStateOptions & { enabled?: boolean; refetchInterval?: number } = {}
) {
  const { enabled = true, refetchInterval, ...asyncOptions } = options;
  const { state, execute, reset, setData, setError } = useAsyncState<T>(null, asyncOptions);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (enabled) {
      execute(queryFn);
    }
  }, [enabled, execute, queryFn]);

  useEffect(() => {
    if (refetchInterval && enabled && state.isSuccess) {
      intervalRef.current = setInterval(() => {
        execute(queryFn);
      }, refetchInterval);
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [refetchInterval, enabled, state.isSuccess, execute, queryFn]);

  const refetch = useCallback(() => {
    return execute(queryFn);
  }, [execute, queryFn]);

  return {
    ...state,
    refetch,
    reset,
    setData,
    setError,
  };
}

/**
 * Development utility hook for profiling component render performance
 * Should only be used during development for identifying render hotspots
 */

import { useRef, useEffect } from 'react';
import { logger } from '@/lib/logger';

interface RenderMetrics {
  componentName: string;
  renderCount: number;
  totalRenderTime: number;
  averageRenderTime: number;
  lastRenderTime: number;
  maxRenderTime: number;
  minRenderTime: number;
}

const renderMetrics = new Map<string, RenderMetrics>();

/**
 * Hook to profile render performance of a component in development
 * @param componentName - Name of the component being profiled
 * @param enabled - Whether profiling is enabled (default: only in development)
 */
export function useRenderProfiler(
  componentName: string, 
  enabled: boolean = process.env.NODE_ENV === 'development'
) {
  const renderStartTime = useRef<number>(0);
  const mountTime = useRef<number>(Date.now());

  // Record start time at the beginning of render
  if (enabled) {
    renderStartTime.current = performance.now();
  }

  useEffect(() => {
    if (!enabled) return;

    const renderEndTime = performance.now();
    const renderTime = renderEndTime - renderStartTime.current;

    // Update metrics
    const existing = renderMetrics.get(componentName);
    if (existing) {
      existing.renderCount += 1;
      existing.totalRenderTime += renderTime;
      existing.averageRenderTime = existing.totalRenderTime / existing.renderCount;
      existing.lastRenderTime = renderTime;
      existing.maxRenderTime = Math.max(existing.maxRenderTime, renderTime);
      existing.minRenderTime = Math.min(existing.minRenderTime, renderTime);
    } else {
      renderMetrics.set(componentName, {
        componentName,
        renderCount: 1,
        totalRenderTime: renderTime,
        averageRenderTime: renderTime,
        lastRenderTime: renderTime,
        maxRenderTime: renderTime,
        minRenderTime: renderTime,
      });
    }

    // Log slow renders in development
    const SLOW_RENDER_THRESHOLD = 16; // One frame at 60fps
    if (renderTime > SLOW_RENDER_THRESHOLD) {
      logger.warn(`[RenderProfiler] Slow render detected in ${componentName}: ${renderTime.toFixed(2)}ms`);
    }
  });

  // Development-only: Add debug utilities to window
  useEffect(() => {
    if (!enabled || typeof window === 'undefined') return;

    // Add global functions for performance debugging
    if (!window.__renderProfiler) {
      window.__renderProfiler = {
        getMetrics: (componentName?: string) => {
          if (componentName) {
            return renderMetrics.get(componentName) || null;
          }
          return Array.from(renderMetrics.values());
        },

        getRankings: (sortBy: 'total' | 'average' | 'count' = 'average') => {
          const metrics = Array.from(renderMetrics.values());
          return metrics.sort((a, b) => {
            switch (sortBy) {
              case 'total':
                return b.totalRenderTime - a.totalRenderTime;
              case 'count':
                return b.renderCount - a.renderCount;
              case 'average':
              default:
                return b.averageRenderTime - a.averageRenderTime;
            }
          });
        },

        getHotspots: (threshold: number = 10) => {
          return Array.from(renderMetrics.values())
            .filter(m => m.averageRenderTime > threshold)
            .sort((a, b) => b.averageRenderTime - a.averageRenderTime);
        },

        reset: (componentName?: string) => {
          if (componentName) {
            renderMetrics.delete(componentName);
          } else {
            renderMetrics.clear();
          }
        },

        logReport: () => {
          const metrics = Array.from(renderMetrics.values())
            .sort((a, b) => b.averageRenderTime - a.averageRenderTime);

          console.group(' Render Performance Report');
          console.table(metrics);

          const hotspots = metrics.filter(m => m.averageRenderTime > 10);
          if (hotspots.length > 0) {
            console.group(' Render Hotspots (>10ms average)');
            hotspots.forEach(metric => {
              console.log(`${metric.componentName}: ${metric.averageRenderTime.toFixed(2)}ms avg (${metric.renderCount} renders)`);
            });
            console.groupEnd();
          }

          console.groupEnd();
        }
      };
    }

    return () => {
      // Cleanup on unmount - remove this component's data after a delay
      // eslint-disable-next-line no-restricted-syntax -- timeout scoped to this effect and self-cleans
      setTimeout(() => {
        renderMetrics.delete(componentName);
      }, 30000); // Clean up after 30 seconds
    };
  }, [componentName, enabled]);
}

// TypeScript declarations for global debugging utilities
declare global {
  interface Window {
    __renderProfiler?: {
      getMetrics: (componentName?: string) => RenderMetrics | RenderMetrics[] | null;
      getRankings: (sortBy?: 'total' | 'average' | 'count') => RenderMetrics[];
      getHotspots: (threshold?: number) => RenderMetrics[];
      reset: (componentName?: string) => void;
      logReport: () => void;
    };
  }
}

export default useRenderProfiler;

import { useEffect, useMemo, useRef } from 'react';
import type { ReportsWorkerRequest, ReportsWorkerResponse } from '@/workers/reports.worker';

interface RunOptions {
  kind: 'csv' | 'json';
  students: unknown[];
  allData: {
    trackingEntries: unknown[];
    emotions: unknown[];
    sensoryInputs: unknown[];
    goals: unknown[];
  };
  options: {
    includeFields: string[];
    dateRange?: { start: Date; end: Date } | undefined;
    anonymize?: boolean;
  };
  onProgress?: (p: { progress: number; message?: string }) => void;
}

export function useReportsWorker() {
  const workerRef = useRef<Worker | null>(null);

  useEffect(() => {
    // eslint-disable-next-line no-restricted-syntax -- Worker is created inside useEffect with proper cleanup
    const w = new Worker(new URL('@/workers/reports.worker.ts', import.meta.url), {
      type: 'module',
    });
    workerRef.current = w;
    return () => {
      w.terminate();
      workerRef.current = null;
    };
  }, []);

  const run = useMemo(() => {
    return async (opts: RunOptions): Promise<string> => {
      if (!workerRef.current) throw new Error('Reports worker not initialized');
      const id = `${Date.now()}_${Math.random().toString(36).slice(2)}`;
      return new Promise<string>((resolve, reject) => {
        const onMessage = (evt: MessageEvent<ReportsWorkerResponse>) => {
          const msg = evt.data;
          if (!msg || (msg as any).id !== id) return;
          if (msg.type === 'progress') {
            opts.onProgress?.({ progress: msg.progress, message: msg.message });
          } else if (msg.type === 'success') {
            workerRef.current?.removeEventListener('message', onMessage as any);
            resolve(msg.content);
          } else if (msg.type === 'error') {
            workerRef.current?.removeEventListener('message', onMessage as any);
            reject(new Error(msg.error));
          }
        };
        // eslint-disable-next-line no-restricted-syntax -- Listener is scoped and removed after completion
        workerRef.current!.addEventListener('message', onMessage as any);

        const payload: ReportsWorkerRequest = {
          id,
          kind: opts.kind,
          payload: {
            students: opts.students,
            allData: opts.allData,
            options: {
              format: opts.kind,
              includeFields: opts.options.includeFields,
              anonymize: opts.options.anonymize,
              dateRange: opts.options.dateRange
                ? {
                    start: opts.options.dateRange.start.toISOString(),
                    end: opts.options.dateRange.end.toISOString(),
                  }
                : null,
            },
          },
        };
        workerRef.current!.postMessage(payload);
      });
    };
  }, []);

  return { run };
}

import { useTranslation as useI18nTranslation } from 'react-i18next';
import { useCallback, useMemo } from 'react';

const namespace = 'translation';

// Define the complete return type interface
interface TranslationHookReturn {
  t: (key: string, options?: Record<string, unknown>) => string;
  tCommon: (key: string, options?: Record<string, unknown>) => string;
  tDashboard: (key: string, options?: Record<string, unknown>) => string;
  tStudent: (key: string, options?: Record<string, unknown>) => string;
  tTracking: (key: string, options?: Record<string, unknown>) => string;
  tAnalytics: (key: string, options?: Record<string, unknown>) => string;
  tSettings: (key: string, options?: Record<string, unknown>) => string;
  changeLanguage: (lng: 'nb' | 'en') => void;
  currentLanguage: 'nb' | 'en';
  formatDate: (date: Date) => string;
  formatDateTime: (date: Date) => string;
  formatNumber: (value: number, options?: Intl.NumberFormatOptions) => string;
  formatCurrency: (value: number, currency?: 'NOK' | 'USD') => string;
  i18n: any;
}

// Type-safe translation hook with Norwegian context
export const useTranslation = (): TranslationHookReturn => {
  const { t, i18n } = useI18nTranslation(namespace);

  const changeLanguage = useCallback((lng: 'nb' | 'en') => {
    i18n.changeLanguage(lng);
    localStorage.setItem('sensoryTracker_language', lng);
  }, [i18n]);

  const currentLanguage = (i18n.language as 'nb' | 'en') || 'nb';
  const locale = currentLanguage === 'nb' ? 'nb-NO' : 'en-US';

  // Helper functions for common translations
  const tCommon = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'common', ...options }), [t]);
  const tDashboard = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'dashboard', ...options }), [t]);
  const tStudent = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'student', ...options }), [t]);
  const tTracking = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'tracking', ...options }), [t]);
  const tAnalytics = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'analytics', ...options }), [t]);
  const tSettings = useCallback((key: string, options?: Record<string, unknown>) => t(key, { ns: 'settings', ...options }), [t]);

  // Locale-aware date/time formatting
  const formatDate = useCallback((date: Date): string => {
    return new Intl.DateTimeFormat(locale, {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
    }).format(date);
  }, [locale]);

  const formatDateTime = useCallback((date: Date): string => {
    return new Intl.DateTimeFormat(locale, {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
    }).format(date);
  }, [locale]);

  const formatNumber = useCallback((value: number, options?: Intl.NumberFormatOptions): string => {
    return new Intl.NumberFormat(locale, options).format(value);
  }, [locale]);

  const formatCurrency = useCallback((value: number, currency: 'NOK' | 'USD' = currentLanguage === 'nb' ? 'NOK' : 'USD'): string => {
    const isFiniteNumber = Number.isFinite(value) ? value : 0;
    return new Intl.NumberFormat(locale, { style: 'currency', currency, currencyDisplay: 'narrowSymbol' }).format(isFiniteNumber);
  }, [locale, currentLanguage]);

  // Include i18n in deps to ensure fresh reference
  return useMemo(() => ({
    t,
    tCommon,
    tDashboard,
    tStudent,
    tTracking,
    tAnalytics,
    tSettings,
    changeLanguage,
    currentLanguage,
    formatDate,
    formatDateTime,
    formatNumber,
    formatCurrency,
    i18n,
  }), [t, tCommon, tDashboard, tStudent, tTracking, tAnalytics, tSettings, changeLanguage, currentLanguage, formatDate, formatDateTime, formatNumber, formatCurrency, i18n]);
};

/**
 * PreprocessingPipeline: A comprehensive data preprocessing pipeline with versioning and serialization.
 * 
 * This class provides a standardized approach to data preprocessing with the following features:
 * - **Configurable Pipeline**: Define preprocessing steps through configuration objects
 * - **Conditional Execution**: Steps can be conditionally executed based on data profiling
 * - **Deterministic Ordering**: Steps are executed in canonical order (cleaning  scaling  encoding  feature engineering)
 * - **Training/Inference Separation**: Fit pipeline on training data, transform on inference data
 * - **Serialization**: Complete pipeline state can be serialized to JSON for reproducibility
 * - **Metadata Tracking**: Comprehensive metadata for debugging and auditing
 * 
 * @example
 * ```typescript
 * // Create pipeline configuration
 * const config: AnyStepConfig[] = [
 *   { name: 'clean', kind: 'cleaning', dropAllNullColumns: true },
 *   { name: 'scale', kind: 'scaling', method: 'standard' },
 *   { name: 'encode', kind: 'encoding', oneHotMaxCategories: 10 }
 * ];
 * 
 * // Initialize and fit pipeline
 * const pipeline = new PreprocessingPipeline(config);
 * const trainedData = pipeline.fit(trainingData);
 * 
 * // Transform new data using fitted pipeline
 * const transformedData = pipeline.transform(newData);
 * 
 * // Serialize for storage/sharing
 * const serialized = pipeline.toJSON();
 * const restored = PreprocessingPipeline.fromJSON(serialized);
 * ```
 * 
 * @version 1.0.0
 * @since 1.0.0
 */

import { stableHash } from '@/lib/hash';
import {
  AnyStepConfig,
  DataProfile,
  Dataset,
  FittedStepState,
  PipelineMetadata,
  SerializedPipeline,
  StepKind,
} from '@/types/preprocessing';

const PIPELINE_VERSION = '1.0.0';

// Canonical order of step kinds
const ORDER: StepKind[] = ['cleaning', 'scaling', 'encoding', 'feature_engineering'];

/** Utility: deep clone plain JSON values */
function deepCloneJSON<T>(v: T): T {
  return JSON.parse(JSON.stringify(v));
}

/** Profiling utilities to drive conditional steps */
function inferDataProfile(data: Dataset): DataProfile {
  const rowCount = data.length;
  const columns = Array.from(
    data.reduce<Set<string>>((acc, row) => {
      Object.keys(row).forEach((k) => acc.add(k));
      return acc;
    }, new Set())
  );

  const inferredTypes: DataProfile['inferredTypes'] = {} as any;
  const missingRatioByColumn: Record<string, number> = {};
  const uniqueCountByColumn: Record<string, number> = {};

  const uniques = new Map<string, Set<unknown>>();

  for (const col of columns) {
    uniques.set(col, new Set());
  }

  for (const row of data) {
    for (const col of columns) {
      const val = (row as any)[col];
      uniques.get(col)!.add(val);
    }
  }

  for (const col of columns) {
    const uniqueSet = uniques.get(col)!;
    uniqueCountByColumn[col] = uniqueSet.size;

    let missing = 0;
    let type: 'number' | 'string' | 'boolean' | 'date' | 'unknown' = 'unknown';

    for (const v of uniqueSet) {
      if (v === null || v === undefined || v !== v) missing++;
      const t = typeof v;
      if (t === 'number') type = 'number';
      else if (t === 'string') type = type === 'number' ? 'number' : 'string';
      else if (t === 'boolean') type = type === 'number' ? 'number' : type === 'string' ? 'string' : 'boolean';
      else if (v instanceof Date) type = 'date';
    }
    missingRatioByColumn[col] = rowCount === 0 ? 0 : missing / rowCount;
    inferredTypes[col] = type;
  }

  const numericColumns = columns.filter((c) => inferredTypes[c] === 'number');
  const categoricalColumns = columns.filter((c) => inferredTypes[c] === 'string' || inferredTypes[c] === 'boolean');

  return { rowCount, columns, inferredTypes, missingRatioByColumn, uniqueCountByColumn, numericColumns, categoricalColumns };
}

/** Simple cleaning: drop all-null columns, trim strings. */
function applyCleaning(dataset: Dataset, cfg: Extract<AnyStepConfig, { kind: 'cleaning' }>): { data: Dataset; state: FittedStepState } {
  const columns = new Set<string>();
  dataset.forEach((r) => Object.keys(r).forEach((k) => columns.add(k)));

  const dropCols: string[] = [];
  if (cfg.dropAllNullColumns) {
    for (const c of columns) {
      let allNull = true;
      for (const row of dataset) {
        const v = (row as any)[c];
        if (v !== null && v !== undefined) {
          allNull = false;
          break;
        }
      }
      if (allNull) dropCols.push(c);
    }
  }

  const out: Dataset = dataset.map((row) => {
    const nr: Record<string, unknown> = {};
    for (const [k, v] of Object.entries(row)) {
      if (dropCols.includes(k)) continue;
      if (cfg.trimStrings && typeof v === 'string') nr[k] = v.trim();
      else nr[k] = v;
    }
    return nr;
  });

  return {
    data: out,
    state: { name: cfg.name, kind: 'cleaning', params: { dropCols, trimStrings: !!cfg.trimStrings } },
  };
}

/** Standard scaling: z = (x - mean) / std for numeric columns. */
function fitScaling(dataset: Dataset, cfg: Extract<AnyStepConfig, { kind: 'scaling' }>, profile: DataProfile) {
  const means: Record<string, number> = {};
  const stds: Record<string, number> = {};

  for (const col of profile.numericColumns) {
    const vals: number[] = dataset
      .map((r) => (typeof (r as any)[col] === 'number' ? ((r as any)[col] as number) : NaN))
      .filter((v) => Number.isFinite(v));
    const mean = vals.length ? vals.reduce((a, b) => a + b, 0) / vals.length : 0;
    const variance = vals.length ? vals.reduce((a, b) => a + (b - mean) * (b - mean), 0) / vals.length : 0;
    const std = Math.sqrt(variance) || 1;
    means[col] = mean;
    stds[col] = std;
  }

  const transform = (data: Dataset): Dataset =>
    data.map((row) => {
      const out: Record<string, unknown> = { ...row };
      for (const col of Object.keys(means)) {
        const v = (row as any)[col];
        out[col] = typeof v === 'number' ? (v - means[col]) / stds[col] : v;
      }
      return out;
    });

  const state: FittedStepState = { name: cfg.name, kind: 'scaling', params: { means, stds, method: cfg.method ?? 'standard' } };
  return { transform, state };
}

/** One-hot encoding for categorical columns with unique count threshold. */
function fitEncoding(dataset: Dataset, cfg: Extract<AnyStepConfig, { kind: 'encoding' }>, profile: DataProfile) {
  const max = cfg.oneHotMaxCategories ?? 20;
  const includeBooleans = cfg.includeBooleans ?? true;

  const cats = profile.columns.filter((c) =>
    (profile.inferredTypes[c] === 'string' || (includeBooleans && profile.inferredTypes[c] === 'boolean')) &&
    profile.uniqueCountByColumn[c] > 1 &&
    profile.uniqueCountByColumn[c] <= max
  );

  const categories: Record<string, string[]> = {};
  for (const c of cats) {
    const set = new Set<string>();
    for (const row of dataset) {
      const v = (row as any)[c];
      const key = typeof v === 'string' ? v : typeof v === 'boolean' ? String(v) : String(v ?? '');
      set.add(key);
    }
    categories[c] = Array.from(set.values()).sort();
  }

  const transform = (data: Dataset): Dataset =>
    data.map((row) => {
      const out: Record<string, unknown> = {};
      for (const [k, v] of Object.entries(row)) {
        if (!categories[k]) {
          out[k] = v;
          continue;
        }
        const key = typeof v === 'string' ? v : typeof v === 'boolean' ? String(v) : String(v ?? '');
        for (const cat of categories[k]) {
          out[`${k}__${cat}`] = key === cat ? 1 : 0;
        }
      }
      return out;
    });

  const state: FittedStepState = { name: cfg.name, kind: 'encoding', params: { categories, includeBooleans, oneHotMaxCategories: max } };
  return { transform, state };
}

/** Simple feature engineering: squared terms for numeric columns up to degree. */
function fitFeatureEngineering(dataset: Dataset, cfg: Extract<AnyStepConfig, { kind: 'feature_engineering' }>, profile: DataProfile) {
  const degree = cfg.polynomialDegree ?? 1;
  const cols = degree >= 2 ? [...profile.numericColumns] : [];

  const transform = (data: Dataset): Dataset =>
    data.map((row) => {
      const out: Record<string, unknown> = { ...row };
      for (const col of cols) {
        const v = (row as any)[col];
        if (typeof v === 'number') out[`${col}__squared`] = v * v;
      }
      return out;
    });

  const state: FittedStepState = { name: cfg.name, kind: 'feature_engineering', params: { degree, columns: cols } };
  return { transform, state };
}

/** Determine if a config should run given profile. */
function shouldRun(cfg: AnyStepConfig, profile: DataProfile): boolean {
  if (cfg.enabled === false) return false;
  if (cfg.when) return !!cfg.when(profile);
  return true;
}

/** Sort steps by canonical ordering, preserving relative order within the same kind. */
function sortSteps(configs: AnyStepConfig[]): AnyStepConfig[] {
  return [...configs].sort((a, b) => ORDER.indexOf(a.kind) - ORDER.indexOf(b.kind));
}

export class PreprocessingPipeline {
  private readonly version = PIPELINE_VERSION;
  private readonly config: AnyStepConfig[];
  private fittedStates: FittedStepState[] = [];
  private metadata?: PipelineMetadata;

  constructor(config: AnyStepConfig[]) {
    this.config = sortSteps(config);
  }

  getMetadata(): PipelineMetadata | undefined {
    return this.metadata ? deepCloneJSON(this.metadata) : undefined;
  }

  /**
   * Fits the pipeline on a training dataset.
   * - Profiles the data to determine which conditional steps to run.
   * - Fits each applicable step (e.g., calculates means/stds for scaling).
   * - Stores the fitted state for each step.
   * - Generates and stores metadata about the fitting process.
   * 
   * @param data The training dataset, an array of records.
   * @param options Optional parameters for the fitting process.
   * @param options.notes Optional user-defined notes to store in the metadata.
   * @returns The transformed dataset after applying all fitted steps.
   */
  fit(data: Dataset, options?: { notes?: string }): Dataset {
    const source = deepCloneJSON(data);
    const profile = inferDataProfile(source);

    // build metadata
    const configHash = stableHash(this.config.map((c) => ({ ...c, when: undefined })));
    this.metadata = {
      createdAt: new Date().toISOString(),
      pipelineVersion: this.version,
      configHash,
      dataSchema: { columns: profile.columns, inferredTypes: profile.inferredTypes },
      notes: options?.notes,
    };

    this.fittedStates = [];

    let current = source;

    for (const cfg of this.config) {
      if (!shouldRun(cfg, profile)) continue;
      if (cfg.kind === 'cleaning') {
        const { data: d, state } = applyCleaning(current, cfg);
        current = d;
        this.fittedStates.push(state);
      } else if (cfg.kind === 'scaling') {
        const { transform, state } = fitScaling(current, cfg, profile);
        current = transform(current);
        this.fittedStates.push(state);
      } else if (cfg.kind === 'encoding') {
        const { transform, state } = fitEncoding(current, cfg, profile);
        current = transform(current);
        this.fittedStates.push(state);
      } else if (cfg.kind === 'feature_engineering') {
        const { transform, state } = fitFeatureEngineering(current, cfg, profile);
        current = transform(current);
        this.fittedStates.push(state);
      }
    }

    return current;
  }

  /**
   * Transforms a dataset using the previously fitted pipeline states.
   * 
   * This method is intended for inference, applying the exact transformations
   * learned during the `fit` phase without re-learning any parameters.
   * 
   * @param data The dataset to transform.
   * @returns The transformed dataset.
   * @throws Will not throw, but returns data as-is if the pipeline has not been fitted.
   */
  transform(data: Dataset): Dataset {
    if (!this.fittedStates.length) return data;

    let current = deepCloneJSON(data);

    for (const state of this.fittedStates) {
      if (state.kind === 'cleaning') {
        const drop = (state.params as any).dropCols as string[];
        const trimStrings = !!(state.params as any).trimStrings;
        current = current.map((row) => {
          const out: Record<string, unknown> = {};
          for (const [k, v] of Object.entries(row)) {
            if (drop.includes(k)) continue;
            out[k] = trimStrings && typeof v === 'string' ? v.trim() : v;
          }
          return out;
        });
      } else if (state.kind === 'scaling') {
        const { means, stds } = state.params as any;
        current = current.map((row) => {
          const out: Record<string, unknown> = { ...row };
          for (const col of Object.keys(means)) {
            const v = (row as any)[col];
            out[col] = typeof v === 'number' ? (v - means[col]) / stds[col] : v;
          }
          return out;
        });
      } else if (state.kind === 'encoding') {
        const { categories } = state.params as any as { categories: Record<string, string[]> };
        current = current.map((row) => {
          const out: Record<string, unknown> = {};
          for (const [k, v] of Object.entries(row)) {
            if (!categories[k]) {
              out[k] = v;
              continue;
            }
            const key = typeof v === 'string' ? v : typeof v === 'boolean' ? String(v) : String(v ?? '');
            for (const cat of categories[k]) {
              out[`${k}__${cat}`] = key === cat ? 1 : 0;
            }
          }
          return out;
        });
      } else if (state.kind === 'feature_engineering') {
        const { columns } = state.params as any as { columns: string[] };
        current = current.map((row) => {
          const out: Record<string, unknown> = { ...row };
          for (const col of columns) {
            const v = (row as any)[col];
            if (typeof v === 'number') out[`${col}__squared`] = v * v;
          }
          return out;
        });
      }
    }

    return current;
  }

  /**
   * Serializes the entire pipeline state to a JSON object.
   * This includes the configuration, all fitted step parameters, and metadata.
   * 
   * @returns A `SerializedPipeline` object ready for JSON stringification.
   */
  toJSON(): SerializedPipeline {
    if (!this.metadata) {
      // Ensure metadata exists even if only transform was used
      this.metadata = {
        createdAt: new Date().toISOString(),
        pipelineVersion: this.version,
        configHash: stableHash(this.config.map((c) => ({ ...c, when: undefined }))),
        dataSchema: { columns: [], inferredTypes: {} as any },
      } as PipelineMetadata;
    }
    return {
      version: this.version,
      config: deepCloneJSON(this.config),
      fitted: deepCloneJSON(this.fittedStates),
      metadata: deepCloneJSON(this.metadata),
    };
  }

  /**
   * Recreates a `PreprocessingPipeline` instance from a serialized state.
   * This allows for perfect reconstruction of a fitted pipeline for later use.
   * 
   * @param payload The `SerializedPipeline` object.
   * @returns A new `PreprocessingPipeline` instance with its state restored.
   */
  static fromJSON(payload: SerializedPipeline): PreprocessingPipeline {
    const pipe = new PreprocessingPipeline(payload.config);
    (pipe as any).fittedStates = deepCloneJSON(payload.fitted);
    (pipe as any).metadata = deepCloneJSON(payload.metadata);
    return pipe;
  }
}

