function isFiniteNumber(n: unknown): n is number {
  return typeof n === 'number' && Number.isFinite(n);
}

describe('analyzeTrendsWithStatistics (finite outputs and threshold respect)', () => {
  it('returns finite values and direction respects configured trendThreshold', () => {
    // Configure thresholds and quality targets minimally
    analyticsConfig.updateConfig({
      enhancedAnalysis: {
        minSampleSize: 5,
        trendThreshold: 0.05, // small threshold so mild slope counts as increasing
        predictionConfidenceThreshold: 0.4,
        anomalyThreshold: 1.5,
        riskAssessmentThreshold: 3,
        qualityTargets: { pointsTarget: 20, timeSpanDaysTarget: 30 },
      } as any,
      timeWindows: { defaultAnalysisDays: 30, recentDataDays: 7, shortTermDays: 14, longTermDays: 90 }
    });

    // Create monotonically increasing data across 10 days
    const now = new Date();
    const data = Array.from({ length: 10 }, (_, i) => ({
      value: 1 + i * 0.1,
      timestamp: new Date(now.getTime() - (9 - i) * 24 * 3600_000),
    }));

    const res = (enhancedPatternAnalysis as any).analyzeTrendsWithStatistics(data);
    expect(res).toBeTruthy();
    // Finite fields
    expect(isFiniteNumber(res.rate)).toBe(true);
    expect(isFiniteNumber(res.significance)).toBe(true);
    expect(isFiniteNumber(res.confidence)).toBe(true);
    expect(isFiniteNumber(res.forecast.next7Days)).toBe(true);
    expect(isFiniteNumber(res.forecast.next30Days)).toBe(true);
    // Direction should be increasing with small threshold
    expect(res.direction === 'increasing' || res.direction === 'stable').toBe(true);

    // Raise threshold high enough to force stable
    analyticsConfig.updateConfig({ enhancedAnalysis: { trendThreshold: Math.abs(res.rate) + 1 } as any });
    const res2 = (enhancedPatternAnalysis as any).analyzeTrendsWithStatistics(data);
    expect(res2?.direction).toBe('stable');
  });
});

describe('detectAnomalies (spike triggers)', () => {
  it('flags anomalies for synthetic intensity spikes and buckets severity', () => {
    analyticsConfig.updateConfig({
      enhancedAnalysis: { anomalyThreshold: 1.2, minSampleSize: 5 } as any,
      alertSensitivity: { level: 'medium', emotionIntensityMultiplier: 1, frequencyMultiplier: 1, anomalyMultiplier: 1 } as any,
    });

    const now = new Date();
    const emotions: EmotionEntry[] = [];
    // Baseline with slight variation (2.8 to 3.2) to ensure non-zero MAD
    for (let i = 0; i < 30; i++) {
      const baseIntensity = 3 + (Math.sin(i) * 0.2); // Small variation around 3
      emotions.push({ id: `e${i}`, studentId: 's1', emotion: 'anxious', intensity: baseIntensity, timestamp: new Date(now.getTime() - (30 - i) * 3600_000), triggers: [] } as any);
    }
    // Add clear outlier spikes
    const spikeHi = { id: 'spike-hi', studentId: 's1', emotion: 'anxious', intensity: 5, timestamp: now, triggers: [] } as any;
    const spikeLo = { id: 'spike-lo', studentId: 's1', emotion: 'anxious', intensity: 1, timestamp: new Date(now.getTime() - 5000), triggers: [] } as any;
    emotions.push(spikeHi, spikeLo);

    const anomalies = enhancedPatternAnalysis.detectAnomalies(emotions, [], []);
    const emotionAnoms = anomalies.filter(a => a.type === 'emotion');
    expect(emotionAnoms.length).toBeGreaterThan(0);
    // Ensure at least one high or medium severity exists due to spikes
    const hasElevated = emotionAnoms.some(a => a.severity === 'high' || a.severity === 'medium');
    expect(hasElevated).toBe(true);
  });
});

describe('generateCorrelationMatrix (config significance bands)', () => {
  function makeEntries(n = 14): TrackingEntry[] {
    const now = new Date();
    const entries: TrackingEntry[] = [] as any;
    for (let i = 0; i < n; i++) {
      // Create strong correlations among some factors
      const noise = 20 + i * 2; // increasing
      const temp = 15 + i * 1;  // increasing, correlated with noise
      const emoIntensity = Math.min(5, 1 + (i / (n - 1)) * 4); // 1..5 increasing
      entries.push({
        id: `t${i}`,
        studentId: 's1',
        timestamp: new Date(now.getTime() - (n - i) * 3600_000),
        emotions: [ { id: `e${i}`, studentId: 's1', emotion: 'happy', intensity: emoIntensity, timestamp: new Date(), triggers: [] } ] as any,
        sensoryInputs: [],
        activities: [],
        environmentalData: { roomConditions: { noiseLevel: noise, temperature: temp, lighting: 'bright' } }
      } as any);
    }
    return entries;
  }

  it('labels significance tiers using enhancedAnalysis.correlationSignificance bands', () => {
    analyticsConfig.updateConfig({
      enhancedAnalysis: {
        minSampleSize: 10,
        correlationSignificance: { low: 0.2, moderate: 0.5, high: 0.8 },
      } as any,
      patternAnalysis: { correlationThreshold: 0.25, minDataPoints: 3, highIntensityThreshold: 4, concernFrequencyThreshold: 0.3, emotionConsistencyThreshold: 0.4, moderateNegativeThreshold: 0.4 } as any,
      taxonomy: { positiveEmotions: ['happy', 'calm', 'joyful'] }
    });

    const entries = makeEntries(18);
    const matrix = enhancedPatternAnalysis.generateCorrelationMatrix(entries);

    expect(matrix.significantPairs.length).toBeGreaterThan(0);
    // All pairs must pass base threshold (low band)
    expect(matrix.significantPairs.every(p => Math.abs(p.correlation) >= 0.2)).toBe(true);
    // Expect at least one high or moderate based on strong constructed correlations
    const hasHighOrModerate = matrix.significantPairs.some(p => p.significance === 'high' || p.significance === 'moderate');
    expect(hasHighOrModerate).toBe(true);
  });
});

import { describe, it, expect, beforeEach, vi } from 'vitest';
import { calculateConfidence, generateInsights } from '@/lib/insights';
import { ANALYTICS_CONFIG } from '@/lib/analyticsConfig';
import type { EmotionEntry, SensoryEntry, TrackingEntry } from '@/types/student';

// Helper to build timestamps relative to a mocked now
const daysAgo = (n: number, now: Date) => new Date(now.getTime() - n * 24 * 60 * 60 * 1000);

describe('calculateConfidence', () => {
  const baseCfg = ANALYTICS_CONFIG.confidence;

  beforeEach(() => {
    vi.useFakeTimers();
    // Ensure consistent default config (analyticsConfig may be mutable across tests)
    // Not strictly necessary when passing cfgParam, but keep parity with code paths.
  });

  it('applies component contributions up to thresholds and clamps beyond with 2-decimal rounding', () => {
    const cfg = baseCfg;

    // Exactly reach thresholds
    const emotions: EmotionEntry[] = Array.from({ length: cfg.THRESHOLDS.EMOTION_ENTRIES }).map((_, i) => ({
      id: `e${i}`, studentId: 's1', emotion: 'happy', intensity: 3, timestamp: new Date()
    }));
    const sensory: SensoryEntry[] = Array.from({ length: cfg.THRESHOLDS.SENSORY_ENTRIES }).map((_, i) => ({
      id: `s${i}`, studentId: 's1', sense: 'visual', response: 'seeking', intensity: 3, timestamp: new Date()
    }));
    const tracking: TrackingEntry[] = Array.from({ length: cfg.THRESHOLDS.TRACKING_ENTRIES }).map((_, i) => ({
      id: `t${i}`, timestamp: new Date(), value: i
    } as any));

    // No recency boost by setting last entry just outside window
    const now = new Date('2025-01-15T12:00:00.000Z');
    vi.setSystemTime(now);
    tracking[tracking.length - 1].timestamp = daysAgo(cfg.THRESHOLDS.DAYS_SINCE_LAST_ENTRY + 1, now);

    const scoreAtThresholds = calculateConfidence(emotions, sensory, tracking, cfg);
    const expectedSum = cfg.WEIGHTS.EMOTION + cfg.WEIGHTS.SENSORY + cfg.WEIGHTS.TRACKING; // each capped at 1 * weight
    expect(scoreAtThresholds).toBeCloseTo(expectedSum, 10);

    // Exceed thresholds -> still clamp to 1 * weight each
    const emotions2 = emotions.concat(emotions); // double count intentionally
    const sensory2 = sensory.concat(sensory, sensory); // exceed further
    const tracking2 = tracking.concat(tracking);
    const scoreClamped = calculateConfidence(emotions2, sensory2, tracking2, cfg);
    expect(scoreClamped).toBeCloseTo(expectedSum, 10);

    // Partial contributions
    const halfEmotions: EmotionEntry[] = Array.from({ length: Math.floor(cfg.THRESHOLDS.EMOTION_ENTRIES / 2) }).map((_, i) => ({
      id: `eh${i}`, studentId: 's1', emotion: 'calm', intensity: 2, timestamp: now
    }));
    const partialScore = calculateConfidence(halfEmotions, [], [], cfg);
    const expectedPartial = (halfEmotions.length / cfg.THRESHOLDS.EMOTION_ENTRIES) * cfg.WEIGHTS.EMOTION;
    // 2-decimal rounding
    expect(partialScore).toBe(Math.round(expectedPartial * 100) / 100);
  });

  it('toggles recency boost at the exact boundary (exclusive as implemented)', () => {
    const cfg = baseCfg;
    const now = new Date('2025-02-01T00:00:00.000Z');
    vi.setSystemTime(now);

    const _emotions: EmotionEntry[] = [];
    const _sensory: SensoryEntry[] = [];
    const tracking: TrackingEntry[] = [{ id: 't1', timestamp: daysAgo(cfg.THRESHOLDS.DAYS_SINCE_LAST_ENTRY, now), value: 1 } as any];

    // Base (no contributions from counts)
    const baseScore = calculateConfidence([], [], tracking, cfg);

    // Because implementation uses exclusive window (<= days - eps), exactly N days ago should NOT get boost
    // Reconstruct expected without recency boost: only tracking count contribution (1/cfg.THRESHOLDS.TRACKING_ENTRIES)
    const trackingOnly = Math.min(tracking.length / cfg.THRESHOLDS.TRACKING_ENTRIES, 1) * cfg.WEIGHTS.TRACKING;
    expect(baseScore).toBe(Math.round(trackingOnly * 100) / 100);

    // Within window (just under threshold) should include boost
    tracking[0].timestamp = daysAgo(cfg.THRESHOLDS.DAYS_SINCE_LAST_ENTRY - 0.0001, now);
    const boosted = calculateConfidence([], [], tracking, cfg);
    const expectedBoosted = Math.min(trackingOnly + cfg.WEIGHTS.RECENCY_BOOST, 1);
    expect(boosted).toBe(Math.round(expectedBoosted * 100) / 100);
  });
});

describe('calculateConfidence edge cases', () => {
  // Use fake timers to align Date.now() inside calculateConfidence with our test timestamps
  vi.useFakeTimers();
  const fixedNow = new Date('2025-02-01T00:00:00.000Z');
  vi.setSystemTime(fixedNow);
  const now = fixedNow.getTime();
  const mkTs = (daysAgo: number) => new Date(now - daysAgo * 24 * 60 * 60 * 1000);

  it('no data returns 0 and rounds/clamps', () => {
    const score = calculateConfidence([], [], [], {
      THRESHOLDS: { EMOTION_ENTRIES: 10, SENSORY_ENTRIES: 10, TRACKING_ENTRIES: 5, DAYS_SINCE_LAST_ENTRY: 7 },
      WEIGHTS: { EMOTION: 0.3, SENSORY: 0.3, TRACKING: 0.4, RECENCY_BOOST: 0.1 }
    });
    expect(score).toBe(0);
  });

  it('recency boost triggers for strictly less than N days and not at exactly N days', () => {
    const trackingLt = [{ id: 't1', studentId: 's1', timestamp: mkTs(6.9) }];
    const trackingEq = [{ id: 't2', studentId: 's1', timestamp: mkTs(7) }];

    const cfg = { THRESHOLDS: { EMOTION_ENTRIES: 1, SENSORY_ENTRIES: 1, TRACKING_ENTRIES: 1, DAYS_SINCE_LAST_ENTRY: 7 }, WEIGHTS: { EMOTION: 0, SENSORY: 0, TRACKING: 0, RECENCY_BOOST: 0.5 } };

    const scoreLt = calculateConfidence([], [], trackingLt as any, cfg);
    const scoreEq = calculateConfidence([], [], trackingEq as any, cfg);

    expect(scoreLt).toBe(0.5);
    expect(scoreEq).toBe(0);
  });
});

