import { TrackingEntry, EmotionEntry, SensoryEntry } from "@/types/student";
import { AnalyticsData } from "@/workers/analytics.worker";
import { subDays, startOfDay } from "date-fns";

interface PrecomputationConfig {
  enabled: boolean;
  maxQueueSize: number;
  batchSize: number;
  idleTimeout: number;
  commonTimeframes: number[]; // days to look back
}

interface PrecomputationTask {
  id: string;
  data: AnalyticsData;
  priority: number;
  timestamp: Date;
}

/**
 * AnalyticsPrecomputationManager handles background pre-computation of common analytics queries
 * to improve performance by having results ready when users need them
 */
export class AnalyticsPrecomputationManager {
  private config: PrecomputationConfig;
  private taskQueue: PrecomputationTask[] = [];
  private isProcessing = false;
  private idleCallbackId: number | null = null;
  private processedTasks = new Set<string>();
  private onAnalyze: (data: AnalyticsData) => void;

  constructor(
    onAnalyze: (data: AnalyticsData) => void,
    config: Partial<PrecomputationConfig> = {}
  ) {
    this.onAnalyze = onAnalyze;
    this.config = {
      enabled: true,
      maxQueueSize: 50,
      batchSize: 5,
      idleTimeout: 5000,
      commonTimeframes: [7, 14, 30], // Last 7, 14, and 30 days
      ...config
    };
  }

  /**
   * Schedule precomputation based on current student data
   */
  schedulePrecomputation(
    allEntries: TrackingEntry[],
    allEmotions: EmotionEntry[],
    allSensoryInputs: SensoryEntry[]
  ): void {
    if (!this.config.enabled || this.isProcessing) return;

    // Clear existing queue
    this.taskQueue = [];

    // Get unique student IDs
    const studentIds = new Set<string>();
    allEntries.forEach(e => e.studentId && studentIds.add(e.studentId));
    allEmotions.forEach(e => e.studentId && studentIds.add(e.studentId));
    allSensoryInputs.forEach(s => s.studentId && studentIds.add(s.studentId));

    // Generate tasks for each student and timeframe
    studentIds.forEach(studentId => {
      this.config.commonTimeframes.forEach(days => {
        const task = this.createPrecomputationTask(
          studentId,
          days,
          allEntries,
          allEmotions,
          allSensoryInputs
        );

        if (task && !this.processedTasks.has(task.id)) {
          this.taskQueue.push(task);
        }
      });
    });

    // Also add combined analytics for all students
    this.config.commonTimeframes.forEach(days => {
      const task = this.createCombinedAnalyticsTask(
        days,
        allEntries,
        allEmotions,
        allSensoryInputs
      );

      if (task && !this.processedTasks.has(task.id)) {
        this.taskQueue.push(task);
      }
    });

    // Sort by priority (smaller timeframes first)
    this.taskQueue.sort((a, b) => a.priority - b.priority);

    // Limit queue size
    if (this.taskQueue.length > this.config.maxQueueSize) {
      this.taskQueue = this.taskQueue.slice(0, this.config.maxQueueSize);
    }

    // Schedule processing during idle time
    this.scheduleIdleProcessing();
  }

  /**
   * Create a precomputation task for a specific student and timeframe
   */
  private createPrecomputationTask(
    studentId: string,
    days: number,
    allEntries: TrackingEntry[],
    allEmotions: EmotionEntry[],
    allSensoryInputs: SensoryEntry[]
  ): PrecomputationTask | null {
    const cutoffDate = subDays(new Date(), days);

    // Filter data for this student and timeframe
    const entries = allEntries.filter(e => 
      e.studentId === studentId && e.timestamp >= cutoffDate
    );
    const emotions = allEmotions.filter(e => 
      e.studentId === studentId && e.timestamp >= cutoffDate
    );
    const sensoryInputs = allSensoryInputs.filter(s => 
      s.studentId === studentId && s.timestamp >= cutoffDate
    );

    // Skip if no data
    if (entries.length === 0 && emotions.length === 0 && sensoryInputs.length === 0) {
      return null;
    }

    const taskId = `student:${studentId}:days:${days}`;

    return {
      id: taskId,
      data: { entries, emotions, sensoryInputs },
      priority: days, // Smaller timeframes have higher priority
      timestamp: new Date()
    };
  }

  /**
   * Create a precomputation task for combined analytics
   */
  private createCombinedAnalyticsTask(
    days: number,
    allEntries: TrackingEntry[],
    allEmotions: EmotionEntry[],
    allSensoryInputs: SensoryEntry[]
  ): PrecomputationTask | null {
    const cutoffDate = subDays(new Date(), days);

    // Filter data for timeframe
    const entries = allEntries.filter(e => e.timestamp >= cutoffDate);
    const emotions = allEmotions.filter(e => e.timestamp >= cutoffDate);
    const sensoryInputs = allSensoryInputs.filter(s => s.timestamp >= cutoffDate);

    // Skip if no data
    if (entries.length === 0 && emotions.length === 0 && sensoryInputs.length === 0) {
      return null;
    }

    const taskId = `combined:days:${days}`;

    return {
      id: taskId,
      data: { entries, emotions, sensoryInputs },
      priority: days + 100, // Combined analytics have lower priority
      timestamp: new Date()
    };
  }

  /**
   * Schedule processing during idle time
   */
  private scheduleIdleProcessing(): void {
    if (this.idleCallbackId !== null) {
      cancelIdleCallback(this.idleCallbackId);
    }

    if ('requestIdleCallback' in window) {
      this.idleCallbackId = requestIdleCallback(
        (deadline) => this.processQueue(deadline),
        { timeout: this.config.idleTimeout }
      );
    } else {
      // Fallback for browsers without requestIdleCallback
      setTimeout(() => this.processQueue(), 1000);
    }
  }

  /**
   * Process queued tasks during idle time
   */
  private processQueue(deadline?: IdleDeadline): void {
    if (this.taskQueue.length === 0) {
      this.isProcessing = false;
      return;
    }

    this.isProcessing = true;
    let processed = 0;

    // Process tasks while we have time
    while (
      this.taskQueue.length > 0 && 
      processed < this.config.batchSize &&
      (!deadline || deadline.timeRemaining() > 10)
    ) {
      const task = this.taskQueue.shift();
      if (task) {
        this.onAnalyze(task.data);
        this.processedTasks.add(task.id);
        processed++;
      }
    }

    // Schedule next batch if there are more tasks
    if (this.taskQueue.length > 0) {
      this.scheduleIdleProcessing();
    } else {
      this.isProcessing = false;
    }
  }

  /**
   * Get common data queries for precomputation
   */
  static getCommonDataQueries(
    entries: TrackingEntry[],
    emotions: EmotionEntry[],
    sensoryInputs: SensoryEntry[]
  ): AnalyticsData[] {
    const queries: AnalyticsData[] = [];
    const now = new Date();

    // Common timeframes
    const timeframes = [
      { days: 7, label: 'week' },
      { days: 14, label: 'twoWeeks' },
      { days: 30, label: 'month' }
    ];

    timeframes.forEach(({ days }) => {
      const cutoffDate = subDays(now, days);

      queries.push({
        entries: entries.filter(e => e.timestamp >= cutoffDate),
        emotions: emotions.filter(e => e.timestamp >= cutoffDate),
        sensoryInputs: sensoryInputs.filter(s => s.timestamp >= cutoffDate)
      });
    });

    // Today's data as half-open interval [startOfDay, startOfNextDay)
    const todayStart = startOfDay(now);
    const todayEndExclusive = new Date(todayStart.getTime() + 24 * 60 * 60 * 1000);
    queries.push({
      entries: entries.filter(e => e.timestamp >= todayStart && e.timestamp < todayEndExclusive),
      emotions: emotions.filter(e => e.timestamp >= todayStart && e.timestamp < todayEndExclusive),
      sensoryInputs: sensoryInputs.filter(s => s.timestamp >= todayStart && s.timestamp < todayEndExclusive)
    });

    // High-activity periods (days with more than average activity)
    const dailyActivity = new Map<string, number>();
    entries.forEach(entry => {
      const dateKey = startOfDay(entry.timestamp).toISOString();
      dailyActivity.set(dateKey, (dailyActivity.get(dateKey) || 0) + 1);
    });

    const avgActivity = Array.from(dailyActivity.values()).reduce((a, b) => a + b, 0) / dailyActivity.size;
    const highActivityDays = Array.from(dailyActivity.entries())
      .filter(([_, count]) => count > avgActivity * 1.5)
      .map(([date]) => new Date(date));

    highActivityDays.slice(0, 3).forEach(date => {
      const dayStart = startOfDay(date);
      const dayEndExclusive = new Date(dayStart.getTime() + 24 * 60 * 60 * 1000);
      queries.push({
        entries: entries.filter(e => e.timestamp >= dayStart && e.timestamp < dayEndExclusive),
        emotions: emotions.filter(e => e.timestamp >= dayStart && e.timestamp < dayEndExclusive),
        sensoryInputs: sensoryInputs.filter(s => s.timestamp >= dayStart && s.timestamp < dayEndExclusive)
      });
    });

    return queries;
  }

  /**
   * Clear processed tasks cache
   */
  clearProcessedTasks(): void {
    this.processedTasks.clear();
  }

  /**
   * Stop all precomputation
   */
  stop(): void {
    this.config.enabled = false;
    this.taskQueue = [];
    if (this.idleCallbackId !== null) {
      cancelIdleCallback(this.idleCallbackId);
      this.idleCallbackId = null;
    }
    this.isProcessing = false;
  }

  /**
   * Resume precomputation
   */
  resume(): void {
    this.config.enabled = true;
  }

  /**
   * Get current status
   */
  getStatus(): {
    enabled: boolean;
    queueSize: number;
    isProcessing: boolean;
    processedCount: number;
  } {
    return {
      enabled: this.config.enabled,
      queueSize: this.taskQueue.length,
      isProcessing: this.isProcessing,
      processedCount: this.processedTasks.size
    };
  }
}

import { STORAGE_KEYS } from '@/lib/analyticsConfig';
import { logger } from '@/lib/logger';

export interface StudentAnalyticsProfile {
  studentId: string;
  isInitialized: boolean;
  lastAnalyzedAt: Date | null;
  analyticsConfig: {
    patternAnalysisEnabled: boolean;
    correlationAnalysisEnabled: boolean;
    predictiveInsightsEnabled: boolean;
    anomalyDetectionEnabled: boolean;
    alertSystemEnabled: boolean;
  };
  minimumDataRequirements: {
    emotionEntries: number;
    sensoryEntries: number;
    trackingEntries: number;
  };
  analyticsHealthScore: number;
}

let profiles: Map<string, StudentAnalyticsProfile> | null = null;

function loadProfilesFromStorage(): Map<string, StudentAnalyticsProfile> {
  const map = new Map<string, StudentAnalyticsProfile>();
  try {
    const raw = typeof localStorage !== 'undefined' ? localStorage.getItem(STORAGE_KEYS.analyticsProfiles) : null;
    if (!raw) return map;
    const obj = JSON.parse(raw) as Record<string, any>;
    for (const [studentId, profile] of Object.entries(obj)) {
      if (profile && typeof (profile as any).studentId === 'string') {
        map.set(studentId, {
          ...(profile as any),
          lastAnalyzedAt: profile.lastAnalyzedAt ? new Date(profile.lastAnalyzedAt) : null,
        });
      }
    }
  } catch (error) {
    logger.error('[analyticsProfiles] Failed to load profiles', { error });
  }
  return map;
}

function ensureProfilesLoaded() {
  if (!profiles) profiles = loadProfilesFromStorage();
}

export function getProfileMap(): Map<string, StudentAnalyticsProfile> {
  ensureProfilesLoaded();
  return profiles as Map<string, StudentAnalyticsProfile>;
}

export function saveProfiles(): void {
  ensureProfilesLoaded();
  try {
    const data = Object.fromEntries((profiles as Map<string, StudentAnalyticsProfile>).entries());
    if (typeof localStorage !== 'undefined') {
      localStorage.setItem(STORAGE_KEYS.analyticsProfiles, JSON.stringify(data));
    }
  } catch (error) {
    logger.error('[analyticsProfiles] Failed to save profiles', { error });
  }
}

export function initializeStudentProfile(studentId: string): void {
  ensureProfilesLoaded();
  if (!studentId || typeof studentId !== 'string') return;
  if ((profiles as Map<string, StudentAnalyticsProfile>).has(studentId)) return;
  const profile: StudentAnalyticsProfile = {
    studentId,
    isInitialized: true,
    lastAnalyzedAt: null,
    analyticsConfig: {
      patternAnalysisEnabled: true,
      correlationAnalysisEnabled: true,
      predictiveInsightsEnabled: true,
      anomalyDetectionEnabled: true,
      alertSystemEnabled: true,
    },
    minimumDataRequirements: {
      emotionEntries: 1,
      sensoryEntries: 1,
      trackingEntries: 1,
    },
    analyticsHealthScore: 0,
  };
  (profiles as Map<string, StudentAnalyticsProfile>).set(studentId, profile);
  saveProfiles();
}

import { analyticsConfig } from '@/lib/analyticsConfig';
import { calculateConfidence, generateInsights } from '@/lib/insights';
import type { EmotionEntry, SensoryEntry, TrackingEntry } from '@/types/student';
import type { InsightOutput } from '@/types/insights';

export interface AnalyticsSummary {
  insights: InsightOutput; // string[] for now (non-breaking)
  confidence: number;
  hasMinimumData: boolean;
  computedAt: string; // ISO timestamp
  tags?: string[];
}

export async function generateAnalyticsSummary(inputs: {
  entries: readonly TrackingEntry[];
  emotions: readonly EmotionEntry[];
  sensoryInputs?: readonly SensoryEntry[];
  results?: {
    patterns: any[]; // Keeping loose here to avoid importing heavy types in facade
    correlations: any[];
    predictiveInsights?: Array<{ description: string; confidence: number }>;
  };
  tags?: string[];
}): Promise<AnalyticsSummary> {
  const cfg = analyticsConfig.getConfig();
  const { entries, emotions } = inputs;
  const patterns = inputs.results?.patterns ?? [];
  const correlations = inputs.results?.correlations ?? [];
  const predictiveInsights = inputs.results?.predictiveInsights ?? [];

  // Confidence uses confidence slice; insights use insights slice
  const confidence = calculateConfidence(emotions, inputs.sensoryInputs ?? [], entries, cfg.confidence);

  // Prefer canonical insights signature when we have results
  const insights = generateInsights(
    { patterns, correlations, predictiveInsights },
    emotions,
    entries,
    cfg.insights
  );

  const hasMinimumData = entries.length >= cfg.insights.MIN_SESSIONS_FOR_FULL_ANALYTICS;

  return {
    insights,
    confidence,
    hasMinimumData,
    computedAt: new Date().toISOString(),
    tags: inputs.tags,
  };
}

/**
 * Fallback implementation for analytics when web workers are not available
 * This runs in the main thread but with throttling to prevent UI blocking
 */

import { AnalyticsData, AnalyticsResults } from '@/workers/analytics.worker';
import { patternAnalysis } from '@/lib/patternAnalysis';
import { enhancedPatternAnalysis } from '@/lib/enhancedPatternAnalysis';
import { logger } from '@/lib/logger';

export class AnalyticsWorkerFallback {
  private isProcessing = false;
  private queue: Array<{
    data: AnalyticsData;
    resolve: (value: AnalyticsResults) => void;
    reject: (error: Error) => void;
  }> = [];

  async processAnalytics(data: AnalyticsData): Promise<AnalyticsResults> {
    return new Promise((resolve, reject) => {
      this.queue.push({ data, resolve, reject });
      this.processQueue();
    });
  }

  private async processQueue() {
    if (this.isProcessing || this.queue.length === 0) return;

    this.isProcessing = true;
    const { data, resolve, reject } = this.queue.shift()!;

    try {
      // Process in chunks to avoid blocking UI
      await new Promise(resolve => setTimeout(resolve, 0));

      const results: AnalyticsResults = {
        patterns: [],
        correlations: [],
        environmentalCorrelations: [], // Initialize with empty array
        predictiveInsights: [],
        anomalies: [],
        insights: []
      };

      // Basic pattern analysis (simplified version)
      if (data.emotions.length > 0) {
        try {
          const emotionPatterns = patternAnalysis.analyzeEmotionPatterns(data.emotions, 30);
          results.patterns.push(...emotionPatterns);
        } catch (e) {
          logger.error('Fallback: Error analyzing emotion patterns', e);
        }
      }

      await new Promise(resolve => setTimeout(resolve, 0)); // Yield to UI

      if (data.sensoryInputs.length > 0) {
        try {
          const sensoryPatterns = patternAnalysis.analyzeSensoryPatterns(data.sensoryInputs, 30);
          results.patterns.push(...sensoryPatterns);
        } catch (e) {
          logger.error('Fallback: Error analyzing sensory patterns', e);
        }
      }

      await new Promise(resolve => setTimeout(resolve, 0)); // Yield to UI

      if (data.entries.length > 2) {
        try {
          const correlations = patternAnalysis.analyzeEnvironmentalCorrelations(data.entries);
          results.correlations = correlations;
          results.environmentalCorrelations = correlations; // Also populate environmentalCorrelations
        } catch (e) {
          logger.error('Fallback: Error analyzing correlations', e);
        }
      }

      await new Promise(resolve => setTimeout(resolve, 0)); // Yield to UI

      // Enhanced analysis - predictive insights and anomaly detection
      if (data.entries.length > 1) {
        try {
          const predictiveInsights = await enhancedPatternAnalysis.generatePredictiveInsights(
            data.emotions,
            data.sensoryInputs,
            data.entries,
            []
          );
          results.predictiveInsights = predictiveInsights;
        } catch (e) {
          logger.error('Fallback: Error generating predictive insights', e);
        }

        await new Promise(resolve => setTimeout(resolve, 0)); // Yield to UI

        try {
          const anomalies = enhancedPatternAnalysis.detectAnomalies(
            data.emotions,
            data.sensoryInputs,
            data.entries
          );
          results.anomalies = anomalies;
        } catch (e) {
          logger.error('Fallback: Error detecting anomalies', e);
        }
      }

      // Generate basic insights
      if (data.entries.length < 5) {
        results.insights.push(
          `Limited data available (${data.entries.length} sessions). Continue collecting data for better insights.`
        );
      } else {
        results.insights.push(
          'Analytics processed successfully. Continue collecting data for more detailed patterns.'
        );
      }

      resolve(results);
    } catch (error) {
      logger.error('Fallback analytics failed', error);
      reject(error instanceof Error ? error : new Error('Analytics processing failed'));
    } finally {
      this.isProcessing = false;
      // Process next item in queue
      setTimeout(() => this.processQueue(), 100);
    }
  }
}

export const analyticsWorkerFallback = new AnalyticsWorkerFallback();

/*
  canonicalSerialize(value: unknown): string
  - Order-insensitive and type-stable canonical serialization with explicit type prefixes
  - Primitives: 
    string   => s:"...escaped..."
    number   => n:<normalized> (NaN, Infinity, -Infinity; -0 serialized as 0)
    boolean  => b:true|false
    null     => l:null
    undefined=> u:undefined
    bigint   => g:<decimal>
  - Date     => d:<toISOString()>
  - RegExp   => r:<regex.toString()>
  - Array    => a:[<items...>] where items are canonicalized then sorted lexicographically
  - Set      => a:[<items...>] (same as Array, order-insensitive)
  - Map      => o:{<k>:<v>,...} where k and v are canonicalized; entries sorted by canonical key
  - Object   => o:{<k>:<v>,...} own enumerable keys sorted lexicographically (k is JSON-escaped string)
  - Typed arrays, ArrayBuffer, DataView => convert to array of numbers and treat as Array (order-insensitive)
  - Fallback => x:<[object Type]> optionally followed by props as o:{...} if enumerable props exist
  - Circular structures => throw descriptive error
*/

export function canonicalSerialize(value: unknown): string {
  const seen = new WeakSet<object>();

  function escapeJSONString(s: string): string {
    // JSON.stringify returns a quoted string with proper escaping
    return JSON.stringify(s) as string;
  }

  function numberToNormalized(n: number): string {
    if (Number.isNaN(n)) return 'n:NaN';
    if (n === Infinity) return 'n:Infinity';
    if (n === -Infinity) return 'n:-Infinity';
    // Treat -0 as 0
    const val = Object.is(n, -0) ? 0 : n;
    // Use toString for canonical JS numeric rendering
    return `n:${val.toString()}`;
  }

  function bigintToNormalized(b: bigint): string {
    return `g:${b.toString()}`;
  }

  function dateToNormalized(d: Date): string {
    return `d:${d.toISOString()}`;
  }

  function regexpToNormalized(r: RegExp): string {
    return `r:${r.toString()}`;
  }

  function arraybufferLikeToNumbers(input: ArrayBuffer | ArrayBufferView | DataView): number[] {
    if (input instanceof DataView) {
      const bytes = new Uint8Array(input.buffer, input.byteOffset, input.byteLength);
      return Array.from(bytes);
    }
    if (ArrayBuffer.isView(input as ArrayBufferView)) {
      const v = input as ArrayBufferView;
      const bytes = new Uint8Array(v.buffer, v.byteOffset, v.byteLength);
      return Array.from(bytes);
    }
    // ArrayBuffer
    const ab = input as ArrayBuffer;
    const bytes = new Uint8Array(ab);
    return Array.from(bytes);
  }

  function isPlainObject(obj: unknown): obj is Record<string, unknown> {
    if (obj === null || typeof obj !== 'object') return false;
    const proto = Object.getPrototypeOf(obj);
    return proto === Object.prototype || proto === null;
  }

  function serialize(v: unknown): string {
    // Primitives and special cases
    if (v === null) return 'l:null';
    const t = typeof v;
    switch (t) {
      case 'string':
        return `s:${escapeJSONString(v as string)}`;
      case 'number':
        return numberToNormalized(v as number);
      case 'boolean':
        return `b:${(v as boolean) ? 'true' : 'false'}`;
      case 'undefined':
        return 'u:undefined';
      case 'bigint':
        return bigintToNormalized(v as bigint);
      case 'symbol': {
        // Fallback path for symbols
        const desc = (v as symbol).description;
        const tag = desc !== undefined ? `Symbol(${desc})` : 'Symbol()';
        return `x:[object Symbol]${serializeSymbolProps(v as symbol, tag)}`;
      }
      case 'function': {
        // Functions typically have no enumerable own props; include name for stability
        const fn = v as Function;
        const name = fn.name || 'anonymous';
        return `x:[object Function]${serializeMaybeProps(fn)}`.replace('[object Function]', `[object Function ${name}]`);
      }
      case 'object':
        break; // handled below
      default:
        // Should not reach here, but provide safe fallback
        return `x:${Object.prototype.toString.call(v)}`;
    }

    // Objects (non-null)
    const obj = v as object;
    if (seen.has(obj)) {
      throw new TypeError('canonicalSerialize: Cannot serialize circular structure');
    }

    // Well-known built-ins
    if (obj instanceof Date) return dateToNormalized(obj);
    if (obj instanceof RegExp) return regexpToNormalized(obj);

    // Array and Array-like handling
    if (Array.isArray(obj)) {
      seen.add(obj);
      const items = obj.map((it) => serialize(it));
      items.sort();
      seen.delete(obj);
      return `a:[${items.join(',')}]`;
    }

    // Typed arrays, ArrayBuffer, DataView -> numbers array
    if (
      typeof ArrayBuffer !== 'undefined' &&
      (obj instanceof ArrayBuffer || ArrayBuffer.isView(obj) || obj instanceof DataView)
    ) {
      const nums = arraybufferLikeToNumbers(obj as any);
      // Serialize as numbers, then sort for order-insensitivity
      const serialized = nums.map((n) => numberToNormalized(n));
      serialized.sort();
      return `a:[${serialized.join(',')}]`;
    }

    // Set -> order-insensitive array of serialized items
    if (obj instanceof Set) {
      seen.add(obj);
      const items = Array.from(obj.values()).map((it) => serialize(it));
      items.sort();
      seen.delete(obj);
      return `a:[${items.join(',')}]`;
    }

    // Map -> entries sorted by canonical key
    if (obj instanceof Map) {
      seen.add(obj);
      const entries = Array.from(obj.entries()).map(([k, val]) => [serialize(k), serialize(val)] as const);
      entries.sort((a, b) => (a[0] < b[0] ? -1 : a[0] > b[0] ? 1 : 0));
      const body = entries.map(([k, val]) => `${k}:${val}`).join(',');
      seen.delete(obj);
      return `o:{${body}}`;
    }

    // Plain object
    if (isPlainObject(obj)) {
      seen.add(obj);
      const rec = obj as Record<string, unknown>;
      const keys = Object.keys(rec).sort();
      const parts: string[] = [];
      for (const k of keys) {
        const v2 = rec[k];
        parts.push(`${escapeJSONString(k)}:${serialize(v2)}`);
      }
      seen.delete(obj);
      return `o:{${parts.join(',')}}`;
    }

    // Fallback for unknown instances: include tag + enumerable props if any
    const tag = Object.prototype.toString.call(obj); // e.g., [object Error]
    const props = serializeMaybeProps(obj);
    return `x:${tag}${props}`;
  }

  function serializeMaybeProps(obj: object): string {
    const rec = obj as Record<string, unknown>;
    const keys = Object.keys(rec);
    if (keys.length === 0) return '';
    keys.sort();
    const parts: string[] = [];
    for (const k of keys) {
      parts.push(`${escapeJSONString(k)}:${serialize(rec[k])}`);
    }
    return `,o:{${parts.join(',')}}`;
  }

  function serializeSymbolProps(_sym: symbol, _tag: string): string {
    // Symbols have no enumerable string keys; no additional props
    return '';
  }

  return serialize(value);
}

import type { EChartsOption, SeriesOption } from 'echarts';
import type { CorrelationMatrix } from '@/lib/enhancedPatternAnalysis';

/**
 * ChartKit  option builders
 *
 * These helpers produce ECharts options using a stable schema and
 * explicitly disable hover-dimming on series. We set emphasis.disabled,
 * focus: 'none', and a fully opaque blur state to improve readability
 * and avoid the visual effect of lines "disappearing" when hovering.
 */

/**
 * Strongly-typed input row for trend charts. Keep this flat and numeric so the
 * chart layer is deterministic and tooltips never receive object-shaped values.
 */
export interface TrendRow {
  date: string; // yyyy-MM-dd (displayed on x-axis)
  avgEmotionIntensity: number;
  positiveEmotions: number;
  negativeEmotions: number;
  totalSensoryInputs: number;
}

/** Configuration for the emotion trends chart. */
export interface EmotionTrendsConfig {
  title?: string;
  showMovingAverage: boolean;
  movingAverageWindow: number; // e.g., 7 for 7d MA
  useDualYAxis: boolean; // sensory on right axis
  thresholds?: { emotion?: number; sensory?: number };
}

/**
 * Compute a simple moving average array for a numeric series.
 */
function computeMovingAverage(values: number[], window: number): number[] {
  if (!Number.isFinite(window) || window <= 1) return values.slice();
  const ma: number[] = new Array(values.length).fill(0);
  let sum = 0;
  for (let i = 0; i < values.length; i++) {
    sum += values[i] ?? 0;
    const outIndex = i - window;
    if (outIndex >= 0) sum -= values[outIndex] ?? 0;
    const denom = Math.min(i + 1, window);
    ma[i] = Number((sum / denom).toFixed(2));
  }
  return ma;
}

/** Ensure numbers are finite; coerce invalid values to 0 for stable visuals. */
function toFiniteNumber(value: unknown): number {
  const n = typeof value === 'number' ? value : Number(value);
  return Number.isFinite(n) ? n : 0;
}

/**
 * Build an ECharts option for the main Emotion & Sensory trends chart.
 * Pure function  safe to unit test.
 */
export function buildEmotionTrendsOption(rows: TrendRow[], config: EmotionTrendsConfig): EChartsOption {
  const dates = rows.map(r => r.date);
  const avg = rows.map(r => toFiniteNumber(r.avgEmotionIntensity));
  const pos = rows.map(r => toFiniteNumber(r.positiveEmotions));
  const neg = rows.map(r => toFiniteNumber(r.negativeEmotions));
  const sensory = rows.map(r => toFiniteNumber(r.totalSensoryInputs));

  const avgMA = config.showMovingAverage
    ? computeMovingAverage(avg, Math.max(2, config.movingAverageWindow))
    : [];

  const series: SeriesOption[] = [
    {
      type: 'line',
      name: 'Avg Emotion Intensity',
      data: avg,
      smooth: true,
      showSymbol: false,
      sampling: 'lttb',
      lineStyle: { width: 3 },
      emphasis: { focus: 'none', disabled: true },
      blur: { itemStyle: { opacity: 1 }, lineStyle: { opacity: 1 } },
      markLine: config.thresholds?.emotion
        ? {
            silent: true,
            data: [
              {
                yAxis: config.thresholds.emotion,
                label: { position: 'end', formatter: 'Emotion Threshold' },
                lineStyle: { type: 'dashed', width: 2 },
              },
            ],
          }
        : undefined,
    },
    ...(config.showMovingAverage
      ? ([
          {
            type: 'line',
            name: `Avg Intensity (${config.movingAverageWindow}d MA)`,
            data: avgMA,
            smooth: true,
            showSymbol: false,
            sampling: 'lttb',
            lineStyle: { width: 2 },
            emphasis: { focus: 'none', disabled: true },
            blur: { itemStyle: { opacity: 1 }, lineStyle: { opacity: 1 } },
          },
        ] as SeriesOption[])
      : []),
    {
      type: 'line',
      name: 'Positive Emotions',
      data: pos,
      smooth: true,
      showSymbol: false,
      sampling: 'lttb',
      lineStyle: { width: 2 },
      emphasis: { focus: 'none', disabled: true },
      blur: { itemStyle: { opacity: 1 }, lineStyle: { opacity: 1 } },
    },
    {
