/** Correlation heatmap option builder (ECharts). */
export function buildCorrelationHeatmapOption(matrix: CorrelationMatrix): EChartsOption {
  const factors = matrix.factors ?? [];
  const values: Array<[number, number, number]> = [];
  for (let i = 0; i < matrix.matrix.length; i++) {
    const row = matrix.matrix[i] || [];
    for (let j = 0; j < row.length; j++) {
      const vRaw = row[j];
      const v = typeof vRaw === 'number' && Number.isFinite(vRaw) ? vRaw : 0;
      values.push([j, i, Number(v.toFixed(2))]);
    }
  }

  // Map for quick significance lookup in tooltip
  const significanceMap = new Map<string, { significance: string; correlation: number }>();
  for (const p of matrix.significantPairs || []) {
    const key1 = `${p.factor1}__${p.factor2}`;
    const key2 = `${p.factor2}__${p.factor1}`;
    significanceMap.set(key1, { significance: p.significance, correlation: p.correlation });
    significanceMap.set(key2, { significance: p.significance, correlation: p.correlation });
  }

  const option: EChartsOption = {
    grid: { top: 80, left: 120, right: 64, bottom: 60, containLabel: true },
    xAxis: {
      type: 'category',
      data: factors,
      axisLabel: { rotate: 40, margin: 16 },
    },
    yAxis: {
      type: 'category',
      data: factors,
      axisLabel: { margin: 12 },
    },
    tooltip: {
      trigger: 'item',
      confine: true,
      appendToBody: true,
      formatter: (p: any) => {
        const i = p?.value?.[1];
        const j = p?.value?.[0];
        const f1 = factors[i] ?? '';
        const f2 = factors[j] ?? '';
        const key = `${f1}__${f2}`;
        const sig = significanceMap.get(key)?.significance;
        const corr = typeof p?.value?.[2] === 'number' ? p.value[2] : Number(p.value?.[2]) || 0;
        const sign = corr > 0 ? 'Positive' : corr < 0 ? 'Negative' : 'Neutral';
        return `<div style="font-weight:600;margin-bottom:6px">${f1}  ${f2}</div>
                <div>Correlation: <b>${corr.toFixed(2)}</b> (${sign})</div>
                ${sig ? `<div>Significance: <b>${sig}</b></div>` : ''}`;
      }
    },
    visualMap: {
      min: -1,
      max: 1,
      calculable: true,
      orient: 'vertical',
      right: 8,
      top: 40,
      text: ['Positiv', 'Negativ'],
      inRange: {
        // Diverging palette: red (neg)  gray (0)  green (pos)
        color: ['#ef4444', '#f3f4f6', '#10b981']
      },
      itemWidth: 12,
      itemHeight: 100,
      textStyle: { color: 'hsl(var(--muted-foreground))' },
    },
    series: [
      {
        type: 'heatmap',
        data: values,
        label: {
          show: true,
          formatter: (params: any) => {
            const v = typeof params?.value?.[2] === 'number' ? params.value[2] : Number(params?.value?.[2]) || 0;
            // Only label moderate/strong correlations to reduce clutter
            return Math.abs(v) >= 0.25 ? v.toFixed(2) : '';
          },
        },
        itemStyle: {
          borderWidth: 1,
          borderColor: '#374151' // Tailwind gray-700 for better contrast on dark backgrounds
        },
        emphasis: { disabled: true, focus: 'none' },
        progressive: 0,
        // Larger cells for readability
        // ECharts sizes cells automatically based on grid/category count
      },
    ],
  };

  return option;
}

import { EmotionEntry, SensoryEntry } from '@/types/student';
import { bin, extent } from 'vega-transforms';

/**
 * Aggregates emotion and sensory entries by day, calculating the total count
 * and average intensity for each day. This is useful for visualizing trends
 * over time.
 *
 * @param entries - An array of EmotionEntry or SensoryEntry objects. Each object
 *   must have a `timestamp` and `intensity` property.
 * @returns An array of objects, where each object represents a day and contains
 *   the date, the total number of entries for that day, and the average
 *   intensity of those entries.
 *
 * @example
 * const entries = [
 *   { timestamp: '2023-01-01T12:00:00Z', intensity: 5 },
 *   { timestamp: '2023-01-01T18:00:00Z', intensity: 3 },
 *   { timestamp: '2023-01-02T09:00:00Z', intensity: 8 },
 * ];
 * const dailyStats = aggregateEntriesByDay(entries);
 * // dailyStats will be:
 * // [
 * //   { date: '2023-01-01', count: 2, averageIntensity: 4 },
 * //   { date: '2023-01-02', count: 1, averageIntensity: 8 },
 * // ]
 */
export function aggregateEntriesByDay(entries: (EmotionEntry | SensoryEntry)[]) {
  const aggregated = entries.reduce((acc, entry) => {
    const date = new Date(entry.timestamp).toISOString().split('T')[0];
    if (!acc[date]) {
      acc[date] = { count: 0, totalIntensity: 0 };
    }
    acc[date].count++;
    acc[date].totalIntensity += entry.intensity;
    return acc;
  }, {} as Record<string, { count: number; totalIntensity: number }>);

  return Object.entries(aggregated).map(([date, { count, totalIntensity }]) => ({
    date,
    count,
    averageIntensity: totalIntensity / count,
  }));
}

/**
 * Computes intensity bins for a collection of emotion or sensory entries.
 * This function uses the vega-transforms `bin` utility to partition the
 * entries into a specified number of bins based on their intensity levels.
 *
 * @param entries - An array of EmotionEntry or SensoryEntry objects. Each
 *   object must have an `intensity` property.
 * @returns An array of objects, where each object represents an intensity
 *   bin. It includes the bin range as a string (e.g., "0-2") and the
 *   number of entries falling within that bin.
 *
 * @example
 * const entries = [
 *   { intensity: 1 },
 *   { intensity: 2 },
 *   { intensity: 5 },
 *   { intensity: 8 },
 *   { intensity: 9 },
 * ];
 * const intensityBins = computeIntensityBins(entries);
 * // Example output (exact bins may vary based on implementation):
 * // [
 * //   { bin: '0-2', count: 2 },
 * //   { bin: '4-6', count: 1 },
 * //   { bin: '8-10', count: 2 },
 * // ]
 */
export function computeIntensityBins(entries: (EmotionEntry | SensoryEntry)[]) {
  const intensityExtent = extent(entries, (d: EmotionEntry | SensoryEntry) => d.intensity);
  const binned = bin({ extent: intensityExtent, maxbins: 5 })(entries, (d: EmotionEntry | SensoryEntry) => d.intensity);

  return binned.map((bin: any) => ({
    bin: `${bin.x0}-${bin.x1}`,
    count: bin.length,
  }));
}

