/**
 * @file src/lib/mock/mockSeeders.ts
 * Optional demo data seeders. These are never invoked automatically.
 * Use explicitly from dev tooling, tests, or a one-off script.
 */
import { dataStorage } from '@/lib/dataStorage';
import { logger } from '@/lib/logger';
import { v4 as uuidv4 } from 'uuid';
import { generateUniversalMockDataForStudent as generateMockData } from '@/lib/universalDataGenerator';

export interface SeedDemoOptions {
  // Seed for existing students in storage
  forExistingStudents?: boolean;
  // Create N new demo students and seed them
  createNewStudents?: number;
  // Batches of tracking entries per student to generate (approximate)
  batchesPerStudent?: number;
}

/**
 * Seed demo tracking data for existing and/or newly-created students.
 * This function is idempotent with respect to student creation (unique ids each run)
 * but will append tracking entries each invocation.
 */
export async function seedDemoData(options: SeedDemoOptions = {}): Promise<{ totalStudentsAffected: number; totalEntriesCreated: number; }>{
  const { forExistingStudents = true, createNewStudents = 0, batchesPerStudent = 1 } = options;
  let totalStudentsAffected = 0;
  let totalEntriesCreated = 0;

  try {
    // Optionally create new demo students
    if (createNewStudents > 0) {
      for (let i = 0; i < createNewStudents; i++) {
        const id = `demo-${uuidv4()}`;
        // Minimal student object; adapt to your domain model if needed
        const student = { id, name: `Demo Student ${i + 1}` } as any;
        try {
          // Persist via storage API if available
          if ((dataStorage as any).saveStudent) {
            (dataStorage as any).saveStudent(student);
          } else {
            // Fallback: push into existing list if your storage supports it
            const students = dataStorage.getStudents();
            if (!students.find(s => s.id === id) && (dataStorage as any).setStudents) {
              (dataStorage as any).setStudents([...students, student]);
            }
          }
        } catch (e) {
          logger.warn('[seedDemoData] Unable to save new demo student', { id, error: e });
        }
      }
    }

    const students = dataStorage.getStudents();
    const targetStudents = forExistingStudents ? students : students.slice(-createNewStudents);

    for (const student of targetStudents) {
      totalStudentsAffected++;
      for (let b = 0; b < Math.max(1, batchesPerStudent); b++) {
        // Generator returns an array of TrackingEntry for this student
        const batch = generateMockData(student.id);
        for (const entry of batch) {
          try {
            dataStorage.saveTrackingEntry(entry);
            totalEntriesCreated++;
          } catch (e) {
            logger.error('[seedDemoData] Failed to save tracking entry', { studentId: student.id, error: e });
          }
        }
      }
    }

    logger.info('[seedDemoData] Demo data seeding complete', { totalStudentsAffected, totalEntriesCreated });
  } catch (error) {
    logger.error('[seedDemoData] Seeding failed', { error });
  }

  return { totalStudentsAffected, totalEntriesCreated };
}

/**
 * Module: modelEvaluation
 *
 * Purpose
 * - Track and query model evaluation runs with metrics and cross-validation metadata
 * - Provide stable signatures for data/config to dedupe noisy writes
 *
 * Storage Strategy
 * - Primary: In-memory Map<ModelType, EvaluationRun[]> for fast reads
 * - Optional persistence: IndexedDB (db: 'model-eval', store: 'model-eval', keyPath: 'id')
 * - Fire-and-forget initialization and persistence; gracefully degrades to memory-only if IDB is unavailable
 * - Lightweight TTL+LRU in-process cache to avoid redundant records
 *
 * API (named exports)
 * - recordEvaluation(run: EvaluationRun): void
 * - getEvaluationHistory(modelType?: ModelType): EvaluationRun[]
 * - clearEvaluationHistory(modelType?: ModelType): void
 * - invalidateEvaluationCacheByTag(tag: string): void
 *
 * Notes
 * - Use logger for diagnostics (no console.*)
 * - Align schemaVersion with runtime analytics schema/versioning for forward compatibility
 */

import { logger } from '@/lib/logger';
import { createCacheKey, stableHash } from '@/lib/analytics/cache-key';
import type { ModelType } from '@/lib/mlModels';

// Types
export interface ClassificationMetrics {
  accuracy?: number;
  precision?: number;
  recall?: number;
  f1?: number;
  auc?: number;
  confusionMatrix?: number[][];
  perClass?: Record<string, { precision?: number; recall?: number; f1?: number; support?: number }>;
}

export interface RegressionMetrics {
  mae?: number;
  mse?: number;
  rmse?: number;
  r2?: number;
  mape?: number;
}

export interface Metrics {
  classification?: ClassificationMetrics;
  regression?: RegressionMetrics;
}

export interface CrossValidationMeta {
  strategy: 'k-fold' | 'time-series' | 'holdout' | string;
  folds?: number;        // for k-fold
  horizon?: number;      // for time-series CV
  windowSize?: number;   // for rolling-window strategies
}

export interface EvaluationRun {
  id: string;
  modelType: ModelType;
  timestamp: number; // epoch ms
  dataSignature: string;   // stable fingerprint of training/validation data
  configSignature: string; // stable fingerprint of the configuration used
  taskType: 'classification' | 'regression' | string;
  metrics: Metrics;
  cv?: CrossValidationMeta;
  notes?: string;
  schemaVersion: string; // align with runtime analytics schema/versioning
}

// In-memory store, indexed by modelType for fast filtering
const memoryStore: Map<ModelType, EvaluationRun[]> = new Map();

// Lightweight TTL + LRU cache to skip redundant recomputation/recording
// Aligns with vcQlb4XB*: include input hashes in keys; purge by TTL and LRU; avoid leaks
interface EvalCacheEntry {
  ts: number; // last seen timestamp (epoch ms)
  tags: string[]; // e.g., ['model-eval', modelType, taskType]
}

const EVAL_CACHE_NAMESPACE = 'model-eval';
const DEFAULT_TTL_MS = 5 * 60 * 1000; // 5 minutes
const MAX_CACHE_ENTRIES = 300; // LRU cap to avoid unbounded growth

const evalCache: Map<string, EvalCacheEntry> = new Map();
let evalCacheTTL = DEFAULT_TTL_MS;

function setEvalCacheTTL(ttlMs: number): void {
  evalCacheTTL = Math.max(0, ttlMs);
}

function makeEvalCacheKey(run: Pick<EvaluationRun, 'modelType' | 'taskType' | 'dataSignature' | 'configSignature' | 'schemaVersion'>): string {
  // Include signatures and identifiers; counts are embedded when createCacheKey is used with structured input
  return createCacheKey({
    namespace: EVAL_CACHE_NAMESPACE,
    input: {
      mt: run.modelType,
      task: run.taskType,
      dataSig: run.dataSignature,
      cfgSig: run.configSignature,
    },
    version: run.schemaVersion,
    normalizeArrayOrder: false,
  });
}

function touchEvalCache(key: string, tags: string[]): void {
  // LRU behavior: delete+set to move to insertion order end
  const now = Date.now();
  if (evalCache.has(key)) {
    evalCache.delete(key);
  }
  evalCache.set(key, { ts: now, tags });
  // Enforce LRU cap
  while (evalCache.size > MAX_CACHE_ENTRIES) {
    const oldestKey = evalCache.keys().next().value as string | undefined;
    if (!oldestKey) break;
    evalCache.delete(oldestKey);
  }
}

function isEvalCacheFresh(key: string): boolean {
  const entry = evalCache.get(key);
  if (!entry) return false;
  const age = Date.now() - entry.ts;
  if (age <= evalCacheTTL) return true;
  // expired: drop it eagerly to keep map tidy
  evalCache.delete(key);
  return false;
}

export function invalidateEvaluationCacheByTag(tag: string): void {
  // Remove any cache entries whose tag array contains the tag (exact match)
  for (const [k, v] of evalCache.entries()) {
    if (v.tags.includes(tag)) {
      evalCache.delete(k);
    }
  }
}

// IDB constants
const DB_NAME = 'model-eval';
const STORE_NAME = 'model-eval'; // single store with keyPath 'id'
const DB_VERSION = 1;

let idbAvailable = typeof indexedDB !== 'undefined';
let initStarted = false;

function openDB(): Promise<IDBDatabase> {
  if (!idbAvailable) return Promise.reject(new Error('IndexedDB not available'));
  return new Promise((resolve, reject) => {
    try {
      const request = indexedDB.open(DB_NAME, DB_VERSION);

      request.onupgradeneeded = (event) => {
        try {
          const db = (event.target as IDBOpenDBRequest).result;
          if (!db.objectStoreNames.contains(STORE_NAME)) {
            db.createObjectStore(STORE_NAME, { keyPath: 'id' });
          }
        } catch (err) {
          logger.error('[modelEvaluation] onupgradeneeded failed', err);
        }
      };

      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    } catch (error) {
      reject(error);
    }
  });
}

async function loadAllFromIDB(): Promise<void> {
  if (!idbAvailable) return;

  try {
    const db = await openDB();
    await new Promise<void>((resolve, reject) => {
      try {
        const tx = db.transaction([STORE_NAME], 'readonly');
        const store = tx.objectStore(STORE_NAME);
        const req = store.openCursor();
        req.onerror = () => reject(req.error);
        req.onsuccess = () => {
          const cursor = req.result as IDBCursorWithValue | null;
          if (cursor) {
            const run = cursor.value as EvaluationRun;
            if (run && run.modelType) {
              const list = memoryStore.get(run.modelType) ?? [];
              list.push(run);
              memoryStore.set(run.modelType, list);
            }
            cursor.continue();
          } else {
            resolve();
          }
        };
      } catch (err) {
        reject(err);
      }
    });
    // Normalize: sort each list by timestamp desc
    for (const [mt, list] of memoryStore.entries()) {
      list.sort((a, b) => b.timestamp - a.timestamp);
      memoryStore.set(mt, list);
    }
  } catch (error) {
    idbAvailable = false; // fallback to memory only
    logger.warn('[modelEvaluation] Failed to initialize from IndexedDB. Falling back to memory only.', error);
  }
}

function ensureInit(): void {
  if (initStarted) return;
  initStarted = true;
  // Fire-and-forget initialization; memory store remains usable
  loadAllFromIDB().catch((err) => {
    logger.warn('[modelEvaluation] Initialization load failed', err);
  });
}

// Persistence helpers (fire-and-forget)
async function persistRun(run: EvaluationRun): Promise<void> {
  if (!idbAvailable) return;
  try {
    const db = await openDB();
    await new Promise<void>((resolve, reject) => {
      const tx = db.transaction([STORE_NAME], 'readwrite');
      const store = tx.objectStore(STORE_NAME);
      const req = store.put(run);
      req.onsuccess = () => resolve();
      req.onerror = () => reject(req.error);
    });
  } catch (error) {
    logger.warn('[modelEvaluation] persistRun failed', error);
  }
}

async function deleteRuns(modelType?: ModelType): Promise<void> {
  if (!idbAvailable) return;
  try {
    const db = await openDB();
    await new Promise<void>((resolve, reject) => {
      const tx = db.transaction([STORE_NAME], 'readwrite');
      const store = tx.objectStore(STORE_NAME);

      if (!modelType) {
        // Clear all
        const req = store.clear();
        req.onsuccess = () => resolve();
        req.onerror = () => reject(req.error);
        return;
      }

      // Delete only matching modelType (no index, iterate)
      const cursorReq = store.openCursor();
      cursorReq.onerror = () => reject(cursorReq.error);
      cursorReq.onsuccess = () => {
        const cursor = cursorReq.result as IDBCursorWithValue | null;
        if (!cursor) { resolve(); return; }
        const val = cursor.value as EvaluationRun;
        if (val?.modelType === modelType) {
          (cursor as IDBCursorWithValue).delete();
        }
        cursor.continue();
      };
    });
  } catch (error) {
    logger.warn('[modelEvaluation] deleteRuns failed', error);
  }
}

// Signature utilities (internal)
function computeStableSignature(namespace: string, input: unknown, version?: string): string {
  try {
    return createCacheKey({ namespace, input, version });
  } catch {
    // Fallback minimal stable hash if createCacheKey throws (e.g., cyclic input)
    try {
      const hash = stableHash(input);
      return [namespace, version ? `v${version}` : undefined, hash].filter(Boolean).join(':');
    } catch (err) {
      logger.warn('[modelEvaluation] computeStableSignature failed; returning placeholder', err);
      return `${namespace}:unknown`;
    }
  }
}

// Public API
export function recordEvaluation(run: EvaluationRun): void {
  ensureInit();

  if (!run || !run.id || !run.modelType || !run.timestamp) {
    logger.warn('[modelEvaluation] recordEvaluation called with invalid run payload', { hasRun: !!run });
    return;
  }

  // Ensure signatures are present; attempt to construct placeholders if missing
  if (!run.dataSignature) {
    const placeholder = computeStableSignature('model-eval:data', { id: run.id, ts: run.timestamp, mt: run.modelType }, run.schemaVersion);
    run = { ...run, dataSignature: placeholder };
  }
  if (!run.configSignature) {
    const placeholder = computeStableSignature('model-eval:config', { id: run.id, task: run.taskType, mt: run.modelType }, run.schemaVersion);
    run = { ...run, configSignature: placeholder };
  }

  // Optional de-duplication: skip if an equivalent evaluation was just recorded within TTL
  try {
    const key = makeEvalCacheKey({
      modelType: run.modelType,
      taskType: run.taskType,
      dataSignature: run.dataSignature,
      configSignature: run.configSignature,
      schemaVersion: run.schemaVersion,
    });
    if (isEvalCacheFresh(key)) {
      logger.debug('[modelEvaluation] Skipping redundant evaluation record (cache fresh)', { key, ttlMs: evalCacheTTL });
      return;
    }
    const tags = [EVAL_CACHE_NAMESPACE, String(run.modelType), String(run.taskType)];
    touchEvalCache(key, tags);
  } catch (err) {
    // Never fail the caller on cache errors; just log and proceed
    logger.warn('[modelEvaluation] eval cache key/touch failed; proceeding without skip', err);
  }

  // Insert into memory store (keep newest first)
  const list = memoryStore.get(run.modelType) ?? [];
  const existingIdx = list.findIndex((r) => r.id === run.id);
  if (existingIdx >= 0) {
    list[existingIdx] = run;
  } else {
    list.push(run);
  }
  list.sort((a, b) => b.timestamp - a.timestamp);
  memoryStore.set(run.modelType, list);

  // Persist asynchronously
  void persistRun(run);
}

export function getEvaluationHistory(modelType?: ModelType): EvaluationRun[] {
  ensureInit();
  if (!modelType) {
    // Aggregate all
    const all: EvaluationRun[] = [];
    for (const arr of memoryStore.values()) all.push(...arr);
    return all.sort((a, b) => b.timestamp - a.timestamp);
  }
  const list = memoryStore.get(modelType);
  return (list ? [...list] : []).sort((a, b) => b.timestamp - a.timestamp);
}

export function clearEvaluationHistory(modelType?: ModelType): void {
  ensureInit();
  if (!modelType) {
    memoryStore.clear();
    // Also clear cache entries for model-eval namespace
    invalidateEvaluationCacheByTag(EVAL_CACHE_NAMESPACE);
  } else {
    memoryStore.delete(modelType);
    // Invalidate cache entries for this model type
    invalidateEvaluationCacheByTag(String(modelType));
  }
  // Fire-and-forget persistence clearing
  void deleteRuns(modelType);
}

import { TrackingEntry, EmotionEntry, SensoryEntry, EnvironmentalEntry, Student } from '@/types/student';
import { dataStorage } from '@/lib/dataStorage';
import { analyticsManager } from '@/lib/analyticsManager';
import { logger } from '@/lib/logger';

/**
 * Session metadata for tracking and recovery
 */
export interface SessionMetadata {
  id: string;
  studentId: string;
  startTime: Date;
  endTime?: Date;
  duration: number;
  dataPoints: number;
  status: 'active' | 'paused' | 'completed' | 'abandoned';
  autoSaved: boolean;
  quality: SessionQuality;
}

/**
 * Session quality assessment
 */
export interface SessionQuality {
  score: number; // 0-100
  completeness: number; // 0-100
  consistency: number; // 0-100
  richness: number; // 0-100
  issues: string[];
  recommendations: string[];
}

/**
 * Session recovery data
 */
export interface SessionRecoveryData {
  sessionId: string;
  studentId: string;
  timestamp: Date;
  data: {
    emotions: Omit<EmotionEntry, 'id' | 'timestamp'>[];
    sensoryInputs: Omit<SensoryEntry, 'id' | 'timestamp'>[];
    environmentalData: Omit<EnvironmentalEntry, 'id' | 'timestamp'> | null;
    notes: string;
  };
  metadata: SessionMetadata;
}

/**
 * Session validation rules
 */
export interface SessionValidationRules {
  minEmotions?: number;
  minSensoryInputs?: number;
  requireEnvironmental?: boolean;
  minDuration?: number; // milliseconds
  maxDuration?: number; // milliseconds
  requireNotes?: boolean;
  customValidators?: Array<(session: SessionRecoveryData) => { isValid: boolean; error?: string }>;
}

/**
 * Session statistics
 */
export interface SessionStatistics {
  totalSessions: number;
  completedSessions: number;
  abandonedSessions: number;
  averageDuration: number;
  averageDataPoints: number;
  averageQuality: number;
  mostCommonIssues: Array<{ issue: string; count: number }>;
}

/**
 * SessionManager class
 * Handles session lifecycle, persistence, validation, and recovery
 */
export class SessionManager {
  private static instance: SessionManager;
  private activeSessions: Map<string, SessionRecoveryData> = new Map();
  private sessionHistory: SessionMetadata[] = [];
  private validationRules: SessionValidationRules = {
    minEmotions: 0,
    minSensoryInputs: 0,
    requireEnvironmental: false,
    minDuration: 60000, // 1 minute
    maxDuration: 2 * 60 * 60 * 1000, // 2 hours
    requireNotes: false,
  };

  private constructor() {
    this.loadSessionHistory();
    this.checkForAbandonedSessions();
  }

  /**
   * Get singleton instance
   */
  static getInstance(): SessionManager {
    if (!SessionManager.instance) {
      SessionManager.instance = new SessionManager();
    }
    return SessionManager.instance;
  }

  /**
   * Create a new session
   */
  createSession(studentId: string): string {
    const sessionId = crypto.randomUUID();
    const now = new Date();

    const sessionData: SessionRecoveryData = {
      sessionId,
      studentId,
      timestamp: now,
      data: {
        emotions: [],
        sensoryInputs: [],
        environmentalData: null,
        notes: '',
      },
      metadata: {
        id: sessionId,
        studentId,
        startTime: now,
        duration: 0,
        dataPoints: 0,
        status: 'active',
        autoSaved: false,
        quality: {
          score: 0,
          completeness: 0,
          consistency: 0,
          richness: 0,
          issues: [],
          recommendations: ['Start by recording emotions', 'Add sensory observations'],
        },
      },
    };

    this.activeSessions.set(sessionId, sessionData);
    this.persistSession(sessionData);

    logger.info('[SessionManager] Created new session', { sessionId, studentId });
    return sessionId;
  }

  /**
   * Update session data
   */
  updateSession(
    sessionId: string,
    updates: Partial<{
      emotions: Omit<EmotionEntry, 'id' | 'timestamp'>[];
      sensoryInputs: Omit<SensoryEntry, 'id' | 'timestamp'>[];
      environmentalData: Omit<EnvironmentalEntry, 'id' | 'timestamp'> | null;
      notes: string;
    }>
  ): boolean {
    const session = this.activeSessions.get(sessionId);
    if (!session) {
      logger.warn('[SessionManager] Session not found', { sessionId });
      return false;
    }

    // Update data
    if (updates.emotions) session.data.emotions = updates.emotions;
    if (updates.sensoryInputs) session.data.sensoryInputs = updates.sensoryInputs;
    if (updates.environmentalData !== undefined) session.data.environmentalData = updates.environmentalData;
    if (updates.notes !== undefined) session.data.notes = updates.notes;

    // Update metadata
    session.metadata.duration = Date.now() - session.metadata.startTime.getTime();
    session.metadata.dataPoints = 
      session.data.emotions.length + 
      session.data.sensoryInputs.length + 
      (session.data.environmentalData ? 1 : 0);

    // Assess quality
    session.metadata.quality = this.assessQuality(session);

    // Persist changes
    this.persistSession(session);

    logger.debug('[SessionManager] Updated session', { sessionId, updates });
    return true;
  }

  /**
   * Complete a session
   */
  async completeSession(sessionId: string): Promise<TrackingEntry | null> {
    const session = this.activeSessions.get(sessionId);
    if (!session) {
      logger.warn('[SessionManager] Session not found', { sessionId });
      return null;
    }

    // Validate session
    const validation = this.validateSession(session);
    if (!validation.isValid) {
      logger.warn('[SessionManager] Session validation failed', { 
        sessionId, 
        errors: validation.errors 
      });
      return null;
    }

    // Create tracking entry
    const timestamp = new Date();
    const trackingEntry: TrackingEntry = {
      id: crypto.randomUUID(),
      studentId: session.studentId,
      timestamp,
      emotions: session.data.emotions.map(e => ({
        ...e,
        id: crypto.randomUUID(),
        timestamp,
      })),
      sensoryInputs: session.data.sensoryInputs.map(s => ({
        ...s,
        id: crypto.randomUUID(),
        timestamp,
      })),
      environmentalData: session.data.environmentalData ? {
        ...session.data.environmentalData,
        id: crypto.randomUUID(),
        timestamp,
      } : undefined,
      notes: session.data.notes || undefined,
      generalNotes: session.data.notes || undefined,
      version: 1,
    };

    // Save to storage
    dataStorage.saveTrackingEntry(trackingEntry);

    // Update session metadata
    session.metadata.endTime = timestamp;
    session.metadata.status = 'completed';
    this.sessionHistory.push(session.metadata);
    this.saveSessionHistory();

    // Remove from active sessions
    this.activeSessions.delete(sessionId);
    this.clearPersistedSession(sessionId);

    // Trigger analytics
    const student = dataStorage.getStudentById(session.studentId);
    if (student) {
      await analyticsManager.triggerAnalyticsForStudent(student);
    }

    logger.info('[SessionManager] Completed session', { 
      sessionId, 
      entryId: trackingEntry.id 
    });

    return trackingEntry;
  }

  /**
   * Abandon a session
   */
  abandonSession(sessionId: string): void {
    const session = this.activeSessions.get(sessionId);
    if (!session) return;

    session.metadata.endTime = new Date();
    session.metadata.status = 'abandoned';
    this.sessionHistory.push(session.metadata);
    this.saveSessionHistory();

    this.activeSessions.delete(sessionId);
    this.clearPersistedSession(sessionId);

    logger.info('[SessionManager] Abandoned session', { sessionId });
  }

  /**
   * Pause a session
   */
  pauseSession(sessionId: string): boolean {
    const session = this.activeSessions.get(sessionId);
    if (!session || session.metadata.status !== 'active') return false;

    session.metadata.status = 'paused';
    this.persistSession(session);

    logger.info('[SessionManager] Paused session', { sessionId });
    return true;
  }

  /**
   * Resume a session
   */
  resumeSession(sessionId: string): boolean {
    const session = this.activeSessions.get(sessionId);
    if (!session || session.metadata.status !== 'paused') return false;

    session.metadata.status = 'active';
    this.persistSession(session);

    logger.info('[SessionManager] Resumed session', { sessionId });
    return true;
  }

  /**
   * Recover sessions from localStorage
   */
  recoverSessions(): SessionRecoveryData[] {
    const recovered: SessionRecoveryData[] = [];
    const keys = Object.keys(localStorage).filter(k => 
      k.startsWith('sensoryTracker_session_')
    );

    for (const key of keys) {
      try {
        const data = localStorage.getItem(key);
        if (data) {
          const session = JSON.parse(data) as SessionRecoveryData;
          // Convert date strings back to Date objects
          session.timestamp = new Date(session.timestamp);
          session.metadata.startTime = new Date(session.metadata.startTime);
          if (session.metadata.endTime) {
            session.metadata.endTime = new Date(session.metadata.endTime);
          }

          // Check if session is not too old (24 hours)
          const age = Date.now() - session.metadata.startTime.getTime();
          if (age < 24 * 60 * 60 * 1000) {
            this.activeSessions.set(session.sessionId, session);
            recovered.push(session);
          } else {
            // Mark as abandoned and clean up
            session.metadata.status = 'abandoned';
            this.sessionHistory.push(session.metadata);
            localStorage.removeItem(key);
          }
        }
      } catch (error) {
        logger.error('[SessionManager] Failed to recover session', { key, error });
        localStorage.removeItem(key);
      }
    }

    if (recovered.length > 0) {
      this.saveSessionHistory();
      logger.info('[SessionManager] Recovered sessions', { count: recovered.length });
    }

    return recovered;
  }

  /**
   * Get active sessions for a student
   */
  getActiveSessionsForStudent(studentId: string): SessionRecoveryData[] {
    return Array.from(this.activeSessions.values()).filter(
      session => session.studentId === studentId && session.metadata.status === 'active'
    );
  }

  /**
   * Get session statistics
   */
  getStatistics(studentId?: string): SessionStatistics {
    const relevantHistory = studentId 
      ? this.sessionHistory.filter(s => s.studentId === studentId)
      : this.sessionHistory;

    const completed = relevantHistory.filter(s => s.status === 'completed');
    const abandoned = relevantHistory.filter(s => s.status === 'abandoned');

    // Count issues
    const issueCount = new Map<string, number>();
    relevantHistory.forEach(session => {
      session.quality.issues.forEach(issue => {
        issueCount.set(issue, (issueCount.get(issue) || 0) + 1);
      });
    });

    const mostCommonIssues = Array.from(issueCount.entries())
      .map(([issue, count]) => ({ issue, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 5);

    return {
      totalSessions: relevantHistory.length,
      completedSessions: completed.length,
      abandonedSessions: abandoned.length,
      averageDuration: completed.length > 0
        ? completed.reduce((sum, s) => sum + s.duration, 0) / completed.length
        : 0,
      averageDataPoints: completed.length > 0
        ? completed.reduce((sum, s) => sum + s.dataPoints, 0) / completed.length
        : 0,
      averageQuality: completed.length > 0
        ? completed.reduce((sum, s) => sum + s.quality.score, 0) / completed.length
        : 0,
      mostCommonIssues,
    };
  }

  /**
   * Validate a session
   */
  private validateSession(session: SessionRecoveryData): { isValid: boolean; errors: string[] } {
    const errors: string[] = [];
    const rules = this.validationRules;

    // Check minimum data requirements
    if (rules.minEmotions && session.data.emotions.length < rules.minEmotions) {
      errors.push(`At least ${rules.minEmotions} emotion(s) required`);
    }

    if (rules.minSensoryInputs && session.data.sensoryInputs.length < rules.minSensoryInputs) {
      errors.push(`At least ${rules.minSensoryInputs} sensory input(s) required`);
    }

    if (rules.requireEnvironmental && !session.data.environmentalData) {
      errors.push('Environmental data is required');
    }

    if (rules.requireNotes && !session.data.notes.trim()) {
      errors.push('Notes are required');
    }

    // Check duration
    const duration = Date.now() - session.metadata.startTime.getTime();
    if (rules.minDuration && duration < rules.minDuration) {
      errors.push(`Session too short (minimum ${rules.minDuration / 1000} seconds)`);
    }

    if (rules.maxDuration && duration > rules.maxDuration) {
      errors.push(`Session too long (maximum ${rules.maxDuration / 1000} seconds)`);
    }

    // Custom validators
    if (rules.customValidators) {
      for (const validator of rules.customValidators) {
        const result = validator(session);
        if (!result.isValid && result.error) {
          errors.push(result.error);
        }
      }
    }

    // Require at least some data
    const totalData = session.data.emotions.length + session.data.sensoryInputs.length;
    if (totalData === 0) {
      errors.push('Session must contain at least one data point');
    }

    return { isValid: errors.length === 0, errors };
  }

  /**
   * Assess session quality
   */
  private assessQuality(session: SessionRecoveryData): SessionQuality {
    const issues: string[] = [];
    const recommendations: string[] = [];

    // Calculate completeness
    let completeness = 0;
    if (session.data.emotions.length > 0) completeness += 30;
    if (session.data.sensoryInputs.length > 0) completeness += 30;
    if (session.data.environmentalData) completeness += 20;
    if (session.data.notes.length > 20) completeness += 20;

    // Calculate consistency
    const consistency = this.calculateConsistency(session);

    // Calculate richness
    const richness = this.calculateRichness(session);

    // Overall score
    const score = (completeness * 0.4 + consistency * 0.3 + richness * 0.3);

    // Generate issues and recommendations
    if (session.data.emotions.length === 0) {
      issues.push('No emotions recorded');
      recommendations.push('Record at least one emotion');
    }

    if (session.data.sensoryInputs.length === 0) {
      issues.push('No sensory inputs recorded');
      recommendations.push('Add sensory observations');
    }

    if (!session.data.environmentalData) {
      issues.push('No environmental data');
      recommendations.push('Record environmental conditions');
    }

    if (session.data.notes.length < 10) {
      issues.push('Minimal or no notes');
      recommendations.push('Add descriptive notes about the session');
    }

    const duration = Date.now() - session.metadata.startTime.getTime();
    if (duration < 2 * 60 * 1000) { // Less than 2 minutes
      issues.push('Very short session');
      recommendations.push('Spend more time observing and recording');
    }

    return {
      score,
      completeness,
      consistency,
      richness,
      issues,
      recommendations,
    };
  }

  /**
   * Calculate consistency score
   */
  private calculateConsistency(session: SessionRecoveryData): number {
    // Check for variety in emotion intensities
    const emotionIntensities = session.data.emotions.map(e => e.intensity);
    const uniqueIntensities = new Set(emotionIntensities).size;
    const emotionConsistency = uniqueIntensities > 1 ? 
      (uniqueIntensities / Math.max(emotionIntensities.length, 1)) * 100 : 0;

    // Check for variety in sensory responses
    const sensoryResponses = session.data.sensoryInputs.map(s => s.response);
    const uniqueResponses = new Set(sensoryResponses).size;
    const sensoryConsistency = uniqueResponses > 1 ? 
      (uniqueResponses / Math.max(sensoryResponses.length, 1)) * 100 : 0;

    return (emotionConsistency + sensoryConsistency) / 2;
  }

  /**
   * Calculate richness score
   */
  private calculateRichness(session: SessionRecoveryData): number {
    let richness = 0;

    // Emotion diversity
    const emotionTypes = new Set(session.data.emotions.map(e => e.emotion)).size;
    richness += Math.min(emotionTypes * 10, 30);

    // Sensory diversity
    const sensoryTypes = new Set(session.data.sensoryInputs.map(s => s.sensoryType || s.type)).size;
    richness += Math.min(sensoryTypes * 10, 30);

    // Notes length
    if (session.data.notes.length > 50) richness += 20;
    else if (session.data.notes.length > 20) richness += 10;

    // Environmental data completeness
    if (session.data.environmentalData) {
      const env = session.data.environmentalData as any;
      if (env.classroom || env.roomConditions) richness += 20;
    }

    return Math.min(richness, 100);
  }

  /**
   * Persist session to localStorage
   */
  private persistSession(session: SessionRecoveryData): void {
    try {
      const key = `sensoryTracker_session_${session.sessionId}`;
      localStorage.setItem(key, JSON.stringify(session));
    } catch (error) {
      logger.error('[SessionManager] Failed to persist session', { 
        sessionId: session.sessionId, 
        error 
      });
    }
  }

  /**
   * Clear persisted session
   */
  private clearPersistedSession(sessionId: string): void {
    const key = `sensoryTracker_session_${sessionId}`;
    localStorage.removeItem(key);
  }

  /**
   * Load session history
   */
  private loadSessionHistory(): void {
    try {
      const data = localStorage.getItem('sensoryTracker_sessionHistory');
      if (data) {
        this.sessionHistory = JSON.parse(data).map((s: any) => ({
          ...s,
          startTime: new Date(s.startTime),
          endTime: s.endTime ? new Date(s.endTime) : undefined,
        }));
      }
    } catch (error) {
      logger.error('[SessionManager] Failed to load session history', { error });
      this.sessionHistory = [];
    }
  }

  /**
   * Save session history
   */
  private saveSessionHistory(): void {
    try {
      localStorage.setItem('sensoryTracker_sessionHistory', JSON.stringify(this.sessionHistory));
    } catch (error) {
      logger.error('[SessionManager] Failed to save session history', { error });
    }
  }

  /**
   * Check for abandoned sessions on startup
   */
  private checkForAbandonedSessions(): void {
    const recovered = this.recoverSessions();
    const now = Date.now();

    recovered.forEach(session => {
      const age = now - session.metadata.startTime.getTime();
      // Auto-abandon sessions older than 2 hours
      if (age > 2 * 60 * 60 * 1000) {
        this.abandonSession(session.sessionId);
      }
    });
  }

  /**
   * Update validation rules
   */
  updateValidationRules(rules: Partial<SessionValidationRules>): void {
    this.validationRules = { ...this.validationRules, ...rules };
    logger.info('[SessionManager] Updated validation rules', { rules });
  }

  /**
   * Get validation rules
   */
  getValidationRules(): SessionValidationRules {
    return { ...this.validationRules };
  }

  /**
   * Clear all session data (for testing/reset)
   */
  clearAllSessions(): void {
    this.activeSessions.clear();
    this.sessionHistory = [];

    // Clear from localStorage
    const keys = Object.keys(localStorage).filter(k => 
      k.startsWith('sensoryTracker_session_') || 
      k === 'sensoryTracker_sessionHistory'
    );
    keys.forEach(key => localStorage.removeItem(key));

    logger.info('[SessionManager] Cleared all sessions');
  }
}

// Export singleton instance
export const sessionManager = SessionManager.getInstance();

/**
 * Utility functions for managing localStorage
 */
import { logger } from '@/lib/logger';

export const storageUtils = {
  /**
   * Check available storage space
   */
  getStorageInfo(): { used: number; available: boolean } {
    let used = 0;
    for (const key in localStorage) {
      if (localStorage.hasOwnProperty(key)) {
        used += localStorage[key].length + key.length;
      }
    }
    return {
      used,
      available: used < 5000000 // 5MB approximate limit
    };
  },

  /**
   * Clear old tracking data to free up space
   */
  clearOldTrackingData(daysToKeep: number = 30): void {
    try {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - daysToKeep);

      // Clear old tracking entries
      const entriesKey = 'sensoryTracker_entries';
      const entriesData = localStorage.getItem(entriesKey);
      if (entriesData) {
        const entries = JSON.parse(entriesData);
        const filteredEntries = entries.filter((entry: { timestamp: string | Date }) => {
          const entryDate = new Date(entry.timestamp);
          return entryDate > cutoffDate;
        });
        localStorage.setItem(entriesKey, JSON.stringify(filteredEntries));
      }

      // Clear old alerts
      const alertsKey = 'sensoryTracker_alerts';
      const alertsData = localStorage.getItem(alertsKey);
      if (alertsData) {
        const alerts = JSON.parse(alertsData);
        const filteredAlerts = alerts.filter((alert: { timestamp: string | Date }) => {
          const alertDate = new Date(alert.timestamp);
          return alertDate > cutoffDate;
        });
        localStorage.setItem(alertsKey, JSON.stringify(filteredAlerts));
      }
    } catch (error) {
      logger.error('Error clearing old data:', error);
    }
  },

  /**
   * Compress data before storing
   */
  compressData(data: unknown): string {
    // Remove unnecessary whitespace from JSON
    return JSON.stringify(data);
  },

  /**
   * Safe storage with quota handling
   */
  safeSetItem(key: string, value: string): void {
    try {
      localStorage.setItem(key, value);
    } catch (e) {
      if (e instanceof DOMException && (
        e.code === 22 || // Quota exceeded
        e.code === 1014 || // NS_ERROR_DOM_QUOTA_REACHED (Firefox)
        e.name === 'QuotaExceededError' ||
        e.name === 'NS_ERROR_DOM_QUOTA_REACHED'
      )) {
        // Try to clear old data and retry
        this.clearOldTrackingData();

        try {
          localStorage.setItem(key, value);
        } catch (retryError) {
          // If still failing, clear all non-essential data
          this.clearNonEssentialData();
          localStorage.setItem(key, value);
        }
      } else {
        throw e;
      }
    }
  },

  /**
   * Clear non-essential data when storage is full
   */
  clearNonEssentialData(): void {
    const essentialKeys = [
      'sensoryTracker_students',
      'sensoryTracker_goals',
      'sensoryTracker_dataVersion'
    ];

    for (const key in localStorage) {
      if (localStorage.hasOwnProperty(key) && 
          key.startsWith('sensoryTracker_') && 
          !essentialKeys.includes(key)) {
        localStorage.removeItem(key);
      }
    }
  }
};

import { describe, it, expect } from 'vitest';
import React from 'react';
import { LoadingSpinner } from '@/components/LoadingSpinner';
import { benchmarkComponentRender, calculateRenderImprovement, trainToyModel } from './performanceTester';

// Mock the LoadingSpinner for testing
const MockLoadingSpinner = ({ size = 'md', message = 'Loading...' }: any) => (
  React.createElement('div', { 
    'data-testid': 'loading-spinner',
    'data-size': size 
  }, message)
);

describe('performanceTester', () => {
  it('completes toy workload under threshold', () => {
    const { avg, timeMs } = trainToyModel(10000);
    // Basic sanity check on result and crude perf threshold
    expect(Number.isFinite(avg)).toBe(true);

    // CI threshold gate: fail if slower than 1500ms
    const THRESHOLD_MS = Number(process.env.CI_PERF_THRESHOLD_MS || 1500);
    expect(timeMs).toBeLessThan(THRESHOLD_MS);
  });

  describe('Component Render Benchmarks', () => {
    it('benchmarks LoadingSpinner render performance', async () => {
      const result = await benchmarkComponentRender(
        'LoadingSpinner',
        () => React.createElement(MockLoadingSpinner, { size: 'md', message: 'Test' }),
        { iterations: 50, timeoutMs: 3000 }
      );

      expect(result.componentName).toBe('LoadingSpinner');
      expect(result.averageRenderTime).toBeGreaterThan(0);
      expect(result.totalRenders).toBe(50);
      expect(result.minRenderTime).toBeLessThanOrEqual(result.averageRenderTime);
      expect(result.maxRenderTime).toBeGreaterThanOrEqual(result.averageRenderTime);

      // Performance threshold: average render should be under 10ms
      expect(result.averageRenderTime).toBeLessThan(10);
    });

    it('tests rerender performance with prop variations', async () => {
      const propVariations = [
        { size: 'sm', message: 'Small' },
        { size: 'lg', message: 'Large' },
        { size: 'md', message: 'Medium' }
      ];

      const result = await benchmarkComponentRender(
        'LoadingSpinner-Rerenders',
        () => React.createElement(MockLoadingSpinner, { size: 'md', message: 'Initial' }),
        { 
          iterations: 30, 
          testRerenders: true, 
          propVariations,
          timeoutMs: 3000
        }
      );

      expect(result.rerenderCount).toBe(30);
      expect(result.totalRenders).toBeGreaterThan(30); // Initial renders + rerenders
    });

    it('calculates render improvement correctly', () => {
      const before = {
        componentName: 'TestComponent',
        averageRenderTime: 10,
        minRenderTime: 8,
        maxRenderTime: 15,
        totalRenders: 100
      };

      const after = {
        componentName: 'TestComponent',
        averageRenderTime: 6,
        minRenderTime: 5,
        maxRenderTime: 9,
        totalRenders: 100
      };

      const improvement = calculateRenderImprovement(before, after);

      expect(improvement.avgRenderTimeReduction).toBe(4);
      expect(improvement.improvementPercentage).toBe(40);
      expect(improvement.significant).toBe(true);
    });

    it('identifies non-significant improvements', () => {
      const before = {
        componentName: 'TestComponent',
        averageRenderTime: 10,
        minRenderTime: 8,
        maxRenderTime: 15,
        totalRenders: 100
      };

      const after = {
        componentName: 'TestComponent',
        averageRenderTime: 9.5,
        minRenderTime: 8,
        maxRenderTime: 14,
        totalRenders: 100
      };

      const improvement = calculateRenderImprovement(before, after);

      expect(improvement.improvementPercentage).toBe(5);
      expect(improvement.significant).toBe(false);
    });
  });
});

/** @file Performance testing utilities for component renders and analytics */
import React, { ReactElement } from 'react';
import { render } from '@testing-library/react';
import { performance } from 'perf_hooks';

/**
 * Performance measurement utilities for React component render optimization
 */
export interface RenderBenchmarkResult {
  componentName: string;
  averageRenderTime: number;
  minRenderTime: number;
  maxRenderTime: number;
  totalRenders: number;
  rerenderCount?: number;
}

export interface ComponentBenchmarkOptions {
  /** Number of render iterations to run */
  iterations?: number;
  /** Whether to include rerender testing */
  testRerenders?: boolean;
  /** Props to vary between renders (for rerender testing) */
  propVariations?: any[];
  /** Timeout per iteration in ms */
  timeoutMs?: number;
}

/**
 * Benchmark component render performance
 * @param componentName - Display name for the component
 * @param componentFactory - Function that returns the component to render
 * @param options - Benchmark configuration options
 */
export async function benchmarkComponentRender(
  componentName: string,
  componentFactory: () => ReactElement,
  options: ComponentBenchmarkOptions = {}
): Promise<RenderBenchmarkResult> {
  const {
    iterations = 100,
    testRerenders = false,
    propVariations = [],
    timeoutMs = 5000
  } = options;

  const renderTimes: number[] = [];
  let rerenderCount = 0;

  const timeout = new Promise((_, reject) => 
    setTimeout(() => reject(new Error(`Benchmark timeout after ${timeoutMs}ms`)), timeoutMs)
  );

  const benchmark = async () => {
    for (let i = 0; i < iterations; i++) {
      const startTime = performance.now();

      const { rerender, unmount } = render(componentFactory());

      const renderTime = performance.now() - startTime;
      renderTimes.push(renderTime);

      // Test rerenders if requested
      if (testRerenders && propVariations.length > 0) {
        const rerenderStartTime = performance.now();
        const variation = propVariations[i % propVariations.length];

        // Create new component with varied props
        rerender(React.cloneElement(componentFactory(), variation));

        const rerenderTime = performance.now() - rerenderStartTime;
        renderTimes.push(rerenderTime);
        rerenderCount++;
      }

      unmount();

      // Yield to event loop
      if (i % 10 === 0) {
        await new Promise(resolve => setTimeout(resolve, 0));
      }
    }
  };

  try {
    await Promise.race([benchmark(), timeout]);
  } catch (error) {
    throw new Error(`Benchmark failed for ${componentName}: ${error}`);
  }

  const averageRenderTime = renderTimes.reduce((sum, time) => sum + time, 0) / renderTimes.length;
  const minRenderTime = Math.min(...renderTimes);
  const maxRenderTime = Math.max(...renderTimes);

  return {
    componentName,
    averageRenderTime,
    minRenderTime,
    maxRenderTime,
    totalRenders: renderTimes.length,
    rerenderCount: testRerenders ? rerenderCount : undefined
  };
}

/**
 * Measure render hotspots by comparing before/after optimization results
 */
export function calculateRenderImprovement(
  before: RenderBenchmarkResult,
  after: RenderBenchmarkResult
): {
  improvementPercentage: number;
  avgRenderTimeReduction: number;
  significant: boolean;
} {
  const avgRenderTimeReduction = before.averageRenderTime - after.averageRenderTime;
  const improvementPercentage = (avgRenderTimeReduction / before.averageRenderTime) * 100;

  // Consider improvement significant if > 10% reduction
  const significant = improvementPercentage > 10;

  return {
    improvementPercentage,
    avgRenderTimeReduction,
    significant
  };
}

/**
 * Legacy performance test function for analytics workloads
 */
export function trainToyModel(iterations = 5000): { avg: number; timeMs: number } {
  const start = performance.now();
  let sum = 0;
  for (let i = 1; i <= iterations; i++) {
    sum += Math.sin(i) * Math.cos(i / 2);
  }
  const timeMs = performance.now() - start;
  const avg = sum / iterations;
  return { avg, timeMs };
}

/**
 * Module: crossValidation
 *
 * Purpose
 * - K-Fold and Time-Series cross-validation utilities for TensorFlow.js models
 *
 * Time-Series Folds
 * - Strategies: 'rolling' (fixed-size sliding window) and 'expanding' (growing window)
 * - Preserve temporal order; do NOT shuffle time-series data
 * - Optional gap and horizon to separate train and validation windows for realistic forecasting
 *
 * Leakage Avoidance
 * - Always create a fresh, untrained model per fold
 * - Fit preprocessing only on training folds; apply to validation folds (caller responsibility)
 */

import * as tf from '@tensorflow/tfjs';
import { TrainingData, ValidationResults, ValidationMetrics, TrainingFold, CrossValidationConfig } from '../../types/ml';

/**
 * Strategy for time-series cross-validation.
 */
export type TimeSeriesStrategy = 'rolling' | 'expanding';

/**
 * Configuration for time-series fold generation.
 * - rolling: fixed-size training window that moves forward; validate on the next horizon
 * - expanding: training window grows from an initial windowSize; validate on the next horizon
 */
export interface TimeSeriesFoldConfig {
  strategy: TimeSeriesStrategy;
  /** Required for both strategies. For expanding, this is the initial training window size. */
  windowSize: number;
  /** Number of time steps to forecast/validate ahead for each fold. Default: 1 */
  horizon?: number;
  /** Optional gap between the end of training window and start of validation window. Default: 0 */
  gap?: number;
  /** Optional max number of folds to generate. If omitted, generate as many as fit within data length. */
  folds?: number;
}

/**
 * Configuration for time-series model validation.
 */
export interface TimeSeriesValidationConfig extends TimeSeriesFoldConfig {
  /** Task type determines which metrics to compute. */
  taskType: 'classification' | 'regression';
}

/** Regression metrics for time-series evaluation. */
export interface RegressionMetrics {
  mse: number;
  rmse: number;
  mae: number;
  mape: number; // expressed in [0,1]; multiply by 100 for percentage
}

/** Per-fold metrics for time-series validation. */
export interface TimeSeriesFoldMetrics {
  classification?: ValidationMetrics;
  regression?: RegressionMetrics;
}

/** Aggregated results for time-series validation. */
export interface TimeSeriesValidationResults {
  foldMetrics: TimeSeriesFoldMetrics[];
  average?: TimeSeriesFoldMetrics;
  stdDeviation?: TimeSeriesFoldMetrics;
}

/**
 * Handles the K-Fold and Time-Series Cross-Validation processes for a TensorFlow.js model.
 */
export class CrossValidator {
  /**
   * Orchestrates the entire k-fold cross-validation process.
   * @param createModel - Factory returning a fresh, untrained model instance.
   * @param data - The full training dataset.
   * @param config - The configuration for the cross-validation process.
   * @returns The aggregated results of the cross-validation.
   */
  public async validateModel(
    createModel: () => tf.Sequential,
    data: TrainingData,
    config: CrossValidationConfig
  ): Promise<ValidationResults> {
    const folds = await this.generateFolds(data, config.folds, config.stratified);
    const foldMetrics: ValidationMetrics[] = [];

    for (const fold of folds) {
      const { trainIndices, validationIndices } = fold;

      const trainFeatures = tf.gather(data.features, trainIndices);
      const trainLabels = tf.gather(data.labels, trainIndices);
      const valFeatures = tf.gather(data.features, validationIndices);
      const valLabels = tf.gather(data.labels, validationIndices);

      const model = createModel(); // Create a fresh model for each fold

      await model.fit(trainFeatures, trainLabels, {
        // Training parameters should be sourced from runtime ML model config (Task 8),
        // e.g., getRuntimeAnalyticsConfig().ml.models[*].training.{epochs,batchSize}.
        epochs: 5, // configurable via runtime config
        batchSize: 32, // configurable via runtime config
        verbose: 0,
      });

      const predictionsTensor = model.predict(valFeatures) as tf.Tensor;
      const predictions = Array.from((await predictionsTensor.argMax(1).data()) as unknown as Iterable<number>);
      const actuals = Array.from((await valLabels.argMax(1).data()) as unknown as Iterable<number>);

      const metrics = this.calculateValidationMetrics(predictions, actuals);
      foldMetrics.push(metrics);

      tf.dispose([trainFeatures, trainLabels, valFeatures, valLabels, predictionsTensor]);
      model.dispose(); // Correct way to dispose a model
    }

    // Aggregate results
    const averageMetrics = this.aggregateMetrics(foldMetrics, (arr) => tf.mean(arr).dataSync()[0]);
    const stdDeviationMetrics = this.aggregateMetrics(foldMetrics, (arr) => tf.moments(arr).variance.sqrt().dataSync()[0]);

    // Aggregate confusion matrices across folds (binary only) and recompute PR/F1/accuracy
    let overallConfusionMatrix: { tp: number; tn: number; fp: number; fn: number } | undefined;
    let overallPRF1: { precision: number; recall: number; f1Score: number; accuracy: number } | undefined;

    const matrices = foldMetrics
      .map((m) => m.confusionMatrix)
      .filter((cm): cm is { tp: number; tn: number; fp: number; fn: number } => !!cm);

    if (matrices.length > 0) {
      const sum = matrices.reduce(
        (acc, cm) => ({
          tp: acc.tp + cm.tp,
          tn: acc.tn + cm.tn,
          fp: acc.fp + cm.fp,
          fn: acc.fn + cm.fn,
        }),
        { tp: 0, tn: 0, fp: 0, fn: 0 }
      );

      overallConfusionMatrix = sum;
      const precDen = sum.tp + sum.fp;
      const recDen = sum.tp + sum.fn;
      const precision = precDen === 0 ? 0 : sum.tp / precDen;
      const recall = recDen === 0 ? 0 : sum.tp / recDen;
      const f1Den = precision + recall;
      const f1Score = f1Den === 0 ? 0 : (2 * precision * recall) / f1Den;
      const total = sum.tp + sum.tn + sum.fp + sum.fn;
      const accuracy = total === 0 ? 0 : (sum.tp + sum.tn) / total;
      overallPRF1 = { precision, recall, f1Score, accuracy };
    }

    return { foldMetrics, averageMetrics, stdDeviationMetrics, overallConfusionMatrix, overallPRF1 };
  }

  /**
   * Splits the dataset into k-folds for training and validation.
   * Can perform stratified sampling if configured.
   * @param data - The training dataset.
   * @param k - The number of folds.
   * @param stratified - Whether to keep class distribution per fold.
   * @returns An array of TrainingFold objects.
   */
  public async generateFolds(
    data: TrainingData,
    k: number,
    stratified: boolean = false
  ): Promise<TrainingFold[]> {
    const numSamples = data.features.shape[0];
    const indices = tf.util.createShuffledIndices(numSamples);

    if (!stratified) {
      const foldSize = Math.floor(numSamples / k);
      const folds: TrainingFold[] = [];

      for (let i = 0; i < k; i++) {
        const validationIndices = Array.from(indices.slice(i * foldSize, (i + 1) * foldSize)) as number[];
        const trainIndices = (Array.from(indices) as number[]).filter((index) => !validationIndices.includes(index));
        folds.push({ trainIndices, validationIndices });
      }
      return folds;
    }

    // Stratified K-Fold
    const labels = await data.labels.data();
    const classIndices: { [key: number]: number[] } = {};

    for (let i = 0; i < numSamples; i++) {
      const label = labels[i];
      if (!classIndices[label]) {
        classIndices[label] = [];
      }
      classIndices[label].push(i);
    }

    const folds: TrainingFold[] = Array.from({ length: k }, () => ({ trainIndices: [], validationIndices: [] }));

    for (const label in classIndices) {
      const indicesForClass = tf.util.createShuffledIndices(classIndices[label].length);
      const shuffledClassIndices = classIndices[label].map((_, i) => classIndices[label][indicesForClass[i]]);

      for (let i = 0; i < shuffledClassIndices.length; i++) {
        const foldIndex = i % k;
        folds[foldIndex].validationIndices.push(shuffledClassIndices[i]);
      }
    }

    for (let i = 0; i < k; i++) {
      const validationIndices = folds[i].validationIndices;
      const allIndices = Array.from({ length: numSamples }, (_, idx) => idx);
      folds[i].trainIndices = allIndices.filter((index) => !validationIndices.includes(index));
    }

    return folds;
  }

  /**
   * Generate time-series folds using rolling or expanding window strategies.
   * The order of data is preserved and shuffle is not allowed.
   * @param data - Sequential dataset (time-ordered). Only the number of samples is used here.
   * @param config - Time-series fold generation config.
   */
  public generateTimeSeriesFolds(data: TrainingData, config: TimeSeriesFoldConfig): TrainingFold[] {
    const n = data.features.shape[0];
    const windowSize = config.windowSize;
    const horizon = config.horizon ?? 1;
    const gap = config.gap ?? 0;

    if (windowSize <= 0) throw new Error('windowSize must be > 0');
    if (horizon <= 0) throw new Error('horizon must be > 0');
    if (gap < 0) throw new Error('gap must be >= 0');

    const folds: TrainingFold[] = [];

    // Compute the maximum starting index for training window such that train and validation are within bounds
    // Train: [start, start + windowLen)
    // Gap:   [start + windowLen, start + windowLen + gap)
    // Val:   [start + windowLen + gap, start + windowLen + gap + horizon)
    const windowLen = config.strategy === 'rolling' ? windowSize : undefined;

    const maxStartExclusive = (start: number, trainLen: number) =>
      start + trainLen + gap + horizon <= n;

    let generated = 0;
    if (config.strategy === 'rolling') {
      for (let start = 0; maxStartExclusive(start, windowSize); start++) {
        const trainStart = start;
        const trainEnd = start + windowSize; // exclusive
        const valStart = trainEnd + gap;
        const valEnd = valStart + horizon; // exclusive

        const trainIndices = Array.from({ length: windowSize }, (_, i) => trainStart + i);
        const validationIndices = Array.from({ length: horizon }, (_, i) => valStart + i);
        folds.push({ trainIndices, validationIndices });

        generated++;
        if (config.folds && generated >= config.folds) break;
      }
    } else {
      // expanding
      for (let start = 0; start < n; start++) {
        // For expanding, we grow the training window from [0, currentEnd)
        const trainLen = windowSize + start;
        if (!maxStartExclusive(0, trainLen)) break;
        const trainStart = 0;
        const trainEnd = trainLen; // exclusive
        const valStart = trainEnd + gap;
        const valEnd = valStart + horizon;

        const trainIndices = Array.from({ length: trainLen }, (_, i) => trainStart + i);
        const validationIndices = Array.from({ length: horizon }, (_, i) => valStart + i);
        folds.push({ trainIndices, validationIndices });

        generated++;
        if (config.folds && generated >= config.folds) break;
      }
    }

    return folds;
  }

  /**
   * Validate a time-series model using rolling or expanding CV.
   * For classification, computes accuracy, precision, recall, f1 (existing path).
   * For regression, computes MSE, RMSE, MAE, MAPE.
   * @param createModel - Factory returning a fresh, untrained model instance per fold.
   * @param data - Sequential dataset.
   * @param config - Time-series validation configuration including taskType.
   */
  public async validateTimeSeriesModel(
    createModel: () => tf.Sequential,
    data: TrainingData,
    config: TimeSeriesValidationConfig
  ): Promise<TimeSeriesValidationResults> {
    const folds = this.generateTimeSeriesFolds(data, config);
    const foldMetrics: TimeSeriesFoldMetrics[] = [];

    for (const fold of folds) {
      const { trainIndices, validationIndices } = fold;

      const trainFeatures = tf.gather(data.features, trainIndices);
      const trainLabels = tf.gather(data.labels, trainIndices);
      const valFeatures = tf.gather(data.features, validationIndices);
      const valLabels = tf.gather(data.labels, validationIndices);

      const model = createModel();
      await model.fit(trainFeatures, trainLabels, {
        epochs: 5,
        batchSize: 32,
        verbose: 0,
      });

      const yPredTensor = model.predict(valFeatures) as tf.Tensor;

      if (config.taskType === 'classification') {
        const preds = Array.from((await yPredTensor.argMax(1).data()) as unknown as Iterable<number>);
        const actuals = Array.from((await valLabels.argMax(1).data()) as unknown as Iterable<number>);
        const classification = this.calculateValidationMetrics(preds, actuals);
        foldMetrics.push({ classification });
      } else {
        // regression
        // Ensure tensors are 1D vectors
        const yPred = Array.from((await yPredTensor.flatten().data()) as Iterable<number>);
        const yTrue = Array.from((await valLabels.flatten().data()) as Iterable<number>);
        const regression = this.calculateRegressionMetrics(yPred, yTrue);
        foldMetrics.push({ regression });
      }

      tf.dispose([trainFeatures, trainLabels, valFeatures, valLabels, yPredTensor]);
      model.dispose();
    }

    // Aggregate metrics
    const average = this.averageTimeSeriesMetrics(foldMetrics);
    const stdDeviation = this.stdTimeSeriesMetrics(foldMetrics);

    return { foldMetrics, average, stdDeviation };
  }

  /**
   * Calculates a set of performance metrics from predictions and actual values.
   * @param predictions - The model's predictions.
   * @param actuals - The true labels.
   * @returns A ValidationMetrics object.
   */
  public calculateValidationMetrics(predictions: number[], actuals: number[]): ValidationMetrics {
    if (predictions.length !== actuals.length) {
      throw new Error('Predictions and actuals must have the same length.');
    }

    const n = actuals.length;
    const correct = predictions.filter((p, i) => p === actuals[i]).length;
    const accuracy = correct / n;

    // Binary metrics when labels are {0,1}
    const uniqueLabels = Array.from(new Set([...predictions, ...actuals]));
    if (uniqueLabels.every((l) => l === 0 || l === 1)) {
      let tp = 0,
        tn = 0,
        fp = 0,
        fn = 0;
      for (let i = 0; i < n; i++) {
        const p = predictions[i];
        const a = actuals[i];
        if (p === 1 && a === 1) tp++;
        else if (p === 0 && a === 0) tn++;
        else if (p === 1 && a === 0) fp++;
        else if (p === 0 && a === 1) fn++;
      }
      const precision = tp + fp === 0 ? 0 : tp / (tp + fp);
      const recall = tp + fn === 0 ? 0 : tp / (tp + fn);
      const f1Score = precision + recall === 0 ? 0 : (2 * precision * recall) / (precision + recall);
      return { accuracy, precision, recall, f1Score, confusionMatrix: { tp, tn, fp, fn } };
    }

    // Multi-class fallback: accuracy only for now
    return { accuracy };
  }

  /** Compute regression error metrics. */
  private calculateRegressionMetrics(predictions: number[], actuals: number[]): RegressionMetrics {
    if (predictions.length !== actuals.length) {
      throw new Error('Predictions and actuals must have the same length.');
    }
    const n = predictions.length;
    if (n === 0) {
      return { mse: 0, rmse: 0, mae: 0, mape: 0 };
    }

    let se = 0; // squared error sum
    let ae = 0; // absolute error sum
    let apeSum = 0; // absolute percentage error sum
    let mapeCount = 0; // count of valid denominators

    for (let i = 0; i < n; i++) {
      const err = predictions[i] - actuals[i];
      se += err * err;
      ae += Math.abs(err);
      const denom = Math.abs(actuals[i]);
      if (denom > 0) {
        apeSum += Math.abs(err) / denom;
        mapeCount += 1;
      }
    }

    const mse = se / n;
    const rmse = Math.sqrt(mse);
    const mae = ae / n;
    const mape = mapeCount === 0 ? 0 : apeSum / mapeCount;

    return { mse, rmse, mae, mape };
  }

  private aggregateMetrics(foldMetrics: ValidationMetrics[], aggFn: (arr: number[]) => number): ValidationMetrics {
    const aggregated: ValidationMetrics = {};
    const metricKeys = Object.keys(foldMetrics[0]) as (keyof ValidationMetrics)[];

    for (const key of metricKeys) {
      if (key === 'confusionMatrix') {
        // Do not average confusion matrices; keep them at fold level if needed.
        continue;
      }
      const values = foldMetrics
        .map((m) => m[key])
        .filter((v): v is number => typeof v === 'number');
      if (values.length > 0) {
        aggregated[key] = aggFn(values);
      }
    }
    return aggregated;
  }

  /** Average time-series metrics across folds. */
  private averageTimeSeriesMetrics(foldMetrics: TimeSeriesFoldMetrics[]): TimeSeriesFoldMetrics | undefined {
    if (foldMetrics.length === 0) return undefined;

    const avg: TimeSeriesFoldMetrics = {};

    // Classification
    const cls = foldMetrics
      .map((f) => f.classification)
      .filter((m): m is ValidationMetrics => !!m);
    if (cls.length > 0) {
      avg.classification = this.aggregateMetrics(cls, (arr) => tf.mean(arr).dataSync()[0]);
    }

    // Regression
    const regs = foldMetrics
      .map((f) => f.regression)
      .filter((m): m is RegressionMetrics => !!m);
    if (regs.length > 0) {
      avg.regression = this.aggregateRegression(regs, (arr) => tf.mean(arr).dataSync()[0]);
    }

    return avg;
  }

  /** Std deviation of time-series metrics across folds. */
  private stdTimeSeriesMetrics(foldMetrics: TimeSeriesFoldMetrics[]): TimeSeriesFoldMetrics | undefined {
    if (foldMetrics.length === 0) return undefined;

    const std: TimeSeriesFoldMetrics = {};

    const cls = foldMetrics
      .map((f) => f.classification)
      .filter((m): m is ValidationMetrics => !!m);
    if (cls.length > 0) {
      std.classification = this.aggregateMetrics(
        cls,
        (arr) => tf.moments(arr).variance.sqrt().dataSync()[0]
      );
    }

    const regs = foldMetrics
      .map((f) => f.regression)
      .filter((m): m is RegressionMetrics => !!m);
    if (regs.length > 0) {
      std.regression = this.aggregateRegression(
        regs,
        (arr) => tf.moments(arr).variance.sqrt().dataSync()[0]
      );
    }

    return std;
  }

  private aggregateRegression(
    foldMetrics: RegressionMetrics[],
    aggFn: (arr: number[]) => number
  ): RegressionMetrics {
    const keys: (keyof RegressionMetrics)[] = ['mse', 'rmse', 'mae', 'mape'];
    const out: RegressionMetrics = { mse: 0, rmse: 0, mae: 0, mape: 0 };
    for (const key of keys) {
      const values = foldMetrics.map((m) => m[key]);
      out[key] = aggFn(values);
    }
    return out;
  }
}

/*
 * Data Leakage Detection Utilities
 * --------------------------------
 * Provides a DataLeakageDetector with:
 * - Train/Test split integrity validation (overlap, duplicates)
 * - Temporal leakage detection for time-series (global and per-entity)
 * - Feature contamination checks (target in features, future-labelled columns, high correlation, near-identity)
 * - Centralized logging of detected risks
 * - Strict vs permissive modes controlling error throwing vs warnings
 *
 * Usage example:
 * const detector = new DataLeakageDetector({ mode: 'strict', temporal: { timeColumn: 'timestamp', entityColumn: 'studentId' } });
 * const report = detector.analyze(records, { trainIndex, testIndex, targetKey: 'label' });
 * // In strict mode, high-risk issues will throw; otherwise, report.issues contains details.
 */

import { logger } from '@/lib/logger';
import { pearsonCorrelation } from '@/lib/statistics';

export type StrictnessMode = 'strict' | 'permissive';

export interface DataLeakageDetectorConfig {
  mode?: StrictnessMode;
  thresholds?: {
    highCorrelation?: number; // correlation considered suspicious
    nearIdentityFraction?: number; // fraction of rows equal feature==target considered near-identity
  };
  temporal?: {
    timeColumn: string; // required to enable temporal checks
    entityColumn?: string; // optional: per-entity temporal validation when present
    allowTrainAfterTest?: boolean; // if true, only warn when train has future relative to test
  };
}

export type Severity = 'low' | 'medium' | 'high';

export type LeakageType =
  | 'SPLIT_OVERLAP'
  | 'SPLIT_DUPLICATES'
  | 'TEMPORAL_GLOBAL'
  | 'TEMPORAL_PER_ENTITY'
  | 'TARGET_IN_FEATURES'
  | 'FUTURE_NAMED_FEATURE'
  | 'HIGH_CORRELATION_FEATURE'
  | 'NEAR_IDENTITY_FEATURE';

export interface LeakageIssue {
  type: LeakageType;
  severity: Severity;
  message: string;
  details?: Record<string, unknown>;
}

export interface LeakageReport {
  issues: LeakageIssue[];
  hasHighRisk: boolean;
  summary: string[];
}

export interface AnalyzeOptions {
  // row-wise records for a tabular dataset
  records: Array<Record<string, unknown>>;
  // zero-based indices of train/test rows (or any unique ids mapping into records index)
  trainIndex: number[];
  testIndex: number[];
  // target (label) column key; if omitted, feature contamination checks are limited to name-based heuristics
  targetKey?: string;
  // subset of feature keys to check. If omitted, all non-target keys present in records[0] are used.
  featureKeys?: string[];
}

function unique<T>(arr: T[]): T[] {
  return Array.from(new Set(arr));
}

function toTime(v: unknown): number | null {
  if (v == null) return null;
  if (v instanceof Date) return v.getTime();
  const n = typeof v === 'number' ? v : Date.parse(String(v));
  return Number.isFinite(n) ? n : null;
}

function isFiniteNumber(v: unknown): v is number {
  return typeof v === 'number' && Number.isFinite(v);
}

function getNumericColumn(records: Array<Record<string, unknown>>, key: string): number[] {
  const out: number[] = new Array(records.length);
  for (let i = 0; i < records.length; i++) {
    const val = records[i]?.[key];
    out[i] = isFiniteNumber(val) ? (val as number) : Number.NaN;
  }
  return out;
}

function fractionEqual(a: unknown[], b: unknown[]): number {
  const n = Math.min(a.length, b.length);
  if (n === 0) return 0;
  let eq = 0, total = 0;
  for (let i = 0; i < n; i++) {
    const av = a[i];
    const bv = b[i];
    if (av === undefined || bv === undefined) continue;
    total++;
    if (av === bv) eq++;
  }
  if (total === 0) return 0;
  return eq / total;
}

function pickFeatureKeys(records: Array<Record<string, unknown>>, targetKey?: string, provided?: string[]): string[] {
  if (Array.isArray(provided) && provided.length > 0) return provided;
  const first = records[0] ?? {};
  const keys = Object.keys(first);
  return keys.filter(k => k !== targetKey);
}

function containsLeakNamePattern(name: string): boolean {
  const lowered = name.toLowerCase();
  // Patterns that often indicate leakage (future knowledge or explicit labels)
  const patterns = [
    'target', 'label', 'outcome', 'y', 'groundtruth', 'ground_truth', 'true_', 'future', 't+1', 't+2', 'next', 'leak', 'post', 'after_event'
  ];
  return patterns.some(p => lowered.includes(p));
}

export class DataLeakageDetector {
  private config: Required<DataLeakageDetectorConfig>;

  constructor(config?: DataLeakageDetectorConfig) {
    this.config = {
      mode: config?.mode ?? 'permissive',
      thresholds: {
        highCorrelation: config?.thresholds?.highCorrelation ?? 0.95,
        nearIdentityFraction: config?.thresholds?.nearIdentityFraction ?? 0.98,
      },
      temporal: {
        timeColumn: config?.temporal?.timeColumn ?? ('timestamp' as string),
        entityColumn: config?.temporal?.entityColumn,
        allowTrainAfterTest: config?.temporal?.allowTrainAfterTest ?? false,
      },
    };
  }

  public analyze(records: AnalyzeOptions['records'], opts: Omit<AnalyzeOptions, 'records'>): LeakageReport {
    const issues: LeakageIssue[] = [];

    // 1) Train/Test split validation
    issues.push(...this.validateTrainTestSplit(opts.trainIndex, opts.testIndex));

    // 2) Temporal leakage detection (if time column present in records)
    if (this.config.temporal?.timeColumn) {
      issues.push(...this.detectTemporalLeakage(records, opts.trainIndex, opts.testIndex));
    }

    // 3) Feature contamination checks
    issues.push(...this.checkFeatureContamination(records, opts.trainIndex, opts.testIndex, opts.targetKey, opts.featureKeys));

    // Logging and strict/permissive behavior
    const summary = issues.map(i => `${i.severity.toUpperCase()} ${i.type}  ${i.message}`);
    for (const issue of issues) {
      const payload = { type: issue.type, severity: issue.severity, details: issue.details };
      if (issue.severity === 'high') {
        logger.warn('[DataLeakageDetector] High-risk leakage detected', payload);
      } else if (issue.severity === 'medium') {
        logger.info('[DataLeakageDetector] Potential leakage risk', payload);
      } else {
        logger.debug('[DataLeakageDetector] Informational leakage note', payload);
      }
    }

    const hasHighRisk = issues.some(i => i.severity === 'high');
    if (this.config.mode === 'strict' && hasHighRisk) {
      // Throwing in strict mode to fail fast
      const err = new Error(`High-risk data leakage detected: \n- ${summary.join('\n- ')}`);
      logger.error('[DataLeakageDetector] Strict mode abort due to leakage', { summary });
      throw err;
    }

    return { issues, hasHighRisk, summary };
  }

  public validateTrainTestSplit(trainIndex: number[], testIndex: number[]): LeakageIssue[] {
    const issues: LeakageIssue[] = [];
    const train = unique(trainIndex);
    const test = unique(testIndex);

    // Overlap check
    const testSet = new Set(test);
    const overlap: number[] = [];
    for (const i of train) {
      if (testSet.has(i)) overlap.push(i);
    }
    if (overlap.length > 0) {
      issues.push({
        type: 'SPLIT_OVERLAP',
        severity: 'high',
        message: `Train/Test overlap detected for ${overlap.length} samples. Splits must be disjoint.`,
        details: { overlapCount: overlap.length, sampleIndices: overlap.slice(0, 25) },
      });
    }

    // Duplicates within splits
    if (train.length !== trainIndex.length || test.length !== testIndex.length) {
      const dupTrain = trainIndex.length - train.length;
      const dupTest = testIndex.length - test.length;
      if (dupTrain > 0) {
        issues.push({
          type: 'SPLIT_DUPLICATES',
          severity: 'medium',
          message: `Duplicate entries in train split (${dupTrain}).`,
          details: { duplicateCount: dupTrain, split: 'train' },
        });
      }
      if (dupTest > 0) {
        issues.push({
          type: 'SPLIT_DUPLICATES',
          severity: 'medium',
          message: `Duplicate entries in test split (${dupTest}).`,
          details: { duplicateCount: dupTest, split: 'test' },
        });
      }
    }

    return issues;
  }

  public detectTemporalLeakage(
    records: Array<Record<string, unknown>>,
    trainIndex: number[],
    testIndex: number[]
  ): LeakageIssue[] {
    const issues: LeakageIssue[] = [];
    const timeKey = this.config.temporal.timeColumn;
    const entityKey = this.config.temporal.entityColumn;

    // Global chronology check: max(train.time) should be <= min(test.time)
    const trainTimes: number[] = [];
    const testTimes: number[] = [];
    for (const i of trainIndex) {
      const t = toTime(records[i]?.[timeKey]);
      if (t != null) trainTimes.push(t);
    }
    for (const i of testIndex) {
      const t = toTime(records[i]?.[timeKey]);
      if (t != null) testTimes.push(t);
    }
    if (trainTimes.length > 0 && testTimes.length > 0) {
      const maxTrain = Math.max(...trainTimes);
      const minTest = Math.min(...testTimes);
      if (maxTrain > minTest) {
        issues.push({
          type: 'TEMPORAL_GLOBAL',
          severity: this.config.temporal.allowTrainAfterTest ? 'medium' : 'high',
          message: `Temporal leakage: training data extends (${new Date(maxTrain).toISOString()}) beyond earliest test timestamp (${new Date(minTest).toISOString()}).`,
          details: { maxTrain, minTest },
        });
      }
    }

    // Per-entity chronology check when entity column available
    if (entityKey) {
      const perEntityTrain: Record<string, number> = {};
      const perEntityTest: Record<string, number> = {};

      for (const i of trainIndex) {
        const row = records[i];
        const ent = String(row?.[entityKey] ?? '');
        const t = toTime(row?.[timeKey]);
        if (ent && t != null) perEntityTrain[ent] = Math.max(perEntityTrain[ent] ?? Number.NEGATIVE_INFINITY, t);
      }
      for (const i of testIndex) {
        const row = records[i];
        const ent = String(row?.[entityKey] ?? '');
        const t = toTime(row?.[timeKey]);
        if (ent && t != null) perEntityTest[ent] = Math.min(perEntityTest[ent] ?? Number.POSITIVE_INFINITY, t);
      }

      const offenders: Array<{ entity: string; maxTrain: number; minTest: number }> = [];
      for (const ent of Object.keys(perEntityTrain)) {
        if (perEntityTest[ent] == null) continue; // entity not in test
        const maxT = perEntityTrain[ent];
        const minT = perEntityTest[ent];
        if (maxT > minT) offenders.push({ entity: ent, maxTrain: maxT, minTest: minT });
      }

      if (offenders.length > 0) {
        issues.push({
          type: 'TEMPORAL_PER_ENTITY',
          severity: this.config.temporal.allowTrainAfterTest ? 'medium' : 'high',
          message: `Temporal leakage per-entity: ${offenders.length} entity(ies) have training data after their test start.`,
          details: { offenders: offenders.slice(0, 25) },
        });
      }
    }

    return issues;
  }

  public checkFeatureContamination(
    records: Array<Record<string, unknown>>,
    trainIndex: number[],
    testIndex: number[],
    targetKey?: string,
    featureKeys?: string[]
  ): LeakageIssue[] {
    const issues: LeakageIssue[] = [];
    const features = pickFeatureKeys(records, targetKey, featureKeys);

    // Name-based heuristics
    for (const f of features) {
      if (containsLeakNamePattern(f)) {
        issues.push({
          type: 'FUTURE_NAMED_FEATURE',
          severity: 'high',
          message: `Feature "${f}" name suggests potential leakage (future/label-related).` ,
          details: { feature: f },
        });
      }
    }

    if (!targetKey) return issues; // cannot proceed with target-based checks

    // Target present as a feature
    if (features.includes(targetKey)) {
      issues.push({
        type: 'TARGET_IN_FEATURES',
        severity: 'high',
        message: `Target key "${targetKey}" is present among features.`,
        details: { targetKey },
      });
    }

    // Compute correlations on train subset (numeric only)
    const yAll = records.map(r => r?.[targetKey]);
    const yNum = getNumericColumn(records, targetKey);

    // Extract feature arrays
    for (const f of features) {
      const xAll = records.map(r => r?.[f]);

      // Near-identity check across all rows (handles categorical as well)
      const fracEq = fractionEqual(xAll, yAll);
      if (fracEq >= this.config.thresholds.nearIdentityFraction) {
        issues.push({
          type: 'NEAR_IDENTITY_FEATURE',
          severity: 'high',
          message: `Feature \"${f}\" matches target in ~${Math.round(fracEq * 100)}% of rows.`,
          details: { feature: f, fractionEqual: fracEq },
        });
        // Do not short-circuit; still compute correlation so reports can include both signals when applicable
      }

      // Correlation (only when both columns are numeric)
      const xNum = getNumericColumn(records, f);
      const trainMask = new Set(trainIndex);
      const xTrain: number[] = [];
      const yTrain: number[] = [];
      for (let i = 0; i < records.length; i++) {
        if (trainMask.has(i)) {
          const xv = xNum[i];
          const yv = yNum[i];
          if (Number.isFinite(xv) && Number.isFinite(yv)) {
            xTrain.push(xv);
            yTrain.push(yv);
          }
        }
      }

      if (xTrain.length >= 3 && yTrain.length >= 3) {
        const r = Math.abs(pearsonCorrelation(xTrain, yTrain));
        if (Number.isFinite(r) && r >= (this.config.thresholds.highCorrelation ?? 0.95)) {
          issues.push({
            type: 'HIGH_CORRELATION_FEATURE',
            severity: containsLeakNamePattern(f) ? 'high' : 'medium',
            message: `Feature "${f}" has very high correlation with target on training data (|r|=${r.toFixed(3)}).`,
            details: { feature: f, correlation: r },
          });
        }
      }
    }

    return issues;
  }
}

export default DataLeakageDetector;

