        { name: 'single', data: generateLargeDataset(1) },
        { name: 'duplicates', data: Array(1000).fill(generateLargeDataset(1)[0]) },
        { name: 'sparse', data: generateLargeDataset(100).filter((_, i) => i % 10 === 0) },
      ];

      for (const { name, data } of edgeCases) {
        const { duration } = await measurePerformance(
          `edge-${name}`,
          () => manager.analyzeStudent(`edge-${name}`, data)
        );

        // Edge cases should be handled quickly
        expect(duration).toBeLessThan(100);
      }
    });
  });

  describe('Benchmark Suite', () => {
    it('comprehensive benchmark', async () => {
      const results = {
        smallDataset: 0,
        mediumDataset: 0,
        largeDataset: 0,
        patternDetection: 0,
        concurrentProcessing: 0,
        cachePerformance: 0,
      };

      // Small dataset
      const small = await measurePerformance(
        'benchmark-small',
        () => manager.analyzeStudent('bench-1', generateLargeDataset(100))
      );
      results.smallDataset = small.duration;

      // Medium dataset
      const medium = await measurePerformance(
        'benchmark-medium',
        () => manager.analyzeStudent('bench-2', generateLargeDataset(1000))
      );
      results.mediumDataset = medium.duration;

      // Large dataset
      const large = await measurePerformance(
        'benchmark-large',
        () => manager.analyzeStudent('bench-3', generateLargeDataset(5000))
      );
      results.largeDataset = large.duration;

      // Pattern detection
      const patterns = await measurePerformance(
        'benchmark-patterns',
        () => enhancedPatternAnalysis.detectPatterns(generateLargeDataset(2000))
      );
      results.patternDetection = patterns.duration;

      // Concurrent processing
      const concurrent = await measurePerformance(
        'benchmark-concurrent',
        () => Promise.all(
          Array.from({ length: 5 }, (_, i) => 
            manager.analyzeStudent(`concurrent-${i}`, generateLargeDataset(500))
          )
        )
      );
      results.concurrentProcessing = concurrent.duration;

      // Cache performance
      const cacheData = generateLargeDataset(1000);
      await manager.analyzeStudent('cache-test', cacheData);
      const cache = await measurePerformance(
        'benchmark-cache',
        () => manager.analyzeStudent('cache-test', cacheData)
      );
      results.cachePerformance = cache.duration;

      // Log benchmark results
      console.log('Performance Benchmark Results:', results);

      // Verify all benchmarks pass thresholds
      expect(results.smallDataset).toBeLessThan(CI_PERF_THRESHOLD_MS / 10);
      expect(results.mediumDataset).toBeLessThan(CI_PERF_THRESHOLD_MS / 2);
      expect(results.largeDataset).toBeLessThan(CI_PERF_THRESHOLD_MS);
      expect(results.patternDetection).toBeLessThan(CI_PERF_THRESHOLD_MS);
      expect(results.concurrentProcessing).toBeLessThan(CI_PERF_THRESHOLD_MS * 2);
      expect(results.cachePerformance).toBeLessThan(10);
    });
  });
});

import { PreprocessingPipeline } from '@/lib/PreprocessingPipeline';
import { AnyStepConfig, SerializedPipeline } from '@/types/preprocessing';

function sampleData() {
  return [
    { a: 1, b: 'x', keep: 1 },
    { a: 3, b: 'y', keep: 2 },
    { a: 5, b: 'x', keep: 3 },
  ];
}

describe('PreprocessingPipeline', () => {
  test('fit/transform determinism and schema metadata', () => {
    const cfg: AnyStepConfig[] = [
      { name: 'clean', kind: 'cleaning', dropAllNullColumns: true },
      { name: 'scale', kind: 'scaling', method: 'standard' },
      { name: 'encode', kind: 'encoding', oneHotMaxCategories: 10 },
      { name: 'poly', kind: 'feature_engineering', polynomialDegree: 2 },
    ];
    const pipe = new PreprocessingPipeline(cfg);
    const fitted = pipe.fit(sampleData());
    const meta = pipe.getMetadata();
    expect(meta?.pipelineVersion).toBeDefined();
    expect(Array.isArray(meta?.dataSchema.columns)).toBe(true);
    const again = pipe.transform(sampleData());
    // transform after fit should be consistent
    expect(again).toEqual(fitted);
  });

  test('serialization and deserialization', () => {
    const cfg: AnyStepConfig[] = [
      { name: 'clean', kind: 'cleaning', dropAllNullColumns: true },
      { name: 'scale', kind: 'scaling' },
      { name: 'encode', kind: 'encoding', oneHotMaxCategories: 10 },
    ];
    const pipe = new PreprocessingPipeline(cfg);
    pipe.fit(sampleData());
    const payload = pipe.toJSON();

    const restored = PreprocessingPipeline.fromJSON(payload as SerializedPipeline);
    const transformedOriginal = pipe.transform(sampleData());
    const transformedRestored = restored.transform(sampleData());

    expect(transformedRestored).toEqual(transformedOriginal);
  });

  test('edge cases: empty data, single sample, extreme values', () => {
    const cfg: AnyStepConfig[] = [
      { name: 'scale', kind: 'scaling' },
      { name: 'encode', kind: 'encoding', oneHotMaxCategories: 10 },
      { name: 'poly', kind: 'feature_engineering', polynomialDegree: 2 },
    ];
    const pipe = new PreprocessingPipeline(cfg);

    // Empty
    const empty: any[] = [];
    const emptyOut = pipe.fit(empty);
    expect(emptyOut).toEqual([]);

    // Single sample
    const single = [{ a: 1, b: 'x' }];
    pipe.fit(single);
    const singleOut = pipe.transform(single);
    expect(Array.isArray(singleOut)).toBe(true);

    // Extreme values
    const extreme = [
      { a: 1e12, b: 'big' },
      { a: -1e12, b: 'small' },
    ];
    pipe.fit(extreme);
    const out = pipe.transform(extreme);
    expect(out.length).toBe(2);
  });

  test('consistency between training (fit) and inference (transform) sequences', () => {
    const cfg: AnyStepConfig[] = [
      { name: 'clean', kind: 'cleaning', dropAllNullColumns: true },
      { name: 'scale', kind: 'scaling' },
      { name: 'encode', kind: 'encoding', oneHotMaxCategories: 10 },
    ];
    const pipe = new PreprocessingPipeline(cfg);
    const trainTransformed = pipe.fit(sampleData());
    const inferTransformed = pipe.transform(sampleData());
    expect(inferTransformed).toEqual(trainTransformed);
  });
});

